INSERT INTO enzyme VALUES ( 'EC 1.1.1.1','Alcohol dehydrogenase','Aldehyde reductase','Zinc or iron.','Acts on primary or secondary alcohols or hemi-acetalss with verybroad specificity; however the enzyme oxidizes methanol much morepoorly than ethanol.The animal, but not the yeast, enzyme acts also on cyclic secondaryalcohols.','(1) An alcohol + NAD(+) = an aldehyde or ketone + NADH.(2) A secondary alcohol + NAD(+) = a ketone + NADH.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.1.1.2','Alcohol dehydrogenase (NADP(+))','Aldehyde reductase (NADPH)','Zinc.','Some members of this group oxidize only primary alcohols; others actalso on secondary alcohols.May be identical with EC 1.1.1.19, EC 1.1.1.33 and EC 1.1.1.55.Re-specific with respect to NADPH.','An alcohol + NADP(+) = an aldehyde + NADPH.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.1.1.3','Homoserine dehydrogenase','NULL','NULL','The enzyme from Saccharomyces cerevisiae acts most rapidly withNAD(+); the Neurospora enzyme with NADP(+).The enzyme from Escherichia coli is a multifunctional protein, whichalso catalyzes the reaction of EC 2.7.2.4.','L-homoserine + NAD(P)(+) = L-aspartate 4-semialdehyde + NAD(P)H.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.1.1.4','(R,R)-butanediol dehydrogenase','(R)-2,3-butanediol dehydrogenase','NULL','Also converts diacetyl into acetoin with NADH as reductant.','(R,R)-butane-2,3-diol + NAD(+) = (R)-acetoin + NADH.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.1.1.5','Transferred entry: 1','NULL','NULL','NULL','NULL', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.1.1.6','Glycerol dehydrogenase','NAD-linked glycerol dehydrogenase','NULL','Also acts on 1,2-propanediol.','Glycerol + NAD(+) = glycerone + NADH.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.1.1.7','Propanediol-phosphate dehydrogenase','NULL','NULL','NULL','Propane-1,2-diol 1-phosphate + NAD(+) = hydroxyacetone phosphate + NADH.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.1.1.8','Glycerol-3-phosphate dehydrogenase (NAD(+))','Alpha-glycerol phosphate dehydrogenase (NAD)','NULL','Also acts on propane-1,2-diol phosphate and glycerone sulfate (butwith a much lower affinity).','sn-glycerol 3-phosphate + NAD(+) = glycerone phosphate + NADH.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.1.1.9','D-xylulose reductase','Xylitol dehydrogenase','NULL','Also acts as an L-erythrulose reductase.','Xylitol + NAD(+) = D-xylulose + NADH.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.1.1.10','L-xylulose reductase','NULL','NULL','NULL','Xylitol + NADP(+) = L-xylulose + NADPH.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.1.1.11','D-arabinitol 4-dehydrogenase','NULL','NULL','NULL','D-arabinitol + NAD(+) = D-xylulose + NADH.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.1.1.12','L-arabinitol 4-dehydrogenase','NULL','NULL','NULL','L-arabinitol + NAD(+) = L-xylulose + NADH.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.1.1.13','L-arabinitol 2-dehydrogenase','L-arabinitol 2-dehydrogenase (ribulose forming)','NULL','NULL','L-arabinitol + NAD(+) = L-ribulose + NADH.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.1.1.14','L-iditol 2-dehydrogenase','Glucitol dehydrogenase','NULL','This enzyme is widely distributed and has been described in archaea,bacteria, yeast, plants and animals.It acts on a number of sugar alcohols, including (but not limited to)L-iditol, D-glucitol, D-xylitol, and D-galactitol.Enzymes from different organisms or tissues display differentsubstrate specificity.The enzyme is specific to NAD(+) and can not use NADP(+).','L-iditol + NAD(+) = L-sorbose + NADH.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.1.1.15','D-iditol 2-dehydrogenase','NULL','NULL','Also converts xylitol into L-xylulose and L-glucitol into L-fructose.','D-iditol + NAD(+) = D-sorbose + NADH.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.1.1.16','Galactitol 2-dehydrogenase','NULL','NULL','Also converts other alditols containing an L-threo-configurationadjacent to a primary alcohol group into the corresponding sugars.','Galactitol + NAD(+) = D-tagatose + NADH.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.1.1.17','Mannitol-1-phosphate 5-dehydrogenase','NULL','NULL','NULL','D-mannitol 1-phosphate + NAD(+) = D-fructose 6-phosphate + NADH.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.1.1.18','Inositol 2-dehydrogenase','Myo-inositol 2-dehydrogenase','NULL','NULL','Myo-inositol + NAD(+) = 2,4,6/3,5-pentahydroxycyclohexanone + NADH.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.1.1.19','Glucuronate reductase','Aldehyde reductase','NULL','Also reduces D-galacturonate.May be identical with EC 1.1.1.2.','L-gulonate + NADP(+) = D-glucuronate + NADPH.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.1.1.20','Glucuronolactone reductase','NULL','NULL','NULL','L-gulono-1,4-lactone + NADP(+) = D-glucurono-3,6-lactone + NADPH.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.1.1.21','Aldehyde reductase','Aldose reductase','NULL','Wide specificity.Formerly EC 1.1.1.139.','Alditol + NAD(P)(+) = aldose + NAD(P)H.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.1.1.22','UDP-glucose 6-dehydrogenase','NULL','NULL','Also acts on UDP-2-deoxyglucose.','UDP-glucose + 2 NAD(+) + H(2)O = UDP-glucuronate + 2 NADH.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.1.1.23','Histidinol dehydrogenase','NULL','NULL','Also oxidizes L-histidinal.The Neurospora enzyme also catalyzes the reactions of EC 3.5.4.19 andEC 3.6.1.31.','L-histidinol + H(2)O + 2 NAD(+) = L-histidine + 2 NADH.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.1.1.24','Quinate dehydrogenase','Quinate 5-dehydrogenase','NULL','The enzyme is specific for quinate as substrate; phenylpyruvate,phenylalanine, cinnamate and shikimate will not act as substrates.NAD(+) cannot be replaced by NADP(+).','Quinate + NAD(+) = 3-dehydroquinate + NADH.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.1.1.25','Shikimate dehydrogenase','5-dehydroshikimate reductase','NULL','NAD(+) cannot replace NADP(+).In higher organisms, this enzyme forms part of a multienzyme complexwith EC 4.2.1.10.','Shikimate + NADP(+) = 3-dehydroshikimate + NADPH.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.1.1.26','Glyoxylate reductase','Glycolate reductase','NULL','Reduces glyoxylate to glycolate or hydroxypyruvate to D-glycerate.','Glycolate + NAD(+) = glyoxylate + NADH.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.1.1.27','L-lactate dehydrogenase','L-lactic acid dehydrogenase','NULL','Also oxidizes other (S)-2-hydroxymonocarboxylic acids.NADP(+) acts, more slowly, with the animal, but not the bacterial,enzyme.','(S)-lactate + NAD(+) = pyruvate + NADH.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.1.1.28','D-lactate dehydrogenase','D-lactic acid dehydrogenase','NULL','NULL','(R)-lactate + NAD(+) = pyruvate + NADH.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.1.1.29','Glycerate dehydrogenase','Hydroxypyruvate dehydrogenase','NULL','NULL','D-glycerate + NAD(+) = hydroxypyruvate + NADH.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.1.1.30','3-hydroxybutyrate dehydrogenase','D-beta-hydroxybutyrate dehydrogenase','NULL','Also oxidizes other 3-hydroxymonocarboxylic acids.','(R)-3-hydroxybutanoate + NAD(+) = acetoacetate + NADH.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.1.1.31','3-hydroxyisobutyrate dehydrogenase','NULL','NULL','NULL','3-hydroxy-2-methylpropanoate + NAD(+) = 2-methyl-3-oxopropanoate + NADH.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.1.1.32','Mevaldate reductase','NULL','NULL','NULL','(R)-mevalonate + NAD(+) = mevaldate + NADH.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.1.1.33','Mevaldate reductase (NADPH)','NULL','NULL','May be identical with EC 1.1.1.2.','(R)-mevalonate + NADP(+) = mevaldate + NADPH.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.1.1.34','Hydroxymethylglutaryl-CoA reductase (NADPH)','3-hydroxy-3-methylglutaryl-coenzyme A reductase','NULL','The enzyme is inactivated by EC 2.7.11.31 and reactivated byEC 3.1.3.47.','(R)-mevalonate + CoA + 2 NADP(+) = (S)-3-hydroxy-3-methylglutaryl-CoA +2 NADPH.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.1.1.35','3-hydroxyacyl-CoA dehydrogenase','Beta-hydroxyacyl dehydrogenase','NULL','Also oxidizes S-3-hydroxyacyl-N-acylthioethanolamine and S-3-hydroxyacylhydrolipoate.Some enzymes act, more slowly, with NADP(+).Broad specificity to acyl chain-length (cf. EC 1.1.1.211).','(S)-3-hydroxyacyl-CoA + NAD(+) = 3-oxoacyl-CoA + NADH.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.1.1.36','Acetoacetyl-CoA reductase','NULL','NULL','NULL','(R)-3-hydroxyacyl-CoA + NADP(+) = 3-oxoacyl-CoA + NADPH.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.1.1.37','Malate dehydrogenase','Malic dehydrogenase','NULL','Also oxidizes some other 2-hydroxydicarboxylic acids.','(S)-malate + NAD(+) = oxaloacetate + NADH.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.1.1.38','Malate dehydrogenase (oxaloacetate-decarboxylating)','Malic enzyme','NULL','Unlike EC 1.1.1.39, this enzyme can also decarboxylate oxaloacetate,cf. EC 1.1.1.40.','(1) (S)-malate + NAD(+) = pyruvate + CO(2) + NADH.(2) Oxaloacetate = pyruvate + CO(2).', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.1.1.39','Malate dehydrogenase (decarboxylating)','Malic enzyme','NULL','Does not decarboxylate added oxaloacetate.','(S)-malate + NAD(+) = pyruvate + CO(2) + NADH.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.1.1.40','Malate dehydrogenase (oxaloacetate-decarboxylating) (NADP(+))','Malic enzyme','NULL','The enzyme catalyzes the oxidative decarboxylation of (S)-malate inthe presence of NADP(+) and divalent metal ions, and decarboxylationof oxaloacetate, cf. EC 1.1.1.38 and EC 1.1.1.39.','(1) (S)-malate + NADP(+) = pyruvate + CO(2) + NADPH.(2) Oxaloacetate = pyruvate + CO(2).', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.1.1.41','Isocitrate dehydrogenase (NAD(+))','Beta-ketoglutaric-isocitric carboxylase','Manganese or magnesium.','Unlike EC 1.1.1.42, oxalosuccinate cannot be used as a substrate.In eukaryotes, isocitrate dehydrogenase exists in two forms: anNAD(+)-linked enzyme found only in mitochondria and displayingallosteric properties, and a non-allosteric, NADP(+)-linked enzymethat is found in both mitochondria and cytoplasm.The enzyme from some species can also use NADP(+) but much moreslowly.','Isocitrate + NAD(+) = 2-oxoglutarate + CO(2) + NADH.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.1.1.42','Isocitrate dehydrogenase (NADP(+))','Dual-cofactor-specific isocitrate dehydrogenase','Manganese or magnesium.','Unlike EC 1.1.1.41, oxalosuccinate can be used as a substrate.In eukaryotes, isocitrate dehydrogenase exists in two forms: anNAD(+)-linked enzyme found only in mitochondria and displayingallosteric properties, and a non-allosteric, NADP(+)-linked enzymethat is found in both mitochondria and cytoplasm.The enzyme from some species can also use NAD(+) but much moreslowly.','Isocitrate + NADP(+) = 2-oxoglutarate + CO(2) + NADPH.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.1.1.43','Phosphogluconate 2-dehydrogenase','6-phosphogluconate 2-dehydrogenase','NULL','NULL','6-phospho-D-gluconate + NAD(P)(+) = 6-phospho-2-dehydro-D-gluconate +NAD(P)H.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.1.1.44','Phosphogluconate dehydrogenase (NADP(+)-dependent, decarboxylating)','6-phosphogluconic carboxylase','NULL','The enzyme participates in the oxidative branch of the pentosephosphate pathway, whose main purpose is to produce NADPH and pentosefor biosynthetic reactions.Highly specific for NADP(+).Cf. EC 1.1.1.343.','6-phospho-D-gluconate + NADP(+) = D-ribulose 5-phosphate + CO(2) + NADPH.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.1.1.45','L-gulonate 3-dehydrogenase','L-3-aldonate dehydrogenase','NULL','Also oxidizes other L-3-hydroxyacids.','L-gulonate + NAD(+) = 3-dehydro-L-gulonate + NADH.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.1.1.46','L-arabinose 1-dehydrogenase','NULL','NULL','NULL','L-arabinose + NAD(+) = L-arabinono-1,4-lactone + NADH.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.1.1.47','Glucose 1-dehydrogenase (NAD(P)(+))','NULL','NULL','This enzyme has similar activity with either NAD(+) or NADP(+).Cf. EC 1.1.1.118 and EC 1.1.1.119.','Beta-D-glucose + NAD(P)(+) = D-glucono-1,5-lactone + NAD(P)H.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.1.1.48','D-galactose 1-dehydrogenase','NULL','NULL','This enzyme is part of the De Ley-Doudoroff pathway, which is used bysome bacteria during growth on D-galactose.','D-galactose + NAD(+) = D-galactono-1,4-lactone + NADH.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.1.1.49','Glucose-6-phosphate dehydrogenase (NADP(+))','G6PD','NULL','The enzyme catalyzes a step of the pentose phosphate pathway.The enzyme is specific for NADP(+), cf. EC 1.1.1.363.','D-glucose 6-phosphate + NADP(+) = 6-phospho-D-glucono-1,5-lactone +NADPH.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.1.1.50','3-alpha-hydroxysteroid 3-dehydrogenase (Si-specific)','3-alpha-HSD','NULL','The enzyme acts on androsterone and other 3-alpha-hydroxysteroids andon 9-, 11- and 15-hydroxyprostaglandin.Si-specific with respect to NAD(+) or NADP(+) (cf. EC 1.1.1.213).','A 3-alpha-hydroxysteroid + NAD(P)(+) = a 3-oxosteroid + NAD(P)H.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.1.1.51','3(or 17)-beta-hydroxysteroid dehydrogenase','NULL','NULL','Also acts on other 3-beta- or 17-beta-hydroxysteroids (cf.EC 1.1.1.209).','Testosterone + NAD(P)(+) = androst-4-ene-3,17-dione + NAD(P)H.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.1.1.52','3-alpha-hydroxycholanate dehydrogenase','NULL','NULL','Also acts on other 3-alpha-hydroxysteroids with an acidic side-chain.','Lithocholate + NAD(+) = 3-oxo-5-beta-cholan-24-oate + NADH.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.1.1.53','3-alpha-(or 20-beta)-hydroxysteroid dehydrogenase','(R)-20-hydroxysteroid dehydrogenase','NULL','The 3-alpha-hydroxy group or 20-beta-hydroxy group of pregnane andandrostane steroids can act as donor.','Androstan-3-alpha,17-beta-diol + NAD(+) = 17-beta-hydroxyandrostan-3-one+ NADH.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.1.1.54','Allyl-alcohol dehydrogenase','NULL','NULL','Also acts on saturated primary alcohols.','Allyl alcohol + NADP(+) = acrolein + NADPH.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.1.1.55','Lactaldehyde reductase (NADPH)','NULL','NULL','May be identical with EC 1.1.1.2.','Propane-1,2-diol + NADP(+) = L-lactaldehyde + NADPH.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.1.1.56','Ribitol 2-dehydrogenase','NULL','NULL','NULL','Ribitol + NAD(+) = D-ribulose + NADH.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.1.1.57','Fructuronate reductase','D-mannonate dehydrogenase','NULL','Also reduces D-tagaturonate.','D-mannonate + NAD(+) = D-fructuronate + NADH.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.1.1.58','Tagaturonate reductase','Altronate dehydrogenase','NULL','NULL','D-altronate + NAD(+) = D-tagaturonate + NADH.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.1.1.59','3-hydroxypropionate dehydrogenase','NULL','NULL','NULL','3-hydroxypropanoate + NAD(+) = 3-oxopropanoate + NADH.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.1.1.60','2-hydroxy-3-oxopropionate reductase','Tartronate semialdehyde reductase','NULL','NULL','D-glycerate + NAD(P)(+) = 2-hydroxy-3-oxopropanoate + NAD(P)H.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.1.1.61','4-hydroxybutyrate dehydrogenase','NULL','NULL','NULL','4-hydroxybutanoate + NAD(+) = succinate semialdehyde + NADH.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.1.1.62','17-beta-estradiol 17-dehydrogenase','17-beta-estradiol dehydrogenase','NULL','The enzyme oxidizes or reduces the hydroxy/keto group on C(17) ofestrogens and androgens in mammals and regulates the biologicalpotency of these steroids.The mammalian enzyme is bifunctional and also catalyzes EC 1.1.1.270.The enzyme also acts on (S)-20-hydroxypregn-4-en-3-one and relatedcompounds, oxidizing the (S)-20-group, but unlike EC 1.1.1.149, it isSi-specific with respect to NAD(P)(+).','17-beta-estradiol + NAD(P)(+) = estrone + NAD(P)H.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.1.1.63','Transferred entry: 1','NULL','NULL','NULL','NULL', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.1.1.64','Testosterone 17-beta-dehydrogenase (NADP(+))','17-ketoreductase','NULL','Also oxidizes 3-hydroxyhexobarbital to 3-oxohexobarbital.','Testosterone + NADP(+) = androst-4-ene-3,17-dione + NADPH.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.1.1.65','Pyridoxine 4-dehydrogenase','PL reductase','NULL','Also oxidizes pyridoxine phosphate.','Pyridoxine + NADP(+) = pyridoxal + NADPH.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.1.1.66','Omega-hydroxydecanoate dehydrogenase','NULL','NULL','Also acts, more slowly, on 9-hydroxynonanoate and11-hydroxyundecanoate.','10-hydroxydecanoate + NAD(+) = 10-oxodecanoate + NADH.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.1.1.67','Mannitol 2-dehydrogenase','Mannitol dehydrogenase','NULL','NULL','D-mannitol + NAD(+) = D-fructose + NADH.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.1.1.68','Transferred entry: 1','NULL','NULL','NULL','NULL', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.1.1.69','Gluconate 5-dehydrogenase','5-keto-D-gluconate 5-reductase','NULL','NULL','D-gluconate + NAD(P)(+) = 5-dehydro-D-gluconate + NAD(P)H.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.1.1.70','Transferred entry: 1','NULL','NULL','NULL','NULL', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.1.1.71','Alcohol dehydrogenase (NAD(P)(+))','Retinal reductase','NULL','Reduces aliphatic aldehydes of chain length from C2 to C14, withgreatest activity on C4, C6 and C8 aldehydes.Also reduces retinal to retinol.','An alcohol + NAD(P)(+) = an aldehyde + NAD(P)H.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.1.1.72','Glycerol dehydrogenase (NADP(+))','NULL','NULL','NULL','Glycerol + NADP(+) = D-glyceraldehyde + NADPH.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.1.1.73','Octanol dehydrogenase','NULL','NULL','Acts, less rapidly, on other long-chain alcohols.','1-octanol + NAD(+) = 1-octanal + NADH.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.1.1.74','Deleted entry','NULL','NULL','NULL','NULL', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.1.1.75','(R)-aminopropanol dehydrogenase','NULL','Potassium.','NULL','(R)-1-aminopropan-2-ol + NAD(+) = aminoacetone + NADH.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.1.1.76','(S,S)-butanediol dehydrogenase','NULL','NULL','This enzyme catalyzes the reversible reduction of (S)-acetoin to(S,S)-butane-2,3-diol.It can also catalyze the irreversible reduction of diacetyl to (S)-acetoin.','(2S,3S)-butane-2,3-diol + NAD(+) = (S)-acetoin + NADH.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.1.1.77','Lactaldehyde reductase','Propanediol oxidoreductase','NULL','NULL','(1) (R)-propane-1,2-diol + NAD(+) = (R)-lactaldehyde + NADH.(2) (S)-propane-1,2-diol + NAD(+) = (S)-lactaldehyde + NADH.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.1.1.78','Methylglyoxal reductase (NADH)','D-lactaldehyde dehydrogenase','NULL','This mammalian enzyme differs from the yeast enzyme, EC 1.1.1.283,in preferring NADH over NADPH as reductant.','(R)-lactaldehyde + NAD(+) = methylglyoxal + NADH.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.1.1.79','Glyoxylate reductase (NADP(+))','NULL','NULL','Also reduces hydroxypyruvate to glycerate.Has some affinity for NAD(+).','Glycolate + NADP(+) = glyoxylate + NADPH.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.1.1.80','Isopropanol dehydrogenase (NADP(+))','NULL','NULL','Also acts on other short-chain secondary alcohols and, slowly,on primary alcohols.','Propan-2-ol + NADP(+) = acetone + NADPH.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.1.1.81','Hydroxypyruvate reductase','D-glycerate dehydrogenase','NULL','NULL','D-glycerate + NAD(P)(+) = hydroxypyruvate + NAD(P)H.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.1.1.82','Malate dehydrogenase (NADP(+))','NADP-linked malate dehydrogenase','NULL','Activated by light.','(S)-malate + NADP(+) = oxaloacetate + NADPH.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.1.1.83','D-malate dehydrogenase (decarboxylating)','NULL','NULL','NULL','(R)-malate + NAD(+) = pyruvate + CO(2) + NADH.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.1.1.84','Dimethylmalate dehydrogenase','NULL','Cobalt or manganese; Potassium or NH(4)(+).','Also acts on (R)-malate.','(R)-3,3-dimethylmalate + NAD(+) = 3-methyl-2-oxobutanoate + CO(2) + NADH.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.1.1.85','3-isopropylmalate dehydrogenase','Beta-IPM dehydrogenase','NULL','NULL','(2R,3S)-3-isopropylmalate + NAD(+) = 4-methyl-2-oxopentanoate + CO(2) +NADH.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.1.1.86','Ketol-acid reductoisomerase','Acetohydroxy acid isomeroreductase','NULL','Formerly EC 1.1.1.89.','(1) (R)-2,3-dihydroxy-3-methylbutanoate + NADP(+) = (S)-2-hydroxy-2-methyl-3-oxobutanoate + NADPH.(2) (2R,3R)-2,3-dihydroxy-3-methylpentanoate + NADP(+) = (S)-2-hydroxy-2-ethyl-3-oxobutanoate + NADPH.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.1.1.87','Homoisocitrate dehydrogenase','(-)-1-hydroxy-1,2,4-butanetricarboxylate:NAD(+) oxidoreductase(decarboxylating)','NULL','Forms part of the lysine biosynthesis pathway in fungi.Formerly EC 1.1.1.155.','(1R,2S)-1-hydroxybutane-1,2,4-tricarboxylate + NAD(+) = 2-oxoadipate +CO(2) + NADH.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.1.1.88','Hydroxymethylglutaryl-CoA reductase','3-hydroxy-3-methylglutaryl-coenzyme A reductase','NULL','NULL','(R)-mevalonate + CoA + 2 NAD(+) = 3-hydroxy-3-methylglutaryl-CoA +2 NADH.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.1.1.89','Transferred entry: 1','NULL','NULL','NULL','NULL', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.1.1.90','Aryl-alcohol dehydrogenase','Benzyl alcohol dehydrogenase','NULL','A group of enzymes with broad specificity toward primary alcoholswith an aromatic or cyclohex-1-ene ring, but with low or no activitytoward short-chain aliphatic alcohols.','An aromatic alcohol + NAD(+) = an aromatic aldehyde + NADH.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.1.1.91','Aryl-alcohol dehydrogenase (NADP(+))','NULL','NULL','Also acts on some aliphatic aldehydes, but cinnamaldehyde was thebest substrate found.','An aromatic alcohol + NADP(+) = an aromatic aldehyde + NADPH.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.1.1.92','Oxaloglycolate reductase (decarboxylating)','NULL','NULL','Also reduces hydroxypyruvate to D-glycerate and glyoxylate toglycolate.','D-glycerate + NAD(P)(+) + CO(2) = 2-hydroxy-3-oxosuccinate + NAD(P)H.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.1.1.93','Tartrate dehydrogenase','Mesotartrate dehydrogenase','Manganese; Monovalent cation.','NULL','(1) Tartrate + NAD(+) = oxaloglycolate + NADH.(2) Meso-tartrate + NAD(+) = oxaloglycolate + NADH.(3) (R,R)-tartrate + NAD(+) = oxaloglycolate + NADH.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.1.1.94','Glycerol-3-phosphate dehydrogenase (NAD(P)(+))','Glycerol 3-phosphate dehydrogenase (NAD(P))','NULL','The enzyme from Escherichia coli shows specificity for the B side ofNADPH.','sn-glycerol 3-phosphate + NAD(P)(+) = glycerone phosphate + NAD(P)H.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.1.1.95','Phosphoglycerate dehydrogenase','3-phosphoglycerate dehydrogenase','NULL','Catalyzes the first committed step in the phosphoserine pathway ofserine biosynthesis in Escherichia coli.Reaction (1) occurs predominantly in the reverse direction and isinhibited by serine and glycine.The enzyme is unusual in that it also acts as a D- and L-2-hydroxyglutarate dehydrogenase (with the D-form being the bettersubstrate) and as a 2-oxoglutarate reductase.','(1) 3-phospho-D-glycerate + NAD(+) = 3-phosphonooxypyruvate + NADH.(2) 2-hydroxyglutarate + NAD(+) = 2-oxoglutarate + NADH.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.1.1.96','Diiodophenylpyruvate reductase','NULL','NULL','Substrates contain an aromatic ring with a pyruvate side chain.The most active substrates are halogenated derivatives.Compounds with hydroxy or amino groups in the 3 or 5 position areinactive.','3-(3,5-diiodo-4-hydroxyphenyl)lactate + NAD(+) = 3-(3,5-diiodo-4-hydroxyphenyl)pyruvate + NADH.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.1.1.97','3-hydroxybenzyl-alcohol dehydrogenase','m-hydroxybenzyl alcohol dehydrogenase','NULL','NULL','3-hydroxybenzyl alcohol + NADP(+) = 3-hydroxybenzaldehyde + NADPH.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.1.1.98','(R)-2-hydroxy-fatty-acid dehydrogenase','D-2-hydroxy fatty acid dehydrogenase','NULL','NULL','(R)-2-hydroxystearate + NAD(+) = 2-oxostearate + NADH.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.1.1.99','(S)-2-hydroxy-fatty-acid dehydrogenase','L-2-hydroxy fatty acid dehydrogenase','NULL','NULL','(S)-2-hydroxystearate + NAD(+) = 2-oxostearate + NADH.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.1.1.100','3-oxoacyl-[acyl-carrier-protein] reductase','NULL','NULL','Exhibits a marked preference for [acyl-carrier-protein] derivativesover CoA derivatives as substrates.','(3R)-3-hydroxyacyl-[acyl-carrier-protein] + NADP(+) = 3-oxoacyl-[acyl-carrier-protein] + NADPH.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.1.1.101','Acylglycerone-phosphate reductase','Palmitoyldihydroxyacetone-phosphate reductase','NULL','Also acts on alkylglycerone 3-phosphate and alkylglycerol3-phosphate.','1-palmitoylglycerol 3-phosphate + NADP(+) = palmitoylglycerone phosphate+ NADPH.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.1.1.102','3-dehydrosphinganine reductase','3-ketosphinganine reductase','NULL','NULL','Sphinganine + NADP(+) = 3-dehydrosphinganine + NADPH.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.1.1.103','L-threonine 3-dehydrogenase','TDH','NULL','Acts in concert with EC 2.3.1.29 in the degradation of threonine toglycine.This threonine-degradation pathway is common to prokaryotic andeukaryotic cells and the two enzymes involved form a complex.In aqueous solution, the product L-2-amino-3-oxobutanoate canspontaneously decarboxylate to form aminoacetone.','L-threonine + NAD(+) = L-2-amino-3-oxobutanoate + NADH.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.1.1.104','4-oxoproline reductase','Hydroxyproline oxidase','NULL','NULL','4-hydroxy-L-proline + NAD(+) = 4-oxoproline + NADH.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.1.1.105','All-trans-retinol dehydrogenase (NAD(+))','NULL','NULL','The enzyme recognizes all-trans-retinol and all-trans-retinal assubstrates and exhibits a strong preference for NAD(+)/NADH ascofactors.Recognizes the substrate both in free form and when bound tocellular-retinol-binding-protein (CRBP1), but has higher affinity forthe bound form.No activity with 11-cis-retinol or 11-cis-retinal (cf. EC 1.1.1.315).Also active with 3-alpha-hydroxysteroids.','All-trans-retinol-[cellular-retinol-binding-protein] + NAD(+) = all-trans-retinal-[cellular-retinol-binding-protein] + NADH.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.1.1.106','Pantoate 4-dehydrogenase','Panthothenase','NULL','NULL','(R)-pantoate + NAD(+) = (R)-4-dehydropantoate + NADH.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.1.1.107','Pyridoxal 4-dehydrogenase','NULL','NULL','The enzyme acts on the hemiacetal form of the substrate.','Pyridoxal + NAD(+) = 4-pyridoxolactone + NADH.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.1.1.108','Carnitine 3-dehydrogenase','NULL','NULL','NULL','Carnitine + NAD(+) = 3-dehydrocarnitine + NADH.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.1.1.109','Transferred entry: 1','NULL','NULL','NULL','NULL', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.1.1.110','Indolelactate dehydrogenase','Indole-3-lactate dehydrogenase','NULL','NULL','(Indol-3-yl)lactate + NAD(+) = (indol-3-yl)pyruvate + NADH.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.1.1.111','3-(imidazol-5-yl)lactate dehydrogenase','NULL','NULL','NULL','(S)-3-(imidazol-5-yl)lactate + NAD(P)(+) = 3-(imidazol-5-yl)pyruvate +NAD(P)H.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.1.1.112','Indanol dehydrogenase','NULL','NULL','3(20)-alpha-hydroxysteroids are also oxidized, more slowly.','Indan-1-ol + NAD(P)(+) = indanone + NAD(P)H.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.1.1.113','L-xylose 1-dehydrogenase','NULL','NULL','Also oxidizes D-arabinose and D-lyxose.','L-xylose + NADP(+) = L-xylono-1,4-lactone + NADPH.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.1.1.114','Apiose 1-reductase','D-apiitol reductase','NULL','NULL','D-apiitol + NAD(+) = D-apiose + NADH.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.1.1.115','Ribose 1-dehydrogenase (NADP(+))','NADP-pentose-dehydrogenase','NULL','Also acts, more slowly, on D-xylose and other pentoses.','D-ribose + NADP(+) + H(2)O = D-ribonate + NADPH.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.1.1.116','D-arabinose 1-dehydrogenase (NAD(+))','NULL','NULL','NULL','D-arabinose + NAD(+) = D-arabinono-1,4-lactone + NADH.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.1.1.117','D-arabinose 1-dehydrogenase (NAD(P)(+))','NULL','NULL','Also acts on L-galactose, 6-deoxy- and 3,6-dideoxy-L-galactose.','D-arabinose + NAD(P)(+) = D-arabinono-1,4-lactone + NAD(P)H.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.1.1.118','Glucose 1-dehydrogenase (NAD(+))','NULL','NULL','NULL','D-glucose + NAD(+) = D-glucono-1,5-lactone + NADH.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.1.1.119','Glucose 1-dehydrogenase (NADP(+))','NULL','NULL','Also oxidizes D-mannose, 2-deoxy-D-glucose and 2-amino-2-deoxy-D-mannose.','D-glucose + NADP(+) = D-glucono-1,5-lactone + NADPH.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.1.1.120','Galactose 1-dehydrogenase (NADP(+))','NULL','NULL','Also acts on L-arabinose, 6-deoxy- and 2-deoxy-D-galactose.','D-galactose + NADP(+) = D-galactono-1,5-lactone + NADPH.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.1.1.121','Aldose 1-dehydrogenase (NAD(+))','NULL','NULL','Acts on D-glucose, 2-deoxy- and 6-deoxy-D-glucose, D-galactose,6-deoxy-D-galactose, 2-deoxy-L-arabinose and D-xylose.','D-aldose + NAD(+) = D-aldonolactone + NADH.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.1.1.122','D-threo-aldose 1-dehydrogenase','(2S,3R)-aldose dehydrogenase','NULL','Acts on L-fucose, D-arabinose and L-xylose.The animal enzyme was also shown to act on L-arabinose, and theenzyme from Pseudomonas caryophylli on L-glucose.','A D-threo-aldose + NAD(+) = a D-threo-aldono-1,5-lactone + NADH.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.1.1.123','Sorbose 5-dehydrogenase (NADP(+))','5-ketofructose reductase','NULL','NULL','L-sorbose + NADP(+) = 5-dehydro-D-fructose + NADPH.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.1.1.124','Fructose 5-dehydrogenase (NADP(+))','5-ketofructose reductase (NADP(+))','NULL','NULL','D-fructose + NADP(+) = 5-dehydro-D-fructose + NADPH.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.1.1.125','2-deoxy-D-gluconate 3-dehydrogenase','2-keto-3-deoxygluconate oxidoreductase','NULL','NULL','2-deoxy-D-gluconate + NAD(+) = 3-dehydro-2-deoxy-D-gluconate + NADH.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.1.1.126','2-dehydro-3-deoxy-D-gluconate 6-dehydrogenase','2-keto-3-deoxy-D-gluconate dehydrogenase','NULL','NULL','2-dehydro-3-deoxy-D-gluconate + NADP(+) = (4S,5S)-4,5-dihydroxy-2,6-dioxohexanoate + NADPH.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.1.1.127','2-dehydro-3-deoxy-D-gluconate 5-dehydrogenase','2-keto-3-deoxygluconate dehydrogenase','NULL','The enzyme from Pseudomonas acts equally well on NAD(+) or NADP(+),while those from Erwinia chrysanthemi and Escherichia coli are morespecific for NAD(+).','2-dehydro-3-deoxy-D-gluconate + NAD(+) = (4S)-4,6-dihydroxy-2,5-dioxohexanoate + NADH.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.1.1.128','Deleted entry','NULL','NULL','NULL','NULL', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.1.1.129','L-threonate 3-dehydrogenase','L-threonic acid dehydrogenase','NULL','NULL','L-threonate + NAD(+) = 3-dehydro-L-threonate + NADH.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.1.1.130','3-dehydro-L-gulonate 2-dehydrogenase','2,3-diketo-L-gulonate reductase','NULL','NULL','3-dehydro-L-gulonate + NAD(P)(+) = (4R,5S)-4,5,6-trihydroxy-2,3-dioxohexanoate + NAD(P)H.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.1.1.131','Mannuronate reductase','Mannonate dehydrogenase','NULL','Formerly EC 1.1.1.180 and EC 1.2.1.34.','D-mannonate + NAD(P)(+) = D-mannuronate + NAD(P)H.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.1.1.132','GDP-mannose 6-dehydrogenase','NULL','NULL','Also uses the corresponding deoxynucleoside diphosphate derivative asa substrate.','GDP-D-mannose + 2 NAD(+) + H(2)O = GDP-D-mannuronate + 2 NADH.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.1.1.133','dTDP-4-dehydrorhamnose reductase','dTDP-4-keto-L-rhamnose reductase','NULL','In the reverse direction, reduction on the 4-position of the hexosemoiety takes place only while the substrate is bound to anotherenzyme that catalyzes epimerization at C-3 and C-5; the complex hasbeen referred to as dTDP-L-rhamnose synthase.','dTDP-beta-L-rhamnose + NADP(+) = dTDP-4-dehydro-beta-L-rhamnose + NADPH.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.1.1.134','dTDP-6-deoxy-L-talose 4-dehydrogenase','NULL','NULL','Oxidation on the 4-position of the hexose moiety takes place onlywhile the substrate is bound to another enzyme that catalyzesepimerization at C-3 and C-5.','dTDP-6-deoxy-beta-L-talose + NADP(+) = dTDP-4-dehydro-beta-L-rhamnose +NADPH.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.1.1.135','GDP-6-deoxy-D-talose 4-dehydrogenase','NULL','NULL','NULL','GDP-6-deoxy-alpha-D-talose + NAD(P)(+) = GDP-4-dehydro-alpha-D-rhamnose +NAD(P)H.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.1.1.136','UDP-N-acetylglucosamine 6-dehydrogenase','UDP-2-acetamido-2-deoxy-D-glucose:NAD oxidoreductase','NULL','This enzyme participates in the biosynthetic pathway for UDP-alpha-D-ManNAc(3)NAcA (UDP-2,3-diacetamido-2,3-dideoxy-alpha-D-mannuronicacid), an important precursor of B-band lipopolysaccharide.','UDP-N-acetyl-alpha-D-glucosamine + 2 NAD(+) + H(2)O = UDP-2-acetamido-2-deoxy-alpha-D-glucuronate + 2 NADH.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.1.1.137','Ribitol-5-phosphate 2-dehydrogenase','NULL','NULL','NULL','D-ribitol 5-phosphate + NAD(P)(+) = D-ribulose 5-phosphate + NAD(P)H.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.1.1.138','Mannitol 2-dehydrogenase (NADP(+))','NADP-dependent mannitol dehydrogenase','NULL','NULL','D-mannitol + NADP(+) = D-fructose + NADPH.', 'NULL', 'NULL');
