INSERT INTO enzyme VALUES ( 'EC 1.1.1.140','Sorbitol-6-phosphate 2-dehydrogenase','Glucitol-6-phosphate dehydrogenase','NULL','NULL','D-sorbitol 6-phosphate + NAD(+) = D-fructose 6-phosphate + NADH.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.1.1.141','15-hydroxyprostaglandin dehydrogenase (NAD(+))','15-hydroxyprostaglandin dehydrogenase','NULL','Acts on prostaglandins E(2), F(2-alpha) and B(1), but not onprostaglandin D(2) (cf. EC 1.1.1.196 and EC 1.1.1.197).','(5Z,13E,15S)-11-alpha,15-dihydroxy-9-oxoprost-5,13-dienoate + NAD(+) =(5Z,13E)-11-alpha-hydroxy-9,15-dioxoprost-5,13-dienoate + NADH.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.1.1.142','D-pinitol dehydrogenase','NULL','NULL','NULL','1D-3-O-methyl-chiro-inositol + NADP(+) = 2D-5-O-methyl-2,3,5/4,6-pentahydroxycyclohexanone + NADPH.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.1.1.143','Sequoyitol dehydrogenase','NULL','NULL','NULL','5-O-methyl-myo-inositol + NAD(+) = 2D-5-O-methyl-2,3,5/4,6-pentahydroxycyclohexanone + NADH.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.1.1.144','Perillyl-alcohol dehydrogenase','NULL','NULL','Oxidizes a number of primary alcohols with the alcohol group allylicto an endocyclic double bond and a 6-membered ring, either aromaticor hydroaromatic.','Perillyl alcohol + NAD(+) = perillyl aldehyde + NADH.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.1.1.145','3-beta-hydroxy-Delta(5)-steroid dehydrogenase','3-beta-hydroxy-5-ene steroid dehydrogenase','NULL','Acts on 3-beta-hydroxyandrost-5-en-17-one to form androst-4-ene-3,17-dione and on 3-beta-hydroxypregn-5-en-20-one to form progesterone.','A 3-beta-hydroxy-Delta(5)-steroid + NAD(+) = a 3-oxo-Delta(5)-steroid +NADH.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.1.1.146','11-beta-hydroxysteroid dehydrogenase','Corticosteroid 11-beta-dehydrogenase','NULL','NULL','An 11-beta-hydroxysteroid + NADP(+) = an 11-oxosteroid + NADPH.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.1.1.147','16-alpha-hydroxysteroid dehydrogenase','NULL','NULL','NULL','A 16-alpha-hydroxysteroid + NAD(P)(+) = a 16-oxosteroid + NAD(P)H.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.1.1.148','Estradiol 17-alpha-dehydrogenase','NULL','NULL','NULL','Estradiol-17-alpha + NAD(P)(+) = estrone + NAD(P)H.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.1.1.149','20-alpha-hydroxysteroid dehydrogenase','NULL','NULL','Re-specific with respect to NAD(P)(+) (cf. EC 1.1.1.62).','17-alpha,20-alpha-dihydroxypregn-4-en-3-one + NAD(P)(+) = 17-alpha-hydroxyprogesterone + NAD(P)H.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.1.1.150','21-hydroxysteroid dehydrogenase (NAD(+))','NULL','NULL','Acts on a number of 21-hydroxycorticosteroids.','Pregnan-21-ol + NAD(+) = pregnan-21-al + NADH.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.1.1.151','21-hydroxysteroid dehydrogenase (NADP(+))','21-hydroxy steroid (nicotinamide adenine dinucleotide phosphate)dehydrogenase','NULL','Acts on a number of 21-hydroxycorticosteroids.','Pregnan-21-ol + NADP(+) = pregnan-21-al + NADPH.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.1.1.152','3-alpha-hydroxy-5-beta-androstane-17-one 3-alpha-dehydrogenase','Etiocholanolone 3-alpha-dehydrogenase','NULL','NULL','3-alpha-hydroxy-5-beta-androstane-17-one + NAD(+) = 5-beta-androstane-3,17-dione + NADH.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.1.1.153','Sepiapterin reductase (L-erythro-7,8-dihydrobiopterin forming)','NULL','NULL','This enzyme catalyzes the final step in the de novo synthesis oftetrahydrobiopterin from GTP.The enzyme, which is found in higher animals and some fungi andbacteria, produces the erythro form of tetrahydrobiopterin.Cf. EC 1.1.1.325.','(1) L-erythro-7,8-dihydrobiopterin + NADP(+) = sepiapterin + NADPH.(2) L-erythro-tetrahydrobiopterin + 2 NADP(+) = 6-pyruvoyl-5,6,7,8-tetrahydropterin + 2 NADPH.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.1.1.154','Ureidoglycolate dehydrogenase','NULL','NULL','NULL','(S)-ureidoglycolate + NAD(P)(+) = oxalureate + NAD(P)H.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.1.1.155','Transferred entry: 1','NULL','NULL','NULL','NULL', 'NULL', 'NULL');
