INSERT INTO enzyme VALUES ( 'EC 1.1.1.174','Cyclohexane-1,2-diol dehydrogenase','NULL','NULL','Also oxidizes, more slowly, the cis isomer and2-hydroxycyclohexanone.','Trans-cyclohexane-1,2-diol + NAD(+) = 2-hydroxycyclohexan-1-one + NADH.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.1.1.175','D-xylose 1-dehydrogenase','D-xylose dehydrogenase','NULL','NULL','D-xylose + NAD(+) = D-xylonolactone + NADH.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.1.1.176','12-alpha-hydroxysteroid dehydrogenase','NULL','NULL','Catalyzes the oxidation of the 12-alpha-hydroxy group of bile acids,both in their free and conjugated form.Also acts on bile alcohols.','3-alpha,7-alpha,12-alpha-trihydroxy-5-beta-cholanate + NADP(+) =3-alpha,7-alpha-dihydroxy-12-oxo-5-beta-cholanate + NADPH.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.1.1.177','Glycerol-3-phosphate 1-dehydrogenase (NADP(+))','Glycerin-3-phosphate dehydrogenase','NULL','NULL','sn-glycerol 3-phosphate + NADP(+) = D-glyceraldehyde 3-phosphate + NADPH.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.1.1.178','3-hydroxy-2-methylbutyryl-CoA dehydrogenase','2-methyl-3-hydroxybutyryl-CoA dehydrogenase','NULL','Also acts, more slowly, on (2S,3S)-2-hydroxy-3-methylpentanoyl-CoA.','(2S,3S)-3-hydroxy-2-methylbutanoyl-CoA + NAD(+) = 2-methylacetoacetyl-CoA+ NADH.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.1.1.179','D-xylose 1-dehydrogenase (NADP(+))','D-xylose-NADP dehydrogenase','NULL','Also acts, more slowly, on L-arabinose and D-ribose.','D-xylose + NADP(+) = D-xylono-1,5-lactone + NADPH.', 'NULL', 'NULL');
