INSERT INTO enzyme VALUES ( 'EC 1.1.1.n4','(-)-trans-carveol dehydrogenase','(4R,6S)-carveol dehydrogenase','NULL','Catalyzes the oxidation of carveol to carvone, with a strongstereoselectivity since it efficiently converts only the (6S)-stereoisomers, of which (-)-(4R,6S)-trans-carveol is the bettersubstrate.Displays a broad substrate specificity with a preference forsubstituted cyclohexanols, and does not catalyze the oxidation ofprimary or short chain aliphatic secondary alcohols.Is also able, albeit more slowly, to oxidize limonene-1,2-diol into1-hydroxy-2-oxolimonene.','(1) (-)-(4R,6S)-trans-carveol + NAD(+) = (-)-(4R)-carvone + NADH.(2) (+)-(4S,6S)-cis-carveol + NAD(+) = (+)-(4S)-carvone + NADH.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.1.1.n5','3-methylmalate dehydrogenase','NULL','Manganese or magnesium.','The enzyme from Methanocaldococcus jannaschii also catalyzes thereaction of EC 1.1.1.85 and EC 1.1.1.83.','(2R,3S)-3-methylmalate + NAD(+) = 2-oxobutanoate + CO(2) + NADH.', 'NULL', 'NULL');
