INSERT INTO enzyme VALUES ( 'EC 1.1.2.2','Mannitol dehydrogenase (cytochrome)','Polyol dehydrogenase','NULL','Acts on polyols with a D-lyxo configuration, such as D-mannitol andD-sorbitol.','D-mannitol + ferricytochrome c = D-fructose + ferrocytochrome c + 2 H(+).', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.1.2.3','L-lactate dehydrogenase (cytochrome)','Cytochrome b2','FMN; Protoheme IX.','NULL','(S)-lactate + 2 ferricytochrome c = pyruvate + 2 ferrocytochrome c +2 H(+).', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.1.2.4','D-lactate dehydrogenase (cytochrome)','D-lactate ferricytochrome c oxidoreductase','FAD.','NULL','(R)-lactate + 2 ferricytochrome c = pyruvate + 2 ferrocytochrome c +2 H(+).', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.1.2.5','D-lactate dehydrogenase (cytochrome c-553)','NULL','NULL','From Desulfovibrio vulgaris.','(R)-lactate + 2 ferricytochrome c-553 = pyruvate + 2 ferrocytochromec-553 + 2 H(+).', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.1.2.6','Polyvinyl alcohol dehydrogenase (cytochrome)','PVA dehydrogenase','Pyrroloquinoline quinone.','The enzyme is involved in bacterial polyvinyl alcohol degradation.Some Gram-negative bacteria degrade polyvinyl alcohol by importing itinto the periplasmic space, where it is oxidized by polyvinyl alcoholdehydrogenase, an enzyme that is coupled to the respiratory chain viacytochrome c.Formerly EC 1.1.99.23.','Polyvinyl alcohol + ferricytochrome c = oxidized polyvinyl alcohol +ferrocytochrome c + H(+).', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.1.2.7','Methanol dehydrogenase (cytochrome c)','MDH','Pyrroloquinoline quinone.','A periplasmic quinoprotein alcohol dehydrogenase that only occurs inmethylotrophic bacteria.It uses the novel specific cytochrome c(L) as acceptor.Acts on a wide range of primary alcohols, including ethanol,duodecanol, chloroethanol, cinnamyl alcohol, and also formaldehyde.Activity is stimulated by ammonia or methylamine.It is usually assayed with phenazine methosulfate.Like all other quinoprotein alcohol dehydrogenases it has an 8-bladedpropeller structure, a calcium ion bound to the PQQ in the activesite and an unusual disulfide ring structure in close proximity tothe PQQ.It differs from EC 1.1.2.8, alcohol dehydrogenase (cytochrome c),in having a high affinity for methanol and in having a secondessential small subunit (no known function).Formerly EC 1.1.99.8.','A primary alcohol + 2 cytochrome c(L)  = an aldehyde + 2 reducedcytochrome c(L) + 2 H(+).', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.1.2.8','Alcohol dehydrogenase (cytochrome c)','NULL','Pyrroloquinoline quinone.','A periplasmic PQQ-containing quinoprotein.Occurs in Pseudomonas and Rhodopseudomonas.The enzyme from Pseudomonas aeruginosa uses a specific induciblecytochrome c(550) as electron acceptor.Acts on a wide range of primary and secondary alcohols, but notmethanol.It has a homodimeric structure (contrasting with the heterotetramericstructure of EC 1.1.2.7, methanol dehydrogenase (cytochrome c)).It is routinely assayed with phenazine methosulfate as electronacceptor.Activity is stimulated by ammonia or amines.Like all other quinoprotein alcohol dehydrogenases it has an 8-bladedpropeller structure, a calcium ion bound to the PQQ in the activesite and an unusual disulfide ring structure in close proximity tothe PQQ.Formerly EC 1.1.99.8.','A primary alcohol + 2 cytochrome c = an aldehyde + 2 reduced cytochrome c+ 2 H(+).', 'NULL', 'NULL');
