INSERT INTO enzyme VALUES ( 'EC 1.1.3.4','Glucose oxidase','Beta-D-glucose:oxygen 1-oxido-reductase','FAD.','NULL','Beta-D-glucose + O(2) = D-glucono-1,5-lactone + H(2)O(2).', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.1.3.5','Hexose oxidase','NULL','Copper.','Also oxidizes D-galactose, D-mannose, maltose, lactose andcellobiose.','Beta-D-glucose + O(2) = D-glucono-1,5-lactone + H(2)O(2).', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.1.3.6','Cholesterol oxidase','Cholesterol-O(2) oxidoreductase','FAD.','Cholesterol oxidases are secreted bacterial bifunctional enzymes thatcatalyze the first two steps in the degradation of cholesterol.The enzyme catalyzes the oxidation of the 3-beta-hydroxyl group to aketo group, and the isomerization of the double bond in the oxidizedsteroid ring system from the Delta(5) position to the Delta(6)position (cf. EC 5.3.3.1).','Cholesterol + O(2) = cholest-5-en-3-one + H(2)O(2).', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.1.3.7','Aryl-alcohol oxidase','Veratryl alcohol oxidase','NULL','Oxidizes many primary alcohols containing an aromatic ring; bestsubstrates are (2-naphthyl)methanol and 3-methoxybenzyl alcohol.','An aromatic primary alcohol + O(2) = an aromatic aldehyde + H(2)O(2).', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.1.3.8','L-gulonolactone oxidase','GLO','FAD.','The product spontaneously isomerizes to L-ascorbate.While most higher animals can synthesize ascorbic acid, primates andguinea pigs cannot.','L-gulono-1,4-lactone + O(2) = L-ascorbate + H(2)O(2).', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.1.3.9','Galactose oxidase','Beta-galactose oxidase','Copper.','NULL','D-galactose + O(2) = D-galacto-hexodialdose + H(2)O(2).', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.1.3.10','Pyranose oxidase','Glucose 2-oxidase','FAD.','Also oxidizes D-xylose, L-sorbose and D-glucono-1,5-lactone, whichhave the same ring conformation and configuration at C-2, C-3 andC-4.','D-glucose + O(2) = 2-dehydro-D-glucose + H(2)O(2).', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.1.3.11','L-sorbose oxidase','NULL','NULL','Also acts on D-glucose, D-galactose and D-xylose, but not onD-fructose.2,6-dichloroindophenol can act as acceptor.','L-sorbose + O(2) = 5-dehydro-D-fructose + H(2)O(2).', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.1.3.12','Pyridoxine 4-oxidase','Pyridoxin 4-oxidase','FAD.','2,6-dichloroindophenol can act as acceptor.','Pyridoxine + O(2) = pyridoxal + H(2)O(2).', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.1.3.13','Alcohol oxidase','AOX','FAD.','Acts on lower primary alcohols and unsaturated alcohols but branched-chain and secondary alcohols are not attacked.','A primary alcohol + O(2) = an aldehyde + H(2)O(2).', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.1.3.14','Catechol oxidase (dimerizing)','NULL','NULL','NULL','4 catechol + 3 O(2) = 2 dibenzo[1,4]dioxin-2,3-dione + 6 H(2)O.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.1.3.15','(S)-2-hydroxy-acid oxidase','Glycolate oxidase','FMN.','Exists as two major isoenzymes; the A form preferentially oxidizesshort-chain aliphatic hydroxy acids; the B form preferentiallyoxidizes long-chain and aromatic hydroxy acids.The rat isoenzyme B also acts as EC 1.4.3.2.Formerly EC 1.1.3.1.','(S)-2-hydroxy acid + O(2) = 2-oxo acid + H(2)O(2).', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.1.3.16','Ecdysone oxidase','NULL','NULL','2,6-dichloroindophenol can act as acceptor.','Ecdysone + O(2) = 3-dehydroecdysone + H(2)O(2).', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.1.3.17','Choline oxidase','NULL','FAD.','In many bacteria, plants and animals, the osmoprotectant betaine issynthesized using different enzymes to catalyze the conversion of (1)choline into betaine aldehyde and (2) betaine aldehyde into betaine.In plants, the first reaction is catalyzed by EC 1.14.15.7, whereasin animals and many bacteria, it is catalyzed by either EC 1.1.99.1or EC 1.1.3.17.The enzyme involved in the second step, EC 1.2.1.8, appears to be thesame in those plants, animals and bacteria that use two separateenzymes.','Choline + 2 O(2) + H(2)O = betaine + 2 H(2)O(2).', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.1.3.18','Secondary-alcohol oxidase','NULL','Iron.','Acts on secondary alcohols with five or more carbons, and polyvinylalcohols with molecular mass over 300 Da.','A secondary alcohol + O(2) = a ketone + H(2)O(2).', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.1.3.19','4-hydroxymandelate oxidase','NULL','FAD; Manganese.','NULL','(S)-2-hydroxy-2-(4-hydroxyphenyl)acetate + O(2) = 4-hydroxybenzaldehyde +CO(2) + H(2)O(2).', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.1.3.20','Long-chain-alcohol oxidase','NULL','NULL','Oxidizes long-chain fatty alcohols; best substrate is dodecylalcohol.','A long-chain alcohol + O(2) = a long-chain aldehyde + H(2)O(2).', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.1.3.21','Glycerol-3-phosphate oxidase','Alpha-glycerophosphate oxidase','FAD.','NULL','sn-glycerol 3-phosphate + O(2) = glycerone phosphate + H(2)O(2).', 'NULL', 'NULL');
