INSERT INTO enzyme VALUES ( 'EC 1.1.3.23','Thiamine oxidase','Thiamine dehydrogenase','FAD.','The product differs from thiamine in replacement of -CH(2)-CH(2)-OHby -CH(2)-COOH; the two-step oxidation proceeds without the releaseof the intermediate aldehyde from the enzyme.','Thiamine + 2 O(2) + H(2)O = thiamine acetic acid + 2 H(2)O(2).', 'NULL', 'NULL');
