INSERT INTO enzyme VALUES ( 'EC 1.1.3.27','Hydroxyphytanate oxidase','NULL','NULL','NULL','L-2-hydroxyphytanate + O(2) = 2-oxophytanate + H(2)O(2).', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.1.3.28','Nucleoside oxidase','NULL','NULL','Other purine and pyrimidine nucleosides (as well as2-deoxynucleosides) are substrates, but ribose and nucleotides arenot substrates.The overall reaction takes place in two separate steps:(1) 2 inosine + O(2) = 2 5-dehydroinosine + 2 H(2)O.(2) 2 5-dehydroinosine + O(2) = 2 9-riburonosylhypoxanthine +2 H(2)O.The 5-dehydro nucleoside is being released from the enzyme to serveas substrate for the second reaction.Differs from EC 1.1.3.39 as it produces water rather than hydrogenperoxide.','Inosine + O(2) = 9-riburonosylhypoxanthine + 2 H(2)O.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.1.3.29','N-acylhexosamine oxidase','NULL','NULL','Also acts on N-glycolylglucosamine, N-acetylgalactosamine and, moreslowly, on N-acetylmannosamine.','N-acetyl-D-glucosamine + O(2) = N-acetyl-D-glucosaminate + H(2)O(2).', 'NULL', 'NULL');
