INSERT INTO enzyme VALUES ( 'EC 1.2.1.38','N-acetyl-gamma-glutamyl-phosphate reductase','N-acetyl-glutamate semialdehyde dehydrogenase','NULL','NULL','N-acetyl-L-glutamate 5-semialdehyde + NADP(+) + phosphate = N-acetyl-5-glutamyl phosphate + NADPH.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.2.1.39','Phenylacetaldehyde dehydrogenase','NULL','NULL','NULL','Phenylacetaldehyde + NAD(+) + H(2)O = phenylacetate + NADH.', 'NULL', 'NULL');
