INSERT INTO enzyme VALUES ( 'EC 1.2.1.42','Hexadecanal dehydrogenase (acylating)','Fatty acyl-CoA reductase','NULL','Also acts, more slowly, on octadecanoyl-CoA.','Hexadecanal + CoA + NAD(+) = hexadecanoyl-CoA + NADH.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.2.1.43','Formate dehydrogenase (NADP(+))','NULL','Iron; Selenium; Tungsten.','NULL','Formate + NADP(+) = CO(2) + NADPH.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.2.1.44','Cinnamoyl-CoA reductase','NULL','NULL','Also acts on a number of substituted cinnamoyl esters of coenzyme A.','Cinnamaldehyde + CoA + NADP(+) = cinnamoyl-CoA + NADPH.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.2.1.45','Transferred entry: 1','NULL','NULL','NULL','NULL', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.2.1.46','Formaldehyde dehydrogenase','NULL','NULL','NULL','Formaldehyde + NAD(+) + H(2)O = formate + NADH.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.2.1.47','4-trimethylammoniobutyraldehyde dehydrogenase','NULL','NULL','NULL','4-trimethylammoniobutanal + NAD(+) + H(2)O = 4-trimethylammoniobutanoate+ NADH.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.2.1.48','Long-chain-aldehyde dehydrogenase','NULL','NULL','The best substrate is dodecylaldehyde.','A long-chain aldehyde + NAD(+) + H(2)O = a long-chain acid anion + NADH.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.2.1.49','2-oxoaldehyde dehydrogenase (NADP(+))','Alpha-ketoaldehyde dehydrogenase','NULL','Not identical with EC 1.2.1.23.','A 2-oxoaldehyde + NADP(+) + H(2)O = a 2-oxo acid + NADPH.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.2.1.50','Long-chain-fatty-acyl-CoA reductase','Acyl-CoA reductase','NULL','Together with EC 6.2.1.19 forms a fatty acid reductase system whichproduces the substrate of EC 1.14.14.3, thus being part of thebacterial luciferase system.','A long-chain aldehyde + CoA + NADP(+) = a long-chain acyl-CoA + NADPH.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.2.1.51','Pyruvate dehydrogenase (NADP(+))','Pyruvate:NADP(+) oxidoreductase','NULL','The Euglena enzyme can also use FAD or methyl viologen as acceptor,more slowly.Inhibited by oxygen.','Pyruvate + CoA + NADP(+) = acetyl-CoA + CO(2) + NADPH.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.2.1.52','Oxoglutarate dehydrogenase (NADP(+))','NULL','NULL','The Euglena enzyme can also use NAD(+) as acceptor, but more slowly.','2-oxoglutarate + CoA + NADP(+) = succinyl-CoA + CO(2) + NADPH.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.2.1.53','4-hydroxyphenylacetaldehyde dehydrogenase','NULL','NULL','With EC 4.2.1.87, brings about the metabolism of octopamine inPseudomonas.','4-hydroxyphenylacetaldehyde + NAD(+) + H(2)O = 4-hydroxyphenylacetate +NADH.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.2.1.54','Gamma-guanidinobutyraldehyde dehydrogenase','NULL','NULL','Involved in the degradation of arginine in Pseudomonas putida (cf.EC 1.2.1.19).','4-guanidinobutanal + NAD(+) + H(2)O = 4-guanidinobutanoate + NADH.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.2.1.55','Transferred entry: 1','NULL','NULL','NULL','NULL', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.2.1.56','Transferred entry: 1','NULL','NULL','NULL','NULL', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.2.1.57','Butanal dehydrogenase','NULL','NULL','Also acts on acetaldehyde, but more slowly.','Butanal + CoA + NAD(P)(+) = butanoyl-CoA + NAD(P)H.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.2.1.58','Phenylglyoxylate dehydrogenase (acylating)','NULL','FAD; Iron-sulfur; Thiamine diphosphate.','The enzyme from Azoarcus evansii is specific for phenylglyoxylate.2-oxoisovalerate is oxidized at 15% of the rate for phenylglyoxylate.Also reduces viologen dyes.','Phenylglyoxylate + NAD(+) + CoA = benzoyl-S-CoA + CO(2) + NADH.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.2.1.59','Glyceraldehyde-3-phosphate dehydrogenase (NAD(P)(+)) (phosphorylating)','NAD(P)-dependent glyceraldehyde-3-phosphate dehydrogenase','NULL','NAD(+) and NADP(+) can be used as cofactors with similar efficiency,unlike EC 1.2.1.12 and EC 1.2.1.13, which are NAD(+)- and NADP(+)-dependent, respectively.','D-glyceraldehyde 3-phosphate + phosphate + NAD(P)(+) = 3-phospho-D-glyceroyl phosphate + NAD(P)H.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.2.1.60','5-carboxymethyl-2-hydroxymuconic-semialdehyde dehydrogenase','Carboxymethylhydroxymuconic semialdehyde dehydrogenase','NULL','Involved in the tyrosine degradation pathway in Arthrobacter sp.','5-carboxymethyl-2-hydroxymuconate semialdehyde + H(2)O + NAD(+) =5-carboxymethyl-2-hydroxymuconate + NADH.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.2.1.61','4-hydroxymuconic-semialdehyde dehydrogenase','NULL','NULL','Involved in the 4-nitrophenol degradation pathway.','4-hydroxymuconic semialdehyde + NAD(+) + H(2)O = maleylacetate + NADH.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.2.1.62','4-formylbenzenesulfonate dehydrogenase','NULL','NULL','Involved in the toluene-4-sulfonate degradation pathway.','4-formylbenzenesulfonate + NAD(+) + H(2)O = 4-sulfobenzoate + NADH.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.2.1.63','6-oxohexanoate dehydrogenase','NULL','NULL','Last step in the cyclohexanol degradation pathway in Acinetobactersp.','6-oxohexanoate + NADP(+) + H(2)O = adipate + NADPH.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.2.1.64','4-hydroxybenzaldehyde dehydrogenase','p-hydroxybenzaldehyde dehydrogenase','NULL','Involved in the toluene degradation pathway in Pseudomonas mendocina.','4-hydroxybenzaldehyde + NAD(+) + H(2)O = 4-hydroxybenzoate + NADH.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.2.1.65','Salicylaldehyde dehydrogenase','NULL','NULL','Involved in the naphthalene degradation pathway in some bacteria.','Salicylaldehyde + NAD(+) + H(2)O = salicylate + NADH.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.2.1.66','Transferred entry: 1','NULL','NULL','NULL','NULL', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.2.1.67','Vanillin dehydrogenase','NULL','NULL','NULL','Vanillin + NAD(+) + H(2)O = vanillate + NADH.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.2.1.68','Coniferyl-aldehyde dehydrogenase','NULL','NULL','Also oxidizes other aromatic aldehydes, but not aliphatic aldehydes.','Coniferyl aldehyde + H(2)O + NAD(P)(+) = ferulate + NAD(P)H.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.2.1.69','Fluoroacetaldehyde dehydrogenase','NULL','NULL','The enzyme from Streptomyces cattleya has a high affinity forfluoroacetate and glycolaldehyde but not for acetaldehyde.','Fluoroacetaldehyde + NAD(+) + H(2)O = fluoroacetate + NADH.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.2.1.70','Glutamyl-tRNA reductase','NULL','NULL','Forms part of the pathway for the biosynthesis of 5-aminolevulinatefrom glutamate, known as the C5 pathway, which is used in mosteubacteria, and in all archaebacteria, algae and plants.However, in the alpha-proteobacteria EC 2.3.1.37 is used in analternative route to produce the product 5-aminolevulinate fromsuccinyl-CoA and glycine.This route is found in the mitochondria of fungi and animals,organelles that are considered to be derived from an endosymbioticalpha-proteobacterium.Although higher plants do not possess EC 2.3.1.37, the protistanEuglena gracilis possesses both the C5 pathway and EC 2.3.1.37.','L-glutamate 1-semialdehyde + NADP(+) + tRNA(Glu) = L-glutamyl-tRNA(Glu) +NADPH.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.2.1.71','Succinylglutamate-semialdehyde dehydrogenase','N-succinylglutamate 5-semialdehyde dehydrogenase','NULL','This is the fourth enzyme in the arginine succinyltransferase (AST)pathway for the catabolism of arginine.This pathway converts the carbon skeleton of arginine into glutamate,with the concomitant production of ammonia and conversion ofsuccinyl-CoA into succinate and CoA.The five enzymes involved in this pathway are EC 2.3.1.109,EC 3.5.3.23, EC 2.6.1.11, EC 1.2.1.71 and EC 3.5.1.96.','N-succinyl-L-glutamate 5-semialdehyde + NAD(+) + H(2)O = N-succinyl-L-glutamate + NADH.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.2.1.72','Erythrose-4-phosphate dehydrogenase','E4P dehydrogenase','NULL','Was originally thought to be an EC 1.2.1.12, but this has since beendisproved, as glyceraldehyde 3-phosphate is not a substrate.Forms part of the pyridoxal-5-phosphate coenzyme biosynthesispathway in Escherichia coli, along with EC 1.1.1.290, EC 2.6.1.52,EC 1.1.1.262, EC 2.6.99.2 and EC 1.4.3.5.','D-erythrose 4-phosphate + NAD(+) + H(2)O = 4-phosphoerythronate + NADH.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.2.1.73','Sulfoacetaldehyde dehydrogenase','NULL','NULL','Part of a bacterial pathway that can utilize the amino group oftaurine as a sole source of nitrogen for growth.At physiological concentrations, NAD(+) cannot be replaced byNADP(+).Specific for sulfoacetaldehyde, as formaldehyde, acetaldehyde,betaine aldehyde, propanal, glyceraldehyde, phosphonoaldehyde,glyoxylate, glycolaldehyde and 2-oxobutyrate are not substrates.','2-sulfoacetaldehyde + H(2)O + NAD(+) = sulfoacetate + NADH.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.2.1.74','Abieta-7,13-dien-18-al dehydrogenase','Abietadienal dehydrogenase','NULL','Abietic acid is the principle component of conifer resin.This enzyme catalyzes the last step of the pathway of abietic acidbiosynthesis in Abies grandis (grand fir).The activity has been demonstrated in cell-free stem extracts ofA.grandis, was present in the cytoplasm, and required NAD(+) ascofactor.The enzyme is expressed constitutively at a high level, and is notinducible by wounding of the plant tissue.','Abieta-7,13-diene-18-al + H(2)O + NAD(+) = abieta-7,13-diene-18-oate +NADH.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.2.1.75','Malonyl CoA reductase (malonate semialdehyde-forming)','Malonyl CoA reductase (NADP)','Magnesium.','Catalyzes the reduction of malonyl-CoA to malonate semialdehyde,a key step in the 3-hydroxypropanoate and the3-hydroxypropanoate/4-hydroxybutyrate cycles, autotrophic CO(2)fixation pathways found in some green non-sulfur phototrophicbacteria and some thermoacidophilic archaea, respectively.The enzyme from Sulfolobus tokodaii has been purified, and found tocontain one RNA molecule per two subunits.The enzyme from Chloroflexus aurantiacus is bifunctional, and alsocatalyzes the next reaction in the pathway, EC 1.1.1.298.','Malonate semialdehyde + CoA + NADP(+) = malonyl-CoA + NADPH.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.2.1.76','Succinate-semialdehyde dehydrogenase (acetylating)','Coenzyme-A-dependent succinate-semialdehyde dehydrogenase','NULL','Catalyzes the NADPH-dependent reduction of succinyl-CoA to succinatesemialdehyde.The enzyme has been described in Clostridium kluyveri, where itparticipates in succinate fermentation, and in Metallosphaera sedula,where it participates in the 3-hydroxypropanoate/4-hydroxybutyratecycle, an autotrophic CO(2) fixation pathway found in somethermoacidophilic archaea.','Succinate semialdehyde + CoA + NADP(+) = succinyl-CoA + NADPH.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.2.1.77','3,4-dehydroadipyl-CoA semialdehyde dehydrogenase (NADP(+))','3,4-dehydroadipyl-CoA semialdehyde dehydrogenase','NULL','This enzyme catalyzes a step in the aerobic benzoyl-coenzyme Acatabolic pathway in Azoarcus evansii and Burkholderia xenovorans.Formerly EC 1.2.1.n1.','Cis-3,4-dehydroadipyl-CoA semialdehyde + NADP(+) + H(2)O = cis-3,4-dehydroadipyl-CoA + NADPH.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.2.1.78','2-formylbenzoate dehydrogenase','2-carboxybenzaldehyde dehydrogenase','NULL','The enzyme is involved in phenanthrene degradation.','2-formylbenzoate + NAD(+) + H(2)O = phthalate + NADH.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.2.1.79','Succinate-semialdehyde dehydrogenase (NADP(+))','NULL','NULL','This enzyme participates in the degradation of glutamate and4-aminobutyrate.It is similar to EC 1.2.1.24 and EC 1.2.1.16 but is specific forNADP(+).The enzyme from Escherichia coli is 20-fold more active with NADP(+)than NAD(+).','Succinate semialdehyde + NADP(+) + H(2)O = succinate + NADPH.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.2.1.80','Long-chain acyl-[acyl-carrier-protein] reductase','Fatty acyl-[acyl-carrier-protein] reductase','Magnesium.','Catalyzes the reaction in the opposite direction.This enzyme, purified from the cyanobacterium Synechococcus elongatusPCC 7942, catalyzes the NAD(P)H-dependent reduction of an activatedfatty acid (acyl-[acp]) to the corresponding aldehyde.Together with EC 4.1.99.5, it is involved in alkane biosynthesis.The natural substrates of the enzyme are C16 to C18 activated fattyacids.','A long-chain aldehyde + an [acyl-carrier protein] + NAD(P)(+) = a long-chain acyl-[acyl-carrier protein] + NAD(P)H.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.2.1.81','Sulfoacetaldehyde dehydrogenase (acylating)','NULL','NULL','The enzyme is involved in degradation of sulfoacetate.In this pathway the reaction is catalyzed in the reverse direction.The enzyme is specific for sulfoacetaldehyde and NADP(+).','2-sulfoacetaldehyde + CoA + NADP(+) = sulfoacetyl-CoA + NADPH.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.2.1.82','Beta-apo-4-carotenal oxygenase','Beta-apo-4-carotenal dehydrogenase','NULL','Neurosporaxanthin is responsible for the orange color of ofNeurospora.','4-apo-beta,psi-caroten-4-al + NAD(+) + H(2)O = neurosporaxanthin +NADH.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.2.1.83','3-succinoylsemialdehyde-pyridine dehydrogenase','NULL','NULL','The enzyme has been characterized from the soil bacterium Pseudomonassp. HZN6.It participates in the nicotine degradation pathway.','4-oxo-4-(pyridin-3-yl)butanal + NADP(+) + H(2)O = 4-oxo-4-(pyridin-3-yl)butanoate + NADPH.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.2.1.84','Alcohol-forming fatty acyl-CoA reductase','NULL','NULL','The enzyme has been characterized from the plant Simmondsia chinensis(jojoba).The alcohol is formed by a four-electron reduction of fatty acyl-CoA.Although the reaction proceeds through an aldehyde intermediate,a free aldehyde is not released.The recombinant enzyme was shown to accept saturated and mono-unsaturated fatty acyl-CoAs of 16 to 22 carbons.','A long-chain acyl-CoA + 2 NADPH = CoA + a long-chain alcohol + 2 NADP(+).', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.2.1.85','2-hydroxymuconate-6-semialdehyde dehydrogenase','NULL','NULL','This substrate for this enzyme is formed by meta ring cleavage ofcatechol (EC 1.13.11.2), and is an intermediate in the bacterialdegradation of several aromatic compounds.Has lower activity with benzaldehyde.Activity with NAD(+) is more than 10-fold higher than with NADP(+).Cf. EC 1.2.1.32.','2-hydroxymuconate-6-semialdehyde + NAD(+) + H(2)O = (2Z,4E)-2-hydroxyhexa-2,4-dienedioate + NADH.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.2.1.86','Geranial dehydrogenase','GaDH','NULL','Does not act on neral.Formerly EC 1.2.1.n3.','Geranial + H(2)O + NAD(+) = geranate + NADH.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.2.1.87','Propanal dehydrogenase (CoA-propanoylating)','NULL','NULL','The enzyme forms a bifunctional complex with EC 4.1.3.43, with atight channel connecting the two subunits.Also acts, more slowly, on glycolaldehyde and butanal.In Pseudomonas species the enzyme forms a bifunctional complex withEC 4.1.3.39.The enzymes from the bacteria Burkholderia xenovorans and Thermusthermophilus also perform the reaction of EC 1.2.1.10.NADP(+) can replace NAD(+) with a much slower rate.','Propanal + CoA + NAD(+) = propanoyl-CoA + NADH.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.2.1.88','L-glutamate gamma-semialdehyde dehydrogenase','1-pyrroline-5-carboxylate dehydrogenase','NULL','This enzyme catalyzes the irreversible oxidation of glutamate-gamma-semialdehyde to glutamate as part of the proline degradation pathway.(S)-1-pyrroline-5-carboxylate, the product of the first enzyme of thepathway (EC 1.5.5.2) is in spontaneous equilibrium with its tautomerL-glutamate gamma-semialdehyde.In many bacterial species, both activities are carried out by asingle bifunctional enzyme.The enzyme can also oxidize other 1-pyrrolines, e.g. 3-hydroxy-1-pyrroline-5-carboxylate is converted into 4-hydroxyglutamate and (R)-1-pyrroline-5-carboxylate is converted into D-glutamate.NADP(+) can also act as acceptor, but with lower activity.Formerly EC 1.5.1.12.','L-glutamate 5-semialdehyde + NAD(+) + H(2)O = L-glutamate + NADH.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.2.1.89','D-glyceraldehyde dehydrogenase (NADP(+))','GADH','NULL','The enzyme from the archaea Thermoplasma acidophilum and Picrophilustorridus is involved in the non-phosphorylative Entner-Doudoroffpathway, cf. EC 1.2.99.8.','D-glyceraldehyde + NADP(+) + H(2)O = D-glycerate + NADPH.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.2.1.n1','Transferred entry: 1','NULL','NULL','NULL','NULL', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.2.1.n2','Fatty acyl-CoA reductase','Hexadecanal dehydrogenase (acylating, NADP dependent)','NULL','NULL','Hexadecanal + CoA + NADP(+) = hexadecanoyl-CoA + NADPH.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.2.1.n3','Transferred entry: 1','NULL','NULL','NULL','NULL', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.2.2.1','Formate dehydrogenase (cytochrome)','NULL','NULL','NULL','Formate + 2 ferricytochrome b1 = CO(2) + 2 ferrocytochrome b1 + 2 H(+).', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.2.2.2','Deleted entry','NULL','NULL','NULL','NULL', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.2.2.3','Formate dehydrogenase (cytochrome c-553)','NULL','NULL','Saccharomyces cerevisiae cytochrome c, ferricyanide and phenazinemethosulfate can act as acceptors.','Formate + ferricytochrome c-553 = CO(2) + ferrocytochrome c-553.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.2.2.4','Carbon-monoxide dehydrogenase (cytochrome b-561)','Carbon monoxide oxidase','FAD; Iron-sulfur; Molybdopterin cytosine dinucleotide.','Oxygen, methylene blue and iodonitrotetrazolium chloride can act asnonphysiological electron acceptors.','CO + H(2)O + 2 ferricytochrome b-561 = CO(2) + 2 H(+) + 2 ferrocytochromeb-561.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.2.3.1','Aldehyde oxidase','Quinoline oxidase','FAD; Iron-sulfur; Molybdenum.','The enzyme from liver exhibits a broad substrate specificity, and isinvolved in the metabolism of xenobiotics, including the oxidation ofN-heterocycles and aldehydes and the reduction of N-oxides,nitrosamines, hydroxamic acids, azo dyes, nitropolycyclic aromatichydrocarbons, and sulfoxides.The enzyme is also responsible for the oxidation of retinal,an activity that was initially attributed to a distinct enzyme(EC 1.2.3.11).Formerly EC 1.2.3.11.','An aldehyde + H(2)O + O(2) = a carboxylate + H(2)O(2).', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.2.3.2','Transferred entry: 1','NULL','NULL','NULL','NULL', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.2.3.3','Pyruvate oxidase','Phosphate-dependent pyruvate oxidase','FAD; Thiamine diphosphate.','Two reducing equivalents are transferred from the resonantcarbanion/enamine forms of 2-hydroxyethyl-thiamine-diphosphate to theadjacent flavin cofactor, yielding 2-acetyl-thiamine diphosphate(AcThDP) and reduced flavin.FADH is reoxidized by O(2) to yield H(2)O(2) and FAD and AcThDP iscleaved phosphorolytically to acetyl phosphate and thiaminediphosphate.','Pyruvate + phosphate + O(2) = acetyl phosphate + CO(2) + H(2)O(2).', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.2.3.4','Oxalate oxidase','NULL','Manganese.','NULL','Oxalate + O(2) + 2 H(+) = 2 CO(2) + H(2)O(2).', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.2.3.5','Glyoxylate oxidase','NULL','NULL','NULL','Glyoxylate + H(2)O + O(2) = oxalate + H(2)O(2).', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.2.3.6','Pyruvate oxidase (CoA-acetylating)','NULL','FAD.','May be identical with EC 1.2.7.1.','Pyruvate + CoA + O(2) = acetyl-CoA + CO(2) + H(2)O(2).', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.2.3.7','Indole-3-acetaldehyde oxidase','IAA oxidase','FAD; Heme; Molybdenum.','Isoform of EC 1.2.3.1.Has a preference for aldehydes having an indole-ring structure assubstrate.May play a role in plant hormone biosynthesis as its activity ishigher in the auxin-overproducing mutant, super-root1, than in wild-type Arabidopsis thaliana.While (indol-3-yl)acetaldehyde is the preferred substrate, it alsooxidizes indole-3-carbaldehyde and acetaldehyde, but more slowly.','(Indol-3-yl)acetaldehyde + H(2)O + O(2) = (indol-3-yl)acetate + H(2)O(2).', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.2.3.8','Pyridoxal oxidase','NULL','Molybdenum.','NULL','Pyridoxal + H(2)O + O(2) = 4-pyridoxate + H(2)O(2).', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.2.3.9','Aryl-aldehyde oxidase','NULL','NULL','Acts on benzaldehyde, vanillin and a number of other aromaticaldehydes, but not on aliphatic aldehydes or sugars.','An aromatic aldehyde + O(2) + H(2)O = an aromatic carboxylate + H(2)O(2).', 'NULL', 'NULL');
