INSERT INTO enzyme VALUES ( 'EC 1.2.3.11','Transferred entry: 1','NULL','NULL','NULL','NULL', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.2.3.12','Transferred entry: 1','NULL','NULL','NULL','NULL', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.2.3.13','4-hydroxyphenylpyruvate oxidase','NULL','NULL','Involved in tyrosine degradation pathway in Arthrobacter sp.','2 4-hydroxyphenylpyruvate + O(2) = 2 4-hydroxyphenylacetate + 2 CO(2).', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.2.3.14','Abscisic-aldehyde oxidase','AO-delta','NULL','Acts on both (+)- and (-)-abscisic aldehyde.Involved in the abscisic-acid biosynthesis pathway in plants, alongwith EC 1.1.1.288, EC 1.13.11.51 and EC 1.14.13.93.While abscisic aldehyde is the best substrate, the enzyme also actswith indole-3-aldehyde, 1-naphthaldehyde and benzaldehyde assubstrates, but more slowly.','Abscisic aldehyde + H(2)O + O(2) = abscisate + H(2)O(2).', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.2.4.1','Pyruvate dehydrogenase (acetyl-transferring)','MtPDC (mitochondrial pyruvate dehydogenase complex)','Thiamine diphosphate.','It is a component (in multiple copies) of the multienzyme pyruvatedehydrogenase complex in which it is bound to a core of molecules ofEC 2.3.1.12, which also binds multiple copies of EC 1.8.1.4.It does not act on free lipoamide or lipoyllysine, but only on thelipoyllysine residue in EC 2.3.1.12.','Pyruvate + [dihydrolipoyllysine-residue acetyltransferase] lipoyllysine =[dihydrolipoyllysine-residue acetyltransferase]S-acetyldihydrolipoyllysine + CO(2).', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.2.4.2','Oxoglutarate dehydrogenase (succinyl-transferring)','2-ketoglutarate dehydrogenase','Thiamine diphosphate.','It is a component of the multienzyme 2-oxoglutarate dehydrogenasecomplex in which multiple copies of it are bound to a core ofmolecules of EC 2.3.1.61, which also binds multiple copies ofEC 1.8.1.4.It does not act on free lipoamide or lipoyllysine, but only on thelipoyllysine residue in EC 2.3.1.61.','2-oxoglutarate + [dihydrolipoyllysine-residue succinyltransferase]lipoyllysine = [dihydrolipoyllysine-residue succinyltransferase]S-succinyldihydrolipoyllysine + CO(2).', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.2.4.3','Transferred entry: 1','NULL','NULL','NULL','NULL', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.2.4.4','3-methyl-2-oxobutanoate dehydrogenase (2-methylpropanoyl-transferring)','2-oxoisocaproate dehydrogenase','Thiamine diphosphate.','It acts not only on 3-methyl-2-oxobutanaoate, but also on 4-methyl-2-oxopentanoate and (S)-3-methyl-2-oxopentanoate, so that it acts onthe 2-oxo acids that derive from the action of transaminases onvaline, leucine and isoleucine.It is a component of the multienzyme 3-methyl-2-oxobutanoatedehydrogenase complex in which multiple copies of it are bound to acore of molecules of EC 2.3.1.168, which also binds multiple copiesof EC 1.8.1.4.It does not act on free lipoamide or lipoyllysine, but only on thelipoyllysine residue in EC 2.3.1.168.Formerly EC 1.2.4.3.','3-methyl-2-oxobutanoate + [dihydrolipoyllysine-residue(2-methylpropanoyl)transferase] lipoyllysine = [dihydrolipoyllysine-residue (2-methylpropanoyl)transferase]S-(2-methylpropanoyl)dihydrolipoyllysine + CO(2).', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.2.5.1','Pyruvate dehydrogenase (quinone)','Pyruvate dehydrogenase','FAD.','This bacterial enzyme is located on the inner surface of thecytoplasmic membrane and coupled to the respiratory chain viaubiquinone.Does not accept menaquinone.Activity is greatly enhanced by lipids.Requires thiamine diphosphate.The enzyme can also form acetoin.','Pyruvate + ubiquinone + H(2)O = acetate + CO(2) + ubiquinol.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.2.7.1','Pyruvate synthase','2-oxobutyrate synthase','Iron-sulfur; Thiamine diphosphate.','The enzyme also decarboxylates 2-oxobutyrate with lower efficiency,but shows no activity with 2-oxoglutarate.This enzyme is a member of the 2-oxoacid oxidoreductases, a family ofenzymes that oxidatively decarboxylate different 2-oxoacids to formtheir CoA derivatives, and are differentiated based on theirsubstrate specificity.For examples of other members of this family, see EC 1.2.7.3 andEC 1.2.7.7.','Pyruvate + CoA + 2 oxidized ferredoxin = acetyl-CoA + CO(2) + 2 reducedferredoxin + 2 H(+).', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.2.7.2','Deleted entry','NULL','NULL','NULL','NULL', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.2.7.3','2-oxoglutarate synthase','2-ketoglutarate ferredoxin oxidoreductase','Iron-sulfur; Thiamine diphosphate.','Highly specific for 2-oxoglutarate.This enzyme is a member of the 2-oxoacid oxidoreductases, a family ofenzymes that oxidatively decarboxylate different 2-oxoacids to formtheir CoA derivatives, and are differentiated based on theirsubstrate specificity.For examples of other members of this family, see EC 1.2.7.1 andEC 1.2.7.7.Formerly EC 1.2.7.9.','2-oxoglutarate + CoA + 2 oxidized ferredoxin = succinyl-CoA + CO(2) +2 reduced ferredoxin + 2 H(+).', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.2.7.4','Carbon-monoxide dehydrogenase (ferredoxin)','NULL','Iron; Nickel; Zinc.','Methyl viologen can act as acceptor.The enzyme from Moorella thermoacetica exists as a complex withEC 2.3.1.169, which catalyzes the overall reaction: methylcorrinoidprotein + CoA + CO(2) + reduced ferredoxin = acetyl-CoA + corrinoidprotein + H(2)O + oxidized ferredoxin.','CO + H(2)O + 2 oxidized ferredoxin = CO(2) + 2 reduced ferredoxin +2 H(+).', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.2.7.5','Aldehyde ferredoxin oxidoreductase','AOR','Iron-sulfur; Tungsten-molybdopterin.','This is an oxygen-sensitive enzyme.Catalyzes the oxidation of aldehydes (including crotonaldehyde,acetaldehyde, formaldehyde and glyceraldehyde) to their correspondingacids.However, it does not oxidize glyceraldehyde 3-phosphate (seeEC 1.2.7.6).Can use ferredoxin or methyl viologen but not NAD(P)(+) as electronacceptor.','An aldehyde + H(2)O + 2 oxidized ferredoxin = a carboxylate + 2 H(+) +2 reduced ferredoxin.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.2.7.6','Glyceraldehyde-3-phosphate dehydrogenase (ferredoxin)','GAPOR','Iron-sulfur; Tungsten-molybdopterin.','Thought to function in place of glyceraldehyde-3-phosphatedehydrogenase and possibly phosphoglycerate kinase in the novelEmbden-Meyerhof-type glycolytic pathway found in Pyrococcus furiosus.Specific for glyceraldehyde-3-phosphate.','D-glyceraldehyde-3-phosphate + H(2)O + 2 oxidized ferredoxin = 3-phospho-D-glycerate + 2 H(+) + 2 reduced ferredoxin.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.2.7.7','3-methyl-2-oxobutanoate dehydrogenase (ferredoxin)','2-ketoisovalerate ferredoxin reductase','Iron-sulfur; Tungsten-molybdopterin.','Preferentially utilizes 2-oxo-acid derivatives of branched chainamino acids, e.g. 3-methyl-2-oxopentanoate, 4-methyl-2-oxo-pentanoate, 2-oxobutyrate and 3-methylthiopropanamine.This enzyme is a member of the 2-oxoacid oxidoreductases, a family ofenzymes that oxidatively decarboxylate different 2-oxoacids to formtheir CoA derivatives, and are differentiated based on theirsubstrate specificity.For examples of other members of this family, see EC 1.2.7.1 andEC 1.2.7.3.','3-methyl-2-oxobutanoate + CoA + 2 oxidized ferredoxin =S-(2-methylpropanoyl)-CoA + CO(2) + 2 reduced ferredoxin + H(+).', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.2.7.8','Indolepyruvate ferredoxin oxidoreductase','3-(indol-3-yl)pyruvate synthase (ferredoxin)','Iron-sulfur; Thiamine diphosphate.','Preferentially utilizes the transaminated forms of aromatic aminoacids and can use phenylpyruvate and 4-hydroxyphenylpyruvate assubstrates.This enzyme, which is found in archaea, is a member of the 2-oxoacidoxidoreductases, a family of enzymes that oxidatively decarboxylatedifferent 2-oxoacids to form their CoA derivatives, and aredifferentiated based on their substrate specificity.For examples of other members of this family, see EC 1.2.7.3 andEC 1.2.7.7.','(Indol-3-yl)pyruvate + CoA + 2 oxidized ferredoxin = S-2-(indol-3-yl)acetyl-CoA + CO(2) + 2 reduced ferredoxin + H(+).', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.2.7.9','Transferred entry: 1','NULL','NULL','NULL','NULL', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.2.7.10','Oxalate oxidoreductase','NULL','Iron-sulfur; Thiamine diphosphate.','NULL','Oxalate + oxidized ferredoxin = 2 CO(2) + reduced ferredoxin.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.2.7.11','2-oxoacid oxidoreductase (ferredoxin)','OFOR','Iron-sulfur; Thiamine diphosphate.','This enzyme is a member of the 2-oxoacid oxidoreductases, a family ofenzymes that oxidatively decarboxylate different 2-oxoacids to formtheir CoA derivatives, and are differentiated based on theirsubstrate specificity.For example, see EC 1.2.7.3 and EC 1.2.7.7.','A 2-oxocarboxylate + CoA + 2 oxidized ferredoxin = an acyl-CoA + CO(2) +2 reduced ferredoxin + 2 H(+).', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.2.99.1','Transferred entry: 1','NULL','NULL','NULL','NULL', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.2.99.2','Carbon-monoxide dehydrogenase (acceptor)','Anaerobic carbon monoxide dehydrogenase','Iron-sulfur; Nickel.','It uses many electron acceptors, including ferredoxin, methylviologen and benzyl viologen and flavins, but not pyridinenucleotides.Forms part of a membrane-bound multienzyme complex with EC 1.12.99.6,which catalyzes the overall reaction: CO + H(2)O = CO(2) + H(2).','CO + H(2)O + A = CO(2) + AH(2).', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.2.99.3','Aldehyde dehydrogenase (pyrroloquinoline-quinone)','Aldehyde dehydrogenase (acceptor)','Pyrroloquinoline quinone.','Wide specificity; acts on straight-chain aldehydes up to C(10),aromatic aldehydes, glyoxylate and glyceraldehyde.','An aldehyde + acceptor + H(2)O = a carboxylate + reduced acceptor.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.2.99.4','Formaldehyde dismutase','NULL','Magnesium; NAD(+) or NADP(+); Zinc.','The enzyme from Mycobacterium sp. DSM 3803 also catalyzes thereactions of EC 1.1.99.36 and EC 1.1.99.37.Formaldehyde and acetaldehyde can act as donors; formaldehyde,acetaldehyde and propanal can act as acceptors.','2 formaldehyde + H(2)O = formate + methanol.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.2.99.5','Formylmethanofuran dehydrogenase','NULL','Molybdenum.','Tungstate can substitute for molybdate.The enzyme catalyzes a reversible reaction in methanogenic bacteria,and is involved in methanogenesis from CO(2) as well as the oxidationof methyl-coenzyme M to CO(2).Methyl viologen can act as acceptor.Also oxidizes N-furfurylformamide.','Formylmethanofuran + H(2)O + acceptor = CO(2) + methanofuran + reducedacceptor.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.2.99.6','Carboxylate reductase','NULL','Tungsten.','Methyl viologen can act as acceptor.In the reverse direction, non-activated acids are reduced by reducedviologens to aldehydes, but not to the corresponding alcohols.','An aldehyde + acceptor + H(2)O = a carboxylate + reduced acceptor.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.2.99.7','Aldehyde dehydrogenase (FAD-independent)','Aldehyde oxidase','Iron-sulfur; Molybdopterin cytosine dinucleotide.','NULL','An aldehyde + H(2)O + acceptor = a carboxylate + reduced acceptor.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.2.99.8','Glyceraldehyde dehydrogenase (FAD-containing)','Glyceraldehyde oxidoreductase','FAD; Iron-sulfur; Molybdopterin guanine dinucleotide.','The enzyme from the archaeon Sulfolobus acidocaldarius catalyzes theoxidation of D-glyceraldehyde in the nonphosphorylative Entner-Doudoroff pathway.With 2,6-dichlorophenolindophenol as artificial electron acceptor,the enzyme shows a broad substrate range, but is most active withD-glyceraldehyde.It is not known which acceptor is utilized in vivo.','D-glyceraldehyde + H(2)O + acceptor = D-glycerate + reduced acceptor.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.3.1.1','Dihydrouracil dehydrogenase (NAD(+))','NULL','Flavoprotein; Iron-sulfur.','The enzyme was originally discovered in the uracil-fermentingbacterium, Clostridium uracilicum, which utilizes uracil and thymineas nitrogen and carbon sources for growth.Since then the enzyme was found in additional organisms includingAlcaligenes eutrophus, Pseudomonas strains and Escherichia coli.','(1) 5,6-dihydrouracil + NAD(+) = uracil + NADH.(2) 5,6-dihydrothymine + NAD(+) = thymine + NADH.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.3.1.2','Dihydropyrimidine dehydrogenase (NADP(+))','Dihydrothymine dehydrogenase','NULL','Also acts on dihydrothymine.','5,6-dihydrouracil + NADP(+) = uracil + NADPH.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.3.1.3','Delta(4)-3-oxosteroid 5-beta-reductase','3-oxo-Delta(4)-steroid 5-beta-reductase','NULL','The enzyme from human efficiently catalyzes the reduction ofprogesterone, androstenedione, 17-alpha-hydroxyprogesterone andtestosterone to 5-beta-reduced metabolites; it can also act onaldosterone, corticosterone and cortisol, but to a lesser extent.The bile acid intermediates 7-alpha,12-alpha-dihydroxy-4-cholesten-3-one and 7-alpha-hydroxy-4-cholesten-3-one can also act as substrates.','(1) 5-beta-cholestan-3-one + NADP(+) = cholest-4-en-3-one + NADPH.(2) 17,21-dihydroxy-5-beta-pregnane-3,11,20-trione + NADP(+) = cortisone+ NADPH.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.3.1.4','Transferred entry: 1','NULL','NULL','NULL','NULL', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.3.1.5','Cucurbitacin Delta(23)-reductase','NULL','Manganese.','Iron or zinc can replace manganese to some extent.Cucurbitacin is a plant triterpenoid.','23,24-dihydrocucurbitacin + NAD(P)(+) = cucurbitacin + NAD(P)H.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.3.1.6','Fumarate reductase (NADH)','NADH-dependent fumarate reductase','NULL','NULL','Succinate + NAD(+) = fumarate + NADH.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.3.1.7','Meso-tartrate dehydrogenase','NULL','NULL','NULL','Meso-tartrate + NAD(+) = dihydroxyfumarate + NADH.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.3.1.8','Acyl-CoA dehydrogenase (NADP(+))','2-enoyl-CoA reductase','NULL','The liver enzyme acts on enoyl-CoA derivatives of carbon chain length4 to 16, with optimum activity on 2-hexenoyl-CoA.In Escherichia coli, cis-specific and trans-specific enzymes exist(cf. EC 1.3.1.37 and EC 1.3.1.38).','Acyl-CoA + NADP(+) = 2,3-dehydroacyl-CoA + NADPH.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.3.1.9','Enoyl-[acyl-carrier-protein] reductase (NADH)','Enoyl-ACP reductase','NULL','The enzyme catalyzes an essential step in fatty acid biosynthesis,the reduction of the 2,3-double bond in enoyl-acyl-[acyl-carrier-protein] derivatives of the elongating fatty acid moiety.The enzyme from the bacterium Escherichia coli accepts substrateswith carbon chain length from 4 to 18.The enzyme from the bacterium Mycobacterium tuberculosis preferssubstrates with carbon chain length from 12 to 24 carbons.','An acyl-[acyl-carrier protein] + NAD(+) = a trans-2,3-dehydroacyl-[acyl-carrier protein] + NADH.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.3.1.10','Enoyl-[acyl-carrier-protein] reductase (NADPH, Si-specific)','Acyl-ACP dehydrogenase','NULL','One of the activities of EC 2.3.1.86, an enzyme found in yeasts(Ascomycota and the Basidiomycota).Catalyzes the reduction of enoyl-acyl-[acyl-carrier protein]derivatives of carbon chain length from 4 to 16.The yeast enzyme is Si-specific with respect to NADP(+).cf. EC 1.3.1.39 and EC 1.3.1.9.','An acyl-[acyl-carrier protein] + NADP(+) = a trans-2,3-dehydroacyl-[acyl-carrier protein] + NADPH.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.3.1.11','2-coumarate reductase','Melilotate dehydrogenase','NULL','NULL','3-(2-hydroxyphenyl)propanoate + NAD(+) = 2-coumarate + NADH.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.3.1.12','Prephenate dehydrogenase','Chorismate mutase--prephenate dehydrogenase','NULL','This enzyme in the enteric bacteria also possesses EC 5.4.99.5activity and converts chorismate into prephenate.','Prephenate + NAD(+) = 4-hydroxyphenylpyruvate + CO(2) + NADH.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.3.1.13','Prephenate dehydrogenase (NADP(+))','NULL','NULL','NULL','Prephenate + NADP(+) = 4-hydroxyphenylpyruvate + CO(2) + NADPH.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.3.1.14','Dihydroorotate dehydrogenase (NAD(+))','DHOD','FAD; FMN; Iron-sulfur.','The enzyme consists of two subunits, an FMN binding catalytic subunitand a FAD and iron-sulfur binding electron transfer subunit.The reaction, which takes place in the cytosol, is the only redoxreaction in the de-novo biosynthesis of pyrimidine nucleotides.Other class 1 dihydroorotate dehydrogenases use either fumarate(EC 1.3.98.1) or NADP(+) (EC 1.3.1.15) as electron acceptor.The membrane bound class 2 dihydroorotate dehydrogenase (EC 1.3.5.2)uses quinone as electron acceptor.','(S)-dihydroorotate + NAD(+) = orotate + NADH.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.3.1.15','Dihydroorotate dehydrogenase (NADP(+))','NULL','FAD; FMN.','Other class 1 dihydroorotate dehydrogenases use either fumarate(EC 1.3.98.1) or NAD(+) (EC 1.3.1.14) as electron acceptor.The membrane bound class 2 dihydroorotate dehydrogenase (EC 1.3.5.2)uses quinone as electron acceptor.','(S)-dihydroorotate + NADP(+) = orotate + NADPH.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.3.1.16','Beta-nitroacrylate reductase','NULL','NULL','NULL','3-nitropropanoate + NADP(+) = 3-nitroacrylate + NADPH.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.3.1.17','3-methyleneoxindole reductase','NULL','NULL','NULL','3-methyl-1,3-dihydroindol-2-one + NADP(+) = 3-methylene-1,3-dihydro-2H-indol-2-one + NADPH.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.3.1.18','Kynurenate-7,8-dihydrodiol dehydrogenase','NULL','NULL','NULL','7,8-dihydro-7,8-dihydroxykynurenate + NAD(+) = 7,8-dihydroxykynurenate +NADH.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.3.1.19','Cis-1,2-dihydrobenzene-1,2-diol dehydrogenase','Cis-benzene glycol dehydrogenase','NULL','NULL','Cis-1,2-dihydrobenzene-1,2-diol + NAD(+) = catechol + NADH.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.3.1.20','Trans-1,2-dihydrobenzene-1,2-diol dehydrogenase','NULL','NULL','NULL','Trans-1,2-dihydrobenzene-1,2-diol + NADP(+) = catechol + NADPH.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.3.1.21','7-dehydrocholesterol reductase','7-dehydrocholesterol dehydrogenase/cholesterol oxidase','NULL','In mammals the enzyme is part of the cholesterol biosynthesis pathwayand catalyzes the reduction reaction.In the nematode Caenorhabditis elegans the enzyme catalyzes theoxidation reaction as part of the biosynthesis of dafachronic acidsthat are important for control of developmental timing and longevity.','Cholesterol + NADP(+) = cholesta-5,7-dien-3-beta-ol + NADPH.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.3.1.22','3-oxo-5-alpha-steroid 4-dehydrogenase (NADP(+))','3-oxo-5-alpha-steroid 4-dehydrogenase','NULL','The enzyme catalyzes the conversion of assorted 3-oxo-Delta(4)steroids into their corresponding 5-alpha form.Substrates for the mammalian enzyme include testosterone,progesterone, and corticosterone.Substrates for the plant enzyme are brassinosteroids such as campest-4-en-3-one and (22-alpha)-hydroxy-campest-4-en-3-one.Cf. EC 1.3.99.5.Formerly EC 1.3.1.4 and EC 1.3.1.30.','A 3-oxo-5-alpha-steroid + NADP(+) = a 3-oxo-Delta(4)-steroid + NADPH.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.3.1.23','Deleted entry','NULL','NULL','NULL','NULL', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.3.1.24','Biliverdin reductase','NULL','NULL','NULL','Bilirubin + NAD(P)(+) = biliverdin + NAD(P)H.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.3.1.25','1,6-dihydroxycyclohexa-2,4-diene-1-carboxylate dehydrogenase','2-hydro-1,2-dihydroxybenzoate dehydrogenase','NULL','NULL','(1R,6S)-1,6-dihydroxycyclohexa-2,4-diene-1-carboxylate + NAD(+) =catechol + CO(2) + NADH.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.3.1.26','Transferred entry: 1','NULL','NULL','NULL','NULL', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.3.1.27','2-hexadecenal reductase','2-alkenal reductase','NULL','Specific for long chain 2-trans- and 2-cis-alkenals, with chainlength optimum around 14 to 16 carbon atoms.','Hexadecanal + NADP(+) = 2-trans-hexadecenal + NADPH.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.3.1.28','2,3-dihydro-2,3-dihydroxybenzoate dehydrogenase','2,3-DHB dehydrogenase','NULL','Formerly EC 1.1.1.109.','2,3-dihydro-2,3-dihydroxybenzoate + NAD(+) = 2,3-dihydroxybenzoate +NADH.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.3.1.29','Cis-1,2-dihydro-1,2-dihydroxynaphthalene dehydrogenase','Cis-dihydrodiol naphthalene dehydrogenase','NULL','Also acts, at half the rate, on cis-anthracene dihydrodiol and cis-phenanthrene dihydrodiol.','Cis-1,2-dihydronaphthalene-1,2-diol + NAD(+) = naphthalene-1,2-diol +NADH.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.3.1.30','Transferred entry: 1','NULL','NULL','NULL','NULL', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.3.1.31','2-enoate reductase','Enoate reductase','FAD; Iron-sulfur.','Acts, in the reverse direction, on a wide range of alkyl and arylalpha-beta-unsaturated carboxylate ions; 2-butenoate was the bestsubstrate tested.','Butanoate + NAD(+) = but-2-enoate + NADH.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.3.1.32','Maleylacetate reductase','Maleolylacetate reductase','NULL','NULL','3-oxoadipate + NAD(P)(+) = 2-maleylacetate + NAD(P)H.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.3.1.33','Protochlorophyllide reductase','NADPH-protochlorophyllide oxidoreductase','NULL','Catalyzes a light-dependent trans-reduction of the D-ring ofprotochlorophyllide; the product has the (7S,8S)-configuration.','Chlorophyllide a + NADP(+) = protochlorophyllide + NADPH.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.3.1.34','2,4-dienoyl-CoA reductase (NADPH)','4-enoyl-CoA reductase (NADPH)','NULL','Best substrates for reduction contain a 2,4-diene structure withchain length C(8) or C(10).','Trans-2,3-didehydroacyl-CoA + NADP(+) = trans,trans-2,3,4,5-tetradehydroacyl-CoA + NADPH.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.3.1.35','Phosphatidylcholine desaturase','Linoleate synthase','NULL','Also desaturates phosphatidylcholine containing the oleoyl group onO-2 of the glycerol residue.','1-acyl-2-oleoyl-sn-glycero-3-phosphocholine + NAD(+) = 1-acyl-2-linoleoyl-sn-glycero-3-phosphocholine + NADH.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.3.1.36','Geissoschizine dehydrogenase','NULL','NULL','Involved in the interconversion of heteroyohimbine alkaloids inCatharanthus roseus.','Geissoschizine + NADP(+) = 4,21-dehydrogeissoschizine + NADPH.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.3.1.37','Cis-2-enoyl-CoA reductase (NADPH)','NULL','NULL','Not identical with EC 1.3.1.38 (cf. EC 1.3.1.8).','Acyl-CoA + NADP(+) = cis-2,3-dehydroacyl-CoA + NADPH.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.3.1.38','Trans-2-enoyl-CoA reductase (NADPH)','NULL','NULL','Not identical with EC 1.3.1.37 (cf. EC 1.3.1.8).','Acyl-CoA + NADP(+) = trans-2,3-dehydroacyl-CoA + NADPH.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.3.1.39','Enoyl-[acyl-carrier-protein] reductase (NADPH, Re-specific)','Acyl-ACP dehydrogenase','NULL','This enzyme completes each cycle of fatty acid elongation bycatalyzing the stereospecific reduction of the double bond atposition 2 of a growing fatty acid chain, while linked to an acyl-carrier protein.It is one of the activities of EC 2.3.1.85.The mammalian enzyme is Re-specific with respect to NADP(+) (cf.EC 1.3.1.10 and and EC 1.3.1.104).','An acyl-[acyl-carrier protein] + NADP(+) = a trans-2,3-dehydroacyl-[acyl-carrier protein] + NADPH.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.3.1.40','2-hydroxy-6-oxo-6-phenylhexa-2,4-dienoate reductase','NULL','NULL','Broad specificity; reduces a number of compounds produced byPseudomonas from aromatic hydrocarbons by ring fission.','2,6-dioxo-6-phenylhexanoate + NADP(+) = 2-hydroxy-6-oxo-6-phenylhexa-2,4-dienoate + NADPH.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.3.1.41','Xanthommatin reductase','NULL','NULL','From Drosophila melanogaster.','5,12-dihydroxanthommatin + NAD(+) = xanthommatin + NADH.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.3.1.42','12-oxophytodienoate reductase','NULL','NULL','Involved in the conversion of linolenate into jasmonate in Zea mays.','8-((1R,2R)-3-oxo-2-((Z)-pent-2-enyl)cyclopentyl)octanoate + NADP(+) =(15Z)-12-oxophyto-10,15-dienoate + NADPH.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.3.1.43','Arogenate dehydrogenase','Arogenic dehydrogenase','NULL','See also EC 1.3.1.12, EC 1.3.1.78 and EC 1.3.1.79.','L-arogenate + NAD(+) = L-tyrosine + NADH + CO(2).', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.3.1.44','Trans-2-enoyl-CoA reductase (NAD(+))','NULL','NULL','The enzyme from Euglena gracilis acts on crotonoyl-CoA and, moreslowly, on trans-hex-2-enoyl-CoA and trans-oct-2-enoyl-CoA.','Acyl-CoA + NAD(+) = trans-didehydroacyl-CoA + NADH.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.3.1.45','2-hydroxyisoflavone reductase','NULL','NULL','In the reverse reaction, a 2-hydroxyisoflavone is reduced to anisoflavanone; 2-hydroxypseudobaptigenin also acts.Involved in the biosynthesis of the pterocarpin phytoalexinsmedicarpin and maackiain.','Vestitone + NADP(+) = 2-hydroxyformononetin + NADPH.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.3.1.46','Biochanin-A reductase','NULL','NULL','Some other isoflavones are reduced to the correspondingisoflavanones.','Dihydrobiochanin A + NADP(+) = biochanin A + NADPH.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.3.1.47','Alpha-santonin 1,2-reductase','NULL','NULL','NULL','1,2-dihydrosantonin + NAD(P)(+) = alpha-santonin + NAD(P)H.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.3.1.48','15-oxoprostaglandin 13-oxidase','15-oxo-Delta(13)-prostaglandin reductase','NULL','Reduces 15-oxoprostaglandins to 13,14-dihydro derivatives.The enzyme from placenta is specific for NAD(+).','11-alpha-hydroxy-9,15-dioxoprost-5-enoate + NAD(P)(+) = (5Z)-(13E)-11-alpha-hydroxy-9,15-dioxoprosta-5,13-dienoate + NAD(P)H.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.3.1.49','Cis-3,4-dihydrophenanthrene-3,4-diol dehydrogenase','NULL','NULL','NULL','(+)-cis-3,4-dihydrophenanthrene-3,4-diol + NAD(+) = phenanthrene-3,4-diol+ NADH.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.3.1.50','Transferred entry: 1','NULL','NULL','NULL','NULL', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.3.1.51','2-hydroxydaidzein reductase','2-hydroxydihydrodaidzein:NADP(+) 2-oxidoreductase','NULL','In the reverse reaction, the 2-hydroxyisoflavone(2-hydroxydaidzein) is reduced to an isoflavanone.Also acts on 2-hydroxyformononetin and to a small extent on2-hydroxygenistein.Involved in the biosynthesis of the phytoalexin glyceollin.The isoflavones biochanin A, daidzein and genestein as well as theflavonoids apigenin, kaempferol and quercetin do not act assubstrates.','2-hydroxy-2,3-dihydrodaidzein + NADP(+) = 2-hydroxydaidzein + NADPH.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.3.1.52','Transferred entry: 1','NULL','NULL','NULL','NULL', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.3.1.53','(3S,4R)-3,4-dihydroxycyclohexa-1,5-diene-1,4-dicarboxylate dehydrogenase','(1R,2S)-dihydroxy-3,5-cyclohexadiene-1,4-dicarboxylate dehydrogenase','Fe(2+).','Involved in the terephthalate degradation pathway in bacteria.Formerly EC 1.3.1.61.','(3S,4R)-3,4-dihydroxycyclohexa-1,5-diene-1,4-dicarboxylate + NAD(+) =3,4-dihydroxybenzoate + CO(2) + NADH.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.3.1.54','Precorrin-6A reductase','Precorrin-6X reductase','NULL','NULL','Precorrin-6B + NADP(+) = precorrin-6A + NADPH.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.3.1.55','Deleted entry','NULL','NULL','NULL','NULL', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.3.1.56','Cis-2,3-dihydrobiphenyl-2,3-diol dehydrogenase','2,3-dihydro-2,3-dihydroxybiphenyl dehydrogenase','NULL','Catalyzes the second step in the biphenyl degradation pathway inbacteria.','Cis-3-phenylcyclohexa-3,5-diene-1,2-diol + NAD(+) = biphenyl-2,3-diol +NADH.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.3.1.57','Phloroglucinol reductase','NULL','NULL','Involved in the gallate anaerobic degradation pathway in bacteria.','Dihydrophloroglucinol + NADP(+) = phloroglucinol + NADPH.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.3.1.58','2,3-dihydroxy-2,3-dihydro-p-cumate dehydrogenase','Biphenyl-2,3-dihydro-2,3-diol dehydrogenase','NULL','Involved in the p-cymene and p-cumate degradation pathways inPseudomonas putida.','Cis-5,6-dihydroxy-4-isopropylcyclohexa-1,3-dienecarboxylate + NAD(+) =2,3-dihydroxy-p-cumate + NADH.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.3.1.59','Deleted entry','NULL','NULL','NULL','NULL', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.3.1.60','Dibenzothiophene dihydrodiol dehydrogenase','NULL','NULL','Involved in the dibenzothiophene degradation pathway in bacteria.','Cis-1,2-dihydroxy-1,2-dihydrodibenzothiophene + NAD(+) = 1,2-dihydroxydibenzothiophene + NADH.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.3.1.61','Deleted entry','NULL','NULL','NULL','NULL', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.3.1.62','Pimeloyl-CoA dehydrogenase','NULL','NULL','Involved in the benzoate degradation (anaerobic) pathway in bacteria.','Pimeloyl-CoA + NAD(+) = 6-carboxyhex-2-enoyl-CoA + NADH.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.3.1.63','2,4-dichlorobenzoyl-CoA reductase','NULL','NULL','Acts in the reverse direction to form part of the 2,4-dichlorobenzoate degradation pathway in bacteria.','4-chlorobenzoyl-CoA + NADP(+) + chloride = 2,4-dichlorobenzoyl-CoA +NADPH.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.3.1.64','Phthalate 4,5-cis-dihydrodiol dehydrogenase','NULL','NULL','Involved in the phthalate degradation pathway in bacteria.','Cis-4,5-dihydroxycyclohexa-1(6),2-diene-1,2-dicarboxylate + NAD(+) = 4,5-dihydroxyphthalate + NADH.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.3.1.65','5,6-dihydroxy-3-methyl-2-oxo-1,2,5,6-tetrahydroquinoline dehydrogenase','NULL','NULL','Acts in the reverse direction to form part of the 3-methylquinolinedegradation pathway in bacteria.','5,6-dihydroxy-3-methyl-2-oxo-1,2,5,6-tetrahydroquinoline + NAD(+) = 5,6-dihydroxy-3-methyl-2-oxo-1,2-dihydroquinoline + NADH.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.3.1.66','Cis-dihydroethylcatechol dehydrogenase','NULL','NULL','Involved in the ethylbenzene degradation pathway in bacteria.','Cis-1,2-dihydro-3-ethylcatechol + NAD(+) = 3-ethylcatechol + NADH.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.3.1.67','Cis-1,2-dihydroxy-4-methylcyclohexa-3,5-diene-1-carboxylatedehydrogenase','NULL','NULL','Involved in the p-xylene degradation pathway in bacteria.','Cis-1,2-dihydroxy-4-methylcyclohexa-3,5-diene-1-carboxylate + NAD(P)(+) =4-methylcatechol + NAD(P)H + CO(2).', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.3.1.68','1,2-dihydroxy-6-methylcyclohexa-3,5-dienecarboxylate dehydrogenase','NULL','NULL','Involved in the o-xylene degradation pathway in bacteria.','1,2-dihydroxy-6-methylcyclohexa-3,5-dienecarboxylate + NAD(+) =3-methylcatechol + NADH + CO(2).', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.3.1.69','Zeatin reductase','NULL','NULL','Formerly EC 1.1.1.242.','Dihydrozeatin + NADP(+) = zeatin + NADPH.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.3.1.70','Delta(14)-sterol reductase','C-14 sterol reductase','NULL','Acts on a range of steroids with a 14(15)-double bond.','4,4-dimethyl-5-alpha-cholesta-8,24-dien-3-beta-ol + NADP(+) = 4,4-dimethyl-5-alpha-cholesta-8,14,24-trien-3-beta-ol + NADPH.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.3.1.71','Delta(24(24(1)))-sterol reductase','C-24(28) sterol reductase','NULL','Acts on a range of steroids with a 24(24(1))-double bond.','Ergosterol + NADP(+) = ergosta-5,7,22,24(24(1))-tetraen-3-beta-ol +NADPH.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.3.1.72','Delta(24)-sterol reductase','Lanosterol Delta(24)-reductase','NULL','Acts on a range of steroids with a 24(25)-double bond, includinglanosterol, desmosterol and zymosterol.','5-alpha-cholest-7-en-3-beta-ol + NADP(+) = 5-alpha-cholesta-7,24-dien-3-beta-ol + NADPH.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.3.1.73','1,2-dihydrovomilenine reductase','NULL','NULL','Forms part of the ajmaline biosynthesis pathway.','17-O-acetylnorajmaline + NADP(+) = 1,2-dihydrovomilenine + NADPH.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.3.1.74','2-alkenal reductase (NAD(P)(+))','2-alkenal reductase','NULL','Highly specific for 4-hydroxynon-2-enal and non-2-enal.Alk-2-enals of shorter chain have lower affinities.Exhibits high activities also for alk-2-enones such as but-3-en-2-oneand pent-3-en-2-one.Inactive with cyclohex-2-en-1-one and 12-oxophytodienoic acid.Involved in the detoxification of alpha,beta-unsaturated aldehydesand ketones (cf. EC 1.3.1.102).','A n-alkanal + NAD(P)(+) = an alk-2-enal + NAD(P)H.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.3.1.75','Divinyl chlorophyllide a 8-vinyl-reductase','4VCR','NULL','Also reduces divinyl protochlorophyllide to protochlorophyllide insome species, providing an alternative pathway.','Chlorophyllide a + NADP(+) = divinyl chlorophyllide a + NADPH.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.3.1.76','Precorrin-2 dehydrogenase','Precorrin-2 oxidase','NULL','Catalyzes the second of three steps leading to the formation ofsiroheme from uroporphyrinogen III.The first step involves the donation of two S-adenosyl-L-methionine-derived methyl groups to carbons 2 and 7 of uroporphyrinogen III toform precorrin-2 (EC 2.1.1.107) and the third step involves thechelation of ferrous iron to sirohydrochlorin to form siroheme(EC 4.99.1.4).In Saccharomyces cerevisiae, the last two steps are carried out by asingle bifunctional enzyme, Met8p.In some bacteria, steps 1-3 are catalyzed by a single multifunctionalprotein called CysG, whereas in Bacillus megaterium, three separateenzymes carry out each of the steps, with SirC being responsible forthe above reaction.','Precorrin-2 + NAD(+) = sirohydrochlorin + NADH.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.3.1.77','Anthocyanidin reductase','ANR','NULL','Forms 2,3-cis-flavan-3-ols.The isomeric 2,3-trans-flavan-3-ols are formed from flavan-3,4-diolsby EC 1.17.1.3.While the enzyme from the legume Medicago truncatula (MtANR) usesboth NADPH and NADH as reductant, that from the crucifer Arabidopsisthaliana (AtANR) uses only NADPH.Also, while the substrate preference of MtANR iscyanidin','A flavan-3-ol + 2 NAD(P)(+) = an anthocyanidin + 2 NAD(P)H.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.3.1.78','Arogenate dehydrogenase (NADP(+))','Arogenic dehydrogenase','NULL','Arogenate dehydrogenases may utilize NAD(+) (EC 1.3.1.43), NADP(+)(EC 1.3.1.78), or both (EC 1.3.1.79).NADP(+)-dependent enzymes usually predominate in higher plants.The enzyme from the cyanobacterium Synechocystis sp. PCC 6803 and theTyrAAT1 isoform of the plant Arabidopsis thaliana cannot useprephenate as a substrate, while the Arabidopsis isoform TyrAAT2 canuse it very poorly.','L-arogenate + NADP(+) = L-tyrosine + NADPH + CO(2).', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.3.1.79','Arogenate dehydrogenase (NAD(P)(+))','Arogenic dehydrogenase','NULL','Arogenate dehydrogenases may utilize NAD(+) (EC 1.3.1.43), NADP(+)(EC 1.3.1.78), or both (EC 1.3.1.79).Enzymes that can utilize both cofactors have been reported from someProteobacteria, including Burkholderia caryophylli, Burkholderiacepacia, Pseudomonas marginata and Delftia acidovorans.','L-arogenate + NAD(P)(+) = L-tyrosine + NAD(P)H + CO(2).', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.3.1.80','Red chlorophyll catabolite reductase','RCC reductase','NULL','Chlorophyll degradation is a characteristic symptom of leafsenescence and fruit ripening.The reaction catalyzed by this enzyme requires reduced ferredoxin,which is generated either by NADPH through the pentose-phosphatepathway or by the action of photosystem I.This reaction takes place without release of the substrate fromEC 1.14.12.20.Depending on the plant species used as the source of enzyme, one oftwo possible C-1 epimers of primary fluorescent chlorophyllcatabolite (pFCC), pFCC-1 or pFCC-2, is normally formed, with allgenera or species within a family producing the same isomer.After modification and export, pFCCs are eventually imported into thevacuole, where the acidic environment causes their non-enzymicconversion into colorless breakdown products called non-fluorescentchlorophyll catabolites (NCCs).','Primary fluorescent chlorophyll catabolite + NADP(+) = red chlorophyllcatabolite + NADPH.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.3.1.81','(+)-pulegone reductase','NULL','NULL','NADH cannot replace NADPH as reductant.The Delta(8,9)-double bond of (+)-cis-isopulegone and the Delta(1,2)-double bond of (+-)-piperitone are not substrates.The enzyme from peppermint (Mentha x piperita) converts (+)-pulegoneinto both (-)-menthone and (+)-isomenthone at a ratio of 70:30 fornative enzyme but it does not catalyze the reverse reaction.','(1) (-)-menthone + NADP(+) = (+)-pulegone + NADPH.(2) (+)-isomenthone + NADP(+) = (+)-pulegone + NADPH.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.3.1.82','(-)-isopiperitenone reductase','NULL','NULL','The reaction occurs in the opposite direction to that shown above.Participates in the menthol-biosynthesis pathway of Mentha plants.(+)-pulegone, (+)-cis-isopulegone and (-)-menthone are notsubstrates.Has a preference for NADPH as the reductant, with NADH being a poorsubstitute.Highly regioselective for the reduction of the endocyclic 1,2-doublebond, and is stereoselective, producing only the 1R-configuredproduct.','(+)-cis-isopulegone + NADP(+) = (-)-isopiperitenone + NADPH.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.3.1.83','Geranylgeranyl diphosphate reductase','Geranylgeranyl reductase','NULL','This enzyme also acts on geranylgeranyl-chlorophyll a.The reaction occurs in three steps.Which order the three double bonds are reduced is not known.','Phytyl diphosphate + 3 NADP(+) = geranylgeranyl diphosphate + 3 NADPH.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.3.1.84','Acrylyl-CoA reductase (NADPH)','NULL','Zinc.','Catalyzes a step in the 3-hydroxypropanoate/4-hydroxybutyrate cycle,an autotrophic CO(2) fixation pathway found in some thermoacidophilicarchaea.The reaction is catalyzed in the opposite direction to that shown.The enzyme from Sulfolobus tokodaii does not act on either NADH orcrotonyl-CoA.Different from EC 1.3.1.8, which acts only on enoyl-CoA derivativesof carbon chain length 4 to 16.','Propanoyl-CoA + NADP(+) = acrylyl-CoA + NADPH.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.3.1.85','Crotonyl-CoA carboxylase/reductase','CCR','NULL','The reaction is catalyzed in the reverse direction.This enzyme, isolated from the bacterium Rhodobacter sphaeroides,catalyzes (E)-but-2-enoyl-CoA-dependent oxidation of NADPH in thepresence of CO(2).When CO(2) is absent, the enzyme catalyzes the reduction of (E)-but-2-enoyl-CoA to butanoyl-CoA, but with only 10% of maximal activity(relative to (E)-but-2-enoyl-CoA carboxylation).','(2S)-ethylmalonyl-CoA + NADP(+) = (E)-but-2-enoyl-CoA + CO(2) + NADPH.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.3.1.86','Crotonyl-CoA reductase','3-hydroxyacyl CoA reductase','NULL','Catalyzes the reaction in the reverse direction.This enzyme from Streptomyces collinus is specific for (E)-but-2-enoyl-CoA, and is proposed to provide butanoyl-CoA as a starter unitfor straight-chain fatty acid biosynthesis.','Butanoyl-CoA + NADP(+) = (E)-but-2-enoyl-CoA + NADPH.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.3.1.87','3-(cis-5,6-dihydroxycyclohexa-1,3-dien-1-yl)propanoate dehydrogenase','2,3-dihydroxy-2,3-dihydro-phenylpropionate dehydrogenase','NULL','This enzyme catalyzes a step in the pathway of phenylpropanoidcompounds degradation.Formerly EC 1.3.1.n1.','(1) 3-(cis-5,6-dihydroxycyclohexa-1,3-dien-1-yl)propanoate + NAD(+) =3-(2,3-dihydroxyphenyl)propanoate + NADH.(2) (2E)-3-(cis-5,6-dihydroxycyclohexa-1,3-dien-1-yl)prop-2-enoate +NAD(+) = (2E)-3-(2,3-dihydroxyphenyl)prop-2-enoate + NADH.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.3.1.88','tRNA-dihydrouridine(16/17) synthase (NAD(P)(+))','tRNA-dihydrouridine synthase 1','FAD.','The enzyme specifically modifies uracil(16) and uracil(17) in tRNA.','(1) 5,6-dihydrouracil(16) in tRNA + NAD(P)(+) = uracil(16) in tRNA +NAD(P)H.(2) 5,6-dihydrouracil(17) in tRNA + NAD(P)(+) = uracil(17) in tRNA +NAD(P)H.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.3.1.89','tRNA-dihydrouridine(47) synthase (NAD(P)(+))','tRNA-dihydrouridine synthase 3','FAD.','The enzyme specifically modifies uracil(47) in tRNA.','5,6-dihydrouracil(47) in tRNA + NAD(P)(+) = uracil(47) in tRNA + NAD(P)H.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.3.1.90','tRNA-dihydrouridine(20a/20b) synthase (NAD(P)(+))','NULL','FAD.','The enzyme specifically modifies uracil(20a) and uracil(20b) in tRNA.','(1) 5,6-dihydrouracil(20a) in tRNA + NAD(P)(+) = uracil(20a) in tRNA +NAD(P)H.(2) 5,6-dihydrouracil(20b) in tRNA + NAD(P)(+) = uracil(20b) in tRNA +NAD(P)H.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.3.1.91','tRNA-dihydrouridine(20) synthase (NAD(P)(+))','tRNA-dihydrouridine synthase 2','FAD.','The enzyme specifically modifies uracil(20) in tRNA.','5,6-dihydrouracil(20) in tRNA + NAD(P)(+) = uracil(20) in tRNA + NAD(P)H.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.3.1.92','Artemisinic aldehyde Delta(11(13)) reductase','NULL','NULL','Cloned from Artemisia annua.In addition to the reduction of artemisinic aldehyde it is also ableto a lesser extent to reduce artemisinic alcohol and artemisinicacid.Part of the biosyntheis of artemisinin.','(11R)-dihydroartemisinic aldehyde + NADP(+) = artemisinic aldehyde +NADPH.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.3.1.93','Very-long-chain enoyl-CoA reductase','NULL','NULL','This is the fourth component of the elongase, a microsomal proteincomplex responsible for extending palmitoyl-CoA and stearoyl-CoA (andmodified forms thereof) to very-long-chain acyl CoAs.Cf. EC 2.3.1.199, EC 1.1.1.330 and EC 4.2.1.134.','A very-long-chain acyl-CoA + NADP(+) = a very-long-chain trans-2,3-dehydroacyl-CoA + NADPH.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.3.1.94','Polyprenol reductase','NULL','NULL','The reaction occurs in the reverse direction with reduction of theterminal double bond next to the alcohol group.Isolated from human fetal brain tissue but present in all eukaryotes.In mammalian cells dolichols are predominantly 18-21 isoprene unitsin length.','Ditrans,polycis-dolichol + NADP(+) = ditrans,polycis-polyprenol + NADPH.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.3.1.95','Acryloyl-CoA reductase (NADH)','NULL','FAD.','The reaction is catalyzed in the opposite direction to that shown.The enzyme from the bacterium Clostridium propionicum is a complexthat includes an electron-transfer flavoprotein (ETF).The ETF is reduced by NADH and transfers the electrons to the activesite.Catalyzes a step in a pathway for L-alanine fermentation topropanoate.Cf. EC 1.3.1.84.','Propanoyl-CoA + NAD(+) = acryloyl-CoA + NADH.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.3.1.96','Botryococcus squalene synthase','NULL','NULL','Isolated from the green alga Botryococcus braunii BOT22.Acts in the reverse direction.cf. EC 2.5.1.21, where squalene is formed directly from farnesyldiphosphate.','Squalene + diphosphate + NADP(+) = presqualene diphosphate + NADPH.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.3.1.97','Botryococcene synthase','NULL','NULL','Isolated from the green alga Botryococcus braunii BOT22.Acts in the reverse direction.Involved in the production of botryococcenes, which are triterpenoidhydrocarbons of isoprenoid origin produced in large amount by thisalga.','C(30) botryococcene + NADP(+) + diphosphate = presqualene diphosphate +NADPH.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.3.1.98','UDP-N-acetylmuramate dehydrogenase','UDP-GlcNAc-enoylpyruvate reductase','FAD.','NADH can to a lesser extent replace NADPH.Formerly EC 1.1.1.158.','UDP-N-acetyl-alpha-D-muramate + NADP(+) = UDP-N-acetyl-3-O-(1-carboxyvinyl)-alpha-D-glucosamine + NADPH.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.3.1.99','Iridoid synthase','NULL','NULL','Isolated from the plant Catharanthus roseus.The reaction may involve cyclization via a Diels-Alder or Michaelreaction.Iridoids are involved in the biosynthesis of many indole alkaloids.The cyclic hemiacetal is readily hydrolyzed to the correspondingdial.','(6E)-8-oxogeranial + NAD(P)H = cis-trans-nepetalactol + NAD(P)(+).', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.3.1.100','Chanoclavine-I aldehyde reductase','NULL','FMN.','The enzyme participates in the biosynthesis of fumigaclavine C,an ergot alkaloid produced by some fungi of the Trichocomaceaefamily.The enzyme catalyzes the reduction of chanoclavine-I aldehyde todihydrochanoclavine-I aldehyde.This hydrolyzes spontaneously to form 6,8-dimethyl-6,7-didehydroergoline, which is converted to festuclavine by EC 1.5.1.44.','Dihydrochanoclavine-I aldehyde + NADP(+) = chanoclavine-I aldehyde +NADPH.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.3.1.101','2,3-bis-O-geranylgeranyl-sn-glycerol 1-phosphate reductase (NAD(P)H)','2,3-digeranylgeranylglycerophospholipid reductase','FAD.','The enzyme from the archaeon Thermoplasma acidophilum is involved inthe biosynthesis of membrane lipids.In vivo the reaction occurs in the reverse direction with theformation of 2,3-bis-O-phytanyl-sn-glycerol 1-phosphate.cf. EC 1.3.99.34.','2,3-bis-(O-phytanyl)-sn-glycerol 1-phosphate + 8 NAD(P)(+) = 2,3-bis-(O-geranylgeranyl)-sn-glycerol 1-phosphate + 8 NAD(P)H.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.3.1.102','2-alkenal reductase (NADP(+))','NADPH-dependent alkenal/one oxidoreductase','NULL','Shows highest activity with 1-nitrocyclohexene but also hassignificant activity with 2-methylpentenal and trans-cinnamaldehyde.Involved in the detoxification of alpha,beta-unsaturated aldehydesand ketones.Has very low activity with NAD as reductant (cf. EC 1.3.1.74).','An n-alkanal + NADP(+) = an alk-2-enal + NADPH.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.3.1.103','2-haloacrylate reductase','NULL','NULL','The enzyme acts in the degradation pathway of unsaturatedorganohalogen compounds by the bacterium Burkholderia sp. WS.','(S)-2-chloropropanoate + NADP(+) = 2-chloroacrylate + NADPH.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.3.1.104','Enoyl-[acyl-carrier-protein] reductase','Acyl-ACP dehydrogenase','NULL','The enzyme completes each cycle of fatty acid elongation bycatalyzing the stereospecific reduction of the double bond atposition 2 of a growing fatty acid chain, while linked to the acyl-carrier protein, in an NADPH-dependent manner.This entry stands for enzymes whose stereo-specificity with respectto NADP(+) is not known (cf. EC 1.3.1.39, EC 1.3.1.10 andEC 1.3.1.9).','An acyl-[acyl-carrier protein] + NADP(+) = a trans-2,3-dehydroacyl-[acyl-carrier protein] + NADPH.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.3.1.105','2-methylene-furan-3-one reductase','Enone oxidoreductase','NULL','In the fruit-ripening process of strawberry (Fragaria x ananassa) thereaction is catalyzed in the reverse direction from that shown.4-hydroxy-2,5-dimethylfuran-3(2H)-one is one of the major aromacompounds in the fruits.The enzyme has also been detected in tomato (Solanum lycopersicum)fruits.NADPH is the preferred cofactor.','4-hydroxy-2,5-dimethylfuran-3(2H)-one + NAD(P)(+) = 4-hydroxy-5-methyl-2-methylenefuran-3(2H)-one + NAD(P)H.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.3.1.106','Cobalt-precorrin-6A reductase','NULL','NULL','The enzyme catalyzes a step in the anaerobic (early cobalt insertion)pathway of adenosylcobalamin biosynthesis.The enzyme from the bacterium Bacillus megaterium has no activitywith NADPH.The equivalent enzyme in the aerobic pathway is EC 1.3.1.54.','Cobalt-precorrin-6B + NAD(+) = cobalt-precorrin-6A + NADH.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.3.1.n1','Transferred entry: 1','NULL','NULL','NULL','NULL', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.3.2.1','Transferred entry: 1','NULL','NULL','NULL','NULL', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.3.2.2','Transferred entry: 1','NULL','NULL','NULL','NULL', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.3.2.3','L-galactonolactone dehydrogenase','Galactonolactone dehydrogenase','NULL','Catalyzes the final step in the biosynthesis of L-ascorbic acid inhigher plants and in nearly all higher animals with the exception ofprimates and some birds.Very specific for its substrate L-galactono-1,4-lactone asD-galactono-gamma-lactone, D-gulono-gamma-lactone, L-gulono-gamma-lactone, D-erythronic-gamma-lactone, D-xylonic-gamma-lactone,L-mannono-gamma-lactone, D-galactonate, D-glucuronate and D-gluconateare not substrates.FAD, NAD(+), NADP(+) and O(2) (cf. EC 1.3.3.12) cannot act aselectron acceptor.','L-galactono-1,4-lactone + 4 ferricytochrome c = L-dehydroascorbate +4 ferrocytochrome c + 4 H(+).', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.3.3.1','Transferred entry: 1','NULL','NULL','NULL','NULL', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.3.3.2','Transferred entry: 1','NULL','NULL','NULL','NULL', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.3.3.3','Coproporphyrinogen oxidase','Coprogen oxidase','Iron.','NULL','Coproporphyrinogen-III + O(2) + 2 H(+) = protoporphyrinogen-IX + 2 CO(2)+ 2 H(2)O.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.3.3.4','Protoporphyrinogen oxidase','Protoporphyrinogen IX oxidase','FAD.','Also slowly oxidizes mesoporphyrinogen IX.','Protoporphyrinogen-IX + 3 O(2) = protoporphyrin-IX + 3 H(2)O(2).', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.3.3.5','Bilirubin oxidase','NULL','NULL','NULL','2 bilirubin + O(2) = 2 biliverdin + 2 H(2)O.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.3.3.6','Acyl-CoA oxidase','NULL','FAD.','Acts on CoA derivatives of fatty acids with chain length from C(8) toC(18).','Acyl-CoA + O(2) = trans-2,3-dehydroacyl-CoA + H(2)O(2).', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.3.3.7','Dihydrouracil oxidase','NULL','FMN.','Also oxidizes dihydrothymine to thymine.','5,6-dihydrouracil + O(2) = uracil + H(2)O(2).', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.3.3.8','Tetrahydroberberine oxidase','THB oxidase','Flavoprotein.','(R)-tetrahydroberberines are not oxidized.Formerly EC 1.5.3.8.','(S)-tetrahydroberberine + 2 O(2) = berberine + 2 H(2)O(2).', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.3.3.9','Secologanin synthase','NULL','Heme-thiolate.','Secologanin is the precursor of the monoterpenoid indole alkaloidsand ipecac alkaloids.','Loganin + NADPH + O(2) = secologanin + NADP(+) + 2 H(2)O.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.3.3.10','Tryptophan alpha,beta-oxidase','L-tryptophan 2,3-oxidase','Heme.','The enzyme from Chromobacterium violaceum is specific for tryptophanderivatives possessing its carboxyl group free or as an amide orester, and an unsubstituted indole ring.Also catalyzes the alpha,beta dehydrogenation of L-tryptophan sidechains in peptides.The product of the reaction can hydrolyze spontaneously to formindolepyruvate.Formerly EC 1.4.3.17.','L-tryptophan + O(2) = alpha,beta-didehydrotryptophan + H(2)O(2).', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.3.3.11','Pyrroloquinoline-quinone synthase','NULL','NULL','So far only a single turnover of the enzyme has been observed,and the pyrroloquinoline quinone remains bound to it; it is not yetknown what releases the product in the bacterium.','6-(2-amino-2-carboxyethyl)-7,8-dioxo-1,2,3,4,7,8-hexahydroquinoline-2,4-dicarboxylate + 3 O(2) = 4,5-dioxo-4,5-dihydro-1H-pyrrolo[2,3-f]quinoline-2,7,9-tricarboxylate + 2 H(2)O(2) + 2 H(2)O.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.3.3.12','L-galactonolactone oxidase','L-xylono-1,4-lactone oxidase','FAD.','Acts on the 1,4-lactones of L-galactonic, D-altronic, L-fuconic,D-arabinic and D-threonic acids.Not identical with EC 1.1.3.8 (cf. EC 1.3.2.3).Formerly EC 1.1.3.24.','L-galactono-1,4-lactone + O(2) = L-ascorbate + H(2)O(2).', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.3.3.13','Albonoursin synthase','Cyclic dipeptide oxidase','Flavoprotein.','The enzyme can also oxidize several other cyclo dipeptides, the bestbeing cyclo(L-tryptophyl-L-tryptophyl) and cyclo(L-phenylalanyl-L-phenylalanyl).','Cyclo(L-leucyl-L-phenylalanyl) + 2 O(2) = albonoursin + 2 H(2)O(2).', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.3.3.14','Aclacinomycin-A oxidase','Aclacinomycin oxidoreductase','FAD.','This bifunctional enzyme is a secreted flavin-dependent enzyme thatis involved in the modification of the terminal sugar residues in thebiosynthesis of aclacinomycins.The enzyme utilizes the same active site to catalyze the oxidation ofthe rhodinose moiety of aclacinomycin N to the cinerulose A moiety ofaclacinomycin A (cf. EC 1.1.3.45) and the oxidation of the latter tothe L-aculose moiety of aclacinomycin Y.','Aclacinomycin A + O(2) = aclacinomycin Y + H(2)O(2).', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.3.5.1','Succinate dehydrogenase (quinone)','Succinate dehydrogenase (ubiquinone)','FAD; Iron-sulfur.','The enzyme is found in the inner mitochondrial membrane in eukaryotesand the plasma membrane of many aerobic or facultative bacteria.It catalyzes succinate oxidation in the citric acid cycle andtransfers the electrons to quinones in the membrane, thusconstituting a part of the aerobic respiratory chain (known ascomplex II).Eukaryotic enzymes utilize ubioquinone while bacterial enzymesutilize either ubiquinone or menaquinone, depending on the organism.Cf. EC 1.3.5.4.','Succinate + a quinone = fumarate + a quinol.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.3.5.2','Dihydroorotate dehydrogenase (quinone)','DHOD','FMN.','This Class 2 dihydroorotate dehydrogenase enzyme contains FMN.The enzyme is found in eukaryotes in the mitochondrial membrane,and in some Gram negative bacteria associated with the cytoplasmicmembrane.The reaction is the only redox reaction in the de-novo biosynthesisof pyrimidine nucleotides.The best quinone electron acceptors for the enzyme from bovine liverare ubiquinone-6 and ubiquinone-7, although simple quinones, such asbenzoquinone, can also act as acceptor at lower rates.Methyl-, ethyl-, tert-butyl and benzyl-(S)-dihydroorotates are alsosubstrates, but 1- and 3-methyl and 1,3-dimethyl methyl-(S)-dihydroorotates are not.Class 1 dihydroorotate dehydrogenases use either fumarate(EC 1.3.98.1), NAD(+) (EC 1.3.1.14) or NADP(+) (EC 1.3.1.15) aselectron acceptor.Formerly EC 1.3.99.11.','(S)-dihydroorotate + a quinone = orotate + a quinol.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.3.5.3','Protoporphyrinogen IX dehydrogenase (menaquinone)','NULL','NULL','This enzyme enables Escherichia coli to synthesize heme in bothaerobic and anaerobic environments.','Protoporphyrinogen IX + 3 menaquinone = protoporphyrin IX + 3 menaquinol.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.3.5.4','Fumarate reductase (quinol)','Fumarate reductase (menaquinone)','NULL','The enzyme, which is found in anaerobic and facultative organismssuch as bacteria, parasitic helminthes, and lower marine organisms,utilizes low potential quinols, such as menaquinol and rhodoquinol,to reduce fumarate as the final step of an anaerobic respiratorychain.The enzyme is known as complex II of the electron transfer chain,similarly to EC 1.3.5.1, to which it is closely related.','Succinate + a quinone = fumarate + a quinol.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.3.5.5','15-cis-phytoene desaturase','PDS','NULL','This enzyme is involved in carotenoid biosynthesis in plants andcyanobacteria.The enzyme from Synechococcus can also use NAD(+) and NADP(+) aselectron acceptor under anaerobic conditions.The enzyme from Gentiana lutea shows no activity with NAD(+) orNADP(+).','15-cis-phytoene + 2 plastoquinone = 9,15,9-tri-cis-zeta-carotene +2 plastoquinol.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.3.5.6','9,9-di-cis-zeta-carotene desaturase','ZDS','NULL','This enzyme is involved in carotenoid biosynthesis in plants andcyanobacteria.Formerly EC 1.14.99.30.','9,9-di-cis-zeta-carotene + 2 quinone = 7,9,7,9-tetra-cis-lycopene +2 quinol.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.3.7.1','6-hydroxynicotinate reductase','6-hydroxynicotinic reductase','NULL','Forms part of the nicotinate-fermentation catabolism pathway inEubacterium barkeri.Other enzymes involved in this pathway are EC 1.17.5.1, EC 3.5.2.18,EC 1.1.1.291, EC 5.4.99.4, EC 5.3.3.6, EC 4.2.1.85 and EC 4.1.3.32.','6-oxo-1,4,5,6-tetrahydronicotinate + oxidized ferredoxin =6-hydroxynicotinate + reduced ferredoxin.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.3.7.2','15,16-dihydrobiliverdin:ferredoxin oxidoreductase','NULL','NULL','Catalyzes the two-electron reduction of biliverdin IX-alpha at theC15 methine bridge.It has been proposed that this enzyme and EC 1.3.7.3 function as adual enzyme complex in the conversion of biliverdin IX-alpha intophycoerythrobilin.','15,16-dihydrobiliverdin + oxidized ferredoxin = biliverdin IX-alpha +reduced ferredoxin.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.3.7.3','Phycoerythrobilin:ferredoxin oxidoreductase','NULL','NULL','Catalyzes the two-electron reduction of the C2 and C3(1) diene systemof 15,16-dihydrobiliverdin.Specific for 15,16-dihydrobiliverdin.It has been proposed that this enzyme and EC 1.3.7.2 function as adual enzyme complex in the conversion of biliverdin IX-alpha intophycoerythrobilin.','(3Z)-phycoerythrobilin + oxidized ferredoxin = 15,16-dihydrobiliverdin +reduced ferredoxin.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.3.7.4','Phytochromobilin:ferredoxin oxidoreductase','P-Phi-B synthase','NULL','Catalyzes the two-electron reduction of biliverdin IX-alpha.Can use [2Fe-2S] ferredoxins from a number of sources as acceptor butnot the [4Fe-4S] ferredoxin from Clostridium pasteurianum.The isomerization of (3Z)-phytochromobilin to (3E)-phytochromobilinis thought to occur prior to covalent attachment to apophytochrome inthe plant cell cytoplasm.Flavodoxins can be used instead of ferredoxin.','(3Z)-phytochromobilin + oxidized ferredoxin = biliverdin IX-alpha +reduced ferredoxin.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.3.7.5','Phycocyanobilin:ferredoxin oxidoreductase','NULL','NULL','Catalyzes the four-electron reduction of biliverdin IX-alpha(2-electron reduction at both the A and D rings).Reaction proceeds via an isolatable 2-electron intermediate,18(1),18(2)-dihydrobiliverdin.Flavodoxins can be used instead of ferredoxin.The direct conversion of biliverdin IX-alpha (BV) to (3Z)-phycocyanobilin (PCB) in the cyanobacteria Synechocystis sp.PCC 6803, Anabaena sp. PCC7120 and Nostoc punctiforme is in contrastto the proposed pathways of PCB biosynthesis in the red algaCyanidium caldarium, which involves (3Z)-phycoerythrobilin (PEB) asan intermediate and in the green alga Mesotaenium caldariorum,in which PCB is an isolable intermediate.','(3Z)-phycocyanobilin + oxidized ferredoxin = biliverdin IX-alpha +reduced ferredoxin.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.3.7.6','Phycoerythrobilin synthase','NULL','NULL','This enzyme, from a cyanophage infecting oceanic cyanobacteria of theProchlorococcus genus, uses a four-electron reduction to carry outthe reactions catalyzed by EC 1.3.7.2 and EC 1.3.7.3.15,16-dihydrobiliverdin is formed as a bound intermediate.Free 15,16-dihydrobiliverdin can also act as a substrate to formphycoerythrobilin.','(3Z)-phycoerythrobilin + 2 oxidized ferredoxin = biliverdin IX-alpha +2 reduced ferredoxin.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.3.7.7','Ferredoxin:protochlorophyllide reductase (ATP-dependent)','Light-independent protochlorophyllide reductase','Iron-sulfur.','Occurs in photosynthetic bacteria, cyanobacteria, green algae andgymnosperms.The enzyme catalyzes trans-reduction of the D-ring ofprotochlorophyllide; the product has the (7S,8S)-configuration.Unlike EC 1.3.1.33 light is not required.The enzyme contains a [4Fe-4S] cluster, and structurally resemblesthe Fe protein/MoFe protein complex of EC 1.18.6.1, which catalyzesan ATP-driven reduction.','Protochlorophyllide a + reduced ferredoxin + 2 ATP + 2 H(2)O =chlorophyllide a + oxidized ferredoxin + 2 ADP + 2 phosphate.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.3.7.8','Benzoyl-CoA reductase','NULL','Iron-sulfur; Magnesium or manganese.','Inactive toward aromatic acids that are not CoA esters but will alsocatalyze the reaction: NH(3) + acceptor + 2 ADP + 2 phosphate =hydroxylamine + reduced acceptor + 2 ATP + H(2)O.In the presence of reduced acceptor, but in the absence of oxidizablesubstrate, the enzyme catalyzes the hydrolysis of ATP to ADP plusphosphate.Formerly EC 1.3.99.15.','Cyclohexa-1,5-diene-1-carbonyl-CoA + oxidized ferredoxin + 2 ADP +2 phosphate = benzoyl-CoA + reduced ferredoxin + 2 ATP + 2 H(2)O.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.3.7.9','4-hydroxybenzoyl-CoA reductase','NULL','Flavin; Iron-sulfur; Molybdenum.','Involved in the anaerobic pathway of phenol metabolism in bacteria.A ferredoxin with two [4Fe-4S] clusters functions as the naturalelectron donor.Formerly EC 1.3.99.20.','Benzoyl-CoA + oxidized ferredoxin = 4-hydroxybenzoyl-CoA +reduced ferredoxin.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.3.7.10','Transferred entry: 1','NULL','NULL','NULL','NULL', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.3.8.1','Short-chain acyl-CoA dehydrogenase','Butanoyl-CoA dehydrogenase','FAD.','One of several enzymes that catalyze the first step in fatty acidsbeta-oxidation.The enzyme catalyzes the oxidation of saturated short-chain acyl-CoAthioesters to give a trans 2,3-unsaturated product by removal of thetwo pro-R-hydrogen atoms.The enzyme from beef liver accepts substrates with acyl chain lengthsof 3 to 8 carbon atoms.The highest activity was reported with either butanoyl-CoA orpentanoyl-CoA.The enzyme from rat has only 10% activity with hexanoyl-CoA (comparedto butanoyl-CoA) and no activity with octanoyl-CoA.Cf. EC 1.3.8.7, EC 1.3.8.8 and EC 1.3.8.9.Formerly EC 1.3.2.1 and EC 1.3.99.2.','Butanoyl-CoA + electron-transfer flavoprotein = 2-butenoyl-CoA + reducedelectron-transfer flavoprotein.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.3.8.2','4,4-diapophytoene desaturase','Dehydrosqualene desaturase','NULL','Typical of Staphylococcus aureus and some other bacteria such asHeliobacillus sp.Responsible for four successive dehydrogenations.In some species it only proceeds as far as all-trans-4,4-diaponeurosporene.','15-cis-4,4-diapophytoene + 4 FAD = all-trans-4,4-diapolycopene +4 FADH(2).', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.3.8.3','(R)-benzylsuccinyl-CoA dehydrogenase','NULL','FAD.','Unlike other acyl-CoA dehydrogenases, this enzyme exhibits highsubstrate- and enantiomer specificity it is highly specific for (R)-benzylsuccinyl-CoA and is inhibited by (S)-benzylsuccinyl-CoA.Forms the third step in the anaerobic toluene metabolic pathway inThauera aromatica.Ferricenium ion is an effective artificial electron acceptor.Formerly EC 1.3.99.21.','(R)-2-benzylsuccinyl-CoA + electron-transferring flavoprotein = (E)-2-benzylidenesuccinyl-CoA + reduced electron-transferring flavoprotein.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.3.8.4','Isovaleryl-CoA dehydrogenase','NULL','FAD.','Pentanoate can act as donor.Formerly EC 1.3.99.10.','Isovaleryl-CoA + electron-transfer flavoprotein = 3-methylcrotonyl-CoA +reduced electron-transfer flavoprotein.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.3.8.5','2-methyl-branched-chain-enoyl-CoA reductase','NULL','FAD.','From Ascaris suum.The reaction functions in shuttling reducing power from the electron-transport chain to 2-methyl branched-chain enoyl CoA.Formerly EC 1.3.1.52.','2-methylbutanoyl-CoA + electron-transfer flavoprotein = (E)-2-methylbut-2-enoyl-CoA + reduced electron-transfer flavoprotein + H(+).', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.3.8.6','Glutaryl-CoA dehydrogenase (ETF)','Glutaryl-CoA dehydrogenase','FAD.','The enzyme catalyzes the oxidation of glutaryl-CoA to glutaconyl-CoA(which remains bound to the enzyme), and the decarboxylation of thelatter to crotonyl-CoA (cf. EC 4.1.1.70).FAD is the electron acceptor in the oxidation of the substrate,and its reoxidation by electron-transfer flavoprotein completes thecatalytic cycle.The anaerobic, sulfate-reducing bacterium Desulfococcus multivoranscontains two glutaryl-CoA dehydrogenases: a decarboxylating enzyme(this entry), and a non-decarboxylating enzyme that only catalyzesthe oxidation to glutaconyl-CoA (EC 1.3.99.32).Formerly EC 1.3.99.7.','Glutaryl-CoA + electron-transfer flavoprotein = crotonyl-CoA + CO(2) +reduced electron-transfer flavoprotein.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.3.8.7','Medium-chain acyl-CoA dehydrogenase','Acyl CoA dehydrogenase','NULL','One of several enzymes that catalyze the first step in fatty acidsbeta-oxidation.The enzyme from pig liver can accept substrates with acyl chainlengths of 4 to 16 carbon atoms, but is most active with C(8) toC(12) compounds.The enzyme from rat does not accept C(16) at all and is most activewith C(6)-C(8) compounds.cf. EC 1.3.8.1, EC 1.3.8.8 and EC 1.3.8.9.Formerly EC 1.3.2.2 and EC 1.3.99.3.','A medium-chain acyl-CoA + electron-transfer flavoprotein = a medium-chaintrans-2,3-dehydroacyl-CoA + reduced electron-transfer flavoprotein.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.3.8.8','Long-chain-acyl-CoA dehydrogenase','NULL','FAD.','One of several enzymes that catalyze the first step in fatty acidsbeta-oxidation.The enzyme from pig liver can accept substrates with acyl chainlengths of 6 to at least 16 carbon atoms.The highest activity was found with C(12), and the rates with C(8)and C(16) were 80% and 70%, respectively.The enzyme from rat can accept substrates with C(8)-C(22).It is most active with C(14) and C(16), and has no activity withC(4), C(6) or C(24).cf. EC 1.3.8.1, EC 1.3.8.8 and EC 1.3.8.9.Formerly EC 1.3.99.3 and EC 1.3.99.13.','Long-chain-acyl-CoA + electron-transfer flavoprotein = long-chain-2,3-dehydroacyl-CoA + reduced electron-transfer flavoprotein.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.3.8.9','Very-long-chain acyl-CoA dehydrogenase','NULL','FAD.','One of several enzymes that catalyze the first step in fatty acidsbeta-oxidation.The enzyme is most active toward long-chain acyl-CoAs such as C(14),C(16) and C(18), but is also active with very-long-chain acyl-CoAs upto 24 carbons.It shows no activity for substrates of less than 12 carbons.Its specific activity toward palmitoyl-CoA is more than 10-fold thatof the long-chain acyl-CoA dehydrogenase.cf. EC 1.3.8.1, EC 1.3.8.7 and EC 1.3.8.8.Formerly EC 1.3.99.3.','A very-long-chain acyl-CoA + electron-transfer flavoprotein = a very-long-chain trans-2,3-dehydroacyl-CoA + reduced electron-transferflavoprotein.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.3.8.10','Cyclohex-1-ene-1-carbonyl-CoA dehydrogenase','NULL','FAD.','The enzyme, characterized from the strict anaerobic bacteriumSyntrophus aciditrophicus, is involved in production of cyclohexane-1-carboxylate, a byproduct produced by that organism duringfermentation of benzoate and crotonate to acetate.','Cyclohex-1-ene-1-carbonyl-CoA + electron-transfer flavoprotein = (E)-2-cyclohex-1,5-diene-1-carbonyl-CoA + reduced electron-transferflavoprotein.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.3.8.11','Cyclohexane-1-carbonyl-CoA dehydrogenase','NULL','FAD.','The enzyme, characterized from the strict anaerobic bacteriumSyntrophus aciditrophicus, is involved in production of cyclohexane-1-carboxylate, a byproduct produced by that organism duringfermentation of benzoate and crotonate to acetate.','Cyclohexane-1-carbonyl-CoA + electron-transfer flavoprotein = cyclohex-1-ene-1-carbonyl-CoA + reduced electron-transfer flavoprotein.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.3.98.1','Dihydroorotate oxidase (fumarate)','DHOD','FMN.','The reaction, which takes place in the cytosol, is the only redoxreaction in the de novo biosynthesis of pyrimidine nucleotides.Molecular oxygen can replace fumarate in vitro.Other class 1 dihydroorotate dehydrogenases use either NAD(+)(EC 1.3.1.14) or NADP(+) (EC 1.3.1.15) as electron acceptor.The membrane bound class 2 dihydroorotate dehydrogenase (EC 1.3.5.2)uses quinone as electron acceptor.Formerly EC 1.3.3.1.','(S)-dihydroorotate + fumarate = orotate + succinate.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.3.98.2','Fumarate reductase (CoM/CoB)','Thiol:fumarate reductase','NULL','The enzyme, isolated from the archaeon Methanobacteriumthermoautotrophicum, is very oxygen sensitive.It cannot use reduced flavins, reduced coenzyme F420, or NAD(P)H asan electron donor.Distinct from EC 1.3.1.6, EC 1.3.5.1, and EC 1.3.5.4.','Fumarate + CoM + CoB = succinate + CoM-S-S-CoB.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.3.99.1','Deleted entry','NULL','NULL','NULL','NULL', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.3.99.2','Transferred entry: 1','NULL','NULL','NULL','NULL', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.3.99.3','Transferred entry: 1','NULL','NULL','NULL','NULL', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.3.99.4','3-oxosteroid 1-dehydrogenase','NULL','NULL','NULL','A 3-oxosteroid + acceptor = a 3-oxo-Delta(1)-steroid + reduced acceptor.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.3.99.5','3-oxo-5-alpha-steroid 4-dehydrogenase (acceptor)','Steroid 5-alpha-reductase','Flavoprotein.','This bacterial enzyme, characterized from Comamonas testosteroni,is involved in androsterone degradation.Cf. EC 1.3.1.22.','A 3-oxo-5-alpha-steroid + acceptor = a 3-oxo-Delta(4)-steroid + reducedacceptor.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.3.99.6','3-oxo-5-beta-steroid 4-dehydrogenase','Delta(4)-3-ketosteroid 5-beta-reductase','NULL','NULL','A 3-oxo-5-beta-steroid + acceptor = a 3-oxo-Delta(4)-steroid + reducedacceptor.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.3.99.7','Transferred entry: 1','NULL','NULL','NULL','NULL', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.3.99.8','2-furoyl-CoA dehydrogenase','Furoyl-CoA hydroxylase','Copper.','The oxygen atom of the -OH produced is derived from water, not O(2).The actual oxidative step is probably dehydrogenation of a hydratedform -CHOH-CH(2)- to -C(OH)=CH-, which tautomerizes non-enzymicallyto -CO-CH(2)-, giving (5-oxo-4,5-dihydro-2-furoyl)-CoA.Methylene blue, nitro blue tetrazolium and a membrane fraction fromPseudomonas putida can act as acceptors.','2-furoyl-CoA + H(2)O + acceptor = S-(5-hydroxy-2-furoyl)-CoA + reducedacceptor.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.3.99.9','Transferred entry: 1','NULL','NULL','NULL','NULL', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.3.99.10','Transferred entry: 1','NULL','NULL','NULL','NULL', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.3.99.11','Transferred entry: 1','NULL','NULL','NULL','NULL', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.3.99.12','2-methylacyl-CoA dehydrogenase','Branched-chain acyl-CoA dehydrogenase','FAD.','Also oxidizes 2-methylpropanoyl-CoA.Not identical with EC 1.3.8.1, EC 1.3.8.7, EC 1.3.8.8 or EC 1.3.8.9.','2-methylbutanoyl-CoA + acceptor = 2-methylbut-2-enoyl-CoA + reducedacceptor.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.3.99.13','Transferred entry: 1','NULL','NULL','NULL','NULL', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.3.99.14','Cyclohexanone dehydrogenase','NULL','NULL','2,6-dichloroindophenol can act as acceptor.The corresponding ketones of cyclopentane and cycloheptane cannot actas donors.','Cyclohexanone + acceptor = cyclohex-2-enone + reduced acceptor.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.3.99.15','Transferred entry: 1','NULL','NULL','NULL','NULL', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.3.99.16','Isoquinoline 1-oxidoreductase','NULL','NULL','The enzyme from Pseudomonas diminuta is specific toward N-containingN-heterocyclic substrates, including isoquinoline, isoquinolin-5-ol,phthalazine and quinazoline.Electron acceptors include 1,2-benzoquinone, cytochrome c,ferricyanide, iodonitrotetrazolium chloride (INT), nitrobluetetrazolium (NBT), Meldola blue and phenazine methosulfate.','Isoquinoline + acceptor + H(2)O = isoquinolin-1(2H)-one + reducedacceptor.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.3.99.17','Quinoline 2-oxidoreductase','NULL','FAD; Iron-sulfur; Molybdenum.','In addition to quinoline, quinolin-2-ol, quinolin-7-ol, quinolin-8-ol, 3-, 4- and 8-methylquinolines and 8-chloroquinoline aresubstrates.Iodonitrotetrazolium chloride (INT) can act as an electron acceptor.','Quinoline + acceptor + H(2)O = isoquinolin-1(2H)-one + reduced acceptor.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.3.99.18','Quinaldate 4-oxidoreductase','Quinaldic acid 4-oxidoreductase','NULL','The enzyme from Pseudomonas sp. AK2 also acts on quinoline-8-carboxylate, whereas that from Serratia marcescens 2CC-1 will oxidizenicotinate; quinaldate is a substrate for both of these enzymes.2,4,6-trinitrobenzene sulfonate acid, 1,4-benzoquinone, 1,2-naphthoquinone, nitroblue tetrazolium (NBT), thionine and menadionewill serve as an electron acceptor for the former enzyme andferricyanide for the latter; Meldola blue, iodonitrotetrazoliumchloride (INT), phenazine methosulfate (PMS), 2,6-dichlorophenolindophenol (DCPIP) and cytochrome c will act aselectron acceptors for both.','Quinaldate + acceptor + H(2)O = kynurenate + reduced acceptor.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.3.99.19','Quinoline-4-carboxylate 2-oxidoreductase','Quinaldic acid 4-oxidoreductase','Flavin; Iron-sulfur; Molybdopterin cytosine dinucleotide.','Quinoline, 4-methylquinoline and 4-chloroquinoline can also serve assubstrates for the enzyme from Agrobacterium sp. 1B.Iodonitrotetrazolium chloride, thionine, menadione and 2,6-dichlorophenolindophenol can act as electron acceptors.','Quinoline-4-carboxylate + acceptor + H(2)O = 2-oxo-1,2-dihydroquinoline-4-carboxylate + reduced acceptor.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.3.99.20','Transferred entry: 1','NULL','NULL','NULL','NULL', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.3.99.21','Transferred entry: 1','NULL','NULL','NULL','NULL', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.3.99.22','Coproporphyrinogen dehydrogenase','Coproporphyrinogen III oxidase','Iron-sulfur.','Differs from EC 1.3.3.3 by using S-adenosyl-L-methionine (AdoMet)instead of oxygen as oxidant.Occurs mainly in bacteria, whereas eukaryotes use the oxygen-dependent oxidase.The reaction starts by using an electron from the reduced form of theenzymes [4Fe-4S] cluster to split AdoMet into methionine and theradical 5-deoxyadenosin-5-yl; this radical initiates attack on the2-carboxyethyl groups, leading to their conversion into vinyl groups.This conversion, -.CH-CH(2)-COO- -','Coproporphyrinogen-III + 2 S-adenosyl-L-methionine = protoporphyrinogen-IX + 2 CO(2) + 2 L-methionine + 2 5-deoxyadenosine.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.3.99.23','All-trans-retinol 13,14-reductase','(13,14)-all-trans-retinol saturase','NULL','The reaction is only known to occur in the opposite direction to thatgiven above, with the enzyme being specific for all-trans-retinol assubstrate.Neither all-trans-retinoic acid nor 9-cis, 11-cis or 13-cis-retinolisomers are substrates.May play a role in the metabolism of vitamin A.','All-trans-13,14-dihydroretinol + acceptor = all-trans-retinol + reducedacceptor.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.3.99.24','2-amino-4-deoxychorismate dehydrogenase','2-amino-2-deoxyisochorismate dehydrogenase','NULL','The sequential action of EC 2.6.1.86 and this enzyme leads to theformation of the benzoxazolinate moiety of the enediyne antitumorantibiotic C-1027.','(2S)-2-amino-4-deoxychorismate + FMN = 3-(1-carboxyvinyloxy)anthranilate+ FMNH(2).', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.3.99.25','Carvone reductase','NULL','NULL','Participates in the carveol and dihydrocarveol degradation pathway ofthe Gram-positive bacterium Rhodococcus erythropolis DCL14.Has not been purified, and requires an unknown cofactor, which isdifferent from NAD(+), NADP(+) or a flavin.','(1) (+)-dihydrocarvone + acceptor = (-)-carvone + reduced acceptor.(2) (-)-isodihydrocarvone + acceptor = (+)-carvone + reduced acceptor.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.3.99.26','All-trans-zeta-carotene desaturase','2-step phytoene desaturase','NULL','This enzyme is involved in carotenoid biosynthesis.','All-trans-zeta-carotene + 2 acceptor = all-trans-lycopene + 2 reducedacceptor.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.3.99.27','1-hydroxycarotenoid 3,4-desaturase','1-hydroxycarotenoid 3,4-dehydrogenase','NULL','The enzymes from Rubrivivax gelatinosus and Rhodobacter sphaeroidesprefer the acyclic carotenoids (e.g. 1-hydroxy-1,2-dihydroneurosporene, 1-hydroxy-1,2-dihydrolycopene) as substrates.The conversion rate for the 3,4-desaturation of the monocyclic1-hydroxy-1,2-dihydro-gamma-carotene is lower.The enzyme from the marine bacterium strain P99-3 shows high activitywith the monocyclic carotenoid 1-hydroxy-1,2-dihydro-gamma-carotene.The enzyme from Rhodobacter sphaeroides utilizes molecular oxygen asthe electron acceptor in vitro.However, oxygen is unlikely to be the natural electron acceptor underanaerobic conditions.','1-hydroxy-1,2-dihydrolycopene + acceptor = 1-hydroxy-3,4-didehydro-1,2-dihydrolycopene + reduced acceptor.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.3.99.28','Phytoene desaturase (neurosporene-forming)','3-step phytoene desaturase','NULL','This enzyme is involved in carotenoid biosynthesis and catalyzes upto three desaturation steps (cf. EC 1.3.99.29, EC 1.3.99.30,EC 1.3.99.31).The enzyme is activated by FAD.NAD(+), NADP(+) or ATP show no activating effect.','15-cis-phytoene + 3 acceptor = all-trans-neurosporene +3 reduced acceptor.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.3.99.29','Phytoene desaturase (zeta-carotene-forming)','2-step phytoene desaturase','NULL','The enzyme is involved in carotenoid biosynthesis and catalyzes up totwo desaturation steps (cf. EC 1.3.99.28, EC 1.3.99.30 andEC 1.3.99.31).','15-cis-phytoene + 2 acceptor = all-trans-zeta-carotene + 2 reducedacceptor.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.3.99.30','Phytoene desaturase (3,4-didehydrolycopene-forming)','5-step phytoene desaturase','NULL','This enzyme is involved in carotenoid biosynthesis and catalyzes upto five desaturation steps (cf. EC 1.3.99.28, EC 1.3.99.29 andEC 1.3.99.31).','15-cis-phytoene + 5 acceptor = all-trans-3,4-didehydrolycopene +5 reduced acceptor.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.3.99.31','Phytoene desaturase (lycopene-forming)','4-step phytoene desaturase','FAD.','The enzyme is involved in carotenoid biosynthesis and catalyzes up tofour desaturation steps (cf. EC 1.3.99.28, EC 1.3.99.29 andEC 1.3.99.30).','15-cis-phytoene + 4 acceptor = all-trans-lycopene + 4 reduced acceptor.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.3.99.32','Glutaryl-CoA dehydrogenase (acceptor)','NULL','FAD.','The anaerobic, sulfate-reducing bacterium Desulfococcus multivoranscontains two glutaryl-CoA dehydrogenases: a decarboxylating enzyme(EC 1.3.8.6), and a nondecarboxylating enzyme (this entry).The two enzymes cause different structural changes around theglutaconyl carboxylate group, primarily due to the presence of eithera tyrosine or a valine residue, respectively, at the active site.','Glutaryl-CoA + acceptor = (E)-glutaconyl-CoA + reduced acceptor.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.3.99.33','Urocanate reductase','NULL','FAD; FMN.','The enzyme functions as part of an anaerobic electron transfer chainthat utilizes urocanate as the terminal electron acceptor.The activity has been demonstrated with the artificial donor reducedmethyl viologen.','Dihydrourocanate + acceptor = urocanate + reduced acceptor.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.3.99.34','2,3-bis-O-geranylgeranyl-sn-glycerol 1-phosphate reductase (donor)','NULL','FAD.','The enzyme is involved in the biosynthesis of archaeal membranelipids.It catalyzes the reduction of all 8 double bonds in 2,3-bis-O-geranylgeranyl-sn-glycerol 1-phosphate and all 4 double bonds in 3-O-geranylgeranyl-sn-glycerol 1-phosphate with comparable activity.Geranylgeranyl diphosphate is partially reduced to phytyl diphosphatewith 10% of the activity (cf. EC 1.3.1.83).Unlike EC 1.3.1.101, the enzyme shows no activity with NADPH.In vitro the enzyme can utilize sodium dithionite as the electrondonor.','2,3-bis-(O-phytanyl)-sn-glycerol 1-phosphate + acceptor = 2,3-bis-(O-geranylgeranyl)-sn-glycerol 1-phosphate + reduced acceptor.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.3.99.35','Chlorophyllide a reductase','NULL','Iron-sulfur.','Found in the purple non-sulfur bacterium Rhodobacter capsulatus.The enzyme catalyzes trans-reduction of the B-ring of chlorophyllidea; the product has the (7R,8R)-configuration.','3-deacetyl-3-vinylbacteriochlorophyllide a + A + ADP + phosphate =chlorophyllide a + AH(2) + ATP + H(2)O.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.3.99.n1','3-hydroxybenzoyl-CoA reductase','3-hydroxybenzoyl-CoA reductase (dearomatising)','Iron-sulfur; Magnesium or manganese.','Reduced methyl viologen can act as electron donor.The enzyme also catalyzes the reaction of EC 1.3.7.8.The reaction is catalyzed by a heterotetramer.','3-hydroxybenzoyl-CoA + reduced acceptor + 2 ATP + 2 H(2)O =3-hydroxycyclohexa-1,5-diene-1-carbonyl-CoA + acceptor + 2 ADP +2 phosphate.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.3.99.n2','Transferred entry: 4','NULL','NULL','NULL','NULL', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.3.99.n3','Cypemycin cysteine dehydrogenase','Cypemycin decarboxylase','NULL','Cypemycin, isolated from the bacterium Streptomyces sp. OH-4156, is apeptide antibiotic, member of the linaridins, a class ofposttranslationally modified ribosomally synthesized peptides.The enzyme decarboxylates and reduces the C-terminal L-cysteineresidue, producing an active ethenethiol.The resulting group reacts with a second dethiolated cysteine to forman aminovinyl-methyl-cysteine loop that is important for theantibiotic activity of the mature peptide.','(1) L-cysteinate group + A + H(+) = (Z)-N-(2-mercaptoethenyl)amino group+ AH(2) + CO(2).(2) [Cypemycin]-Cys-C-term + acceptor = [cypemycin]-N-(Z)-ethenethiol +CO(2) + reduced acceptor.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.4.1.1','Alanine dehydrogenase','NULL','NULL','NULL','L-alanine + H(2)O + NAD(+) = pyruvate + NH(3) + NADH.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.4.1.2','Glutamate dehydrogenase','Glutamic dehydrogenase','NULL','NULL','L-glutamate + H(2)O + NAD(+) = 2-oxoglutarate + NH(3) + NADH.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.4.1.3','Glutamate dehydrogenase (NAD(P)(+))','Glutamic dehydrogenase','NULL','NULL','L-glutamate + H(2)O + NAD(P)(+) = 2-oxoglutarate + NH(3) + NAD(P)H.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.4.1.4','Glutamate dehydrogenase (NADP(+))','Glutamic dehydrogenase','NULL','NULL','L-glutamate + H(2)O + NADP(+) = 2-oxoglutarate + NH(3) + NADPH.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.4.1.5','L-amino-acid dehydrogenase','NULL','NULL','Acts on aliphatic amino acids.','An L-amino acid + H(2)O + NAD(+) = a 2-oxo acid + NH(3) + NADH.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.4.1.6','Transferred entry: 1','NULL','NULL','NULL','NULL', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.4.1.7','Serine 2-dehydrogenase','L-serine:NAD oxidoreductase (deaminating)','NULL','NULL','L-serine + H(2)O + NAD(+) = 3-hydroxypyruvate + NH(3) + NADH.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.4.1.8','Valine dehydrogenase (NADP(+))','NULL','NULL','NULL','L-valine + H(2)O + NADP(+) = 3-methyl-2-oxobutanoate + NH(3) + NADPH.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.4.1.9','Leucine dehydrogenase','LeuDH','NULL','Also acts on isoleucine, valine, norvaline and norleucine.','L-leucine + H(2)O + NAD(+) = 4-methyl-2-oxopentanoate + NH(3) + NADH.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.4.1.10','Glycine dehydrogenase','NULL','NULL','NULL','Glycine + H(2)O + NAD(+) = glyoxylate + NH(3) + NADH.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.4.1.11','L-erythro-3,5-diaminohexanoate dehydrogenase','NULL','NULL','NULL','L-erythro-3,5-diaminohexanoate + H(2)O + NAD(+) = (S)-5-amino-3-oxohexanoate + NH(3) + NADH.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.4.1.12','2,4-diaminopentanoate dehydrogenase','NULL','NULL','Also acts, more slowly, on 2,5-diaminohexanoate forming 2-amino-5-oxohexanoate, which then cyclizes non-enzymically to 1-pyrroline-2-methyl-5-carboxylate.','2,4-diaminopentanoate + H(2)O + NAD(P)(+) = 2-amino-4-oxopentanoate +NH(3) + NAD(P)H.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.4.1.13','Glutamate synthase (NADPH)','Glutamate (reduced nicotinamide adenine dinucleotide phosphate) synthase','FAD; FMN; Iron-sulfur.','The reaction takes place in the opposite direction.The protein is composed of two subunits, alpha and beta.The alpha subunit is composed of two domains, one hydrolyzingL-glutamine to NH(3) and L-glutamate (cf. EC 3.5.1.2), the othercombining the produced NH(3) with 2-oxoglutarate to produce a secondmolecule of L-glutamate (cf. EC 1.4.1.4).The beta subunit transfers electrons to the cosubstrate.The NH(3) is channeled through a 31 A channel in the active protein.In the absence of the beta subunit, coupling between the two domainsof the alpha subunit is compromised and some ammonium can beproduced.In the intact alpha-beta complex, ammonia production only takes placeas part of the overall reaction.Formerly EC 2.6.1.53.','2 L-glutamate + NADP(+) = L-glutamine + 2-oxoglutarate + NADPH.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.4.1.14','Glutamate synthase (NADH)','L-glutamate synthase','FMN.','NULL','2 L-glutamate + NAD(+) = L-glutamine + 2-oxoglutarate + NADH.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.4.1.15','Lysine dehydrogenase','NULL','NULL','NULL','L-lysine + NAD(+) = 1,2-didehydropiperidine-2-carboxylate + NH(3) + NADH.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.4.1.16','Diaminopimelate dehydrogenase','Meso-diaminopimelate D-dehydrogenase','NULL','NULL','Meso-2,6-diaminoheptanedioate + H(2)O + NADP(+) = L-2-amino-6-oxoheptanedioate + NH(3) + NADPH.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.4.1.17','N-methylalanine dehydrogenase','NULL','NULL','NULL','N-methyl-L-alanine + H(2)O + NADP(+) = pyruvate + methylamine + NADPH.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.4.1.18','Lysine 6-dehydrogenase','L-lysine 6-dehydrogenase','NULL','Highly specific for L-lysine as substrate, although (S)-(2-aminoethyl)-L-cysteine can act as a substrate, but more slowly.While the enzyme from Agrobacterium tumefaciens can use only NAD(+),that from the thermophilic bacterium Geobacillus stearothermophiluscan also use NADP(+), but more slowly.','L-lysine + NAD(+) = (S)-2,3,4,5-tetrahydropyridine-2-carboxylate + NADH +NH(3).', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.4.1.19','Tryptophan dehydrogenase','L-Trp-dehydrogenase','Calcium.','NULL','L-tryptophan + NAD(P)(+) = (indol-3-yl)pyruvate + NH(3) + NAD(P)H.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.4.1.20','Phenylalanine dehydrogenase','L-phenylalanine dehydrogenase','NULL','The enzymes from Bacillus badius and Sporosarcina ureae are highlyspecific for L-phenylalanine; that from Bacillus sphaericus also actson L-tyrosine.','L-phenylalanine + H(2)O + NAD(+) = phenylpyruvate + NH(3) + NADH.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.4.1.21','Aspartate dehydrogenase','NAD-dependent aspartate dehydrogenase','NULL','Catalyzes the first step in NAD biosynthesis from aspartate.Strictly specific for L-aspartate as substrate.Has a higher affinity for NAD(+) than NADP(+).','L-aspartate + H(2)O + NAD(P)(+) = oxaloacetate + NH(3) + NAD(P)H.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.4.1.23','Valine dehydrogenase (NAD(+))','NULL','NULL','The enzyme from Streptomyces spp. has no activity with NADP(+) (cf.EC 1.4.1.8).','L-valine + H(2)O + NAD(+) = 3-methyl-2-oxobutanoate + NH(3) + NADH.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.4.1.24','3-dehydroquinate synthase II','DHQ synthase II','NULL','The enzyme, which was isolated from the archaeon Methanocaldococcusjannaschii, plays a key role in an alternative pathway for thebiosynthesis of 3-dehydroquinate (DHQ), an intermediate of thecanonical pathway for the biosynthesis of aromatic amino acids.The enzyme catalyzes a two-step reaction - an oxidative deamination,followed by cyclization.','2-amino-3,7-dideoxy-D-threo-hept-6-ulosonate + H(2)O + NAD(+) =3-dehydroquinate + NH(3) + NADH.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.4.2.1','Glycine dehydrogenase (cytochrome)','Glycine-cytochrome c reductase','NULL','NULL','Glycine + H(2)O + 2 ferricytochrome c = glyoxylate + NH(3) +2 ferrocytochrome c + 2 H(+).', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.4.3.1','D-aspartate oxidase','Aspartic oxidase','FAD.','NULL','D-aspartate + H(2)O + O(2) = oxaloacetate + NH(3) + H(2)O(2).', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.4.3.2','L-amino-acid oxidase','Ophio-amino-acid oxidase','FAD.','NULL','An L-amino acid + H(2)O + O(2) = a 2-oxo acid + NH(3) + H(2)O(2).', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.4.3.3','D-amino-acid oxidase','NULL','FAD.','Wide specificity for D-amino acids.Also acts on glycine.','A D-amino acid + H(2)O + O(2) = a 2-oxo acid + NH(3) + H(2)O(2).', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.4.3.4','Monoamine oxidase','Adrenaline oxidase','FAD.','Catalyzes the oxidative deamination of neurotransmitters and biogenicamines.Acts on primary amines, and also on some secondary and tertiaryamines.It differs from EC 1.4.3.21, as it can oxidize secondary and tertiaryamines but not methylamine.Inhibited by acetylenic compounds such as chlorgyline, 1-deprenyl andpargyline but, unlike EC 1.4.3.21 and EC 1.4.3.22, it is notinhibited by semicarbazide.Formerly EC 1.4.3.9.','RCH(2)NHR + H(2)O + O(2) = RCHO + RNH(2) + H(2)O(2).', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.4.3.5','Pyridoxal 5-phosphate synthase','PMP oxidase','FMN.','In Escherichia coli, the coenzyme pyridoxal 5-phosphate issynthesized de novo by a pathway that involves EC 1.2.1.72,EC 1.1.1.290, EC 2.6.1.52, EC 1.1.1.262, EC 2.6.99.2 and EC 1.4.3.5.','(1) Pyridoxamine 5-phosphate + H(2)O + O(2) = pyridoxal 5-phosphate +NH(3) + H(2)O(2).(2) Pyridoxine 5-phosphate + O(2) = pyridoxal 5-phosphate + H(2)O(2).', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.4.3.6','Transferred entry: 1','NULL','NULL','NULL','NULL', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.4.3.7','D-glutamate oxidase','D-glutamic oxidase','NULL','NULL','D-glutamate + H(2)O + O(2) = 2-oxoglutarate + NH(3) + H(2)O(2).', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.4.3.8','Ethanolamine oxidase','NULL','Cobalt.','NULL','Ethanolamine + H(2)O + O(2) = glycolaldehyde + NH(3) + H(2)O(2).', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.4.3.9','Transferred entry: 1','NULL','NULL','NULL','NULL', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.4.3.10','Putrescine oxidase','NULL','FAD.','4-aminobutanal condenses non-enzymically to 1-pyrroline.','Putrescine + O(2) + H(2)O = 4-aminobutanal + NH(3) + H(2)O(2).', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.4.3.11','L-glutamate oxidase','NULL','FAD.','The enzyme from Azotobacter previously listed under this number,which did not produce H(2)O(2), was a crude cell-free extract whichprobably contained catalase.','L-glutamate + O(2) + H(2)O = 2-oxoglutarate + NH(3) + H(2)O(2).', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.4.3.12','Cyclohexylamine oxidase','NULL','FAD.','Some other cyclic amines can act instead of cyclohexylamine, but notsimple aliphatic and aromatic amides.','Cyclohexylamine + O(2) + H(2)O = cyclohexanone + NH(3) + H(2)O(2).', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.4.3.13','Protein-lysine 6-oxidase','Lysyl oxidase','NULL','Also acts on protein 5-hydroxylysine.Catalyzes the final known enzymic step required for collagen andelastin cross-linking in the biosynthesis of normal matureextracellular matrices.These reactions play an important role for the development,elasticity and extensibility of connective tissue.Also active on free amines, such as cadaverine or benzylamine.','Peptidyl-L-lysyl-peptide + O(2) + H(2)O = peptidyl-allysyl-peptide +NH(3) + H(2)O(2).', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.4.3.14','L-lysine oxidase','NULL','FAD.','Also acts, more slowly, on L-ornithine, L-phenylalanine, L-arginineand L-histidine.','L-lysine + O(2) + H(2)O = 6-amino-2-oxohexanoate + NH(3) + H(2)O(2).', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.4.3.15','D-glutamate(D-aspartate) oxidase','NULL','FAD.','D-glutamate and D-aspartate are oxidized at the same rate.Other D-monoaminodicarboxylates, and other D- and L-amino acids,are not oxidized.Cf. EC 1.4.3.7 and EC 1.4.3.1.','(1) D-glutamate + H(2)O + O(2) = 2-oxoglutarate + NH(3) + H(2)O(2).(2) D-aspartate + H(2)O + O(2) = oxaloacetate + NH(3) + H(2)O(2).', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.4.3.16','L-aspartate oxidase','LASPO','FAD.','L-aspartate oxidase catalyzes the first step in the de novobiosynthesis of NAD(+) in some bacteria.O(2) can be replaced by fumarate as electron acceptor, yieldingsuccinate.The ability of the enzyme to use both O(2) and fumarate in cofactorreoxidation enables it to function under both aerobic and anaerobicconditions.Iminosuccinate can either be hydrolyzed to form oxaloacetate andNH(3) or can be used by EC 2.5.1.72 in the production of quinolinate.','L-aspartate + O(2) = iminosuccinate + H(2)O(2).', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.4.3.17','Transferred entry: 1','NULL','NULL','NULL','NULL', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.4.3.18','Deleted entry','NULL','NULL','NULL','NULL', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.4.3.19','Glycine oxidase','NULL','FAD.','The enzyme from Bacillus subtilis is active with glycine, sarcosine,N-ethylglycine, D-alanine, D-alpha-aminobutyrate, D-proline,D-pipecolate and N-methyl-D-alanine.It differs from EC 1.4.3.3 due to its activity on sarcosine andD-pipecolate.The intermediate 2-iminoacetate is used directly by EC 2.8.1.10.','Glycine + H(2)O + O(2) = glyoxylate + NH(3) + H(2)O(2).', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.4.3.20','L-lysine 6-oxidase','L-lysine-epsilon-oxidase','NULL','Differs from EC 1.4.3.13 by using free L-lysine rather than theprotein-bound form.N(2)-acetyl-L-lysine is also a substrate, but N(6)-acetyl-L-lysine,which has an acetyl group at position 6, is not.Also acts on L-ornithine, D-lysine and 4-hydroxy-L-lysine, but moreslowly.The amines cadaverine and putrescine are not substrates.','L-lysine + O(2) + H(2)O = (S)-2-amino-6-oxohexanoate + H(2)O(2) + NH(3).', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.4.3.21','Primary-amine oxidase','Amine oxidase','NULL','A group of enzymes that oxidize primary monoamines but have little orno activity toward diamines, such as histamine, or toward secondaryand tertiary amines.They are copper quinoproteins (2,4,5-trihydroxyphenylalanine quinone)and, unlike EC 1.4.3.4, are sensitive to inhibition by carbonyl-groupreagents, such as semicarbazide.In some mammalian tissues the enzyme also functions as a vascular-adhesion protein (VAP-1).Formerly EC 1.4.3.6.','RCH(2)NH(2) + H(2)O + O(2) = RCHO + NH(3) + H(2)O(2).', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.4.3.22','Diamine oxidase','Amine oxidase','NULL','A group of enzymes that oxidize diamines, such as histamine, and alsosome primary monoamines but have little or no activity towardsecondary and tertiary amines.They are copper quinoproteins (2,4,5-trihydroxyphenylalanine quinone)and, like EC 1.4.3.21 but unlike EC 1.4.3.4, they are sensitive toinhibition by carbonyl-group reagents, such as semicarbazide.Formerly EC 1.4.3.6.','Histamine + H(2)O + O(2) = (imidazol-4-yl)acetaldehyde + NH(3) +H(2)O(2).', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.4.3.23','7-chloro-L-tryptophan oxidase','NULL','NULL','This enzyme catalyzes a step in the biosynthesis of rebeccamycin,an indolocarbazole alkaloid produced by the actinobacteriumLechevalieria aerocolonigenes.During catalysis, the bound FAD is reoxidized at the expense ofmolecular oxygen, producing one molecule of hydrogen peroxide.The enzyme shows significant preference for 7-chloro-L-tryptophanover L-tryptophan.','7-chloro-L-tryptophan + O(2) = 2-imino-3-(7-chloroindol-3-yl)propanoate +H(2)O(2).', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.4.3.24','Pseudooxynicotine oxidase','NULL','FAD.','This enzyme, characterized from the soil bacterium Pseudomonas sp.HZN6, is involved the nicotine degradation.','4-(methylamino)-1-(pyridin-3-yl)butan-1-one + H(2)O + O(2) = 4-oxo-4-(pyridin-3-yl)butanal + methylamine + H(2)O(2).', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.4.4.1','Transferred entry: 1','NULL','NULL','NULL','NULL', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.4.4.2','Glycine dehydrogenase (aminomethyl-transferring)','Glycine cleavage system P-protein','Pyridoxal 5-phosphate.','A component of the glycine cleavage system, which is composed of fourcomponents that only loosely associate: the P protein (EC 1.4.4.2),the T protein (EC 2.1.2.10), the L protein (EC 1.8.1.4) and thelipoyl-bearing H protein.Previously known as glycine synthase.','Glycine + [glycine-cleavage complex H protein]-N(6)-lipoyl-L-lysine =[glycine-cleavage complex H protein]-S-aminomethyl-N(6)-dihydrolipoyl-L-lysine + CO(2).', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.4.5.1','D-amino acid dehydrogenase (quinone)','DadA','FAD; Iron-sulfur.','This enzyme may be the same as EC 1.4.99.1.','A D-amino acid + H(2)O + a quinone = a 2-oxo acid + NH(3) + a quinol.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.4.7.1','Glutamate synthase (ferredoxin)','Ferredoxin-dependent glutamate synthase','FAD; FMN; Iron-sulfur.','The protein is composed of two domains, one hydrolyzing L-glutamineto NH(3) and L-glutamate (cf. EC 3.5.1.2), the other combining theproduced NH(3) with 2-oxoglutarate to produce a second molecule ofL-glutamate.The NH(3) is channeled through a 24 A channel in the active protein.No hydrolysis of glutamine takes place without ferredoxin and2-oxoglutarate being bound to the protein.','2 L-glutamate + 2 oxidized ferredoxin = L-glutamine + 2-oxoglutarate +2 reduced ferredoxin + 2 H(+).', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.4.9.1','Methylamine dehydrogenase (amicyanin)','Amine dehydrogenase','Tryptophan tryptophylquinone.','The enzyme oxidizes aliphatic monoamines and diamines, histamine andethanolamine, but not secondary and tertiary amines, quaternaryammonium salts or aromatic amines.Formerly EC 1.4.98.1 and EC 1.4.99.3.','Methylamine + H(2)O + amicyanin = formaldehyde + ammonia + reducedamicyanin.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.4.9.2','Aralkylamine dehydrogenase (azurin)','Aromatic amine dehydrogenase','NULL','Phenazine methosulfate can act as acceptor.Acts on aromatic amines and, more slowly, on some long-chainaliphatic amines, but not on methylamine or ethylamine.Formerly EC 1.4.99.4.','ArCH(2)NH(2) + H(2)O + 2 azurin = ArCHO + NH(3) + 2 reduced azurin.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.4.98.1','Transferred entry: 1','NULL','NULL','NULL','NULL', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.4.99.1','D-amino-acid dehydrogenase','NULL','FAD.','Acts to some extent on all D-amino acids except D-aspartate andD-glutamate.','A D-amino acid + H(2)O + acceptor = a 2-oxo acid + NH(3) + reducedacceptor.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.4.99.2','Taurine dehydrogenase','NULL','NULL','NULL','Taurine + H(2)O + acceptor = sulfoacetaldehyde + NH(3) + reducedacceptor.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.4.99.3','Transferred entry: 1','NULL','NULL','NULL','NULL', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.4.99.4','Transferred entry: 1','NULL','NULL','NULL','NULL', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.4.99.5','Glycine dehydrogenase (cyanide-forming)','HCN synthase','FAD.','The enzyme is membrane-bound, and the 2-electron acceptor is acomponent of the respiratory chain.The enzyme can act with various artificial electron acceptors,including phenazine methosulfate.','Glycine + 2 A = HCN + CO(2) + 2 AH(2).', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.5.1.1','Pyrroline-2-carboxylate reductase','NULL','NULL','Reduces 1-pyrroline-2-carboxylate to L-proline and also 1,2-didehydropiperidine-2-carboxylate to L-pipecolate.','L-proline + NAD(P)(+) = 1-pyrroline-2-carboxylate + NAD(P)H.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.5.1.2','Pyrroline-5-carboxylate reductase','P5CR','NULL','Also reduces 1-pyrroline-3-hydroxy-5-carboxylate to L-hydroxyproline.','L-proline + NAD(P)(+) = 1-pyrroline-5-carboxylate + NAD(P)H.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.5.1.3','Dihydrofolate reductase','Tetrahydrofolate dehydrogenase','NULL','The enzyme from animals and some micro-organisms also slowly reducesfolate to 5,6,7,8-tetrahydrofolate.Formerly EC 1.5.1.4.','5,6,7,8-tetrahydrofolate + NADP(+) = 7,8-dihydrofolate + NADPH.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.5.1.4','Transferred entry: 1','NULL','NULL','NULL','NULL', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.5.1.5','Methylenetetrahydrofolate dehydrogenase (NADP(+))','NULL','NULL','NULL','5,10-methylenetetrahydrofolate + NADP(+) = 5,10-methenyltetrahydrofolate + NADPH.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.5.1.6','Formyltetrahydrofolate dehydrogenase','10-formyltetrahydrofolate dehydrogenase','NULL','NULL','10-formyltetrahydrofolate + NADP(+) + H(2)O = tetrahydrofolate + CO(2) +NADPH.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.5.1.7','Saccharopine dehydrogenase (NAD(+), L-lysine-forming)','Lysine-2-oxoglutarate reductase','NULL','NULL','N(6)-(L-1,3-dicarboxypropyl)-L-lysine + NAD(+) + H(2)O = L-lysine +2-oxoglutarate + NADH.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.5.1.8','Saccharopine dehydrogenase (NADP(+), L-lysine-forming)','Lysine-2-oxoglutarate reductase','NULL','NULL','N(6)-(L-1,3-dicarboxypropyl)-L-lysine + NADP(+) + H(2)O = L-lysine +2-oxoglutarate + NADPH.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.5.1.9','Saccharopine dehydrogenase (NAD(+), L-glutamate-forming)','Aminoadipic semialdehyde synthase','NULL','NULL','N(6)-(L-1,3-dicarboxypropyl)-L-lysine + NAD(+) + H(2)O = L-glutamate +(S)-2-amino-6-oxohexanoate + NADH.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.5.1.10','Saccharopine dehydrogenase (NADP(+), L-glutamate-forming)','Aminoadipate semialdehyde-glutamate reductase','NULL','NULL','N(6)-(L-1,3-dicarboxypropyl)-L-lysine + NADP(+) + H(2)O = L-glutamate +(S)-2-amino-6-oxohexanoate + NADPH.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.5.1.11','D-octopine dehydrogenase','D-octopine synthase','NULL','Also acts, in the reverse direction, on L-ornithine, L-lysine andL-histidine.','N(2)-(D-1-carboxyethyl)-L-arginine + NAD(+) + H(2)O = L-arginine +pyruvate + NADH.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.5.1.12','Transferred entry: 1','NULL','NULL','NULL','NULL', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.5.1.13','Transferred entry: 1','NULL','NULL','NULL','NULL', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.5.1.14','Transferred entry: 1','NULL','NULL','NULL','NULL', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.5.1.15','Methylenetetrahydrofolate dehydrogenase (NAD(+))','NULL','NULL','NULL','5,10-methylenetetrahydrofolate + NAD(+) = 5,10-methenyltetrahydrofolate +NADH.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.5.1.16','D-lysopine dehydrogenase','D-lysopine synthase','NULL','In the reverse reaction, a number of L-amino acids can act instead ofL-lysine, and 2-oxobutanoate and, to a lesser extent, glyoxylate canact instead of pyruvate.','N(2)-(D-1-carboxyethyl)-L-lysine + NADP(+) + H(2)O = L-lysine + pyruvate+ NADPH.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.5.1.17','Alanopine dehydrogenase','NULL','NULL','In the reverse reaction, L-alanine can be replaced by L-cysteine,L-serine, or L-threonine; glycine acts very slowly (cf. EC 1.5.1.22).','2,2-iminodipropanoate + NAD(+) + H(2)O = L-alanine + pyruvate + NADH.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.5.1.18','Ephedrine dehydrogenase','NULL','NULL','The product immediately hydrolyzes to methylamine and 1-hydroxy-1-phenylpropan-2-one.Acts on a number of related compounds including (-)-sympatol, (+)-pseudoephedrine and (+)-norephedrine.','(-)-ephedrine + NAD(+) = (R)-2-methylimino-1-phenylpropan-1-ol + NADH.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.5.1.19','D-nopaline dehydrogenase','D-nopaline synthase','NULL','In the reverse direction, forms D-nopaline from L-arginine andD-ornaline from L-ornithine.','N(2)-(D-1,3-dicarboxypropyl)-L-arginine + NADP(+) + H(2)O = L-arginine +2-oxoglutarate + NADPH.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.5.1.20','Methylenetetrahydrofolate reductase (NAD(P)H)','5,10-CH(2)-H(4)folate reductase','FAD.','Menadione can also serve as an electron acceptor.Formerly EC 1.1.1.68, EC 1.1.1.171, EC 1.1.99.15 and EC 1.7.99.5.','5-methyltetrahydrofolate + NAD(P)(+) = 5,10-methylenetetrahydrofolate +NAD(P)H.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.5.1.21','Delta(1)-piperideine-2-carboxylate reductase','NULL','NULL','Formerly EC 1.5.1.14.','L-pipecolate + NADP(+) = Delta(1)-piperideine-2-carboxylate + NADPH.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.5.1.22','Strombine dehydrogenase','NULL','NULL','Catalyzes the reaction of EC 1.5.1.17, more slowly.Does not act on L-strombine.','N-(carboxymethyl)-D-alanine + NAD(+) + H(2)O = glycine + pyruvate + NADH.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.5.1.23','Tauropine dehydrogenase','NULL','NULL','In the reverse reaction, alanine can act instead of taurine, moreslowly, and 2-oxobutanoate and 2-oxopentanoate can act instead ofpyruvate.','Tauropine + NAD(+) + H(2)O = taurine + pyruvate + NADH.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.5.1.24','N(5)-(carboxyethyl)ornithine synthase','NULL','NULL','In the reverse direction L-lysine can act instead of L-ornithine,more slowly.Acts on the amino group (cf. EC 1.5.1.16).','N(5)-(L-1-carboxyethyl)-L-ornithine + NADP(+) + H(2)O = L-ornithine +pyruvate + NADPH.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.5.1.25','Thiomorpholine-carboxylate dehydrogenase','Ketimine reductase','NULL','The product is the cyclic imine of the 2-oxoacid corresponding toS-(2-aminoethyl)cysteine.In the reverse direction, a number of other cyclic unsaturatedcompounds can act as substrates, more slowly.','Thiomorpholine 3-carboxylate + NAD(P)(+) = 3,4-dehydro-thiomorpholine-3-carboxylate + NAD(P)H.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.5.1.26','Beta-alanopine dehydrogenase','NULL','NULL','NULL','Beta-alanopine + NAD(+) + H(2)O = beta-alanine + pyruvate + NADH.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.5.1.27','1,2-dehydroreticulinium reductase (NADPH)','1,2-dehydroreticulinium ion reductase','NULL','Reduces the 1,2-dehydroreticulinium ion to (R)-reticuline, which is adirect precursor of morphinan alkaloids in the poppy plant.The enzyme does not catalyze the reverse reaction to any significantextent under physiological conditions.','(R)-reticuline + NADP(+) = 1,2-dehydroreticulinium + NADPH.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.5.1.28','Opine dehydrogenase','NULL','NULL','In the forward direction, the enzyme from Arthrobacter sp. acts alsoon secondary amine dicarboxylates such asN-(1-carboxyethyl)methionine and N-(1-carboxyethyl)phenylalanine.Dehydrogenation forms an imine, which dissociates to the amino acidand pyruvate.In the reverse direction, the enzyme acts also on neutral amino acidsas an amino donor.They include L-amino acids such as 2-aminopentanoic acid,2-aminobutyric acid, 2-aminohexanoic acid, 3-chloroalanine,O-acetylserine, methionine, isoleucine, valine, phenylalanine,leucine and alanine.The amino acceptors include 2-oxoacids such as pyruvate,oxaloacetate, glyoxylate and 2-oxobutyrate.','(2S)-2-((1-(R)-carboxyethyl)amino)pentanoate + NAD(+) + H(2)O = L-2-aminopentanoic acid + pyruvate + NADH.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.5.1.29','Transferred entry: 1','NULL','NULL','NULL','NULL', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.5.1.30','Flavin reductase (NADPH)','Flavin mononucleotide reductase','NULL','The enzyme from Entamoeba histolytica reduces riboflavin andgalactoflavin, and, more slowly, FMN and FAD.NADH is oxidized more slowly than NADPH.Formerly EC 1.6.8.2.','Reduced riboflavin + NADP(+) = riboflavin + NADPH.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.5.1.31','Berberine reductase','(R)-canadine synthase','NULL','Involved in alkaloid biosynthesis in Corydalis cava to give (R)-canadine with the opposite configuration to the precursor ofberberine (see EC 1.3.3.8).Also acts on 7,8-dihydroberberine.','(R)-canadine + 2 NADP(+) = berberine + 2 NADPH.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.5.1.32','Vomilenine reductase','NULL','NULL','Forms part of the ajmaline biosynthesis pathway.','1,2-dihydrovomilenine + NADP(+) = vomilenine + NADPH.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.5.1.33','Pteridine reductase','Pteridine reductase 1','NULL','The enzyme from Leishmania (both amastigote and promastigote forms)catalyzes the NADPH-dependent reduction of folate and a wide varietyof unconjugated pterins, including biopterin, to their tetrahydroforms.It also catalyzes the reduction of 7,8-dihydropterins and 7,8-dihydrofolate to their tetrahydro forms.In contrast to EC 1.5.1.3 and EC 1.5.1.34, pteridine reductase willnot catalyze the reduction of the quinonoid form of dihydrobiopterin.The enzyme is specific for NADPH; no activity has been detected withNADH.It also differs from EC 1.5.1.3 in being specific for the B side ofNADPH.Formerly EC 1.1.1.253.','5,6,7,8-tetrahydrobiopterin + 2 NADP(+) = biopterin + 2 NADPH.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.5.1.34','6,7-dihydropteridine reductase','6,7-dihydropteridine:NAD(P)H oxidoreductase','NULL','The substrate is the quinonoid form of dihydropteridine.Not identical with EC 1.5.1.3.Formerly EC 1.6.99.7 and EC 1.6.99.10.','A 5,6,7,8-tetrahydropteridine + NAD(P)(+) = a 6,7-dihydropteridine +NAD(P)H.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.5.1.35','Transferred entry: 1','NULL','NULL','NULL','NULL', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.5.1.36','Flavin reductase (NADH)','NADH-dependent flavin reductase','NULL','The enzyme from Escherichia coli W catalyzes the reduction of freeflavins by NADH.The enzyme has similar affinity to FAD, FMN and riboflavin.Activity with NADPH is more than 2 orders of magnitude lower thanactivity with NADH.','Reduced flavin + NAD(+) = flavin + NADH.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.5.1.37','FAD reductase (NADH)','NADH-dependent FAD reductase','NULL','The enzyme from Burkholderia phenoliruptrix can reduce either FAD orflavin mononucleotide (FMN) but prefers FAD.Unlike EC 1.5.1.36, the enzyme can not reduce riboflavin.The enzyme does not use NADPH as acceptor.','FADH(2) + NAD(+) = FAD + NADH.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.5.1.38','FMN reductase (NADPH)','Flavin reductase P','FMN.','The enzymes from bioluminescent bacteria contain FMN, while theenzyme from Escherichia coli does not.The enzyme often forms a two-component system with monooxygenasessuch as luciferase.Unlike EC 1.5.1.39, this enzyme does not use NADH as acceptor.While FMN is the preferred substrate, the enzyme can also use FAD andriboflavin with lower activity.Formerly EC 1.5.1.29 and EC 1.6.8.1.','FMNH(2) + NADP(+) = FMN + NADPH.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.5.1.39','FMN reductase (NAD(P)H)','NULL','FMN.','The enzyme can utilize NADH and NADPH with similar reaction rates.Different from EC 1.5.1.42 and EC 1.5.1.38.The luminescent bacterium Vibrio harveyi possesses all three enzymes,while the bacterium Aliivibrio fischeri contains only this non-specific type.Also reduces riboflavin and FAD, but more slowly.Formerly EC 1.5.1.29 and EC 1.6.8.1.','FMNH(2) + NAD(P)(+) = FMN + NAD(P)H.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.5.1.40','8-hydroxy-5-deazaflavin:NADPH oxidoreductase','NULL','NULL','The enzyme has an absolute requirement for both the 5-deazaflavinstructure and the presence of an 8-hydroxy group in the substrate.','Reduced coenzyme F420 + NADP(+) = oxidized coenzyme F420 + NADPH.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.5.1.41','Riboflavin reductase (NAD(P)H)','Flavin mononucleotide reductase','NULL','Catalyzes the reduction of soluble flavins by reduced pyridinenucleotides.Highest activity with riboflavin.When NADH is used as acceptor, the enzyme can also utilize FMN andFAD as substrates, with lower activity than riboflavin.When NADPH is used as acceptor, the enzyme has a very low activitywith FMN and no activity with FAD.Formerly EC 1.5.1.29 and EC 1.6.8.1.','Reduced riboflavin + NAD(P)(+) = riboflavin + NAD(P)H.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.5.1.42','FMN reductase (NADH)','NADH-dependent FMN reductase','NULL','The enzyme often forms a two-component system with monooxygenases.Unlike EC 1.5.1.38 and EC 1.5.1.39, this enzyme has a strongpreference for NADH over NADPH, although some activity with thelatter is observed.While FMN is the preferred substrate, FAD can also be used with muchlower activity.','FMNH(2) + NAD(+) = FMN + NADH.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.5.1.43','Carboxynorspermidine synthase','Carboxynorspermidine dehydrogenase','NULL','The reaction takes place in the opposite direction.Part of a bacterial polyamine biosynthesis pathway.L-aspartate 4-semialdehyde and propane-1,3-diamine/putrescine form aSchiff base that is reduced to formcarboxynorspermidine/carboxyspermidine, respectively.The enzyme from the bacterium Vibrio cholerae is essential forbiofilm formation.The enzyme from Campylobacter jejuni only produces carboxyspermidinein vivo even though it also can produce carboxynorspermidine invitro.','(1) Carboxynorspermidine + H(2)O + NADP(+) = L-aspartate 4-semialdehyde +propane-1,3-diamine + NADPH.(2) Carboxyspermidine + H(2)O + NADP(+) = L-aspartate 4-semialdehyde +putrescine + NADPH.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.5.1.44','Festuclavine dehydrogenase','Festuclavine synthase','NULL','The enzyme participates in the biosynthesis of fumigaclavine C,an ergot alkaloid produced by some fungi of the Trichocomaceaefamily.The reaction proceeds in vivo in the opposite direction to the oneshown here.','Festuclavine + NAD(+) = 6,8-dimethyl-6,7-didehydroergoline + NADH.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.5.1.45','FAD reductase (NAD(P)H)','NULL','NULL','This enzyme, isolated from the bacterium Geobacillusthermodenitrificans, participates in the pathway of tryptophandegradation.The enzyme is part of a system that also includes a bifunctionalriboflavin kinase/FMN adenylyltransferase and EC 1.14.14.8.It can reduce either FAD or flavin mononucleotide (FMN) but prefersFAD.The enzyme has a slight preference for NADPH as acceptor.Cf. EC 1.5.1.37.','FADH(2) + NAD(P)(+) = FAD + NAD(P)H.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.5.1.46','Agroclavine dehydrogenase','NULL','NULL','The enzyme participates in the biosynthesis of ergotamine, an ergotalkaloid produced by some fungi of the Clavicipitaceae family.The reaction is catalyzed in the opposite direction to that shown.The substrate for the enzyme is an iminium intermediate that isformed spontaneously from chanoclavine-I aldehyde in the presence ofglutathione.','Agroclavine + NADP(+) = 6,8-dimethyl-6,7,8,9-tetradehydroergoline +NADPH.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.5.1.47','Dihydromethanopterin reductase','H(2)MPT reductase','NULL','The enzyme, characterized from the bacterium Methylobacteriumextorquens, is involved in tetrahydromethanopterin biosynthesis.The specific activity with NADH is 15% of that with NADPH at the sameconcentration.It does not reduce 7,8-dihydrofolate (cf. EC 1.5.1.3).','5,6,7,8-tetrahydromethanopterin + NAD(P)(+) = 7,8-dihydromethanopterin +NAD(P)H.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.5.3.1','Sarcosine oxidase','NULL','FAD.','The flavin is both covalently and non-covalently bound in a molarratio of 1:1.','Sarcosine + H(2)O + O(2) = glycine + formaldehyde + H(2)O(2).', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.5.3.2','N-methyl-L-amino-acid oxidase','NULL','Flavoprotein.','NULL','An N-methyl-L-amino acid + H(2)O + O(2) = an L-amino acid + formaldehyde+ H(2)O(2).', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.5.3.3','Deleted entry','NULL','NULL','NULL','NULL', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.5.3.4','N(6)-methyl-lysine oxidase','Epsilon-alkyllysinase','NULL','NULL','N(6)-methyl-L-lysine + H(2)O + O(2) = L-lysine + formaldehyde + H(2)O(2).', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.5.3.5','(S)-6-hydroxynicotine oxidase','NULL','FAD.','NULL','(S)-6-hydroxynicotine + H(2)O + O(2) = 1-(6-hydroxypyridin-3-yl)-4-(methylamino)butan-1-one + H(2)O(2).', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.5.3.6','(R)-6-hydroxynicotine oxidase','NULL','FAD.','NULL','(R)-6-hydroxynicotine + H(2)O + O(2) = 1-(6-hydroxypyridin-3-yl)-4-(methylamino)butan-1-one + H(2)O(2).', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.5.3.7','L-pipecolate oxidase','NULL','NULL','The product reacts with water to form (S)-2-amino-6-oxohexanoate.','L-pipecolate + O(2) = (S)-2,3,4,5-tetrahydropyridine-2-carboxylate +H(2)O(2).', 'NULL', 'NULL');
