INSERT INTO enzyme VALUES ( 'EC 1.5.3.10','Dimethylglycine oxidase','NULL','FAD.','Does not oxidize sarcosine.','N,N-dimethylglycine + H(2)O + O(2) = sarcosine + formaldehyde + H(2)O(2).', 'NULL', 'NULL');
