INSERT INTO enzyme VALUES ( 'EC 1.5.99.3','L-pipecolate dehydrogenase','NULL','NULL','The product reacts with water to form (S)-2-amino-6-oxohexanoate.','L-pipecolate + acceptor = (S)-2,3,4,5-tetrahydropyridine-2-carboxylate +reduced acceptor.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.5.99.4','Nicotine dehydrogenase','NULL','FMN.','Acts on both (R)- and (S)-isomers.','Nicotine + acceptor + H(2)O = (S)-6-hydroxynicotine + reduced acceptor.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.5.99.5','Methylglutamate dehydrogenase','NULL','NULL','A number of N-methyl-substituted amino acids can act as donors.2,6-dichloroindophenol is the best acceptor.','N-methyl-L-glutamate + acceptor + H(2)O = L-glutamate + formaldehyde +reduced acceptor.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.5.99.6','Spermidine dehydrogenase','NULL','FAD; Heme.','Ferricyanide, 2,6-dichloroindophenol and cytochrome c can act asacceptor.4-aminobutanal condenses non-enzymically to 1-pyrroline.','Spermidine + acceptor + H(2)O = propane-1,3-diamine + 4-aminobutanal +reduced acceptor.', 'NULL', 'NULL');
