INSERT INTO enzyme VALUES ( 'EC 1.7.1.1','Nitrate reductase (NADH)','Assimilatory NADH:nitrate reductase','FAD or FMN; Heme; Molybdenum.','Formerly EC 1.6.6.1.','Nitrite + NAD(+) + H(2)O = nitrate + NADH.', 'NULL', 'NULL');
