INSERT INTO enzyme VALUES ( 'EC 1.7.1.3','Nitrate reductase (NADPH)','Assimilatory NADPH-nitrate reductase','FAD; Heme; Iron-sulfur; Molybdenum.','Formerly EC 1.6.6.3.','Nitrite + NADP(+) + H(2)O = nitrate + NADPH.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.7.1.4','Nitrite reductase (NAD(P)H)','Assimilatory nitrite reductase','FAD; Iron; Iron-sulfur; Siroheme.','The enzymes from the fungi Neurospora crassa, Emericella nidulans andCandida nitratophila and the bacterium Aliivibrio fischeri can useeither NADPH or NADH as electron donor.Cf. EC 1.7.1.15.Formerly EC 1.6.6.4.','Ammonia + 3 NAD(P)(+) + 2 H(2)O = nitrite + 3 NAD(P)H.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.7.1.5','Hyponitrite reductase','NADH:hyponitrite oxidoreductase','Metal ions.','Formerly EC 1.6.6.6.','2 hydroxylamine + 2 NAD(+) = hyponitrous acid + 2 NADH.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.7.1.6','Azobenzene reductase','Azo reductase','NULL','The reaction occurs in the reverse direction to that shown above.Other azo dyes, such as Methyl Red, Rocceline, Solar Orange andSumifix Black B can also be reduced.Formerly EC 1.6.6.7.','N,N-dimethyl-1,4-phenylenediamine + aniline + 2 NADP(+) =4-(dimethylamino)azobenzene + 2 NADPH.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.7.1.7','GMP reductase','Guanosine 5-monophosphate oxidoreductase','NULL','Formerly EC 1.6.6.8.','Inosine 5-phosphate + NH(3) + NADP(+) = guanosine 5-phosphate + NADPH.', 'NULL', 'NULL');
