INSERT INTO enzyme VALUES ( 'EC 1.13.99.3','Tryptophan 2-dioxygenase','Indole-3-alkane alpha-hydroxylase','Heme.','Acts on a number of indole-3-alkane derivatives, oxidizing the3-side-chain in the 2-position.Best substrates are L-tryptophan and 5-hydroxy-L-tryptophan.','L-tryptophan + O(2) = (indol-3-yl)glycolaldehyde + CO(2) + NH(3).', 'NULL', 'NULL');
