INSERT INTO enzyme VALUES ( 'EC 1.14.11.1','Gamma-butyrobetaine dioxygenase','Gamma-BBH','Iron; L-ascorbate.','NULL','4-trimethylammoniobutanoate + 2-oxoglutarate + O(2) = 3-hydroxy-4-trimethylammoniobutanoate + succinate + CO(2).', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.14.11.2','Procollagen-proline dioxygenase','Procollagen-proline 4-dioxygenase','Iron; L-ascorbate.','NULL','L-proline-[procollagen] + 2-oxoglutarate + O(2) = trans-4-hydroxy-L-proline-[procollagen] + succinate + CO(2).', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.14.11.3','Pyrimidine-deoxynucleoside 2-dioxygenase','Deoxyuridine 2-dioxygenase','Iron; L-ascorbate.','Also acts on thymidine (cf. EC 1.14.11.10).','2-deoxyuridine + 2-oxoglutarate + O(2) = uridine + succinate + CO(2).', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.14.11.4','Procollagen-lysine 5-dioxygenase','Lysine hydroxylase','Iron; L-ascorbate.','NULL','L-lysine-[procollagen] + 2-oxoglutarate + O(2) = (2S,5R)-5-hydroxy-L-lysine-[procollagen] + succinate + CO(2).', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.14.11.5','Transferred entry: 1','NULL','NULL','NULL','NULL', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.14.11.6','Thymine dioxygenase','Thymine 7-hydroxylase','Iron; L-ascorbate.','Also acts on 5-hydroxymethyluracil to oxidize its -CH(2)-OH groupfirst to -CHO and then to -COOH.Formerly EC 1.14.11.5.','Thymine + 2-oxoglutarate + O(2) = 5-hydroxymethyluracil + succinate +CO(2).', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.14.11.7','Procollagen-proline 3-dioxygenase','Procollagen-proline,2-oxoglutarate 3-dioxygenase','Iron; L-ascorbate.','NULL','L-proline-[procollagen] + 2-oxoglutarate + O(2) = trans-3-hydroxy-L-proline-[procollagen] + succinate + CO(2).', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.14.11.8','Trimethyllysine dioxygenase','Epsilon-trimethyllysine 2-oxoglutarate dioxygenase','Fe(2+); L-ascorbate.','NULL','N(6),N(6),N(6)-trimethyl-L-lysine + 2-oxoglutarate + O(2) = 3-hydroxy-N(6),N(6),N(6)-trimethyl-L-lysine + succinate + CO(2).', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.14.11.9','Flavanone 3-dioxygenase','(2S)-flavanone 3-hydroxylase','Iron; L-ascorbate.','NULL','A flavanone + 2-oxoglutarate + O(2) = a dihydroflavonol + succinate +CO(2).', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.14.11.10','Pyrimidine-deoxynucleoside 1-dioxygenase','Deoxyuridine-uridine 1-dioxygenase','Iron; L-ascorbate.','Cf. EC 1.14.11.3.','2-deoxyuridine + 2-oxoglutarate + O(2) = uracil + 2-deoxyribonolactone +succinate + CO(2).', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.14.11.11','Hyoscyamine (6S)-dioxygenase','Hyoscyamine (6S)-hydroxylase','Iron; L-ascorbate.','NULL','L-hyoscyamine + 2-oxoglutarate + O(2) = (6S)-hydroxyhyoscyamine +succinate + CO(2).', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.14.11.12','Gibberellin-44 dioxygenase','Gibberellin A44 oxidase','Iron.','NULL','Gibberellin 44 + 2-oxoglutarate + O(2) = gibberellin 19 + succinate +CO(2).', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.14.11.13','Gibberellin 2-beta-dioxygenase','Gibberellin 2-beta-hydroxylase','Iron.','Also acts on a number of other gibberellins.','Gibberellin 1 + 2-oxoglutarate + O(2) = 2-beta-hydroxygibberellin 1 +succinate + CO(2).', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.14.11.14','6-beta-hydroxyhyoscyamine epoxidase','Hydroxyhyoscyamine dioxygenase','Iron; L-ascorbate.','NULL','(6S)-6-hydroxyhyoscyamine + 2-oxoglutarate + O(2) = scopolamine +succinate + CO(2) + H(2)O.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.14.11.15','Gibberellin 3-beta-dioxygenase','Gibberellin 3-beta-hydroxylase','Iron; L-ascorbate.','NULL','Gibberellin 20 + 2-oxoglutarate + O(2) = gibberellin 1 + succinate +CO(2).', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.14.11.16','Peptide-aspartate beta-dioxygenase','Aspartate beta-hydroxylase','Iron.','Some vitamin K-dependent coagulation factors, as well as syntheticpeptides based on the structure of the first epidermal growth factordomain of human coagulation factor IX or X, can act as acceptors.','Peptide L-aspartate + 2-oxoglutarate + O(2) = peptide 3-hydroxy-L-aspartate + succinate + CO(2).', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.14.11.17','Taurine dioxygenase','2-aminoethanesulfonate dioxygenase','Iron; L-ascorbate.','The enzyme from Escherichia coli also acts on pentanesulfonate,3-(N-morpholino)propanesulfonate and 2-(1,3-dioxoisoindolin-2-yl)ethanesulfonate, but at lower rates.','Taurine + 2-oxoglutarate + O(2) = sulfite + aminoacetaldehyde + succinate+ CO(2).', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.14.11.18','Phytanoyl-CoA dioxygenase','Phytanoyl-CoA 2 oxoglutarate dioxygenase','Iron; L-ascorbate.','Part of the peroxisomal phytanic acid alpha-oxidation pathway.','Phytanoyl-CoA + 2-oxoglutarate + O(2) = 2-hydroxyphytanoyl-CoA +succinate + CO(2).', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.14.11.19','Leucocyanidin oxygenase','Anthocyanidin synthase','Iron; L-ascorbate.','Involved in the pathway by which many flowering plants makeanthocyanin (glycosylated anthocyandin) flower pigments.The intermediates are transformed into cis- and trans-dihydroquercetin, which the enzyme can also oxidize to quercetin.Acidification of the products gives anthocyanidin, which, however,may not be a natural precursor of the anthocyanins.','Leucocyanidin + 2-oxoglutarate + O(2) = cis- and trans-dihydroquercetins+ succinate + CO(2) + H(2)O.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.14.11.20','Deacetoxyvindoline 4-hydroxylase','D17H','Iron; L-ascorbate.','Also acts, to a lesser extent, on 16-methoxy-2,3-dihydrotabersonine.','Deacetoxyvindoline + 2-oxoglutarate + O(2) = deacetylvindoline +succinate + CO(2).', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.14.11.21','Clavaminate synthase','Clavaminic acid synthase','Iron.','Catalyzes three separate oxidative reactions in the pathway for thebiosythesis of the beta-lactamase inhibitor clavulanate inStreptomyces clavuligerus.The first step (hydroxylation) is separated from the latter two(oxidative cyclization and desaturation) by the action ofEC 3.5.3.22.The three reactions are all catalyzed at the same nonheme iron site.','(1) Deoxyamidinoproclavaminate + 2-oxoglutarate + O(2) =amidinoproclavaminate + succinate + CO(2).(2) Proclavaminate + 2-oxoglutarate + O(2) = dihydroclavaminate +succinate + CO(2) + H(2)O.(3) Dihydroclavaminate + 2-oxoglutarate + O(2) = clavaminate + succinate+ CO(2) + H(2)O.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.14.11.22','Flavone synthase','Flavone synthase I','Iron; L-ascorbate.','NULL','A flavanone + 2-oxoglutarate + O(2) = a flavone + succinate + CO(2) +H(2)O.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.14.11.23','Flavonol synthase','Flavonoid 2-oxoglutarate-dependent dioxygenase','Iron.','In addition to the desaturation of (2R,3R)-dihydroflavonols toflavonols, the enzyme from the satsuma Citrus unshiu also has a non-specific activity that trans-hydroxylates the flavanones (2S)-naringenin and the unnatural (2R)-naringenin at C-3 to kaempferol and(2R,3R)-dihydrokaempferol, respectively.','A dihydroflavonol + 2-oxoglutarate + O(2) = a flavonol + succinate +CO(2) + H(2)O.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.14.11.24','2-deoxymugineic-acid 2-dioxygenase','IDS3','Iron.','It is also likely that this enzyme can catalyze the hydroxylation of3-epihydroxy-2-deoxymugineic acid to form 3-epihydroxymugineic acid.','2-deoxymugineic acid + 2-oxoglutarate + O(2) = mugineic acid + succinate+ CO(2).', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.14.11.25','Mugineic-acid 3-dioxygenase','IDS2','Fe(2+).','NULL','(1) Mugineic acid + 2-oxoglutarate + O(2) = 3-epihydroxymugineic acid +succinate + CO(2).(2) 2-deoxymugineic acid + 2-oxoglutarate + O(2) = 3-epihydroxy-2-deoxymugineic acid + succinate + CO(2).', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.14.11.26','Deacetoxycephalosporin-C hydroxylase','3-methylcephem hydroxylase','Iron.','The enzyme can also use 3-exomethylenecephalosporin C as a substrateto form deacetoxycephalosporin C, although more slowly.In Acremonium chrysogenum, the enzyme forms part of a bifunctionalprotein along with EC 1.14.20.1.It is a separate enzyme in Streptomyces clavuligerus.','Deacetoxycephalosporin C + 2-oxoglutarate + O(2) = deacetylcephalosporinC + succinate + CO(2).', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.14.11.27','[Histone H3]-lysine-36 demethylase','H3-K36-specific demethylase','Fe(2+).','Of the seven potential methylation sites in histones H3 (K4, K9, K27,K36, K79) and H4 (K20, R3) from HeLa cells, the enzyme is specificfor Lys-36.Lysine residues exist in three methylation states (mono-, di- andtrimethylated).The enzyme preferentially demethylates the dimethyl form of Lys-36(K36me2), which is its natural substrate, to form the monomethyl andunmethylated forms of Lys-36.It can also demethylate the monomethyl- but not the trimethyl form ofLys-36.','(1) Protein N(6),N(6)-dimethyl-L-lysine + 2-oxoglutarate + O(2) = proteinN(6)-methyl-L-lysine + succinate + formaldehyde + CO(2).(2) Protein N(6)-methyl-L-lysine + 2-oxoglutarate + O(2) = proteinL-lysine + succinate + formaldehyde + CO(2).', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.14.11.28','Proline 3-hydroxylase','P-3-H','Fe(2+).','Unlike the proline hydroxylases involved in collagen biosynthesis(EC 1.14.11.2 and EC 1.14.11.7), this enzyme does not requireascorbate for activity although it does increase the activity of theenzyme.The enzyme is specific for L-proline as D-proline, trans-4-hydroxy-L-proline, cis-4-hydroxy-L-proline and 3,4-dehydro-DL-proline are notsubstrates.','L-proline + 2-oxoglutarate + O(2) = cis-3-hydroxy-L-proline + succinate +CO(2).', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.14.11.29','Hypoxia-inducible factor-proline dioxygenase','HIF hydroxylase','Iron; L-ascorbate.','Contains iron, and requires ascorbate.Specifically hydroxylates a proline residue in HIF-alpha, the alphasubunit of the transcriptional regulator HIF (hypoxia-induciblefactor), which targets HIF for proteasomal destruction.The requirement of oxygen for the hydroxylation reaction enablesanimals to respond to hypoxia.','Hypoxia-inducible factor-L-proline + 2-oxoglutarate + O(2) = hypoxia-inducible factor-trans-4-hydroxy-L-proline + succinate + CO(2).', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.14.11.30','Hypoxia-inducible factor-asparagine dioxygenase','HIF hydroxylase','Iron; L-ascorbate.','Contains iron, and requires ascorbate.Catalyzes hydroxylation of an asparagine in the C-terminaltranscriptional activation domain of HIF-alpha, the alpha subunit ofthe transcriptional regulator HIF (hypoxia-inducible factor), whichreduces its interaction with the transcriptional coactivator proteinp300.The requirement of oxygen for the hydroxylation reaction enablesanimals to respond to hypoxia.','Hypoxia-inducible factor-L-asparagine + 2-oxoglutarate + O(2) = hypoxia-inducible factor-(3S)-3-hydroxy-L-asparagine + succinate + CO(2).', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.14.11.31','Thebaine 6-O-demethylase','NULL','Iron.','Requires Fe(2+).Catalyzes a step in morphine biosynthesis.The product neopinione spontaneously rearranges to the more stablecodeinone.The enzyme also catalyzes the 6-O-demethylation of oripavine tomorphinone, with lower efficiency.','Thebaine + 2-oxoglutarate + O(2) = neopinone + formaldehyde + succinate +CO(2).', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.14.11.32','Codeine 3-O-demethylase','Codeine O-demethylase','Iron.','Requires Fe(2+).Catalyzes a step in morphine biosynthesis.The enzyme also catalyzes the 3-O-demethylation of thebaine tooripavine, with lower efficiency.','Codeine + 2-oxoglutarate + O(2) = morphine + formaldehyde + succinate +CO(2).', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.14.11.33','DNA oxidative demethylase','Alkylated DNA repair protein','Iron.','Activity is slightly stimulated by ascorbate.Catalyzes oxidative demethylation of the DNA base lesions N(1)-methyladenine, N(3)-methylcytosine, N(1)-methylguanine, and N(3)-methylthymine.It works better on single-stranded DNA (ssDNA) and is capable ofrepairing damaged bases in RNA.','DNA-base-CH(3) + 2-oxoglutarate + O(2) = DNA-base + formaldehyde +succinate + CO(2).', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.14.11.34','2-oxoglutarate/L-arginine monooxygenase/decarboxylase (succinate-forming)','Ethylene-forming enzyme','NULL','This is one of two simultaneous reactions catalyzed by the enzyme,which is responsible for ethylene production in bacteria of thePseudomonas syringae group.In the other reaction (EC 1.13.12.19) the enzyme catalyzes thedioxygenation of 2-oxoglutarate forming ethylene and three moleculesof carbon dioxide.The enzyme catalyzes two cycles of the ethylene-forming reaction foreach cycle of the succinate-forming reaction, so that thestoichiometry of the products ethylene and succinate is 2:1.','2-oxoglutarate + L-arginine + O(2) = succinate + CO(2) + guanidine + (S)-1-pyrroline-5-carboxylate + H(2)O.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.14.11.35','1-deoxypentalenic acid 11-beta-hydroxylase','NULL','Iron; L-ascorbate.','Isolated from the bacterium Streptomyces avermitilis.Part of the pathway for pentalenolactone biosynthesis.','1-deoxypentalenate + 2-oxoglutarate + O(2) = 1-deoxy-11-beta-hydroxypentalenate + succinate + CO(2).', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.14.11.36','Pentalenolactone F synthase','NULL','Iron; L-ascorbate.','Isolated from the bacteria Streptomyces exfoliatus, Streptomycesarenae and Streptomyces avermitilis.Part of the pentalenolactone biosynthesis pathway.','Pentalenolactone D + 2 2-oxoglutarate + 2 O(2) = pentalenolactone F +2 succinate + 2 CO(2) + H(2)O.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.14.11.37','Kanamycin B dioxygenase','NULL','Ascorbate; Fe(2+).','Found in the bacterium Streptomyces kanamyceticus where it isinvolved in the conversion of the aminoglycoside antibiotic kanamycinB to kanamycin A.','Kanamycin B + 2-oxoglutarate + O(2) = 2-dehydrokanamycin A + succinate +NH(3) + CO(2).', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.14.11.38','Verruculogen synthase','NULL','Fe(2+).','Found in the fungus Aspergillus fumigatus.Both atoms of a dioxygen molecule are incorporated into verruculogen.Involved in the biosynthetic pathways of several indole alkaloidssuch as fumitremorgin A.','Fumitremorgin B + 2-oxoglutarate + 2 O(2) + AH(2) = verruculogen +succinate + CO(2) + H(2)O + A.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.14.11.39','L-asparagine oxygenase','L-asparagine 3-hydroxylase','Fe(2+).','The enzyme is only able to hydroxylate free L-asparagine.It is not active toward D-asparagine.The beta-hydroxylated asparagine produced is incorporated at position9 of the calcium-dependent antibiotic (CDA), an 11-residue non-ribosomally synthesized acidic lipopeptide lactone.Formerly EC 1.14.11.n1.','L-asparagine + 2-oxoglutarate + O(2) = (2S,3S)-3-hydroxyasparagine +succinate + CO(2).', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.14.11.40','Enduracididine beta-hydroxylase','NULL','NULL','The enzyme is involved in biosynthesis of the nonproteinogenic aminoacid beta-hydroxyenduracididine, a component of the mannopeptimycins(cyclic glycopeptide antibiotic), produced by Streptomyceshygroscopicus NRRL 30439.','L-enduracididine + 2-oxoglutarate + O(2) = (3S)-3-hydroxy-L-enduracididine + succinate + CO(2).', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.14.11.41','L-arginine hydroxylase','NULL','Fe(2+).','The enzyme is involved in the biosynthesis of the cyclic pentapeptideantibiotic viomycin.It differs from EC 1.14.11.34, because it does not form guanidine and(S)-1-pyrroline-5-carboxylate from 3-hydroxy-L-arginine.','L-arginine + 2-oxoglutarate + O(2) = (3S)-3-hydroxy-L-arginine +succinate + CO(2).', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.14.11.42','tRNA(Phe) (7-(3-amino-3-carboxypropyl)wyosine(37)-C(2))-hydroxylase','NULL','Fe(2+).','The enzyme is not active with wybutosine.','7-(3-amino-3-carboxypropyl)wyosine(37) in tRNA(Phe) + 2-oxoglutarate +O(2) = 7-(2-hydroxy-3-amino-3-carboxypropyl)wyosine(37) in tRNA(Phe) +succinate + CO(2).', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.14.11.43','(S)-dichlorprop dioxygenase (2-oxoglutarate)','(S)-mecoprop dioxygenase','Fe(2+).','The enzymes from the Gram-negative bacteria Delftia acidovorans MC1and Sphingomonas herbicidovorans MH are involved in the degradationof the (S)-enantiomer of the phenoxyalkanoic acid herbicides mecopropand dichlorprop.','(1) (S)-2-(4-chloro-2-methylphenoxy)propanoate + 2-oxoglutarate + O(2) =4-chloro-2-methylphenol + pyruvate + succinate + CO(2).(2) (S)-(2,4-dichlorophenoxy)propanoate + 2-oxoglutarate + O(2) = 2,4-dichlorophenol + pyruvate + succinate + CO(2).', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.14.11.44','(R)-dichlorprop dioxygenase (2-oxoglutarate)','(R)-mecoprop dioxygenase','Fe(2+).','The enzymes from the Gram-negative bacteria Delftia acidovorans MC1and Sphingomonas herbicidovorans MH are involved in the degradationof the (R)-enantiomer of the phenoxyalkanoic acid herbicides mecopropand dichlorprop.','(1) (R)-2-(4-chloro-2-methylphenoxy)propanoate + 2-oxoglutarate + O(2) =4-chloro-2-methylphenol + pyruvate + succinate + CO(2).(2) (R)-(2,4-dichlorophenoxy)propanoate + 2-oxoglutarate + O(2) = 2,4-dichlorophenol + pyruvate + succinate + CO(2).', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.14.11.n1','Transferred entry: 1','NULL','NULL','NULL','NULL', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.14.11.n2','Methylcytosine dioxygenase','NULL','NULL','Dioxygenase that catalyzes the conversion of the modified genomicbase 5-methylcytosine (5mC) into 5-hydroxymethylcytosine (5hmC).Might initiate a process leading to cytosine demethylation throughdeamination into 5-hydroxymethyluracil (5hmU) and subsequentreplacement by unmethylated cytosine by the base excision repairsystem.Methylation at the C5 position of cytosine bases is an epigeneticmodification of the mammalian genome which plays an important role intranscriptional regulation.','DNA 5-methylcytosine + 2-oxoglutarate + O(2) =DNA 5-hydroxymethylcytosine + succinate + CO(2).', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.14.11.n3','L-proline cis-4-hydroxylase','NULL','NULL','NULL','L-proline + 2-oxoglutarate + O(2) = cis-4-hydroxy-L-proline + succinate +CO(2).', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.14.11.n4','Ankyrin-repeat-histidine dioxagenase','NULL','NULL','NULL','Ankyrin-repeat-L-histidine + 2-oxoglutarate + O(2) = ankyrin-repeat-(3S)-3-hydroxy-L-histidine + succinate + CO(2).', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.14.12.1','Anthranilate 1,2-dioxygenase (deaminating, decarboxylating)','Anthranilate hydroxylase','Iron.','NULL','Anthranilate + NAD(P)H + O(2) = catechol + CO(2) + NAD(P)(+) + NH(3).', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.14.12.2','Transferred entry: 1','NULL','NULL','NULL','NULL', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.14.12.3','Benzene 1,2-dioxygenase','Benzene hydroxylase','FAD; Iron; Iron-sulfur.','A system, containing a reductase which is an iron-sulfur flavoprotein(FAD), an iron-sulfur oxygenase and ferredoxin.','Benzene + NADH + O(2) = cis-cyclohexa-3,5-diene-1,2-diol + NAD(+).', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.14.12.4','3-hydroxy-2-methylpyridinecarboxylate dioxygenase','Methylhydroxypyridine carboxylate dioxygenase','FAD.','NULL','3-hydroxy-2-methylpyridine-5-carboxylate + NAD(P)H + O(2) =2-(acetamidomethylene)succinate + NAD(P)(+).', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.14.12.5','5-pyridoxate dioxygenase','5-pyridoxate oxidase','Flavoprotein.','NULL','3-hydroxy-4-hydroxymethyl-2-methylpyridine-5-carboxylate + NADPH + O(2) =2-(acetamidomethylene)-3-(hydroxymethyl)succinate + NADP(+).', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.14.12.6','Transferred entry: 1','NULL','NULL','NULL','NULL', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.14.12.7','Phthalate 4,5-dioxygenase','PDO','FMN; Iron; Iron-sulfur.','A system, containing a reductase which is an iron-sulfur flavoprotein(FMN), an iron-sulfur oxygenase, and no independent ferredoxin.','Phthalate + NADH + O(2) = cis-4,5-dihydroxycyclohexa-1(6),2-diene-1,2-dicarboxylate + NAD(+).', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.14.12.8','4-sulfobenzoate 3,4-dioxygenase','NULL','FMN; Iron; Iron-sulfur.','A system, containing a reductase which is an iron-sulfur flavoprotein(FMN), an iron-sulfur oxygenase, and no independent ferredoxin.','4-sulfobenzoate + NADH + O(2) = 3,4-dihydroxybenzoate + sulfite + NAD(+).', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.14.12.9','4-chlorophenylacetate 3,4-dioxygenase','NULL','Iron.','A system, containing a reductase and an iron-sulfur oxygenase, and noindependent ferredoxin.Also acts on 4-bromophenyl acetate.Formerly EC 1.13.99.4.','4-chlorophenylacetate + NADH + O(2) = 3,4-dihydroxyphenylacetate +chloride + NAD(+).', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.14.12.10','Benzoate 1,2-dioxygenase','Benzoate hydroxylase','Fe(2+).','A system, containing a reductase which is an iron-sulfur flavoprotein(FAD), and an iron-sulfur oxygenase.Formerly EC 1.13.99.2.','Benzoate + NADH + O(2) = (1R,6S)-1,6-dihydroxycyclohexa-2,4-diene-1-carboxylate + NAD(+).', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.14.12.11','Toluene dioxygenase','Toluene 1,2-dioxygenase','NULL','A system, containing a reductase which is an iron-sulfur flavoprotein(FAD), an iron-sulfur oxygenase, and a ferredoxin.Some other aromatic compounds, including ethylbenzene, 4-xylene andsome halogenated toluenes, are converted into the corresponding cis-dihydrodiols.','Toluene + NADH + O(2) = (1S,2R)-3-methylcyclohexa-3,5-diene-1,2-diol +NAD(+).', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.14.12.12','Naphthalene 1,2-dioxygenase','NULL','Iron.','Comprises a multicomponent system, containing a reductase that is aniron-sulfur flavoprotein (FAD; EC 1.18.1.3), an iron-sulfuroxygenase, and ferredoxin.','Naphthalene + NADH + O(2) = (1R,2S)-1,2-dihydronaphthalene-1,2-diol +NAD(+).', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.14.12.13','2-halobenzoate 1,2-dioxygenase','2-chlorobenzoate 1,2-dioxygenase','Iron.','A multicomponent enzyme system composed of a dioxygenase componentand an electron transfer component.The latter contains FAD.The enzyme, characterized from the bacterium Burkholderia cepacia2CBS, has a broad substrate specificity.Substrates include 2-fluorobenzoate, 2-chlorobenzoate,2-bromobenzoate, and 2-iodobenzoate, which are processed in thisorder of preference.','A 2-halobenzoate + NADH + O(2) = catechol + a halide anion + NAD(+) +CO(2).', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.14.12.14','2-aminobenzenesulfonate 2,3-dioxygenase','2-aminosulfobenzene 2,3-dioxygenase','NULL','NULL','2-aminobenzenesulfonate + NADH + O(2) = 2,3-dihydroxybenzenesulfonate +NH(3) + NAD(+).', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.14.12.15','Terephthalate 1,2-dioxygenase','1,4-dicarboxybenzoate 1,2-dioxygenase','NULL','In Comamonas testoteroni, contains a Rieske [2Fe-2S] center.','Terephthalate + NADH + O(2) = (1R,6S)-dihydroxycyclohexa-2,4-diene-1,4-dicarboxylate + NAD(+).', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.14.12.16','2-hydroxyquinoline 5,6-dioxygenase','2-oxo-1,2-dihydroquinoline 5,6-dioxygenase','NULL','3-methylquinolin-2-ol, quinolin-8-ol and quinolin-2,8-diol are alsosubstrates.Quinolin-2-ols exist largely as their quinolin-2(1H)-one tautomers.','Quinolin-2-ol + NADH + O(2) = 2,5,6-trihydroxy-5,6-dihydroquinoline +NAD(+).', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.14.12.17','Nitric oxide dioxygenase','NOD','FAD; Heme.','It has been proposed that FAD functions as the electron carrier fromNADPH to the ferric heme prosthetic group.','2 nitric oxide + 2 O(2) + NAD(P)H = 2 nitrate + NAD(P)(+) + H(+).', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.14.12.18','Biphenyl 2,3-dioxygenase','Biphenyl dioxygenase','Iron.','The enzyme from Burkholderia fungorum strain LB400 (previouslyPseudomonas sp.) is part of a multicomponent system composed of anNADH:ferredoxin oxidoreductase (FAD cofactor), a [2Fe-2S] Rieske-typeferredoxin, and a terminal oxygenase that contains a [2Fe-2S] Rieske-type iron-sulfur cluster and a catalytic mononuclear nonheme ironcenter.Chlorine-substituted biphenyls can also act as substrates.Similar to the three-component enzyme systems EC 1.14.12.3 andEC 1.14.12.11.','Biphenyl + NADH + O(2) = (1S,2R)-3-phenylcyclohexa-3,5-diene-1,2-diol +NAD(+).', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.14.12.19','3-phenylpropanoate dioxygenase','3-phenylpropionate dioxygenase','Iron.','This enzyme catalyzes a step in the pathway of phenylpropanoidcompounds degradation.It catalyzes the insertion of both atoms of molecular oxygen intopositions 2 and 3 of the phenyl ring of 3-phenylpropanoate or (2E)-3-phenylprop-2-enoate.','(1) 3-phenylpropanoate + NADH + O(2) = 3-(cis-5,6-dihydroxycyclohexa-1,3-dien-1-yl)propanoate + NAD(+).(2) (2E)-3-phenylprop-2-enoate + NADH + O(2) = (2E)-3-(2,3-dihydroxyphenyl)prop-2-enoate + NAD(+).', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.14.12.20','Pheophorbide a oxygenase','PaO','NULL','Catalyzes a key reaction in chlorophyll degradation, which occursduring leaf senescence and fruit ripening in higher plants.Requires reduced ferredoxin, which is generated either by NADPHthrough the pentose-phosphate pathway or by the action of photosystemI.While still attached to this enzyme, the product is rapidly convertedinto primary fluorescent chlorophyll catabolite by the action ofEC 1.3.1.80.Pheophorbide b acts as an inhibitor.In (18)O(2) labelling experiments, only the aldehyde oxygen islabelled, suggesting that the other oxygen atom may originate fromH(2)O.','Pheophorbide a + NADPH + O(2) = red chlorophyll catabolite + NADP(+).', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.14.12.21','Benzoyl-CoA 2,3-dioxygenase','Benzoyl-CoA dioxygenase/reductase','FAD; Iron; Iron-sulfur.','The enzyme is involved in aerobic benzoate metabolism in Azoarcusevansii.BoxB functions as the oxygenase part of benzoyl-CoA oxygenase inconjunction with BoxA, the reductase component, which upon binding ofbenzoyl-CoA, transfers two electrons to the ring in the course ofdioxygenation.Formerly EC 1.14.12.n1.','Benzoyl-CoA + NADPH + O(2) = 2,3-dihydro-2,3-dihydroxybenzoyl-CoA +NADP(+).', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.14.12.22','Carbazole 1,9a-dioxygenase','CARDO','Iron; Iron-sulfur.','This enzyme catalyzes the first reaction in the pathway of carbazoledegradation.The enzyme attacks at the 1 and 9a positions of carbazole, resultingin the formation of a highly unstable hemiaminal intermediate thatundergoes a spontaneous cleavage and rearomatization, resulting in2-aminobiphenyl-2,3-diol.In most bacteria the enzyme is a complex composed of a terminaloxygenase, a ferredoxin, and a ferredoxin reductase.The terminal oxygenase component contains a nonheme iron center and aRieske [2Fe-2S] iron-sulfur cluster.','9H-carbazole + NAD(P)H + O(2) = 2-aminobiphenyl-2,3-diol + NAD(P)(+).', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.14.12.n1','Transferred entry: 1','NULL','NULL','NULL','NULL', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.14.13.1','Salicylate 1-monooxygenase','Salicylate hydroxylase','FAD.','NULL','Salicylate + NADH + O(2) = catechol + NAD(+) + H(2)O + CO(2).', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.14.13.2','4-hydroxybenzoate 3-monooxygenase','p-hydroxybenzoate hydroxylase','FAD.','Most enzymes from Pseudomonas are highly specific for NAD(P)H (cf.EC 1.14.13.33).','4-hydroxybenzoate + NADPH + O(2) = protocatechuate + NADP(+) + H(2)O.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.14.13.3','Transferred entry: 1','NULL','NULL','NULL','NULL', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.14.13.4','Melilotate 3-monooxygenase','2-hydroxyphenylpropionate hydroxylase','FAD.','NULL','3-(2-hydroxyphenyl)propanoate + NADH + O(2) = 3-(2,3-dihydroxyphenyl)propanoate + NAD(+) + H(2)O.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.14.13.5','Imidazoleacetate 4-monooxygenase','Imidazoleacetate hydroxylase','FAD.','Formerly EC 1.14.1.5.','4-imidazoleacetate + NADH + O(2) = 5-hydroxy-4-imidazoleacetate + NAD(+)+ H(2)O.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.14.13.6','Orcinol 2-monooxygenase','Orcinol hydroxylase','FAD.','NULL','Orcinol + NADH + O(2) = 2,3,5-trihydroxytoluene + NAD(+) + H(2)O.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.14.13.7','Phenol 2-monooxygenase','Phenol hydroxylase','FAD.','Also active with resorcinol and 2-methylphenol.','Phenol + NADPH + O(2) = catechol + NADP(+) + H(2)O.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.14.13.8','Flavin-containing monooxygenase','Dimethylaniline monooxygenase (N-oxide-forming)','FAD.','A broad spectrum monooxygenase that accepts substrates as diverse ashydrazines, phosphines, boron-containing compounds, sulfides,selenides, iodide, as well as primary, secondary and tertiary amines.Is distinct from other monooxygenases in that the enzyme forms arelatively stable hydroperoxy flavin intermediate.Generally converts nucleophilic heteroatom-containing chemicals anddrugs into harmless, readily excreted metabolites.For example, N-oxygenation is largely responsible for thedetoxification of the dopaminergic neurotoxin 1-methyl-4-phenyl-1,2,3,6-tetrahydropyridine (MPTP).Formerly EC 1.13.12.11.','N,N-dimethylaniline + NADPH + O(2) = N,N-dimethylaniline N-oxide +NADP(+) + H(2)O.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.14.13.9','Kynurenine 3-monooxygenase','Kynurenine 3-hydroxylase','FAD.','Formerly EC 1.14.1.2 and EC 1.99.1.5.','L-kynurenine + NADPH + O(2) = 3-hydroxy-L-kynurenine + NADP(+) + H(2)O.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.14.13.10','2,6-dihydroxypyridine 3-monooxygenase','NULL','Flavoprotein.','NULL','2,6-dihydroxypyridine + NADH + O(2) = 2,3,6-trihydroxypyridine + NAD(+) +H(2)O.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.14.13.11','Trans-cinnamate 4-monooxygenase','CA4H','Heme-thiolate.','Also acts on NADH, more slowly.','Trans-cinnamate + NADPH + O(2) = 4-hydroxycinnamate + NADP(+) + H(2)O.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.14.13.12','Benzoate 4-monooxygenase','Benzoate 4-hydroxylase','5,6,7,8-tetrahydropteridine; Iron.','NULL','Benzoate + NADPH + O(2) = 4-hydroxybenzoate + NADP(+) + H(2)O.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.14.13.13','Calcidiol 1-monooxygenase','25-hydroxycholecalciferol 1-monooxygenase','Heme-thiolate.','NULL','Calcidiol + NADPH + O(2) = calcitriol + NADP(+) + H(2)O.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.14.13.14','Trans-cinnamate 2-monooxygenase','Cinnamic 2-hydroxylase','NULL','NULL','Trans-cinnamate + NADPH + O(2) = 2-hydroxycinnamate + NADP(+) + H(2)O.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.14.13.15','Cholestanetriol 26-monooxygenase','5-beta-cholestane-3-alpha,7-alpha,12-alpha-triol 26-hydroxylase','Heme-thiolate.','The enzyme requires ferredoxin and ferredoxin reductase.It catalyzes the first three sterol side chain oxidations in bileacid biosynthesis via the neutral (classic) pathway.Can also act on cholesterol, cholest-5-en-3-beta,7-alpha-diol,7-alpha-hydroxycholest-4-en-3-one, and 5-beta-cholestane-3-alpha,7-alpha-diol.The enzyme can also hydroxylate cholesterol at positions 24 and 25.','5-beta-cholestane-3-alpha,7-alpha,12-alpha-triol + 3 NADPH + 3 O(2) =(25R)-3-alpha,7-alpha,12-alpha-trihydroxy-5-beta-cholestan-26-oate +3 NADP(+) + 4 H(2)O.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.14.13.16','Cyclopentanone monooxygenase','Cyclopentanone 1,2-monooxygenase','NULL','NULL','Cyclopentanone + NADPH + O(2) = 5-valerolactone + NADP(+) + H(2)O.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.14.13.17','Cholesterol 7-alpha-monooxygenase','Cholesterol 7-alpha-hydroxylase','Heme-thiolate.','NULL','Cholesterol + NADPH + O(2) = 7-alpha-hydroxycholesterol + NADP(+) +H(2)O.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.14.13.18','4-hydroxyphenylacetate 1-monooxygenase','4-HPA 1-hydroxylase','FAD.','Also acts on 4-hydroxyhydratropate forming 2-methylhomogentisate andon 4-hydroxyphenoxyacetate forming hydroquinone and glycolate.','4-hydroxyphenylacetate + NAD(P)H + O(2) = homogentisate + NAD(P)(+) +H(2)O.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.14.13.19','Taxifolin 8-monooxygenase','NULL','Flavoprotein.','Converts a flavanol into a flavanone.Also acts on fustin, but not on catechin, quercetin ormollisacacidin.','Taxifolin + NAD(P)H + O(2) = 2,3-dihydrogossypetin + NAD(P)(+) + H(2)O.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.14.13.20','2,4-dichlorophenol 6-monooxygenase','2,4-dichlorophenol hydroxylase','FAD.','Also acts, more slowly, on 4-chlorophenol and 4-chloro-2-methylphenol.NADH can act instead of NADPH, but more slowly.','2,4-dichlorophenol + NADPH + O(2) = 3,5-dichlorocatechol + NADP(+) +H(2)O.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.14.13.21','Flavonoid 3-monooxygenase','Flavonoid 3-hydroxylase','NULL','Acts on a number of flavonoids, including naringenin anddihydrokaempferol.Does not act on 4-coumarate or 4-coumaroyl-CoA.','A flavonoid + NADPH + O(2) = a 3-hydroxyflavonoid + NADP(+) + H(2)O.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.14.13.22','Cyclohexanone monooxygenase','Cyclohexanone 1,2-monooxygenase','FAD.','In the catalytic mechanism of this enzyme, the nucleophilic speciesthat attacks the carbonyl group is a peroxyflavin intermediate thatis generated by reaction of the enzyme-bound flavin cofactor withNAD(P)H and oxygen.This enzyme is able to catalyze a wide range of oxidative reactions,including enantioselective Baeyer-Villiger reactions, sulfoxidations,amine oxidations and epoxidations.','Cyclohexanone + NADPH + O(2) = hexano-6-lactone + NADP(+) + H(2)O.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.14.13.23','3-hydroxybenzoate 4-monooxygenase','3-hydroxybenzoate 4-hydroxylase','FAD.','Also acts on a number of analogs of 3-hydroxybenzoate substituted inthe 2, 4, 5 and 6 positions.Formerly EC 1.14.99.13.','3-hydroxybenzoate + NADPH + O(2) = 3,4-dihydroxybenzoate + NADP(+) +H(2)O.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.14.13.24','3-hydroxybenzoate 6-monooxygenase','3-hydroxybenzoate 6-hydroxylase','FAD.','Also acts on a number of analogs of 3-hydroxybenzoate substituted inthe 2, 4, 5 and 6 positions.NADPH can act instead of NADH, more slowly.','3-hydroxybenzoate + NADH + O(2) = 2,5-dihydroxybenzoate + NAD(+) + H(2)O.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.14.13.25','Methane monooxygenase (soluble)','Methane hydroxylase','NULL','The enzyme is soluble, in contrast to the particulate enzyme,EC 1.14.18.3.Broad specificity; many alkanes can be hydroxylated, and alkenes areconverted into the corresponding epoxides; CO is oxidized to CO(2),ammonia is oxidized to hydroxylamine, and some aromatic compounds andcyclic alkanes can also be hydroxylated, but more slowly.','Methane + NAD(P)H + O(2) = methanol + NAD(P)(+) + H(2)O.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.14.13.26','Phosphatidylcholine 12-monooxygenase','Oleate Delta(12)-hydroxylase','NULL','NULL','1-acyl-2-oleoyl-sn-glycero-3-phosphocholine + NADH + O(2) = 1-acyl-2-((S)-12-hydroxyoleoyl)-sn-glycero-3-phosphocholine + NAD(+) + H(2)O.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.14.13.27','4-aminobenzoate 1-monooxygenase','4-aminobenzoate dehydrogenase','FAD.','Acts on anthranilate and 4-aminosalicylate but not on salicylate (cf.EC 1.14.13.1).','4-aminobenzoate + NAD(P)H + O(2) = 4-hydroxyaniline + NAD(P)(+) + H(2)O +CO(2).', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.14.13.28','3,9-dihydroxypterocarpan 6A-monooxygenase','3,9-dihydroxypterocarpan 6A-hydroxylase','Heme-thiolate.','The product of the reaction is the biosynthetic precursor of thephytoalexin glyceollin in soybean.','(6aR,11aR)-3,9-dihydroxypterocarpan + NADPH + O(2) = (6aS,11aS)-3,6a,9-trihydroxypterocarpan + NADP(+) + H(2)O.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.14.13.29','4-nitrophenol 2-monooxygenase','4-nitrophenol hydroxylase','FAD.','NULL','4-nitrophenol + NADH + O(2) = 4-nitrocatechol + NAD(+) + H(2)O.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.14.13.30','Leukotriene-B(4) 20-monooxygenase','Leukotriene-B(4) 20-hydroxylase','Heme-thiolate.','Not identical with EC 1.14.13.34.','(6Z,8E,10E,14Z)-(5S,12R)-5,12-dihydroxyicosa-6,8,10,14-tetraenoate +NADPH + O(2) = (6Z,8E,10E,14Z)-(5S,12R)-5,12,20-trihydroxyicosa-6,8,10,14-tetraenoate + NADP(+) + H(2)O.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.14.13.31','2-nitrophenol 2-monooxygenase','Nitrophenol oxygenase','NULL','Involved in the metabolism of nitro-aromatic compounds by a strain ofPseudomonas putida.','2-nitrophenol + NADPH + O(2) = catechol + nitrite + NADP(+) + H(2)O.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.14.13.32','Albendazole monooxygenase','Albendazole sulfoxidase','FAD.','NULL','Albendazole + NADPH + O(2) = albendazole S-oxide + NADP(+) + H(2)O.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.14.13.33','4-hydroxybenzoate 3-monooxygenase (NAD(P)H)','4-hydroxybenzoate 3-hydroxylase','FAD.','The enzyme from Corynebacterium cyclohexanicum is highly specific for4-hydroxybenzoate, but uses NADH and NADPH at approximately equalrates (cf. EC 1.14.13.2).It is less specific for NADPH than EC 1.14.13.2.','4-hydroxybenzoate + NAD(P)H + O(2) = 3,4-dihydroxybenzoate + NAD(P)(+) +H(2)O.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.14.13.34','Leukotriene-E(4) 20-monooxygenase','Leukotriene-E(4) omega-hydroxylase','NULL','Also acts on N-acetyl-leukotriene E(4), more slowly.Not identical with EC 1.14.13.30.','(7E,9E,11Z,14Z)-(5S,6R)-6-(cystein-S-yl)-5-hydroxyicosa-7,9,11,14-tetraenoate + NADPH + O(2) = 20-hydroxyleukotriene E(4) + NADP(+) +H(2)O.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.14.13.35','Anthranilate 3-monooxygenase (deaminating)','Anthranilate 2,3-hydroxylase (deaminating)','NULL','The enzyme from Aspergillus niger is an iron protein; that from theyeast Trichosporon cutaneum is a flavoprotein (FAD).Formerly EC 1.14.12.2.','Anthranilate + NADPH + O(2) = 2,3-dihydroxybenzoate + NADP(+) + NH(3).', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.14.13.36','5-O-(4-coumaroyl)-D-quinate 3-monooxygenase','5-O-(4-coumaroyl)-D-quinate/shikimate 3-hydroxylase','NULL','Also acts on trans-5-O-(4-coumaroyl)shikimate.','Trans-5-O-(4-coumaroyl)-D-quinate + NADPH + O(2) = trans-5-O-caffeoyl-D-quinate + NADP(+) + H(2)O.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.14.13.37','Methyltetrahydroprotoberberine 14-monooxygenase','(S)-cis-N-methyltetrahydroprotoberberine-14-hydroxylase','Heme-thiolate.','NULL','(S)-N-methylcanadine + NADPH + O(2) = allocryptopine + NADP(+) + H(2)O.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.14.13.38','Anhydrotetracycline monooxygenase','Anhydrotetracycline oxygenase','NULL','Involved in the biosynthesis of tetracyclines in Streptomyces sp.','Anhydrotetracycline + NADPH + O(2) = 12-dehydrotetracycline + NADP(+) +H(2)O.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.14.13.39','Nitric-oxide synthase (NADPH dependent)','Endothelium-derived relaxation factor-forming enzyme','NULL','This eukaryotic enzyme, which is found in plants and animals,consists of oxygenase and reductase domains that are linked via aregulatory calmodulin-binding domain.Upon calcium-induced calmodulin binding, the reductase and oxygenasedomains form a complex, allowing electrons to flow from NADPH via FADand FMN to the active center.May produce superoxide under certain conditions.Cf. EC 1.14.13.165.','2 L-arginine + 3 NADPH + 4 O(2) = 2 L-citrulline + 2 nitric oxide +3 NADP(+) + 4 H(2)O.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.14.13.40','Anthraniloyl-CoA monooxygenase','2-aminobenzoyl-CoA monooxygenase/reductase','FAD.','The non-aromatic product is unstable and releases CO(2) and NH(3),forming 1,4-cyclohexanedione.','Anthraniloyl-CoA + 2 NAD(P)H + O(2) = 2-amino-5-oxocyclohex-1-enecarboxyl-CoA + H(2)O + 2 NAD(P)(+).', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.14.13.41','Tyrosine N-monooxygenase','CYP79A1','Heme-thiolate.','Involved in the biosynthesis of the cyanogenic glucoside dhurrin insorghum, along with EC 1.14.13.68 and EC 2.4.1.85.Some 2-(4-hydroxyphenyl)-1-nitroethane is formed as a side product.','L-tyrosine + 2 O(2) + 2 NADPH = (Z)-(4-hydroxyphenylacetaldehyde oxime) +2 NADP(+) + CO(2) + 3 H(2)O.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.14.13.42','Deleted entry','NULL','NULL','NULL','NULL', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.14.13.43','Questin monooxygenase','Questin oxygenase','NULL','The enzyme cleaves the anthraquinone ring of questin to form abenzophenone.Involved in the biosynthesis of the seco-anthraquinone (+)-geodin.','Questin + NADPH + O(2) = demethylsulochrin + NADP(+).', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.14.13.44','2-hydroxybiphenyl 3-monooxygenase','NULL','NULL','Also converts 2,2-dihydroxybiphenyl into 2,2,3-trihydroxy-biphenyl.','2-hydroxybiphenyl + NADH + O(2) = 2,3-dihydroxybiphenyl + NAD(+) + H(2)O.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.14.13.45','Transferred entry: 1','NULL','NULL','NULL','NULL', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.14.13.46','(-)-menthol monooxygenase','NULL','NULL','NULL','(-)-menthol + NADPH + O(2) = p-menthane-3,8-diol + NADP(+) + H(2)O.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.14.13.47','(S)-limonene 3-monooxygenase','(-)-limonene 3-hydroxylase','Heme-thiolate.','High specificity, but NADH can act instead of NADPH, although moreslowly.','(-)-(S)-limonene + NADPH + O(2) = (-)-trans-isopiperitenol + NADP(+) +H(2)O.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.14.13.48','(S)-limonene 6-monooxygenase','(-)-limonene 6-hydroxylase','Heme-thiolate.','High specificity, but NADH can act instead of NADPH, although moreslowly.','(-)-(S)-limonene + NADPH + O(2) = (-)-trans-carveol + NADP(+) + H(2)O.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.14.13.49','(S)-limonene 7-monooxygenase','(-)-limonene 7-monooxygenase','Heme-thiolate.','High specificity, but NADH can act instead of NADPH, although moreslowly.','(-)-(S)-limonene + NADPH + O(2) = (-)-perillyl alcohol + NADP(+) + H(2)O.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.14.13.50','Pentachlorophenol monooxygenase','PCB 4-monooxygenase','FAD.','The enzyme displaces a diverse range of substituents from the4-position of polyhalogenated phenols but requires that a halogensubstituent be present at the 2-position.The enzyme converts many polyhalogenated phenols into hydroquinones,and requires that a halogen substituent be present at C-2.If C-4 carries a halogen substituent, reaction 1 is catalyzed, e.g.2,4,6-triiodophenol is oxidized to 2,6-diiodohydroquinone; if C-4 isunsubstituted, reaction 2 is catalyzed.','(1) Pentachlorophenol + 2 NADPH + O(2) = 2,3,5,6-tetrachlorohydroquinone+ 2 NADP(+) + chloride + H(2)O.(2) 2,3,5,6-tetrachlorophenol + NADPH + O(2) = 2,3,5,6-tetrachlorohydroquinone + NADP(+) + H(2)O.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.14.13.51','6-oxocineole dehydrogenase','NULL','NULL','The product undergoes non-enzymic cleavage and subsequent ringclosure to form the lactone 4,5-dihydro-5,5-dimethyl-4-(3-oxobutyl)furan-2(3H)-one.','6-oxocineole + NADPH + O(2) = 1,6,6-trimethyl-2,7-dioxabicyclo[3.2.2]nonan-3-one + NADP(+) + H(2)O.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.14.13.52','Isoflavone 3-hydroxylase','NULL','Heme-thiolate.','Also acts on biochanin A and other isoflavones with a 4-methoxygroup.Involved in the biosynthesis of the pterocarpin phytoalexinsmedicarpin and maackiain.','Formononetin + NADPH + O(2) = calycosin + NADP(+) + H(2)O.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.14.13.53','4-methoxyisoflavone 2-hydroxylase','Isoflavone 2-hydroxylase','Heme-thiolate.','Acts on isoflavones with a 4-methoxy group, such as formononetin andbiochanin A.Involved in the biosynthesis of the pterocarpin phytoalexinsmedicarpin and maackiain.EC 1.14.13.89 is less specific and acts on other isoflavones as wellas 4-methoxyisoflavones.','Formononetin + NADPH + O(2) = 2-hydroxyformononetin + NADP(+) + H(2)O.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.14.13.54','Ketosteroid monooxygenase','Steroid-ketone monooxygenase','FAD.','A single FAD-containing enzyme catalyzes three types of monooxygenase(Baeyer-Villiger oxidation) reaction.The oxidative esterification of a number of derivatives ofprogesterone to produce the corresponding 17-alpha-hydroxysteroid17-acetate ester, such as testosterone acetate, is shown in reaction(2).The oxidative lactonization of a number of derivatives ofandrostenedione to produce the 13,17-secoandrosteno-17,13-alpha-lactone, such as testololactone, is shown in reaction (3).The oxidative cleavage of the 17-beta-side-chain of 17-alpha-hydroxyprogesterone to produce androstenedione and acetate is shownin reaction (4).reaction (2) is also catalyzed by EC 1.14.99.4 and reactions (3) and(4) correspond to that catalyzed by EC 1.14.99.12.The possibility that a single enzyme is responsible for the reactionsascribed to EC 1.14.99.4 and EC 1.14.99.12 in other tissues cannot beexcluded.','(1) Ketosteroid + NADPH + O(2) = steroid ester/lactone + NADP(+) + H(2)O.(2) Progesterone + NADPH + O(2) = testosterone acetate + NADP(+) + H(2)O.(3) Androstenedione + NADPH + O(2) = testololactone + NADP(+) + H(2)O.(4) 17-alpha-hydroxyprogesterone + NADPH + O(2) = androstenedione +NADP(+) + H(2)O + acetate.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.14.13.55','Protopine 6-monooxygenase','Protopine 6-hydroxylase','Heme-thiolate.','Involved in benzophenanthridine alkaloid synthesis in higher plants.','Protopine + NADPH + O(2) = 6-hydroxyprotopine + NADP(+) + H(2)O.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.14.13.56','Dihydrosanguinarine 10-monooxygenase','Dihydrosanguinarine 10-hydroxylase','Heme-thiolate.','Involved in benzophenanthridine alkaloid synthesis in higher plants.','Dihydrosanguinarine + NADPH + O(2) = 10-hydroxydihydrosanguinarine +NADP(+) + H(2)O.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.14.13.57','Dihydrochelirubine 12-monooxygenase','Dihydrochelirubine 12-hydroxylase','Heme-thiolate.','NULL','Dihydrochelirubine + NADPH + O(2) = 12-hydroxydihydrochelirubine +NADP(+) + H(2)O.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.14.13.58','Benzoyl-CoA 3-monooxygenase','Benzoyl-CoA 3-hydroxylase','FMN or FAD.','Benzoate is not a substrate.','Benzoyl-CoA + NADPH + O(2) = 3-hydroxybenzoyl-CoA + NADP(+) + H(2)O.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.14.13.59','L-lysine N(6)-monooxygenase (NADPH)','Lysine N(6)-hydroxylase','FAD.','The enzyme from strain EN 222 of Escherichia coli is highly specificfor L-lysine; L-ornithine and L-homolysine are, for example,not substrates.','L-lysine + NADPH + O(2) = N(6)-hydroxy-L-lysine + NADP(+) + H(2)O.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.14.13.60','Transferred entry: 1','NULL','NULL','NULL','NULL', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.14.13.61','2-hydroxyquinoline 8-monooxygenase','2-oxo-1,2-dihydroquinoline 8-monooxygenase','Iron.','Quinolin-2-ol exists largely as the quinolin-2(1H)-one tautomer.','Quinolin-2-ol + NADH + O(2) = quinolin-2,8-diol + NAD(+) + H(2)O.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.14.13.62','4-hydroxyquinoline 3-monooxygenase','1-H-4-oxoquinoline 3-monooxygenase','NULL','Quinolin-4-ol exists largely as the quinolin-4(1H)-one tautomer.','Quinolin-4-ol + NADH + O(2) = quinolin-3,4-diol + NAD(+) + H(2)O.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.14.13.63','3-hydroxyphenylacetate 6-hydroxylase','3-hydroxyphenylacetate 6-monooxygenase','FAD.','3-hydroxyphenylacetate 6-hydroxylase from Flavobacterium sp. ishighly specific for 3-hydroxyphenylacetate and uses NADH and NADPH aselectron donors with similar efficiency.','3-hydroxyphenylacetate + NAD(P)H + O(2) = 2,5-dihydroxyphenylacetate +NAD(P)(+) + H(2)O.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.14.13.64','4-hydroxybenzoate 1-hydroxylase','4-hydroxybenzoate 1-monooxygenase','FAD.','The enzyme from Candida parapsilosis is specific for4-hydroxybenzoate derivatives and prefers NADH to NADPH as electrondonor.','4-hydroxybenzoate + NAD(P)H + O(2) = hydroquinone + NAD(P)(+) + H(2)O +CO(2).', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.14.13.65','Deleted entry','NULL','NULL','NULL','NULL', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.14.13.66','2-hydroxycyclohexanone 2-monooxygenase','NULL','NULL','The product decomposes spontaneously to 6-oxohexanoic acid (adipicsemialdehyde).Formerly EC 1.14.12.6.','2-hydroxycyclohexan-1-one + NADPH + O(2) = 6-hydroxyhexan-6-olide +NADP(+) + H(2)O.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.14.13.67','Quinine 3-monooxygenase','Nifedipine oxidase','Heme-thiolate.','NULL','Quinine + NADPH + O(2) = 3-hydroxyquinine + NADP(+) + H(2)O.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.14.13.68','4-hydroxyphenylacetaldehyde oxime monooxygenase','4-hydroxybenzeneacetaldehyde oxime monooxygenase','Heme-thiolate.','Involved in the biosynthesis of the cyanogenic glucoside dhurrin insorghum, along with EC 1.14.13.41 and EC 2.4.1.85.','(Z)-4-hydroxyphenylacetaldehyde oxime + NADPH + O(2) = (S)-4-hydroxymandelonitrile + NADP(+) + 2 H(2)O.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.14.13.69','Alkene monooxygenase','Alkene epoxygenase','Iron.','The enzyme from Xanthobacter sp. strain Py2 is a multicomponentenzyme comprising:(1) an NADH reductase, which provides the reductant for O(2)activation.(2) a Rieske-type ferredoxin, which is an electron-transfer protein.(3) an oxygenase, which contains the catalytic center for alkeneepoxidation.(4) a small protein of unknown function that is essential foractivity.The enzyme oxygenates C(2) to C(6) aliphatic alkenes.With 1,2-epoxypropane as substrate, the stereospecificity of theepoxypropane formed is 95% (R) and 5% (S).','Propene + NADH + O(2) = 1,2-epoxypropane + NAD(+) + H(2)O.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.14.13.70','Sterol 14-alpha-demethylase','Cytochrome P450 51','Heme-thiolate.','This heme-thiolate (P450) enzyme acts on a range of steroids with a14-alpha-methyl group, such as obtusifoliol and lanosterol.The enzyme catalyzes two successive hydroxylations of the 14-alpha-methyl group, followed by elimination as formate, leaving a 14(15)double bond.','A 14-alpha-methylsteroid + 3 O(2) + 3 NADPH = a Delta(14)-steroid +formate + 3 NADP(+) + 4 H(2)O.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.14.13.71','N-methylcoclaurine 3-monooxygenase','(S)-N-methylcoclaurine 3-hydroxylase','Heme-thiolate.','A P450 protein involved in benzylisoquinoline alkaloid synthesis inhigher plants.','(S)-N-methylcoclaurine + NADPH + O(2) = (S)-3-hydroxy-N-methylcoclaurine+ NADP(+) + H(2)O.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.14.13.72','Methylsterol monooxygenase','4-methylsterol oxidase','NULL','Requires cytochrome b5.Also acts on 4-alpha-methyl-5-alpha-cholest-7-en-3-beta-ol.The sterol can be based on cycloartenol as well as lanosterol.Formerly EC 1.14.99.16.','(1) 4,4-dimethyl-5-alpha-cholest-7-en-3-beta-ol + NAD(P)H + O(2) =4-beta-hydroxymethyl-4-alpha-methyl-5-alpha-cholest-7-en-3-beta-ol +NAD(P)(+) + H(2)O.(2) 4-beta-hydroxymethyl-4-alpha-methyl-5-alpha-cholest-7-en-3-beta-ol +NAD(P)H + O(2) = 3-beta-hydroxy-4-beta-methyl-5-alpha-cholest-7-ene-4-alpha-carbaldehyde + NAD(P)(+) + 2 H(2)O.(3) 3-beta-hydroxy-4-beta-methyl-5-alpha-cholest-7-ene-4-alpha-carbaldehyde + NAD(P)H + O(2) = 3-beta-hydroxy-4-beta-methyl-5-alpha-cholest-7-ene-4-alpha-carboxylate + NAD(P)(+) + H(2)O.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.14.13.73','Tabersonine 16-hydroxylase','NULL','Heme-thiolate.','NULL','Tabersonine + NADPH + O(2) = 16-hydroxytabersonine + NADP(+) + H(2)O.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.14.13.74','7-deoxyloganin 7-hydroxylase','NULL','Heme-thiolate.','NULL','7-deoxyloganin + NADPH + O(2) = loganin + NADP(+) + H(2)O.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.14.13.75','Vinorine hydroxylase','NULL','Heme-thiolate.','Forms a stage in the biosynthesis of the indole alkaloid ajmaline.','Vinorine + NADPH + O(2) = vomilenine + NADP(+) + H(2)O.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.14.13.76','Taxane 10-beta-hydroxylase','5-alpha-taxadienol-10-beta-hydroxylase','Heme-thiolate.','Involved in the biosynthesis of the diterpenoid antineoplastic drugtaxol (paclitaxel).','Taxa-4(20),11-dien-5-alpha-yl acetate + NADPH + O(2) = 10-beta-hydroxytaxa-4(20),11-dien-5-alpha-yl acetate + NADP(+) + H(2)O.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.14.13.77','Taxane 13-alpha-hydroxylase','NULL','Heme-thiolate.','Involved in the biosynthesis of the diterpenoid antineoplastic drugtaxol (paclitaxel).','Taxa-4(20),11-dien-5-alpha-ol + NADPH + O(2) = taxa-4(20),11-dien-5-alpha,13-alpha-diol + NADP(+) + H(2)O.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.14.13.78','Ent-kaurene oxidase','NULL','NULL','Requires cytochrome P450.Catalyzes three sucessive oxidations of the 4-methyl group of ent-kaurene giving kaurenoic acid.','(1) Ent-kaur-16-ene + NADPH + O(2) = ent-kaur-16-en-19-ol + NADP(+) +H(2)O.(2) Ent-kaur-16-en-19-ol + NADPH + O(2) = ent-kaur-16-en-19-al + NADP(+)+ 2 H(2)O.(3) Ent-kaur-16-en-19-al + NADPH + O(2) = ent-kaur-16-en-19-oate +NADP(+) + H(2)O.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.14.13.79','Ent-kaurenoic acid oxidase','NULL','NULL','Requires cytochrome P450.Catalyzes three sucessive oxidations of ent-kaurenoic acid.The second step includes a ring-B contraction giving the gibbaneskeleton.In Cucurbita maxima, ent-6-alpha,7-alpha-dihydroxykaur-16-en-19-oateis also formed.','Ent-kaur-16-en-19-oate + 3 NADPH + 3 O(2) = gibberellin A(12) + 3 NADP(+)+ 4 H(2)O.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.14.13.80','(R)-limonene 6-monooxygenase','(+)-limonene 6-monooxygenase','Heme-thiolate.','The reaction is stereospecific with over 95% yield of (+)-trans-carveol from (R)-limonene.(S)-Limonene, the substrate for EC 1.14.13.48 is not a substrate.Forms part of the carvone biosynthesis pathway in Carum carvi(caraway) seeds.','(+)-(R)-limonene + NADPH + O(2) = (+)-trans-carveol + NADP(+) + H(2)O.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.14.13.81','Magnesium-protoporphyrin IX monomethyl ester (oxidative) cyclase','Mg-protoporphyrin IX monomethyl ester oxidative cyclase','Iron.','The cyclase activity in Chlamydomonas reinhardtii is associatedexclusively with the membranes, whereas that from cucumber cotyledonsrequires both membrane and soluble fractions for activity.','Magnesium-protoporphyrin IX 13-monomethyl ester + 3 NADPH + 3 O(2) =divinylprotochlorophyllide + 3 NADP(+) + 5 H(2)O.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.14.13.82','Vanillate monooxygenase','4-hydroxy-3-methoxybenzoate demethylase','NULL','Forms part of the vanillin degradation pathway in Arthrobacter sp.Formerly EC 1.2.3.12.','Vanillate + O(2) + NADH = 3,4-dihydroxybenzoate + NAD(+) + H(2)O +formaldehyde.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.14.13.83','Precorrin-3B synthase','Precorrin-3X synthase','Iron-sulfur.','An oxygen atom from dioxygen is incorporated into the macrocycle atC-20.In the aerobic cobalamin biosynthesis pathway, four enzymes areinvolved in the conversion of precorrin-3A to precorrin-6A.This enzyme carries out the first of the four steps, yieldingprecorrin-3B as the product.This is followed by three methylation reactions, which introduce amethyl group at C-17 (CobJ; EC 2.1.1.131), C-11 (CobM; 2.1.1.133) andC-1 (CobF; EC 2.1.1.152) of the macrocycle, giving rise to precorrin-4, precorrin-5 and precorrin-6A, respectively.','Precorrin-3A + NADH + O(2) = precorrin-3B + NAD(+) + H(2)O.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.14.13.84','4-hydroxyacetophenone monooxygenase','HAPMO','FAD.','The enzyme from Pseudomonas fluorescens ACB catalyzes the conversionof a wide range of acetophenone derivatives.Highest activity occurs with compounds bearing an electron-donatingsubstituent at the para position of the aromatic ring.In the absence of substrate, the enzyme can act as an NADPH oxidase(EC 1.6.3.1).','(4-hydroxyphenyl)ethan-1-one + NADPH + O(2) = 4-hydroxyphenyl acetate +NADP(+) + H(2)O.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.14.13.85','Glyceollin synthase','Dimethylallyl-3,6a,9-trihydroxypterocarpan cyclase','Heme-thiolate.','Glyceollins II and III are formed from 2-dimethylallyl-(6aS,11aS)-3,6a,9-trihydroxypterocarpan whereas glyceollin I is formed from the4-isomer.','2-(or 4-)dimethylallyl-(6aS,11aS)-3,6a,9-trihydroxypterocarpan + NADPH +O(2) = glyceollin + NADP(+) + 2 H(2)O.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.14.13.86','Transferred entry: 1','NULL','NULL','NULL','NULL', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.14.13.87','Licodione synthase','(2S)-flavanone 2-hydroxylase','Heme-thiolate.','It probably forms 2-hydroxyliquiritigenin which spontaneously formslicodione.NADH can act instead of NADPH, but more slowly.','Liquiritigenin + NADPH + O(2) = licodione + NADP(+) + H(2)O.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.14.13.88','Flavonoid 3,5-hydroxylase','F3,5H','Heme-thiolate.','The 3,5-dihydroxyflavanone is formed via the 3-hydroxyflavanone.In Petunia hybrida the enzyme acts on naringenin, eriodictyol,dihydroquercetin (taxifolin) and dihydrokaempferol (aromadendrin).The enzyme catalyzes the hydroxylation of 5,7,4-trihydroxyflavanone(naringenin) at either the 3 position to form eriodictyol or at boththe 3 and 5 positions to form 5,7,3,4,5-pentahydroxyflavanone(dihydrotricetin).The enzyme also catalyzes the hydroxylation of3,5,7,3,4-pentahydroxyflavanone (taxifolin) at the 5 position,forming ampelopsin.NADH is not a good substitute for NADPH.','A flavanone + 2 NADPH + 2 O(2) = a 3,5-dihydroxyflavanone + 2 NADP(+) +2 H(2)O.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.14.13.89','Isoflavone 2-hydroxylase','CYP Ge-3','Heme-thiolate.','Acts on daidzein, formononetin and genistein.EC 1.14.13.53 has the same reaction but is more specific as itrequires a 4-methoxyisoflavone.','An isoflavone + NADPH + O(2) = a 2-hydroxyisoflavone + NADP(+) + H(2)O.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.14.13.90','Zeaxanthin epoxidase','Zea-epoxidase','FAD.','Active under conditions of low light.Along with EC 1.10.99.3, this enzyme forms part of the xanthophyll(or violaxanthin) cycle, which is involved in protecting the plantagainst damage by excess light.It will also epoxidize lutein in some higher-plant species.','Zeaxanthin + 2 NAD(P)H + 2 O(2) = violaxanthin + 2 NAD(P)(+) + 2 H(2)O.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.14.13.91','Deoxysarpagine hydroxylase','DOSH','Heme-thiolate.','NULL','10-deoxysarpagine + NADPH + O(2) = sarpagine + NADP(+) + H(2)O.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.14.13.92','Phenylacetone monooxygenase','PAMO','FAD.','NADH cannot replace NADPH as coenzyme.In addition to phenylacetone, which is the best substrate found todate, this Baeyer-Villiger monooxygenase can oxidize other aromaticketones (1-(4-hydroxyphenyl)propan-2-one, 1-(4-hydroxyphenyl)propan-2-one and 3-phenylbutan-2-one), some alipatic ketones (e.g. dodecan-2-one) and sulfides (e.g. 1-methyl-4-(methylsulfanyl)benzene).','Phenylacetone + NADPH + O(2) = benzyl acetate + NADP(+) + H(2)O.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.14.13.93','(+)-abscisic acid 8-hydroxylase','(+)-ABA 8-hydroxylase','Heme-thiolate.','Catalyzes the first step in the oxidative degradation of abscisicacid and is considered to be the pivotal enzyme in controlling therate of degradation of this plant hormone.CO inhibits the reaction, but its effects can be reversed by thepresence of blue light.The 8-hydroxyabscisate formed can be converted into (-)-phaseicacid, most probably spontaneously.Other enzymes involved in the abscisic-acid biosynthesis pathway areEC 1.1.1.288, EC 1.2.3.14 and EC 1.13.11.51.','(+)-abscisate + NADPH + O(2) = 8-hydroxyabscisate + NADP(+) + H(2)O.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.14.13.94','Lithocholate 6-beta-hydroxylase','6-beta-hydroxylase','Heme-thiolate.','Expression of the gene for this enzyme is 50-fold higher in malecompared to female hamsters.','Lithocholate + NADPH + O(2) = 6-beta-hydroxylithocholate + NADP(+) +H(2)O.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.14.13.95','7-alpha-hydroxycholest-4-en-3-one 12-alpha-hydroxylase','7-alpha-hydroxy-4-cholesten-3-one 12-alpha-monooxygenase','Heme-thiolate.','Requires EC 1.6.2.4 and cytochrome b5 for maximal activity.Important in bile acid biosynthesis, being responsible for thebalance between the formation of cholic acid and chenodeoxycholicacid.','7-alpha-hydroxycholest-4-en-3-one + NADPH + O(2) = 7-alpha,12-alpha-dihydroxycholest-4-en-3-one + NADP(+) + H(2)O.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.14.13.96','5-beta-cholestane-3-alpha,7-alpha-diol 12-alpha-hydroxylase','5-beta-cholestane-3-alpha,7-alpha-diol 12-alpha-monooxygenase','Heme-thiolate.','Key enzyme in the biosynthesis of the bile acid cholic acid(3-alpha,7-alpha,12-alpha-trihydroxy-5-beta-cholanoic acid).The activity determines the biosynthetic ratio between cholic acidand chenodeoxycholic acid.Can also hydroxylate the substrate at the 25 and 26 position, but toa lesser extent.','5-beta-cholestane-3-alpha,7-alpha-diol + NADPH + O(2) = 5-beta-cholestane-3-alpha,7-alpha,12-alpha-triol + NADP(+) + H(2)O.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.14.13.97','Taurochenodeoxycholate 6-alpha-hydroxylase','Taurochenodeoxycholate 6-alpha-monooxygenase','Heme-thiolate.','Requires cytochrome b5 for maximal activity.Acts on taurochenodeoxycholate, taurodeoxycholate and less readily onlithocholate and chenodeoxycholate.In adult pig (Sus scrofa), hyocholic acid replaces cholic acid as aprimary bile acid.','(1) Taurochenodeoxycholate + NADPH + O(2) = taurohyocholate + NADP(+) +H(2)O.(2) Lithocholate + NADPH + O(2) = hyodeoxycholate + NADP(+) + H(2)O.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.14.13.98','Cholesterol 24-hydroxylase','Cholesterol 24-monooxygenase','Heme-thiolate.','Can also produce 25-hydroxycholesterol.In addition, it can further hydroxylate the product to 24,25-dihydroxycholesterol and 24,27-dihydroxycholesterol.This reaction is the first step in the enzymatic degradation ofcholesterol in the brain as hydroxycholesterol can pass the blood-brain barrier whereas cholesterol cannot.','Cholesterol + NADPH + O(2) = (24S)-24-hydroxycholesterol + NADP(+) +H(2)O.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.14.13.99','24-hydroxycholesterol 7-alpha-hydroxylase','24-hydroxycholesterol 7-alpha-monooxygenase','Heme-thiolate.','Found in liver microsomes and in ciliary non-pigmented epithelium.Specific for (24R)-cholest-5-ene-3-beta,24-diol as substrate.','(24R)-cholest-5-ene-3-beta,24-diol + NADPH + O(2) = (24R)-cholest-5-ene-3-beta,7-alpha,24-triol + NADP(+) + H(2)O.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.14.13.100','25/26-hydroxycholesterol 7-alpha-hydroxylase','25-hydroxycholesterol 7-alpha-hydroxylase','Heme-thiolate.','Unlike EC 1.14.13.99 which is specific for its oxysterol substrate,this enzyme can also metabolize the oxysterols 24,25-epoxycholesterol, 22-hydroxycholesterol and 24-hydroxycholesterol,but to a lesser extent.Formerly EC 1.14.13.60.','(1) Cholest-5-ene-3-beta,25-diol + NADPH + O(2) = cholest-5-ene-3-beta,7-alpha,25-triol + NADP(+) + H(2)O.(2) (25R)-cholest-5-ene-3-beta,26-diol + NADPH + O(2) = (25R)-cholest-5-ene-3-beta,7-alpha,26-triol + NADP(+) + H(2)O.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.14.13.101','Senecionine N-oxygenase','Senecionine monooxygenase (N-oxide-forming)','NULL','NADH cannot replace NADPH.While pyrrolizidine alkaloids of the senecionine and monocrotalinetypes are generally good substrates (e.g. senecionine, retrorsine andmonocrotaline), the enzyme does not use ester alkaloids lacking anhydroxy group at C-7 (e.g. supinine and phalaenopsine), 1,2-dihydro-alkaloids (e.g. sarracine) or unesterified necine bases (e.g.senkirkine) as substrates.Senecionine N-oxide is used by insects as a chemical defense: it isnon-toxic, but it is bioactivated to a toxic form by the action ofcytochrome P-450 oxidase when absorbed by insectivores.','Senecionine + NADPH + O(2) = senecionine N-oxide + NADP(+) + H(2)O.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.14.13.102','Psoralen synthase','NULL','NULL','Specific for (+)-marmesin, and to a much lesser extent5-hydroxymarmesin, as substrate.Furanocoumarins protect plants from fungal invasion and herbivoreattack.(+)-columbianetin, the angular furanocoumarin analog of the linearfuranocoumarin (+)-marmesin, is not a substrate for the enzyme but itis a competitive inhibitor.','(+)-marmesin + NADPH + O(2) = psoralen + NADP(+) + acetone + 2 H(2)O.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.14.13.103','8-dimethylallylnaringenin 2-hydroxylase','8-DMAN 2-hydroxylase','NULL','Specific for sophoraflavanone B as substrate.NADPH cannot be replaced by NADH, FAD or FMN.Along with EC 2.5.1.70 and EC 2.5.1.71, this enzyme forms part of thesophoraflavanone-G biosynthesis pathway.','Sophoraflavanone B + NADPH + O(2) = leachianone G + NADP(+) + H(2)O.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.14.13.104','(+)-menthofuran synthase','(+)-MFS','Heme-thiolate.','The conversion of substrate into product involves the hydroxylationof the syn-methyl (C9), intramolecular cyclization to the hemiketaland dehydration to the furan.This is the second cytochrome P-450-mediated step of monoterpenemetabolism in peppermint, with the other step being catalyzed byEC 1.14.13.47.','(+)-pulegone + NADPH + O(2) = (+)-menthofuran + NADP(+) + H(2)O.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.14.13.105','Monocyclic monoterpene ketone monooxygenase','1-hydroxy-2-oxolimonene 1,2-monooxygenase','FAD.','This Baeyer-Villiger monooxygenase enzyme from the Gram-positivebacterium Rhodococcus erythropolis DCL14 has wide substratespecificity, catalyzing the lactonization of a large number ofmonocyclic monoterpene ketones and substituted cyclohexanones.Both (1R,4S)- and (1S,4R)-1-hydroxymenth-8-en-2-one are metabolized,with the lactone product spontaneously rearranging to form3-isopropenyl-6-oxoheptanoate.','(1) (-)-menthone + NADPH + O(2) = (4R,7S)-7-isopropyl-4-methyloxepan-2-one + NADP(+) + H(2)O.(2) Dihydrocarvone + NADPH + O(2) = 4-isopropenyl-7-methyloxepan-2-one +NADP(+) + H(2)O.(3) (Iso)-dihydrocarvone + NADPH + O(2) = 6-isopropenyl-3-methyloxepan-2-one + NADP(+) + H(2)O.(4) 1-hydroxymenth-8-en-2-one + NADPH + O(2) = 3-isopropenyl-6-oxoheptanoate + NADP(+) + H(2)O.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.14.13.106','Epi-isozizaene 5-monooxygenase','CYP170A1','NULL','This enzyme, from the soil-dwelling bacterium Streptomyces coelicolorA3(2), catalyzes two sequential allylic oxidation reactions.The substrate epi-isozizaene, which is formed by the action ofEC 4.2.3.37 is first oxidized to yield the epimeric intermediates(5R)-albaflavenol and (5S)-albaflavenol, which can be furtheroxidized to yield the sesquiterpenoid antibiotic albaflavenone.','(+)-epi-isozizaene + 2 NADPH + 2 O(2) = albaflavenone + 2 NADP(+) +3 H(2)O.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.14.13.107','Limonene 1,2-monooxygenase','NULL','FAD.','Limonene is the most widespread terpene and is formed by more than300 plants.Rhodococcus erythropolis DCL14, a Gram-positive bacterium, is able togrow on both (S)-limonene and (R)-limonene as the sole source ofcarbon and energy.NADPH can act instead of NADH, although more slowly.It has not been established if the product formed is optically pureor a mixture of two enantiomers.','(1) (S)-limonene + NAD(P)H + O(2) = 1,2-epoxymenth-8-ene + NAD(P)++ H(2)O.(2) (R)-limonene + NAD(P)H + O(2) = 1,2-epoxymenth-8-ene + NAD(P)++ H(2)O.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.14.13.108','Abieta-7,13-diene hydroxylase','Abietadiene hydroxylase','Heme-thiolate.','This enzyme catalyzes a step in the pathway of abietic acidbiosynthesis.The activity has been demonstrated in cell-free stem extracts ofAbies grandis (grand fir) and Pinus contorta (lodgepole pine).Activity is induced by wounding of the plant tissue.','Abieta-7,13-diene + NADPH + O(2) = abieta-7,13-dien-18-ol + NADP(+) +H(2)O.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.14.13.109','Abieta-7,13-dien-18-ol hydroxylase','Abietadienol hydroxylase','Heme-thiolate.','Catalyzes a step in the pathway of abietic acid biosynthesis.The activity has been demonstrated in cell-free stem extracts ofAbies grandis (grand fir) and Pinus contorta (lodgepole pine),and the gene encoding the enzyme has been identified in Pinus taeda(loblolly pine).The recombinant enzyme catalyzed the oxidation of multiple diterpenealcohol and aldehydes, including levopimaradienol, isopimara-7,15-dienol, isopimara-7,15-dienal, dehydroabietadienol,and dehydroabietadienal.It is not able to oxidize abietadiene.','Abieta-7,13-dien-18-ol + NADPH + O(2) = abieta-7,13-dien-18-al + NADP(+)+ 2 H(2)O.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.14.13.110','Geranylgeraniol 18-hydroxylase','GGOH-18-hydroxylase','Heme-thiolate.','NULL','Geranylgeraniol + NADPH + O(2) = 18-hydroxygeranylgeraniol + NADP(+) +H(2)O.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.14.13.111','Methanesulfonate monooxygenase','Mesylate monooxygenase','Flavoprotein; Iron-sulfur.','Methanesulfonate is the simplest of the sulfonates and is a substratefor the growth of certain methylotrophic microorganisms.Compared with EC 1.14.14.5, this enzyme has a restricted substraterange that includes only the short-chain aliphatic sulfonates(methane- to butanesulfonate) and excludes all larger molecules, suchas arylsulfonates and aromatic sulfonates.The enzyme from the bacterium Methylosulfonomonas methylovora is amulticomponent system comprising an hydroxylase, a reductase (MsmD)and a ferredoxin (MsmC).The hydroxylase has both large (MsmA) and small (MsmB) subunits, witheach large subunit containing a Rieske-type [2Fe-2S] center.Formerly EC 1.14.14.6.','Methanesulfonate + NADH + O(2) = formaldehyde + NAD(+) + sulfite + H(2)O.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.14.13.112','3-epi-6-deoxocathasterone 23-monooxygenase','CYP90C1','NULL','This enzyme is involved in brassinosteroid biosynthesis.C-23 hydroxylation shortcuts bypass campestanol, 6-deoxocathasterone,and 6-deoxoteasterone and lead directly from (22S,24R)-22-hydroxy-5-alpha-ergostan-3-one and 3-epi-6-deoxocathasterone to 3-dehydro-6-deoxoteasterone and 6-deoxotyphasterol.','(1) 3-epi-6-deoxocathasterone + NADPH + O(2) = 6-deoxotyphasterol +NADP(+) + H(2)O.(2) (22S,24R)-22-hydroxy-5-alpha-ergostan-3-one + NADPH + O(2) =3-dehydro-6-deoxoteasterone + NADP(+) + H(2)O.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.14.13.113','FAD-dependent urate hydroxylase','FAD-dependent urate oxidase','Flavoprotein.','The reaction is part of the purine catabolic pathway in the bacteriumKlebsiella pneumoniae.The enzyme is different from EC 1.7.3.3, found in most plants, whichproduces hydrogen peroxide.The product of the enzyme is a substrate for EC 3.5.2.17.','Urate + NADH + O(2) = 5-hydroxyisourate + NAD(+) + H(2)O.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.14.13.114','6-hydroxynicotinate 3-monooxygenase','6HNA monooxygenase','FAD.','The reaction is involved in the aerobic catabolism of nicotinic acid.','6-hydroxynicotinate + NADH + O(2) = 2,5-dihydroxypyridine + NAD(+) +H(2)O + CO(2).', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.14.13.115','Angelicin synthase','CYP71AJ4','Heme-thiolate.','This P450 monooxygenase enzyme is involved in the formation ofangular furanocoumarins.Attacks its substrate by syn-elimination of hydrogen from C-3.','(+)-columbianetin + NADPH + O(2) = angelicin + NADP(+) + acetone +2 H(2)O.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.14.13.116','Geranylhydroquinone 3-hydroxylase','GHQ 3-hydroxylase','NULL','Contains cytochrome P450.','Geranylhydroquinone + NADPH + O(2) = 3-hydroxygeranylhydroquinone +NADP(+) + H(2)O.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.14.13.117','Isoleucine N-monooxygenase','NULL','Heme-thiolate.','This enzyme catalyzes two successive N-hydroxylations ofL-isoleucine, the first committed steps in the biosynthesis of thecyanogenic glucoside lotaustralin in the plant Lotus japonicus.The product of the two hydroxylations, N,N-dihydroxy-L-isoleucine,is extremely labile and dehydrates spontaneously.The dehydrated product is then subject to a decarboxylation thatproduces the oxime.It is still not known whether the decarboxylation is spontaneous orcatalyzed by the enzyme.The product, (E)-2-methylbutanal oxime, undergoes a spontaneousisomerization to the (Z) form.The enzyme can also accept L-valine as substrate, with a loweractivity.It is different from EC 1.14.13.118 which prefers L-valine.','L-isoleucine + 2 H(+) + 2 NADPH + 2 O(2) = (E)-2-methylbutanal oxime +CO(2) + 3 H(2)O + 2 NADP(+).', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.14.13.118','Valine N-monooxygenase','NULL','Heme-thiolate.','This enzyme catalyzes two successive N-hydroxylations of L-valine,the first committed steps in the biosynthesis of the cyanogenicglucoside linamarin in the plant Manihot esculenta (cassava).The product of the two hydroxylations, N,N-dihydroxy-L-valine,is extremely labile and dehydrates spontaneously.The dehydrated product is then subject to a decarboxylation thatproduces the oxime.It is still not known whether the decarboxylation is spontaneous orcatalyzed by the enzyme.The product, (E)-2-methylpropanal oxime, undergoes a spontaneousisomerization to the (Z) form.The enzyme can also accept L-isoleucine as substrate, with a loweractivity.It is different from EC 1.14.13.117 which prefers L-isoleucine.','L-valine + 2 H(+) + 2 NADPH + 2 O(2) = (Z)-2-methylpropanal oxime + CO(2)+ 3 H(2)O + 2 NADP(+).', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.14.13.119','5-epiaristolochene 1,3-dihydroxylase','5-epi-aristolochene 1,3-dihydroxylase','Heme-thiolate.','Kinetic studies suggest that 1-beta-hydroxyepiaristolochene is mainlyformed first followed by hydroxylation at C-3.However the reverse order via 3-alpha-hydroxyepiaristolochene doesoccur.','5-epiaristolochene + 2 NADPH + 2 O(2) = capsidiol + 2 NADP(+) + 2 H(2)O.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.14.13.120','Costunolide synthase','NULL','Heme-thiolate.','The enzyme hydroxylates carbon C-6 of germacra-1(10),4,11(13)-trien-12-oate to give 6-alpha-hydroxygermacra-1(10),4,11(13)-trien-12-oate,which probably spontaneously cyclises to form the lactone ring.','Germacra-1(10),4,11(13)-trien-12-oate + NADPH + O(2) = (+)-costunolide +NADP(+) + 2 H(2)O.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.14.13.121','Premnaspirodiene oxygenase','HPO','Heme-thiolate.','The enzyme from the plant Hyoscymus muticus also hydroxylatesvalencene at C-2 to give the alpha-hydroxy compound, nootkatol,and this is converted into nootkatone.5-Epiaristolochene and epieremophilene are hydroxylated at C-2 togive a 2-beta-hydroxy derivative which is not further oxidized.','(-)-vetispiradiene + 2 NADPH + 2 O(2) = solavetivone + 2 NADP(+) +3 H(2)O.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.14.13.122','Chlorophyllide-a oxygenase','CAO','Iron.','Catalyzes two successive hydroxylations at the 7-methyl group ofchlorophyllide a.The second step yields the aldehyde hydrate, which loses H(2)Ospontaneously to form chlorophyllide b.Chlorophyll a and protochlorophyllide a are not substrates.Formerly EC 1.13.12.14.','Chlorophyllide a + 2 O(2) + 2 NADPH = chlorophyllide b + 3 H(2)O +2 NADP(+).', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.14.13.123','Germacrene A hydroxylase','NULL','Heme-thiolate.','This is probably part of the biosynthesis of many sesquiterpenoidlactones.In Lactuca sativa EC 1.14.13.213 is a mutifunctional enzyme withEC 1.1.1.314.','(+)-germacrene A + NADPH + O(2) = germacra-1(10),4,11(13)-trien-12-ol +NADP(+) + H(2)O.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.14.13.124','Phenylalanine N-monooxygenase','CYP79A2','Heme-thiolate.','This enzyme catalyzes two successive N-hydroxylations ofL-phenylalanine, the first committed steps in the biosynthesis ofbenzylglucosinolate.The product of the two hydroxylations, N,N-dihydroxy-L-phenylalanine,is extremely labile and dehydrates spontaneously.The dehydrated product is then subject to a decarboxylation thatproduces the oxime.It is still not known whether the decarboxylation is spontaneous orcatalyzed by the enzyme.The product, (E)-phenylacetaldoxime, undergoes a spontaneousisomerization to the (Z) form.Formerly EC 1.14.13.n1.','L-phenylalanine + 2 NADPH + 2 O(2) = (E)-phenylacetaldoxime + 2 NADP(+) +3 H(2)O + CO(2).', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.14.13.125','Tryptophan N-monooxygenase','CYP79B1','Heme-thiolate.','This enzyme catalyzes two successive N-hydroxylations ofL-tryptophan, the first steps in the biosynthesis of the both auxinand the indole alkaloid phytoalexin camalexin.The product of the two hydroxylations, N,N-dihydroxy-L-tryptophan,is extremely labile and dehydrates spontaneously.The dehydrated product is then subject to a decarboxylation thatproduces the oxime.It is still not known whether the decarboxylation is spontaneous orcatalyzed by the enzyme.The product, (E)-indol-3-ylacetaldoxime, undergoes a spontaneousisomerization to the (Z) form.Formerly EC 1.14.13.n2.','L-tryptophan + 2 NADPH + 2 O(2) = indole-3-acetaldoxime + 2 NADP(+) +3 H(2)O + CO(2).', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.14.13.126','Vitamin D(3) 24-hydroxylase','CYP24A1','Heme-thiolate.','The second donor, NADPH, donates electrons through EC 1.18.1.2 and a[2Fe-2S] ferredoxin.The enzyme can perform up to 6 rounds of hydroxylation of thesubstrate calcitriol leading to calcitroic acid.The human enzyme also shows 23-hydroxylating activity leading to1-alpha,25 dihydroxyvitamin D(3)-26,23-lactone as end product whilethe mouse and rat enzymes do not.Formerly EC 1.14.13.n4.','(1) Calcitriol + NADPH + O(2) = calcitetrol + NADP(+) + H(2)O.(2) Calcidiol + NADPH + O(2) = secalciferol + NADP(+) + H(2)O.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.14.13.127','3-(3-hydroxy-phenyl)propanoic acid hydroxylase','NULL','FAD.','This enzyme participates in a meta-cleavage pathway employed by thebacterium Escherichia coli for the degradation of variousphenylpropanoid compounds.Formerly EC 1.14.13.n3.','(1) 3-(3-hydroxyphenyl)propanoate + NADH + O(2) = 3-(2,3-dihydroxyphenyl)propanoate + H(2)O + NAD(+).(2) (2E)-3-(3-hydroxyphenyl)prop-2-enoate + NADH + O(2) = (2E)-3-(2,3-dihydroxyphenyl)prop-2-enoate + H(2)O + NAD(+).', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.14.13.128','7-methylxanthine demethylase','NULL','Iron.','NADH is the preferred cofactor.Part of the caffeine degradation pathway in Pseudomonas putida.','7-methylxanthine + O(2) + NAD(P)H = xanthine + NAD(P)(+) + H(2)O +formaldehyde.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.14.13.129','Beta-carotene 3-hydroxylase','Beta-carotene 3,3-monooxygenase','Iron.','Also acts on other carotenoids with a beta-end group.In some species canthaxanthin is the preferred substrate.','Beta-carotene + 2 NADH + 2 O(2) = zeaxanthin + 2 NAD(+) + 2 H(2)O.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.14.13.130','Pyrrole-2-carboxylate monooxygenase','Pyrrole-2-carboxylate ooxygenase','FAD.','The enzyme initiates the degradation of pyrrole-2-carboxylate.','Pyrrole-2-carboxylate + NADH + O(2) = 5-hydroxypyrrole-2-carboxylate +NAD(+) + H(2)O.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.14.13.131','Dimethyl-sulfide monooxygenase','Dimethylsulfide monooxygenase','NULL','The enzyme has lower activity with diethyl sulfide and other short-chain alkyl methyl sulfides.Its activity is stimulated by combined addition of FMN, and, afterdepletion of cations, of Mg(2+) and Fe(2+).The enzyme from Hyphomicrobium is a two component system thatincludes an FMN-dependent reductase subunit and a monooxygenasesubunit.','Dimethyl sulfide + O(2) + NADH = methanethiol + formaldehyde + NAD(+) +H(2)O.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.14.13.132','Squalene monooxygenase','Squalene epoxidase','FAD.','This enzyme, together with EC 5.4.99.7, was formerly known assqualene oxidocyclase.The electron donor, NADPH, is coupled via EC 1.6.2.4.Formerly EC 1.14.1.3, EC 1.14.99.7 and EC 1.99.1.13.','Squalene + NADPH + O(2) = (3S)-2,3-epoxy-2,3-dihydrosqualene + NADP(+) +H(2)O.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.14.13.133','Pentalenene oxygenase','NULL','Heme-thiolate.','The enzyme is involved in the biosynthesis of pentalenolactone andrelated antibiotics.','Pentalenene + 2 NADPH + 2 O(2) = pentalen-13-al + 2 NADP(+) + 3 H(2)O.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.14.13.134','Beta-amyrin 11-oxidase','CYP88D6','NULL','Requires cytochrome P450.Part of the glycyrrhizin biosynthesis pathway.The enzyme is also able to oxidize 30-hydroxy-beta-amyrin to11-alpha,30-dihydroxy-beta-amyrin but this is not thought to be partof glycyrrhizin biosynthesis.','Beta-amyrin + 2 O(2) + 2 NADPH = 11-oxo-beta-amyrin + 3 H(2)O +2 NADP(+).', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.14.13.135','1-hydroxy-2-naphthoate hydroxylase','1-hydroxy-2-naphthoic acid hydroxylase','NULL','The enzyme is involved in the catabolic pathway for the degradationof chrysene in some bacteria.','1-hydroxy-2-naphthoate + NAD(P)H + O(2) = 1,2-dihydroxynaphthalene +NAD(P)(+) + H(2)O + CO(2).', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.14.13.136','2-hydroxyisoflavanone synthase','CYT93C','NULL','The reaction involves the migration of the 2-phenyl group of theflavanone to the 3-position of the isoflavanone.The 2-hydroxyl group is derived from the oxygen molecule.EC 4.2.1.105, acts on the products with loss of water and formationof genistein and daidzein, respectively.Formerly EC 1.14.13.86.','(1) Liquiritigenin + O(2) + NADPH = 2,7,4-trihydroxyisoflavanone + H(2)O+ NADP(+).(2) (2S)-naringenin + O(2) + NADPH = 2,4,5,7-tetrahydroxyisoflavanone +H(2)O + NADP(+).', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.14.13.137','Indole-2-monooxygenase','CYP71C4','NULL','The enzyme is involved in the biosynthesis of protective andallelophatic benzoxazinoids in some plants, most commonly from thefamily of Poacea (grasses).It is a member of the cytochrome P450 dependent monooxygenases.','Indole + NAD(P)H + O(2) = indolin-2-one + NAD(P)(+) + H(2)O.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.14.13.138','Indolin-2-one monooxygenase','CYP71C2','NULL','The enzyme is involved in the biosynthesis of protective andallelophatic benzoxazinoids in some plants, most commonly from thefamily of Poacea (grasses).It is a member of the cytochrome P450 dependent monooxygenases.','Indolin-2-one + NAD(P)H + O(2) = 3-hydroxyindolin-2-one + NAD(P)(+) +H(2)O.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.14.13.139','3-hydroxyindolin-2-one monooxygenase','CYP71C1','NULL','The enzyme is involved in the biosynthesis of protective andallelophatic benzoxazinoids in some plants, most commonly from thefamily of Poacea (grasses).It is a member of the cytochrome P450 dependent monooxygenases.','3-hydroxyindolin-2-one + NAD(P)H + O(2) = 2-hydroxy-2H-1,4-benzoxazin-3(4H)-one + NAD(P)(+) + H(2)O.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.14.13.140','2-hydroxy-1,4-benzoxazin-3-one monooxygenase','CYP71C3','NULL','The enzyme is involved in the biosynthesis of protective andallelophatic benzoxazinoids in some plants, most commonly from thefamily of Poacea (grasses).It is a member of the cytochrome P450 dependent monooxygenases.','2-hydroxy-2H-1,4-benzoxazin-3(4H)-one + NAD(P)H + O(2) = 2,4-dihydroxy-2H-1,4-benzoxazin-3(4H)-one + NAD(P)(+) + H(2)O.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.14.13.141','Cholest-4-en-3-one 26-monooxygenase','Cholest-4-en-3-one 27-monooxygenase','Heme-thiolate.','This enzyme, found in several bacterial pathogens, is involved indegradation of the host cholesterol.It catalyzes the hydroxylation of the C26 carbon, followed byoxidation of the alcohol to the carboxylic acid via the aldehydeintermediate.These activities are required to initiate the degradation of thealkyl side-chain of cholesterol.The enzyme also accepts cholesterol as a substrate, but unlikeEC 1.14.13.15, this enzyme is specific for C26 and prefers cholest-4-en-3-one.','Cholest-4-en-3-one + NADH + O(2) = 26-hydroxycholest-4-en-3-one + NAD(+)+ H(2)O.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.14.13.142','3-ketosteroid 9-alpha-monooxygenase','3-ketosteroid 9-alpha-hydroxylase','Iron; Iron-sulfur.','The enzyme is involved in the cholesterol degradation pathway ofseveral bacterial pathogens, such as Mycobacterium tuberculosis.It is a two-component system consisting of a terminal oxygenase(KshA) and a ferredoxin reductase (KshB).The oxygenase contains a Rieske-type iron-sulfur center and non-hemeiron.The reductase component is a flavoprotein containing an NAD-bindingdomain and a plant-type iron-sulfur cluster.The product of the enzyme is unstable, and spontaneously converts to3-hydroxy-9,10-seconandrost-1,3,5(10)-triene-9,17-dione.','Androsta-1,4-diene-3,17-dione + NADH + O(2) = 9-alpha-hydroxyandrosta-1,4-diene-3,17-dione + NAD(+) + H(2)O.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.14.13.143','Ent-isokaurene C2-hydroxylase','CYP71Z6','NULL','This is the initial step in the conversion of ent-isokaurene to theantibacterial oryzalides in rice, Oryza sativa.','Ent-isokaurene + O(2) + NADPH = ent-2-alpha-hydroxyisokaurene + H(2)O +NADP(+).', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.14.13.144','9-beta-pimara-7,15-diene oxidase','CYP99A3','NULL','A rice, Oryza sativa, enzyme involved in the phytoalexin momilactonebiosynthesis.It also acts similarly on 9-beta-stemod-13(17)-ene.','9-beta-pimara-7,15-diene + 3 O(2) + 3 NADPH = 9-beta-pimara-7,15-dien-19-oate + 3 NADP(+) + 4 H(2)O.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.14.13.145','Ent-cassa-12,15-diene 11-hydroxylase','CYP76M7','NULL','A rice, Oryza sativa, enzyme involved in the biosynthesis of theantifungal phytocassanes.','Ent-cassa-12,15-diene + O(2) + NADPH = ent-11-beta-hydroxycassa-12,15-diene + NADP(+) + H(2)O.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.14.13.146','Taxoid 14-beta-hydroxylase','NULL','NULL','Requires cytochrome P450.From the yew Taxus cuspidata.Also acts on taxa-4(20),11-dien-5-alpha-yl acetate.','10-beta-hydroxytaxa-4(20),11-dien-5-alpha-yl acetate + O(2) + NADPH =10-beta,14-beta-dihydroxytaxa-4(20),11-dien-5?-yl acetate + NADP(+) +H(2)O.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.14.13.147','Taxoid 7-beta-hydroxylase','NULL','NULL','Requires cytochrome P450.From the yew tree Taxus cuspidata.Does not act on earlier intermediates in taxol biosynthesis.','Taxusin + O(2) + NADPH = 7-beta-hydroxytaxusin + NADP(+) + H(2)O.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.14.13.148','Trimethylamine monooxygenase','Flavin-containing monooxygenase 3','Flavoprotein.','The bacterial enzyme enables bacteria to use trimethylamine as thesole source of carbon and energy.The mammalian enzyme is involved in detoxification of trimethylamine.Mutations in the human enzyme cause the inheritable disease known astrimethylaminuria (fish odor syndrome).','N,N,N-trimethylamine + NADPH + O(2) = N,N,N-trimethylamine N-oxide +NADP(+) + H(2)O.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.14.13.149','Phenylacetyl-CoA 1,2-epoxidase','Phenylacetyl-CoA monooxygenase','NULL','Part of the aerobic pathway of phenylacetate catabolism inEscherichia coli and Pseudomonas putida.Formerly EC 1.14.13.n9.','Phenylacetyl-CoA + NADPH + O(2) = 2-(1,2-epoxy-1,2-dihydrophenyl)acetyl-CoA + NADP(+) + H(2)O.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.14.13.150','Alpha-humulene 10-hydroxylase','CYP71BA1','NULL','The recommended numbering of humulene gives 10-hydroxy-alpha-humuleneas the product rather than 8-hydroxy-alpha-humulene as used by theliterature.','Alpha-humulene + O(2) + NADPH = 10-hydroxy-alpha-humulene + NADP(+) +H(2)O.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.14.13.151','Linalool 8-monooxygenase','CYP111','Heme-thiolate.','The secondary electron donor is a specific [2Fe-2S] ferredoxin fromthe same bacterial strain.Formerly EC 1.14.99.28.','Linalool + 2 NADH + 2 O(2) = (6E)-8-oxolinalool + 2 NAD(+) + 3  H(2)O.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.14.13.152','Geraniol 8-hydroxylase','CYP76B10','NULL','Also hydroxylates nerol and citronellol, cf. EC 1.14.13.151.The recommended numbering of geraniol gives 8-hydroxygeraniol as theproduct rather than 10-hydroxygeraniol.The cloned enzyme also catalyzed, but less efficiently,the 3-hydroxylation of naringenin (cf. EC 1.14.13.21).','Geraniol + NADPH + O(2) = (6E)-8-hydroxygeraniol + NADP(+) + H(2)O.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.14.13.153','(+)-sabinene 3-hydroxylase','NULL','NULL','The enzyme has been characterized from Salvia officinalis (sage).','(+)-sabinene + NADPH + O(2) = (+)-cis-sabinol + NADP(+) + H(2)O.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.14.13.154','Erythromycin 12 hydroxylase','NULL','NULL','The enzyme is responsible for the C-12 hydroxylation of themacrolactone ring, one of the last steps in erythromycinbiosynthesis.It shows 1200-1900-fold preference for erythromycin D over thealternative substrate erythromycin B.','Erythromycin D + NADPH + O(2) = erythromycin C + NADP(+) + H(2)O.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.14.13.155','Alpha-pinene monooxygenase','NULL','NULL','Involved in the catabolism of alpha-pinene.','(-)-alpha-pinene + NADH + O(2) = alpha-pinene oxide + NAD(+) + H(2)O.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.14.13.156','1,8-cineole 2-endo-monooxygenase','CYP176A','Heme-thiolate.','Isolated from the bacterium Citrobacter braakii, which can use 1,8-cineole as the sole source of carbon.','1,8-cineole + NADPH + O(2) = 2-endo-hydroxy-1,8-cineole + NADP(+) +H(2)O.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.14.13.157','1,8-cineole 2-exo-monooxygenase','CYP3A4','Heme-thiolate.','The mammalian enzyme, expressed in liver microsomes, performs avariety of oxidation reactions of structurally unrelated compounds,including stereoids, fatty acids, and xenobiotics.Cf. EC 1.14.13.97, EC 1.14.13.67 and EC 1.14.13.32.','1,8-cineole + NADPH + O(2) = 2-exo-hydroxy-1,8-cineole + NADP(+) + H(2)O.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.14.13.158','Amorpha-4,11-diene 12 monooxygenase','CYP71AV1','Heme-thiolate.','Cloned from the plant Artemisia annua (sweet wormwood).Part of the biosynthetic pathway of artemisinin.','Amorpha-4,11-diene + 3 O(2) + 3 NADPH = artemisinate + 3 NADP(+) +4 H(2)O.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.14.13.159','Vitamin D 25-hydroxylase','CYP2R1','NULL','A microsomal enzyme isolated from human and mouse liver whichbioactivates vitamin D(3).While multiple isoforms (CYP27A1, CYP2J2/3, CYP3A4, CYP2D25 andCYP2C11) are able to catalyze the reaction in vitro, only CYP2R1 isthought to catalyze the reaction in humans in vivo.','Calciol + O(2) + NADPH = calcidiol + NADP(+) H(2)O.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.14.13.160','(2,2,3-trimethyl-5-oxocyclopent-3-enyl)acetyl-CoA 1,5-monooxygenase','2-oxo-Delta(3)-4,5,5-trimethylcyclopentenylacetyl-CoA 1,2-monooxygenase','FAD.','Forms part of the catabolism of camphor.It acts on the CoA ester in preference to the free acid.','((1R)-2,2,3-trimethyl-5-oxocyclopent-3-enyl)acetyl-CoA + O(2) + NADPH =((2R)-3,3,4-trimethyl-6-oxo-3,6-dihydro-1H-pyran-2-yl)acetyl-CoA +NADP(+) + H(2)O.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.14.13.161','(+)-camphor 6-exo-hydroxylase','(+)-camphor 6-hydroxylase','NULL','A cytochrome P-450 monooxygenase isolated from Salvia officinalis(sage).Involved in the catabolism of camphor in senescent tissue.','(+)-camphor + NADPH + O(2) = (+)-6-exo-hydroxycamphor + NADP(+) + H(2)O.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.14.13.162','2,5-diketocamphane 1,2-monooxygenase','2,5-diketocamphane 1,2-monooxygenase oxygenating component','FMN; Iron.','A Baeyer-Villiger monooxygenase isolated from camphor-grown strainsof Pseudomonas putida and encoded on the cam plasmid.Involved in the degradation of (+)-camphor.Requires a dedicated NADH-FMN reductase (cf. EC 1.5.1.42).Can accept several bicyclic ketones including (+)- and (-)-camphorand adamantanone.The product spontaneously converts to ((1R)-5-oxo-2,2,3-trimethylcyclopent-3-enyl)acetate.Formerly EC 1.14.15.2.','(+)-bornane-2,5-dione + O(2) + NADH = (+)-5-oxo-1,2-campholide + NAD(+) +H(2)O.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.14.13.163','6-hydroxy-3-succinoylpyridine 3-monooxygenase','6-hydroxy-3-succinoylpyridine hydroxylase','FAD.','The enzyme catalyzes a reaction in the nicotine degradation pathwayof Pseudomonas species.','4-(6-hydroxypyridin-3-yl)-4-oxobutanoate + 2 NADH + O(2) = 2,5-dihydroxypyridine + succinate semialdehyde + 2 NAD(+) + H(2)O.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.14.13.164','Transferred entry: 1','NULL','NULL','NULL','NULL', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.14.13.165','Nitric-oxide synthase (NAD(P)H-dependent)','Nitric oxide synthetase','5,6,7,8-tetrahydrobiopterin; Ferriprotoporphyrin IX.','Most of the bacterial and archaeal enzymes consist of only an oxidasedomain and function together with bacterial ferredoxins.The enzyme from the delta-proteobacterium Sorangium cellulosum alsoincludes a reductase domain that binds FAD, FMN and a [2Fe-2S]cluster.The similar enzymes from plants and animals use only NADPH asacceptor (cf. EC 1.14.13.39).','2 L-arginine + 3 NAD(P)H + 4 O(2) = 2 L-citrulline + 2 nitric oxide +3 NAD(P)(+) + 4 H(2)O.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.14.13.166','4-nitrocatechol 4-monooxygenase','NULL','FAD.','The enzyme catalyzes the oxidation of 4-nitrocatechol with theconcomitant removal of the nitro group as nitrite.Forms a two-component system with a flavoprotein reductase.The enzymes from the bacteria Lysinibacillus sphaericus JS905 andRhodococcus sp. strain PN1 were shown to also catalyze EC 1.14.13.29,while the enzyme from Pseudomonas sp. WBC-3 was shown to alsocatalyze EC 1.14.13.167.','4-nitrocatechol + NAD(P)H + O(2) = 2-hydroxy-1,4-benzoquinone + nitrite +NAD(P)(+) + H(2)O.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.14.13.167','4-nitrophenol 4-monooxygenase','NULL','FAD.','The enzyme catalyzes the first step in a degradation pathway for4-nitrophenol, the oxidation of 4-nitrophenol at position 4 with theconcomitant removal of the nitro group as nitrite.The enzyme from the bacterium Pseudomonas sp. WBC-3 also catalyzesEC 1.14.13.166.','4-nitrophenol + NADPH + O(2) = 1,4-benzoquinone + nitrite + NADP(+) +H(2)O.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.14.13.168','Indole-3-pyruvate monooxygenase','NULL','NULL','This plant enzyme, along with EC 2.6.1.99, is responsible for thebiosynthesis of the plant hormone indole-3-acetate from L-tryptophan.','(Indol-3-yl)pyruvate + NADPH + O(2) = (indol-3-yl)acetate + NADP(+) +H(2)O + CO(2).', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.14.13.169','Sphinganine C(4)-monooxygenase','Sphingolipid C4-hydroxylase','NULL','The enzyme is involved in the biosynthesis of sphingolipids in yeastand plants.','Sphinganine + NADPH + O(2) = phytosphingosine + NADP(+) + H(2)O.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.14.13.170','Pentalenolactone D synthase','NULL','FAD.','Isolated from the bacteria Streptomyces exfoliatus and Streptomycesarenae.The ketone undergoes a biological Baeyer-Villiger reaction.Part of the pathway of pentalenolactone biosynthesis.','1-deoxy-11-oxopentalenate + NADPH + O(2) = pentalenolactone D + NADP(+) +H(2)O.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.14.13.171','Neopentalenolactone D synthase','NULL','FAD.','Isolated from the bacterium Streptomyces avermitilis.The ketone undergoes a biological Baeyer-Villiger reaction.','1-deoxy-11-oxopentalenate + NADPH + O(2) = neopentalenolactone D +NADP(+) + H(2)O.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.14.13.172','Salicylate 5-hydroxylase','NULL','NULL','This enzyme, which was characterized from the bacterium Ralstonia sp.U2, comprises a multicomponent system, containing a reductase that isan iron-sulfur flavoprotein (FAD; EC 1.18.1.7), an iron-sulfuroxygenase, and ferredoxin.','Salicylate + NADH + O(2) = 2,5-dihydroxybenzoate + NAD(+) + H(2)O.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.14.13.173','11-oxo-beta-amyrin 30-oxidase','CYP72A','Heme-thiolate.','The enzyme is involved in the biosynthesis of the triterpenoidsaponin glycyrrhizin in the plant Glycyrrhiza uralensis (licorice).The enzyme from the plant Medicago truncatula can also hydroxylatebeta-amyrin.','11-oxo-beta-amyrin + 3 O(2) + 3 NADPH = glycyrrhetinate + 3 NADP(+) +4 H(2)O.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.14.13.174','Averantin hydroxylase','AVN hydroxylase','Heme-thiolate.','A P450 monooxygenase isolated from Aspergillus parasiticus.There is no reaction with (1R)-averantin.Involved in aflatoxin biosynthesis.','(1) (1S)-averantin + NADPH + O(2) = (1S,5S)-5-hydroxyaverantin +NADP(+) + H(2)O.(2) (1S)-averantin + NADPH + O(2) = (1S,5R)-5-hydroxyaverantin +NADP(+) + H(2)O.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.14.13.175','Aflatoxin B synthase','NULL','Heme-thiolate.','Isolated from the mold Aspergillus parasiticus.','(1) 8-O-methylsterigmatocystin + 2 NADPH + 2 O(2) = aflatoxin B(1) +2 NADP(+) + H(2)O + methanol + CO(2).(2) 8-O-methyldihydrosterigmatocystin + 2 NADPH + 2 O(2) = aflatoxin B(2)+ 2 NADP(+) + H(2)O + methanol + CO(2).', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.14.13.176','Tryprostatin B 6-hydroxylase','NULL','Heme-thiolate.','Involved in the biosynthetic pathways of several indole alkaloidssuch as tryprostatins, fumitremorgins and verruculogen.','Tryprostatin B + NADPH + O(2) = 6-hydroxytryprostatin B + NADP(+) +H(2)O.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.14.13.177','Fumitremorgin C monooxygenase','NULL','NULL','Involved in the biosynthetic pathway of the indole alkaloidverruculogen.','Fumitremorgin C + 2 NADPH + 2 O(2) = 12-alpha,13-alpha-dihydroxyfumitremorgin C + 2 NADP(+) + 2 H(2)O.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.14.13.178','Methylxanthine N(1)-demethylase','NULL','Iron.','The enzyme from the bacterium Pseudomonas putida shares anNAD(P)H-FMN reductase subunit with EC 1.14.13.179, and has a 5-foldhigher activity with NADH than with NADPH.Also demethylate 1-methylxantine with lower efficiency.Forms part of the degradation pathway of methylxanthines.','(1) Caffeine + O(2) + NAD(P)H = theobromine + NAD(P)(+) + H(2)O +formaldehyde.(2) Theophylline + O(2) + NAD(P)H = 3-methylxanthine + NAD(P)(+) + H(2)O+ formaldehyde.(3) Paraxanthine + O(2) + NAD(P)H = 7-methylxanthine + NAD(P)(+) + H(2)O+ formaldehyde.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.14.13.179','Methylxanthine N(3)-demethylase','NULL','Iron.','The enzyme from the bacterium Pseudomonas putida shares anNAD(P)H-FMN reductase subunit with EC 1.14.13.178, and has higheractivity with NADH than with NADPH.Also demethylates caffeine and theophylline with lower efficiency.Forms part of the degradation pathway of methylxanthines.','(1) Theobromine + O(2) + NAD(P)H = 7-methylxanthine + NAD(P)(+) + H(2)O +formaldehyde.(2) 3-methylxanthine + O(2) + NAD(P)H = xanthine + NAD(P)(+) + H(2)O +formaldehyde.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.14.13.180','Aklavinone 12-hydroxylase','Aklavinone 11-hydroxylase','FAD.','The enzymes from the Gram-positive bacteria Streptomyces peucetiusand Streptomyces purpurascens participate in the biosynthesis ofdaunorubicin, doxorubicin and rhodomycins.','Aklavinone + NADPH + O(2) = epsilon-rhodomycinone + NADP(+) + H(2)O.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.14.13.181','13-deoxydaunorubicin hydroxylase','NULL','NULL','The enzymes from the Gram-positive bacteria Streptomyces sp. C5 andStreptomyces peucetius show broad substrate specificity forstructures based on an anthracycline aglycone, but have a strongpreference for 4-methoxy anthracycline intermediates(13-deoxydaunorubicin and 13-dihydrodaunorubicin) over their4-hydroxy analogs (13-deoxycarminomycin and 13-dihydrocarminomycin),as well as a preference for substrates hydroxylated at the C-13rather than the C-14 position.','(1) 13-deoxydaunorubicin + NADPH + O(2) = 13-dihydrodaunorubicin +NADP(+) + H(2)O.(2) 13-dihydrodaunorubicin + NADPH + O(2) = daunorubicin + NADP(+) +2 H(2)O.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.14.13.182','2-heptyl-3-hydroxy-4(1H)-quinolone synthase','2-heptyl-3,4-dihydroxyquinoline synthase','NULL','The enzyme from the bacterium Pseudomonas aeruginosa catalyzes theterminal step in biosynthesis of the signal molecule 2-heptyl-3,4-dihydroxyquinoline that plays a role in regulation of virulencegenes.','2-heptyl-4(1H)-quinolone + NADH + O(2) = 2-heptyl-3-hydroxy-4(1H)-quinolone + NAD(+) + H(2)O.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.14.13.183','Dammarenediol 12-hydroxylase','CYP716A47','NULL','Involved in the biosynthetic pathway of ginsenosides.Isolated from ginseng (Panax ginseng).','Dammarenediol-II + NADPH + O(2) = protopanaxadiol + NADP(+) + H(2)O.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.14.13.184','Protopanaxadiol 6-hydroxylase','CYP716A53v2','Heme-thiolate.','Involved in the biosynthetic pathway of ginsenosides.Isolated from the rhizomes of ginseng (Panax ginseng).','Protopanaxadiol + NADPH + O(2) = protopanaxatriol + NADP(+) + H(2)O.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.14.13.185','Pikromycin synthase','CYP107L1','Heme-thiolate.','Involved in the biosynthesis of a number of bacterial macrolideantibiotics containing a desosamine glycoside unit.With 10-deoxymethymycin it hydroxylates at either C-10 to givemethymycin or C-12 to give neomethymycin.','(1) Narbomycin + NADPH + O(2) = pikromycin + NADP(+) + H(2)O.(2) Narbomycin + NADPH + O(2) = neopikromycin + NADP(+) + H(2)O.(3) Narbomycin + 2 NADPH + 2 O(2) = novapikromyin + 2 NADP(+) + 2 H(2)O.(4) 10-deoxymethymycin + NADPH + O(2) = methymycin + NADP(+) + H(2)O.(5) 10-deoxymethymycin + NADPH + O(2) = neomethymycin + NADP(+) + H(2)O.(6) 10-deoxymethymycin + 2 NADPH + 2 O(2) = novamethymycin + 2 NADP(+) +2 H(2)O.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.14.13.186','20-oxo-5-O-mycaminosyltylactone 23-monooxygenase','NULL','Heme-thiolate.','Involved in the biosynthetic pathway of the macrolide antibiotictylosin, which is produced by several species of Streptomycesbacteria.','20-oxo-5-O-beta-mycaminosyltylactone + NADPH + O(2) = 5-O-beta-mycaminosyltylonolide + NADP(+) + H(2)O.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.14.13.187','L-evernosamine nitrososynthase','NULL','FAD.','Isolated from the bacterium Micromonospora carbonacea var. africana.The nitroso group is probably spontaneously oxidized to a nitro groupgiving dTDP-beta-L-evernitrose, which is involved in the biosynthesisof the antibiotic everninomycin.The reaction was studied using dTDP-beta-L-4-epi-vancosamine (dTDP-4-O-desmethyl-beta-L-evernitrosamine).','dTDP-beta-L-evernosamine + 2 NADPH + 2 O(2) = dTDP-2,3,6-trideoxy-3-C-methyl-4-O-methyl-3-nitroso-beta-L-arabino-hexopyranose + 2 NADP(+) +3 H(2)O.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 1.14.13.188','6-deoxyerythronolide B hydroxylase','CYP107A1','Heme-thiolate.','Isolated from the bacterium Saccharopolyspora erythraea.The enzyme is involved in the biosynthesis of the antibioticerythromycin.','6-deoxyerythronolide B + NADPH + O(2) = erythronolide B + NADP(+) +H(2)O.', 'NULL', 'NULL');
