INSERT INTO enzyme VALUES ( 'EC 2.1.1.1','Nicotinamide N-methyltransferase','NULL','NULL','NULL','S-adenosyl-L-methionine + nicotinamide = S-adenosyl-L-homocysteine +1-methylnicotinamide.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 2.1.1.2','Guanidinoacetate N-methyltransferase','NULL','NULL','NULL','S-adenosyl-L-methionine + guanidinoacetate = S-adenosyl-L-homocysteine +creatine.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 2.1.1.3','Thetin--homocysteine S-methyltransferase','NULL','NULL','NULL','Dimethylsulfonioacetate + L-homocysteine = S-methylthioglycolate +L-methionine.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 2.1.1.4','Acetylserotonin O-methyltransferase','Hydroxyindole O-methyltransferase','NULL','Some other hydroxyindoles also act as acceptor, more slowly.','S-adenosyl-L-methionine + N-acetylserotonin = S-adenosyl-L-homocysteine +melatonin.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 2.1.1.5','Betaine--homocysteine S-methyltransferase','NULL','Zinc.','NULL','Trimethylammonioacetate + L-homocysteine = dimethylglycine +L-methionine.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 2.1.1.6','Catechol O-methyltransferase','NULL','NULL','The mammalian enzymes act more rapidly on catecholamines such asadrenaline or noradrenaline than on catechols.','S-adenosyl-L-methionine + a catechol = S-adenosyl-L-homocysteine +a guaiacol.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 2.1.1.7','Nicotinate N-methyltransferase','NULL','NULL','NULL','S-adenosyl-L-methionine + nicotinate = S-adenosyl-L-homocysteine +N-methylnicotinate.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 2.1.1.8','Histamine N-methyltransferase','NULL','NULL','NULL','S-adenosyl-L-methionine + histamine = S-adenosyl-L-homocysteine + N(tau)-methylhistamine.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 2.1.1.9','Thiol S-methyltransferase','NULL','NULL','H(2)S and a variety of alkyl, aryl and heterocyclic thiols andhydroxy thiols can act as acceptors.','S-adenosyl-L-methionine + a thiol = S-adenosyl-L-homocysteine +a thioether.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 2.1.1.10','Homocysteine S-methyltransferase','NULL','NULL','The enzyme uses S-adenosyl-L-methionine as methyl donor less activelythan S-methyl-L-methionine.','S-methyl-L-methionine + L-homocysteine = 2 L-methionine.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 2.1.1.11','Magnesium protoporphyrin IX methyltransferase','(-)-S-adenosyl-L-methionine:magnesium-protoporphyrin IXmethyltransferase','NULL','NULL','S-adenosyl-L-methionine + magnesium protoporphyrin IX = S-adenosyl-L-homocysteine + magnesium protoporphyrin IX 13-methyl ester.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 2.1.1.12','Methionine S-methyltransferase','NULL','Zinc or manganese.','NULL','S-adenosyl-L-methionine + L-methionine = S-adenosyl-L-homocysteine +S-methyl-L-methionine.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 2.1.1.13','Methionine synthase','5-methyltetrahydrofolate--homocysteine S-methyltransferase','Cobalamin; Zinc.','The enzyme becomes inactivated occasionally during its cycle byoxidation of Co(I) to Co(II).Reactivation by reductive methylation is catalyzed by the enzymeitself, with S-adenosyl-L-methionine as the methyl donor and areducing system.For the mammalian enzyme, the reducing system involves NADPH andEC 1.16.1.8.In bacteria, the reducing agent is flavodoxin, and no furthercatalyst is needed (the flavodoxin is kept in the reduced state byNADPH and EC 1.18.1.2).Acts on the monoglutamate as well as the triglutamate folate,in contrast with EC 2.1.1.14, which acts only on the triglutamate.','5-methyltetrahydrofolate + L-homocysteine = tetrahydrofolate +L-methionine.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 2.1.1.14','5-methyltetrahydropteroyltriglutamate--homocysteine S-methyltransferase','Cobalamin-independent methionine synthase','Zinc.','Requires phosphate.The enzyme from Escherichia coli also requires a reducing system.Unlike EC 2.1.1.13 this enzyme does not contain cobalamin.','5-methyltetrahydropteroyltri-L-glutamate + L-homocysteine =tetrahydropteroyltri-L-glutamate + L-methionine.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 2.1.1.15','Fatty-acid O-methyltransferase','NULL','NULL','Oleic acid is the most effective fatty acid acceptor.','S-adenosyl-L-methionine + a fatty acid = S-adenosyl-L-homocysteine +a fatty acid methyl ester.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 2.1.1.16','Methylene-fatty-acyl-phospholipid synthase','Cyclopropane synthetase','NULL','The enzyme transfers a methyl group to the 10-position of a Delta(9)-olefinic acyl chain in phosphatidylglycerol, phosphatidylinositol,or, more slowly, phosphatidylethanolamine; subsequent proton transferproduces a 10-methylene group (cf. EC 2.1.1.79).','S-adenosyl-L-methionine + phospholipid olefinic fatty acid = S-adenosyl-L-homocysteine + phospholipid methylene fatty acid.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 2.1.1.17','Phosphatidylethanolamine N-methyltransferase','NULL','NULL','NULL','S-adenosyl-L-methionine + phosphatidylethanolamine = S-adenosyl-L-homocysteine + phosphatidyl-N-methylethanolamine.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 2.1.1.18','Polysaccharide O-methyltransferase','NULL','NULL','NULL','S-adenosyl-L-methionine + a (1-', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 2.1.1.19','Trimethylsulfonium--tetrahydrofolate N-methyltransferase','NULL','NULL','NULL','Trimethylsulfonium + tetrahydrofolate = dimethylsulfide +5-methyltetrahydrofolate.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 2.1.1.20','Glycine N-methyltransferase','Glycine methyltransferase','NULL','Thought to play an important role in the regulation of methyl groupmetabolism in the liver and pancreas by regulating the ratio betweenS-adenosyl-L-methionine and S-adenosyl-L-homocysteine.Inhibited by 5-methyltetrahydrofolate pentaglutamate.Sarcosine, which has no physiological role, is converted back intoglycine by the action of EC 1.5.8.3.','S-adenosyl-L-methionine + glycine = S-adenosyl-L-homocysteine +sarcosine.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 2.1.1.21','Methylamine--glutamate N-methyltransferase','N-methylglutamate synthase','NULL','NULL','Methylamine + L-glutamate = NH(3) + N-methyl-L-glutamate.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 2.1.1.22','Carnosine N-methyltransferase','NULL','NULL','NULL','S-adenosyl-L-methionine + carnosine = S-adenosyl-L-homocysteine +anserine.', 'NULL', 'NULL');
