INSERT INTO enzyme VALUES ( 'EC 2.1.1.24','Transferred entry: 2','NULL','NULL','NULL','NULL', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 2.1.1.25','Phenol O-methyltransferase','NULL','NULL','Acts on a wide variety of simple alkyl-, methoxy- and halo-phenols.','S-adenosyl-L-methionine + phenol = S-adenosyl-L-homocysteine + anisole.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 2.1.1.26','Iodophenol O-methyltransferase','NULL','NULL','NULL','S-adenosyl-L-methionine + 2-iodophenol = S-adenosyl-L-homocysteine +2-iodophenol methyl ether.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 2.1.1.27','Tyramine N-methyltransferase','NULL','NULL','Has some activity on phenylethylamine analogs.','S-adenosyl-L-methionine + tyramine = S-adenosyl-L-homocysteine +N-methyltyramine.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 2.1.1.28','Phenylethanolamine N-methyltransferase','Noradrenaline N-methyltransferase','NULL','Acts on various phenylethanolamines; converts noradrenaline intoadrenaline.','S-adenosyl-L-methionine + phenylethanolamine = S-adenosyl-L-homocysteine+ N-methylphenylethanolamine.', 'NULL', 'NULL');
