INSERT INTO enzyme VALUES ( 'EC 2.1.1.33','tRNA (guanine(46)-N(7))-methyltransferase','N(7)-methylguanine methylase','NULL','The enzyme specifically methylates guanine(46) at N(7) in tRNA.','S-adenosyl-L-methionine + guanine(46) in tRNA = S-adenosyl-L-homocysteine+ N(7)-methylguanine(46) in tRNA.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 2.1.1.34','tRNA (guanosine(18)-2-O)-methyltransferase','tRNA (Gm18) 2-O-methyltransferase','NULL','The enzyme catalyzes the methylation of guanosine(18) in tRNA.','S-adenosyl-L-methionine + guanosine(18) in tRNA = S-adenosyl-L-homocysteine + 2-O-methylguanosine(18) in tRNA.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 2.1.1.35','tRNA (uracil(54)-C(5))-methyltransferase','5-methyluridine(54) tRNA methyltransferase','NULL','Unlike this enzyme, EC 2.1.1.74 uses 5,10-methylenetetrahydrofolateand FADH(2) to supply the atoms for methylation of U54.','S-adenosyl-L-methionine + uracil(54) in tRNA = S-adenosyl-L-homocysteine+ 5-methyluracil(54) in tRNA.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 2.1.1.36','Transferred entry: 2','NULL','NULL','NULL','NULL', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 2.1.1.37','DNA (cytosine-5-)-methyltransferase','Cytosine 5-methyltransferase','NULL','See the REBASE database for a complete list of these enzymes:http://rebase.neb.com/rebase/Formerly EC 2.1.1.73.','S-adenosyl-L-methionine + DNA = S-adenosyl-L-homocysteine +DNA containing 5-methylcytosine.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 2.1.1.38','O-demethylpuromycin O-methyltransferase','NULL','NULL','Puromycin is the antibiotic derived from N(6)-dimethyladenosine byreplacing the 3-hydroxy group with an amino group and acylating thiswith 4-O-methyltyrosine.','S-adenosyl-L-methionine + O-demethylpuromycin = S-adenosyl-L-homocysteine+ puromycin.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 2.1.1.39','Inositol 3-methyltransferase','Inositol L-1-methyltransferase','NULL','NULL','S-adenosyl-L-methionine + myo-inositol = S-adenosyl-L-homocysteine +1D-3-O-methyl-myo-inositol.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 2.1.1.40','Inositol 1-methyltransferase','Inositol 3-O-methyltransferase','NULL','NULL','S-adenosyl-L-methionine + myo-inositol = S-adenosyl-L-homocysteine +1D-1-O-methyl-myo-inositol.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 2.1.1.41','Sterol 24-C-methyltransferase','24-sterol C-methyltransferase','Glutathione.','Acts on a range of sterols with a 24(25)-double bond in the sidechain.While zymosterol is the preferred substrate it also acts ondesmosterol, 5-alpha-cholesta-7,24-dien-3-beta-ol, 5-alpha-cholesta-5,7,24-trien-3-beta-ol, 4-alpha-methylzymosterol and others.S-adenosyl-L-methionine attacks the Si face of the 24(25) double bondand the C-24 hydrogen is transferred to C-25 on the Re face of thedouble bond.','S-adenosyl-L-methionine + 5-alpha-cholesta-8,24-dien-3-beta-ol =S-adenosyl-L-homocysteine + 24-methylene-5-alpha-cholest-8-en-3-beta-ol.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 2.1.1.42','Flavone 3-O-methyltransferase','o-dihydric phenol methyltransferase','NULL','The enzyme prefers flavones with vicinal 3,4-dihydroxyl groups.','S-adenosyl-L-methionine + 3-hydroxyflavone = S-adenosyl-L-homocysteine +3-methoxyflavone.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 2.1.1.43','Histone-lysine N-methyltransferase','Protein methylase 3','NULL','NULL','S-adenosyl-L-methionine + L-lysine-[histone] = S-adenosyl-L-homocysteine+ N(6)-methyl-L-lysine-[histone].', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 2.1.1.44','L-histidine N(alpha)-methyltransferase','Dimethylhistidine N-methyltransferase','NULL','Part of the biosynthetic pathway of ergothioneine.','3 S-adenosyl-L-methionine + L-histidine = 3 S-adenosyl-L-homocysteine +hercynine.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 2.1.1.45','Thymidylate synthase','NULL','NULL','NULL','5,10-methylenetetrahydrofolate + dUMP = dihydrofolate + dTMP.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 2.1.1.46','Isoflavone 4-O-methyltransferase','NULL','Magnesium.','The enzyme catalyzes the methylation of daidzein and genistein.It does not methylate naringenin, apigenin, luteolin or kaempferol.','S-adenosyl-L-methionine + a 4-hydroxyisoflavone = S-adenosyl-L-homocysteine + a 4-methoxyisoflavone.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 2.1.1.47','Indolepyruvate C-methyltransferase','Indolepyruvate 3-methyltransferase','NULL','NULL','S-adenosyl-L-methionine + (indol-3-yl)pyruvate = S-adenosyl-L-homocysteine + (S)-3-(indol-3-yl)-2-oxobutanoate.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 2.1.1.48','Transferred entry: 2','NULL','NULL','NULL','NULL', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 2.1.1.49','Amine N-methyltransferase','Arylamine N-methyltransferase','NULL','An enzyme of very broad specificity; many primary, secondary andtertiary amines can act as acceptors, including tryptamine, aniline,nicotine and a variety of drugs and other xenobiotics.Formerly EC 2.1.1.81.','S-adenosyl-L-methionine + an amine = S-adenosyl-L-homocysteine +a methylated amine.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 2.1.1.50','Loganate O-methyltransferase','NULL','NULL','Also acts on secologanate.Methylates the 11-carboxy group of the monoterpenoid loganate.','S-adenosyl-L-methionine + loganate = S-adenosyl-L-homocysteine + loganin.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 2.1.1.51','Transferred entry: 2','NULL','NULL','NULL','NULL', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 2.1.1.52','Transferred entry: 2','NULL','NULL','NULL','NULL', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 2.1.1.53','Putrescine N-methyltransferase','NULL','NULL','NULL','S-adenosyl-L-methionine + putrescine = S-adenosyl-L-homocysteine +N-methylputrescine.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 2.1.1.54','Deoxycytidylate C-methyltransferase','NULL','NULL','dCMP is methylated by formaldehyde in the presence oftetrahydrofolate.CMP, dCTP and CTP can act as acceptor, more slowly.','5,10-methylenetetrahydrofolate + dCMP = dihydrofolate + deoxy-5-methylcytidylate.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 2.1.1.55','tRNA (adenine-N(6)-)-methyltransferase','NULL','NULL','NULL','S-adenosyl-L-methionine + tRNA = S-adenosyl-L-homocysteine + tRNAcontaining N(6)-methyladenine.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 2.1.1.56','mRNA (guanine-N(7)-)-methyltransferase','Guanine-7-methyltransferase','NULL','Adds an N(7)-methylguanine cap to mRNA.The nucleoside next to the terminal guanosine may be either guanosineor adenosine.','S-adenosyl-L-methionine + G(5)pppR-RNA = S-adenosyl-L-homocysteine +m(7)G(5)pppR-RNA.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 2.1.1.57','Methyltransferase cap1','Cap1-MTase','NULL','This enzyme catalyzes the methylation of the ribose on the firsttranscribed nucleotide of mRNA or snRNA molecules, which may beeither guanosine or adenosine.This methylation event is known as cap1, and occurs in all mRNAs andsnRNAs of higher eukaryotes, including insects, vertebrates and theirviruses.The human enzyme can also methylate mRNA molecules that lackmethylation on the capping 5-triphosphoguanosine.','S-adenosyl-L-methionine + a 5-(N(7)-methyl 5-triphosphoguanosine)-(purine-ribonucleotide)-(mRNA) = S-adenosyl-L-homocysteine + a 5-(N(7)-methyl 5-triphosphoguanosine)-(2-O-methyl-purine-ribonucleotide)-(mRNA).', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 2.1.1.58','Transferred entry: 2','NULL','NULL','NULL','NULL', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 2.1.1.59','[Cytochrome c]-lysine N-methyltransferase','Cytochrome c (lysine) methyltransferase','NULL','NULL','S-adenosyl-L-methionine + [cytochrome c]-L-lysine = S-adenosyl-L-homocysteine + [cytochrome c]-N(6)-methyl-L-lysine.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 2.1.1.60','Calmodulin-lysine N-methyltransferase','NULL','NULL','NULL','S-adenosyl-L-methionine + calmodulin L-lysine = S-adenosyl-L-homocysteine+ calmodulin N(6)-methyl-L-lysine.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 2.1.1.61','tRNA (5-methylaminomethyl-2-thiouridylate)-methyltransferase','NULL','NULL','Specific for the terminal methyl group of 5-methylaminomethyl-2-thiouridylate.','S-adenosyl-L-methionine + tRNA containing 5-aminomethyl-2-thiouridine =S-adenosyl-L-homocysteine + tRNA containing 5-methylaminomethyl-2-thiouridylate.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 2.1.1.62','mRNA (2-O-methyladenosine-N(6)-)-methyltransferase','NULL','NULL','mRNA containing an N(6),2-O-dimethyladenosine cap.','S-adenosyl-L-methionine + m(7)G(5)pppAm = S-adenosyl-L-homocysteine +m(7)G(5)pppm(6)Am.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 2.1.1.63','Methylated-DNA--[protein]-cysteine S-methyltransferase','6-O-methylguanine-DNA methyltransferase','NULL','This protein is involved in the repair of alkylated DNA.It acts only on the alkylated DNA (cf. EC 3.2.2.20 and EC 3.2.2.21).This enzyme catalyzes only one turnover and therefore is not strictlycatalytic.','DNA (containing 6-O-methylguanine) + protein L-cysteine = DNA (without6-O-methylguanine) + protein S-methyl-L-cysteine.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 2.1.1.64','3-demethylubiquinol 3-O-methyltransferase','NULL','NULL','This enzyme is involved in ubiquinone biosynthesis.Ubiquinones from different organisms have a different number ofprenyl units (for example, ubiquinone-6 in Saccharomyces, ubiquinone-9 in rat and ubiquinone-10 in human), and thus the natural substratefor the enzymes from different organisms has a different number ofprenyl units.However, the enzyme usually shows a low degree of specificityregarding the number of prenyl units.For example, the human COQ3 enzyme can restore biosynthesis ofubiquinone-6 in coq3 deletion mutants of yeast.The enzymes from yeast, Escherichia coli and rat also catalyze themethylation of 3,4-dihydroxy-5-all-trans-polyprenylbenzoate (areaction that is classified as EC 2.1.1.114).','S-adenosyl-L-methionine + 3-demethylubiquinone-n = S-adenosyl-L-homocysteine + ubiquinone-n.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 2.1.1.65','Licodione 2-O-methyltransferase','NULL','NULL','As well as licodione (1-(2,4-dihydroxyphenyl)-3-(4-hydroxyphenyl)-1,3-propanedione), the 2-hydroxy-derivative and isoliquiritigenincan act as acceptors, more slowly.','S-adenosyl-L-methionine + licodione = S-adenosyl-L-homocysteine + 2-O-methyllicodione.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 2.1.1.66','Transferred entry: 2','NULL','NULL','NULL','NULL', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 2.1.1.67','Thiopurine S-methyltransferase','NULL','NULL','Also acts, more slowly, on thiopyrimidines and aromatic thiols.Not identical with EC 2.1.1.9.','S-adenosyl-L-methionine + a thiopurine = S-adenosyl-L-homocysteine +a thiopurine S-methylether.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 2.1.1.68','Caffeate O-methyltransferase','NULL','NULL','3,4-dihydroxybenzaldehyde and catechol can act as acceptor, moreslowly.','S-adenosyl-L-methionine + 3,4-dihydroxy-trans-cinnamate = S-adenosyl-L-homocysteine + 3-methoxy-4-hydroxy-trans-cinnamate.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 2.1.1.69','5-hydroxyfuranocoumarin 5-O-methyltransferase','Bergaptol 5-O-methyltransferase','NULL','Converts bergaptol into bergapten, which has therapeutic potential inthe treatment of psoriasis as it has photosensitizing andantiproliferative activities.Methylates the 5-hydroxy group of some hydroxy- and methylcoumarins,such as 5-hydroxyxanthotoxin, but has little activity on non-coumarinphenols; caffeate, 5-hydroxyferulate and daphnetin are notsubstrates.Cu(2+), Zn(2+) and Co(2+) cause enzyme inhibition.See also EC 2.1.1.70.Formerly EC 2.1.1.92.','(1) S-adenosyl-L-methionine + a 5-hydroxyfurocoumarin = S-adenosyl-L-homocysteine + a 5-methoxyfurocoumarin.(2) S-adenosyl-L-methionine + bergaptol = S-adenosyl-L-homocysteine +bergapten.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 2.1.1.70','8-hydroxyfuranocoumarin 8-O-methyltransferase','Furanocoumarin 8-methyltransferase','NULL','Converts xanthotoxol into xanthotoxin, which has therapeuticpotential in the treatment of psoriasis as it has photosensitizingand antiproliferative activities.Methylates the 8-hydroxy group of some hydroxy- and methylcoumarins,but has little activity on non-coumarin phenols.See also EC 2.1.1.69.Formerly EC 2.1.1.93.','(1) S-adenosyl-L-methionine + an 8-hydroxyfurocoumarin = S-adenosyl-L-homocysteine + an 8-methoxyfurocoumarin.(2) S-adenosyl-L-methionine + xanthotoxol = S-adenosyl-L-homocysteine +xanthotoxin.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 2.1.1.71','Phosphatidyl-N-methylethanolamine N-methyltransferase','NULL','NULL','Also catalyzes the transfer of a further methylgroup, producingphosphatidylcholine.','S-adenosyl-L-methionine + phosphatidyl-N-methylethanolamine = S-adenosyl-L-homocysteine + phosphatidyl-N-dimethylethanolamine.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 2.1.1.72','Site-specific DNA-methyltransferase (adenine-specific)','Modification methylase','NULL','This is a large group of enzymes, the majority of which, with enzymesof similar site specificity listed as EC 3.1.21.3, EC 3.1.21.4 andEC 3.1.21.5, form so-called restriction-modification systems.See the REBASE database for a complete list of these enzymes:http://rebase.neb.com/rebase/','S-adenosyl-L-methionine + DNA adenine = S-adenosyl-L-homocysteine +DNA 6-methylaminopurine.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 2.1.1.73','Transferred entry: 2','NULL','NULL','NULL','NULL', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 2.1.1.74','Methylenetetrahydrofolate--tRNA-(uracil(54)-C(5))-methyltransferase(FADH(2)-oxidizing)','Folate-dependent ribothymidyl synthase','NULL','Up to 25% of the bases in mature tRNA are post-translationallymodified or hypermodified.One almost universal post-translational modification is theconversion of U54 into ribothymidine in the T-Psi-C loop, and thismodification is found in most species studied to date.Unlike this enzyme, which uses 5,10-methylenetetrahydrofolate andFADH(2) to supply the atoms for methylation of U54, EC 2.1.1.35 usesS-adenosyl-L-methionine.Formerly EC 2.1.2.12.','5,10-methylenetetrahydrofolate + uracil(54) in tRNA + FADH(2) =tetrahydrofolate + 5-methyluracil(54) in tRNA + FAD.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 2.1.1.75','Apigenin 4-O-methyltransferase','Flavonoid O-methyltransferase','NULL','Converts apigenin into acacetin.Naringenin (5,7,4-trihydroxyflavanone) can also act as an acceptor,but more slowly.','S-adenosyl-L-methionine + apigenin = S-adenosyl-L-homocysteine +acacetin.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 2.1.1.76','Quercetin 3-O-methyltransferase','Flavonol 3-O-methyltransferase','NULL','Specific for quercetin.Related enzymes bring about the 3-O-methylation of other flavonols,such as galangin and kaempferol.','S-adenosyl-L-methionine + 3,5,7,3,4-pentahydroxyflavone = S-adenosyl-L-homocysteine + 3-methoxy-5,7,3,4-tetrahydroxyflavone.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 2.1.1.77','Protein-L-isoaspartate(D-aspartate) O-methyltransferase','L-isoaspartyl protein carboxyl methyltransferase','NULL','D-aspartate (but not L-aspartate) residues in proteins can also actas acceptors.Formerly EC 2.1.1.24.','S-adenosyl-L-methionine + protein L-isoaspartate = S-adenosyl-L-homocysteine + protein L-isoaspartate alpha-methyl ester.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 2.1.1.78','Isoorientin 3-O-methyltransferase','NULL','NULL','Also acts on isoorientin 2-O-rhamnoside.Involved in the biosynthesis of flavones.','S-adenosyl-L-methionine + isoorientin = S-adenosyl-L-homocysteine +isoscoparin.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 2.1.1.79','Cyclopropane-fatty-acyl-phospholipid synthase','CFA synthase','NULL','Adds a methylene group across the 9,10 position of a Delta(9)-olefinic acyl chain in phosphatidylethanolamine or, more slowly,phosphatidylglycerol or phosphatidylinositol forming a cyclopropanederivative (cf. EC 2.1.1.16).','S-adenosyl-L-methionine + phospholipid olefinic fatty acid = S-adenosyl-L-homocysteine + phospholipid cyclopropane fatty acid.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 2.1.1.80','Protein-glutamate O-methyltransferase','Methyl-accepting chemotaxis protein O-methyltransferase','NULL','Forms ester groups with L-glutamate residues in a number of membraneproteins.Formerly EC 2.1.1.24.','S-adenosyl-L-methionine + protein L-glutamate = S-adenosyl-L-homocysteine+ protein L-glutamate methyl ester.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 2.1.1.81','Transferred entry: 2','NULL','NULL','NULL','NULL', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 2.1.1.82','3-methylquercetin 7-O-methyltransferase','3-methylquercitin 7-O-methyltransferase','NULL','Involved with EC 2.1.1.76 and EC 2.1.1.83 in the methylation ofquercetin to 3,7,4-trimethylquercetin in Chrysosplenium americanum.Does not act on flavones, dihydroflavonols, or their glucosides.','S-adenosyl-L-methionine + 3,4,5,7-tetrahydroxy-3-methoxyflavone =S-adenosyl-L-homocysteine + 3,4,5-trihydroxy-3,7-dimethoxyflavone.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 2.1.1.83','3,7-dimethylquercetin 4-O-methyltransferase','3,7-dimethylquercitin 4-O-methyltransferase','NULL','3,7-dimethylquercetagetin can also act as acceptor.Involved with EC 2.1.1.76 and EC 2.1.1.82 in the methylation ofquercetin to 3,7,4-trimethylquercetin in Chrysosplenium americanum.Does not act on flavones, dihydroflavonols, or their glucosides.','S-adenosyl-L-methionine + 3,4,5-trihydroxy-3,7-dimethoxyflavone =S-adenosyl-L-homocysteine + 3,5-dihydroxy-3,4,7-trimethoxyflavone.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 2.1.1.84','Methylquercetagetin 6-O-methyltransferase','Flavonol 6-O-methyltransferase','NULL','The enzymes from Chrysosplenium americanum also methylates3,7,3-trimethylquercetagetin at the 6-position.Does not act on flavones, dihydroflavonols, or their glucosides.','S-adenosyl-L-methionine + 3,4,5,6-tetrahydroxy-3,7-dimethoxyflavone =S-adenosyl-L-homocysteine + 3,4,5-trihydroxy-3,6,7-trimethoxyflavone.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 2.1.1.85','Protein-histidine N-methyltransferase','Protein methylase IV','NULL','Highly specific for histidine residues, for example, in actin.','S-adenosyl-L-methionine + protein L-histidine = S-adenosyl-L-homocysteine+ protein N(tau)-methyl-L-histidine.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 2.1.1.86','Tetrahydromethanopterin S-methyltransferase','N(5)-methyltetrahydromethanopterin--coenzyme M methyltransferase','NULL','Involved in the formation of methane from CO in methanogenic archaea.','5-methyl-5,6,7,8-tetrahydromethanopterin + 2-mercaptoethanesulfonate =5,6,7,8-tetrahydromethanopterin + 2-(methylthio)ethanesulfonate.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 2.1.1.87','Pyridine N-methyltransferase','NULL','NULL','NULL','S-adenosyl-L-methionine + pyridine = S-adenosyl-L-homocysteine +N-methylpyridinium.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 2.1.1.88','8-hydroxyquercetin 8-O-methyltransferase','8-hydroxyquercitin 8-O-methyltransferase','NULL','Also acts on 8-hydroxykaempferol, but not on the glycosides of8-hydroxyflavonols.An enzyme from the flower buds of Lotus corniculatus.','S-adenosyl-L-methionine + 3,3,4,5,7,8-hexahydroxyflavone = S-adenosyl-L-homocysteine + 3,3,4,5,7-pentahydroxy-8-methoxyflavone.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 2.1.1.89','Tetrahydrocolumbamine 2-O-methyltransferase','NULL','NULL','Involved in the biosynthesis of the berberine alkaloids.','S-adenosyl-L-methionine + 5,8,13,13a-tetrahydrocolumbamine = S-adenosyl-L-homocysteine + tetrahydropalmatine.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 2.1.1.90','Methanol--corrinoid protein Co-methyltransferase','Methanol--5-hydroxybenzimidazolylcobamide Co-methyltransferase','NULL','The enzyme, which catalyzes the transfer of methyl groups frommethanol to a methanol-specific corrinoid protein (MtaC), is involvedin methanogenesis from methanol.Methylation of the corrinoid protein requires the central cobalt tobe in the Co(I) state.During methylation the cobalt is oxidized to the Co(III) state.Free cob(I)alamin can substitute for the corrinoid protein in vitro.Inactivated by oxygen and other oxidizing agents, and reactivated bycatalytic amounts of ATP and hydrogen.','Methanol + a [Co(I) methanol-specific corrinoid protein] = a [methyl-Co(III) methanol-specific corrinoid protein] + H(2)O.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 2.1.1.91','Isobutyraldoxime O-methyltransferase','Aldoxime methyltransferase','NULL','Oximes of C(4) to C(6) aldehydes can act as acceptors.The most active substrate is 2-methylbutyraldoxime.','S-adenosyl-L-methionine + 2-methylpropanal oxime = S-adenosyl-L-homocysteine + 2-methylpropanal O-methyloxime.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 2.1.1.92','Transferred entry: 2','NULL','NULL','NULL','NULL', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 2.1.1.93','Transferred entry: 2','NULL','NULL','NULL','NULL', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 2.1.1.94','Tabersonine 16-O-methyltransferase','11-demethyl-17-deacetylvindoline 11-methyltransferase','NULL','Involved in the biosynthesis of vindoline from tabersonine in theMadagascar periwinkle, Catharanthus roseus.','S-adenosyl-L-methionine + 16-hydroxytabersonine = S-adenosyl-L-homocysteine + 16-methoxytabersonine.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 2.1.1.95','Tocopherol O-methyltransferase','NULL','NULL','NULL','S-adenosyl-L-methionine + gamma-tocopherol = S-adenosyl-L-homocysteine +alpha-tocopherol.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 2.1.1.96','Thioether S-methyltransferase','NULL','NULL','Also acts on dimethyl selenide, dimethyl telluride, diethyl sulfide,1,4-dithiane and many other thioethers.','S-adenosyl-L-methionine + dimethyl sulfide = S-adenosyl-L-homocysteine +trimethylsulfonium.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 2.1.1.97','3-hydroxyanthranilate 4-C-methyltransferase','NULL','NULL','Involved in the biosynthesis of the antibiotic actinomycin inStreptomyces antibioticus.','S-adenosyl-L-methionine + 3-hydroxyanthranilate = S-adenosyl-L-homocysteine + 3-hydroxy-4-methylanthranilate.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 2.1.1.98','Diphthine synthase','NULL','NULL','The trimethylated product, diphthine, is converted into diphthamideby EC 6.3.1.14.','3 S-adenosyl-L-methionine + 2-(3-carboxy-3-aminopropyl)-L-histidine-[translation elongation factor 2] = 3 S-adenosyl-L-homocysteine +2-(3-carboxy-3-(trimethylammonio)propyl)-L-histidine-[translationelongation factor 2].', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 2.1.1.99','3-hydroxy-16-methoxy-2,3-dihydrotabersonine N-methyltransferase','16-methoxy-2,3-dihydro-3-hydroxytabersonine methyltransferase','NULL','Involved in the biosynthesis of vindoline from tabersonine in theMadagascar periwinkle Catharanthus roseus.','S-adenosyl-L-methionine + 3-hydroxy-16-methoxy-2,3-dihydrotabersonine =S-adenosyl-L-homocysteine + deacetoxyvindoline.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 2.1.1.100','Protein-S-isoprenylcysteine O-methyltransferase','Isoprenylcysteine carboxylmethyltransferase','NULL','C-terminal S-geranylgeranylcysteine and S-geranylcysteine residuesare also methylated, more slowly.Formerly EC 2.1.1.24.','S-adenosyl-L-methionine + protein C-terminal S-farnesyl-L-cysteine =S-adenosyl-L-homocysteine + protein C-terminal S-farnesyl-L-cysteinemethyl ester.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 2.1.1.101','Macrocin O-methyltransferase','MOMT','Magnesium.','The 3-hydroxyl group of a 2-O-methyl-6-deoxy-D-allose residue in themacrolide antibiotic macrocin acts as methyl acceptor.Isolated from Streptomyces fradiae.Tylosin is a macrolide antibiotic.Not identical with EC 2.1.1.102.','S-adenosyl-L-methionine + macrocin = S-adenosyl-L-homocysteine + tylosin.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 2.1.1.102','Demethylmacrocin O-methyltransferase','DMOMT','Magnesium.','The 2-hydroxyl group of a 6-deoxy-D-allose residue indemethylmacrocin acts as methyl acceptor.Isolated from Streptomyces fradiae.Also acts on demethyllactenocin giving lactenocin.Involved in the biosynthesis of the macrolide antibiotic tylosin.Not identical with EC 2.1.1.101.','S-adenosyl-L-methionine + demethylmacrocin = S-adenosyl-L-homocysteine +macrocin.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 2.1.1.103','Phosphoethanolamine N-methyltransferase','NULL','NULL','The enzyme may catalyze the transfer of two further methyl groups tothe product.','S-adenosyl-L-methionine + ethanolamine phosphate = S-adenosyl-L-homocysteine + N-methylethanolamine phosphate.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 2.1.1.104','Caffeoyl-CoA O-methyltransferase','Trans-caffeoyl-CoA 3-O-methyltransferase','NULL','NULL','S-adenosyl-L-methionine + caffeoyl-CoA = S-adenosyl-L-homocysteine +feruloyl-CoA.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 2.1.1.105','N-benzoyl-4-hydroxyanthranilate 4-O-methyltransferase','NULL','NULL','Involved in the biosynthesis of phytoalexins.','S-adenosyl-L-methionine + N-benzoyl-4-hydroxyanthranilate = S-adenosyl-L-homocysteine + N-benzoyl-4-methoxyanthranilate.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 2.1.1.106','Tryptophan 2-C-methyltransferase','NULL','NULL','D-tryptophan and (indol-3-yl)pyruvate can also act as acceptors,but more slowly.','S-adenosyl-L-methionine + L-tryptophan = S-adenosyl-L-homocysteine + L-2-methyltryptophan.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 2.1.1.107','Uroporphyrinogen-III C-methyltransferase','Adenosylmethionine-uroporphyrinogen III methyltransferase','NULL','This enzyme catalyzes two sequential methylation reactions, the firstforming precorrin-1 and the second leading to the formation ofprecorrin-2.It is the first of three steps leading to the formation of sirohemefrom uroporphyrinogen III.The second step involves an NAD(+)-dependent dehydrogenation to formsirohydrochlorin from precorrin-2 (EC 1.3.1.76) and the third stepinvolves the chelation of Fe(2+) to sirohydrochlorin to form siroheme(EC 4.99.1.4).In Saccharomyces cerevisiae, the last two steps are carried out by asingle bifunctional enzyme, Met8p.In some bacteria, steps 1-3 are catalyzed by a single multifunctionalprotein called CysG, whereas in Bacillus megaterium, three separateenzymes carry out each of the steps, with SirA being responsible forthe above reaction.Also involved in the biosynthesis of cobalamin.','(1) S-adenosyl-L-methionine + uroporphyrinogen III = S-adenosyl-L-homocysteine + precorrin-1.(2) S-adenosyl-L-methionine + precorrin-1 = S-adenosyl-L-homocysteine +precorrin-2.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 2.1.1.108','6-hydroxymellein O-methyltransferase','NULL','NULL','3,4-dehydro-6-hydroxymellein can also act as acceptor.6-methoxymellein is a phytoalexin produced by carrot tissue.','S-adenosyl-L-methionine + 6-hydroxymellein = S-adenosyl-L-homocysteine +6-methoxymellein.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 2.1.1.109','Demethylsterigmatocystin 6-O-methyltransferase','NULL','NULL','Dihydrodemethylsterigmatocystin can also act as acceptor.Involved in the biosynthesis of aflatoxins in fungi.','S-adenosyl-L-methionine + 6-demethylsterigmatocystin = S-adenosyl-L-homocysteine + sterigmatocystin.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 2.1.1.110','Sterigmatocystin 8-O-methyltransferase','O-methyltransferase II','NULL','Dihydrosterigmatocystin can also act as acceptor.Involved in the biosynthesis of aflatoxins in fungi.','(1) S-adenosyl-L-methionine + sterigmatocystin = S-adenosyl-L-homocysteine + 8-O-methylsterigmatocystin.(2) S-adenosyl-L-methionine + dihydrosterigmatocystin = S-adenosyl-L-homocysteine + 8-O-methyldihydrosterigmatocystin.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 2.1.1.111','Anthranilate N-methyltransferase','NULL','NULL','Involved in the biosynthesis of acridine alkaloids in plant tissues.','S-adenosyl-L-methionine + anthranilate = S-adenosyl-L-homocysteine +N-methylanthranilate.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 2.1.1.112','Glucuronoxylan 4-O-methyltransferase','NULL','NULL','NULL','S-adenosyl-L-methionine + glucuronoxylan D-glucuronate = S-adenosyl-L-homocysteine + glucuronoxylan 4-O-methyl-D-glucuronate.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 2.1.1.113','Site-specific DNA-methyltransferase (cytosine-N(4)-specific)','Modification methylase','NULL','This is a large group of enzymes, the majority of which, with enzymesof similar site specificity listed as EC 3.1.21.3, EC 3.1.21.4 andEC 3.1.21.5, form so-called restriction-modification systems.See the REBASE database for a complete list of these enzymes:http://rebase.neb.com/rebase/','S-adenosyl-L-methionine + DNA cytosine = S-adenosyl-L-homocysteine +DNA N(4)-methylcytosine.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 2.1.1.114','Polyprenyldihydroxybenzoate methyltransferase','3,4-dihydroxy-5-hexaprenylbenzoate methyltransferase','NULL','This enzyme is involved in ubiquinone biosynthesis.Ubiquinones from different organisms have a different number ofprenyl units (for example, ubiquinone-6 in Saccharomyces, ubiquinone-9 in rat and ubiquinone-10 in human), and thus the natural substratefor the enzymes from different organisms has a different number ofprenyl units.However, the enzyme usually shows a low degree of specificityregarding the number of prenyl units.For example, the human COQ3 enzyme can restore biosynthesis ofubiquinone-6 in coq3 deletion mutants of yeast.The enzymes from yeast and rat also catalyze the methylation of3-demethylubiquinol-6 and 3-demethylubiquinol-9, respectively (thisactivity is classified as EC 2.1.1.64).','S-adenosyl-L-methionine + 3,4-dihydroxy-5-all-trans-polyprenylbenzoate =S-adenosyl-L-homocysteine + 3-methoxy-4-hydroxy-5-all-trans-polyprenylbenzoate.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 2.1.1.115','(RS)-1-benzyl-1,2,3,4-tetrahydroisoquinoline N-methyltransferase','(RS)-tetrahydrobenzylisoquinoline N-methyltransferase','NULL','Broad substrate specificity for (RS)-1-benzyl-1,2,3,4-tetrahydroisoquinolines; including coclaurine, norcoclaurine,isococlaurine, norarmepavine, norreticuline and tetrahydropapaverine.Both R- and S-enantiomers are methylated.Participates in the pathway leading to benzylisoquinoline alkaloidsynthesis in plants.The physiological substrate is likely to be coclaurine.Was earlier termed norreticuline N-methyltransferase.However, norreticuline has not been found to occur in nature and thatname does not reflect the broad specificity of the enzyme for (RS)-1-benzyl-1,2,3,4-tetrahydroisoquinolines.','S-adenosyl-L-methionine + (RS)-1-benzyl-1,2,3,4-tetrahydroisoquinoline =S-adenosyl-L-homocysteine + N-methyl-(RS)-1-benzyl-1,2,3,4-tetrahydroisoquinoline.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 2.1.1.116','3-hydroxy-N-methyl-(S)-coclaurine 4-O-methyltransferase','NULL','NULL','Involved in isoquinoline alkaloid metabolism in plants.Has also been shown to catalyze the methylation of (RS)-laudanosoline, (S)-3-hydroxycoclaurine and (RS)-7-O-methylnoraudanosoline.','S-adenosyl-L-methionine + 3-hydroxy-N-methyl-(S)-coclaurine =S-adenosyl-L-homocysteine + (S)-reticuline.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 2.1.1.117','(S)-scoulerine 9-O-methyltransferase','NULL','NULL','The product of this reaction is a precursor for protoberberinealkaloids in plants.','S-adenosyl-L-methionine + (S)-scoulerine = S-adenosyl-L-homocysteine +(S)-tetrahydrocolumbamine.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 2.1.1.118','Columbamine O-methyltransferase','NULL','NULL','The product of this reaction is a protoberberine alkaloid that iswidely distributed in the plant kingdom.Distinct in specificity from EC 2.1.1.88.','S-adenosyl-L-methionine + columbamine = S-adenosyl-L-homocysteine +palmatine.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 2.1.1.119','10-hydroxydihydrosanguinarine 10-O-methyltransferase','NULL','NULL','Part of the pathway for synthesis of benzophenanthridine alkaloids inplants.','S-adenosyl-L-methionine + 10-hydroxydihydrosanguinarine = S-adenosyl-L-homocysteine + dihydrochelirubine.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 2.1.1.120','12-hydroxydihydrochelirubine 12-O-methyltransferase','NULL','NULL','Part of the pathway for synthesis of benzophenanthridine alkaloidmacarpine in plants.','S-adenosyl-L-methionine + 12-hydroxydihydrochelirubine = S-adenosyl-L-homocysteine + dihydromacarpine.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 2.1.1.121','6-O-methylnorlaudanosoline 5-O-methyltransferase','NULL','NULL','Nororientaline is a precursor of the alkaloid papaverine.','S-adenosyl-L-methionine + 6-O-methylnorlaudanosoline = S-adenosyl-L-homocysteine + nororientaline.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 2.1.1.122','(S)-tetrahydroprotoberberine N-methyltransferase','Tetrahydroprotoberberine cis-N-methyltransferase','NULL','Involved in the biosynthesis of isoquinoline alkaloids in plants.','S-adenosyl-L-methionine + (S)-7,8,13,14-tetrahydroprotoberberine =S-adenosyl-L-homocysteine + cis-N-methyl-(S)-7,8,13,14-tetrahydroprotoberberine.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 2.1.1.123','[Cytochrome c]-methionine S-methyltransferase','NULL','NULL','NULL','S-adenosyl-L-methionine + [cytochrome c]-methionine = S-adenosyl-L-homocysteine + [cytochrome c]-S-methyl-methionine.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 2.1.1.124','[Cytochrome c]-arginine N-methyltransferase','NULL','NULL','Formerly EC 2.1.1.23.','S-adenosyl-L-methionine + [cytochrome c]-arginine = S-adenosyl-L-homocysteine + [cytochrome c]-N(omega)-methyl-arginine.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 2.1.1.125','Histone-arginine N-methyltransferase','Histone protein methylase','NULL','Forms the N(omega)-monomethyl- and N(omega),N(omega)-dimethyl,but not the N(omega),N(omega)-dimethyl-arginine derivatives.The name protein methylase I is misleading since it has been used fora number of enzymes with different specificities.Formerly EC 2.1.1.23.','S-adenosyl-L-methionine + arginine-[histone] = S-adenosyl-L-homocysteine+ N(omega)-methyl-arginine-[histone].', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 2.1.1.126','[Myelin basic protein]-arginine N-methyltransferase','Myelin basic protein methylase','NULL','Forms the N(omega)-monomethyl-, N(omega),N(omega)-dimethyl- andN(omega),N(omega)-dimethyl-arginine derivatives.The name protein methylase I is misleading since it has been used fora number of enzymes with different specificities.Formerly EC 2.1.1.23.','S-adenosyl-L-methionine + [myelin basic protein]-arginine = S-adenosyl-L-homocysteine + [myelin basic protein]-N(omega)-methyl-arginine.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 2.1.1.127','[Ribulose-bisphosphate carboxylase]-lysine N-methyltransferase','Ribulose-bisphosphate-carboxylase/oxygenase N-methyltransferase','NULL','The enzyme catalyzes three successive methylations of Lys-14 in thelarge subunits of hexadecameric higher plant ribulose-bisphosphate-carboxylase (EC 4.1.1.39).Only the three methylated form is observed.The enzyme from pea (Pisum sativum) also three-methylates a specificlysine in the chloroplastic isoforms of fructose-bisphosphatealdolase (EC 4.1.2.13).','3 S-adenosyl-L-methionine + [ribulose-bisphosphate carboxylase]-L-lysine= 3 S-adenosyl-L-homocysteine + [ribulose-bisphosphate carboxylase]-N(6),N(6),N(6)-trimethyl-L-lysine.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 2.1.1.128','(RS)-norcoclaurine 6-O-methyltransferase','NULL','NULL','The enzyme will also catalyze the 6-O-methylation of (RS)-norlaudanosoline to form 6-O-methyl-norlaudanosoline, but thisalkaloid has not been found to occur in plants.','S-adenosyl-L-methionine + (RS)-norcoclaurine = S-adenosyl-L-homocysteine+ (RS)-coclaurine.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 2.1.1.129','Inositol 4-methyltransferase','Myo-inositol 4-O-methyltransferase','NULL','The enzyme from Vigna umbellata is highly specific for S-adenosyl-L-methionine.The enzyme also methylates 1L-1,2,4/3,5-cyclohexanepentol, 2,4,6/3,5-pentahydroxycyclohexanone, D,L-2,3,4,6/5-pentacyclohexanone and2,2-anhydro-2-C-hydroxymethyl-myo-inositol, but at lower rates thanthat of myo-inositol.Formerly EC 2.1.1.134.','S-adenosyl-L-methionine + myo-inositol = S-adenosyl-L-homocysteine +1D-4-O-methyl-myo-inositol.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 2.1.1.130','Precorrin-2 C(20)-methyltransferase','Precorrin-2 C20-methyltransferase','NULL','NULL','S-adenosyl-L-methionine + precorrin-2 = S-adenosyl-L-homocysteine +precorrin-3A.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 2.1.1.131','Precorrin-3B C(17)-methyltransferase','Precorrin-3 methylase','NULL','In the aerobic cobalamin biosythesis pathway, four enzymes areinvolved in the conversion of precorrin-3A to precorrin-6A.The first of the four steps is carried out by EC 1.14.13.83 (CobG),yielding precorrin-3B as the product.This is followed by three methylation reactions, which introduce amethyl group at C-17 (CobJ; EC 2.1.1.131), C-11 (CobM; 2.1.1.133) andC-1 (CobF; EC 2.1.1.152) of the macrocycle, giving rise to precorrin-4, precorrin-5 and precorrin-6A, respectively.','S-adenosyl-L-methionine + precorrin-3B = S-adenosyl-L-homocysteine +precorrin-4.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 2.1.1.132','Precorrin-6B C(5,15)-methyltransferase (decarboxylating)','Precorrin-6 methyltransferase','NULL','The enzyme, which participates in the aerobic adenosylcobalaminbiosynthesis pathway, has S-adenosyl-L-methionine-dependentmethyltransferase and decarboxylase activities.The enzyme is a fusion protein with two active sites; one catalyzesthe methylation at C(15) and the decarboxylation, while the othercatalyzes the methylation at C(5).','2 S-adenosyl-L-methionine + precorrin-6B = 2 S-adenosyl-L-homocysteine +precorrin-8X + CO(2).', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 2.1.1.133','Precorrin-4 C(11)-methyltransferase','Precorrin-3 methylase','NULL','In the aerobic cobalamin biosythesis pathway, four enzymes areinvolved in the conversion of precorrin-3A to precorrin-6A.The first of the four steps is carried out by EC 1.14.13.83 (CobG),yielding precorrin-3B as the product.This is followed by three methylation reactions, which introduce amethyl group at C-17 (CobJ; EC 2.1.1.131), C-11 (CobM; 2.1.1.133) andC-1 (CobF; EC 2.1.1.152) of the macrocycle, giving rise to precorrin-4, precorrin-5 and precorrin-6A, respectively.','S-adenosyl-L-methionine + precorrin-4 = S-adenosyl-L-homocysteine +precorrin-5.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 2.1.1.134','Transferred entry: 2','NULL','NULL','NULL','NULL', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 2.1.1.135','Transferred entry: 1','NULL','NULL','NULL','NULL', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 2.1.1.136','Chlorophenol O-methyltransferase','Halogenated phenol O-methyltransferase','NULL','The enzyme from Trichoderma virgatum, when cultured in the presenceof halogenated phenol, also acts on a range of mono-, di- andtrichlorophenols.','S-adenosyl-L-methionine + trichlorophenol = S-adenosyl-L-homocysteine +trichloroanisole.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 2.1.1.137','Arsenite methyltransferase','Methylarsonite methyltransferase','NULL','An enzyme of the biotransformation pathway that formsdimethylarsinate from inorganic arsenite and arsenate.It methylates arsenite to form methylarsonate, Me-AsO(3)H(2), whichis reduced by EC 1.20.4.2 to methylarsonite, Me-As(OH)(2).Methylarsonite is also a substrate for this enzyme which converts itinto the much less toxic compound dimethylarsinate (cacodylate),Me(2)As(O)-OH.','(1) S-adenosyl-L-methionine + arsenite = S-adenosyl-L-homocysteine +methylarsonate.(2) S-adenosyl-L-methionine + methylarsonite = S-adenosyl-L-homocysteine+ dimethylarsinate.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 2.1.1.138','Deleted entry','NULL','NULL','NULL','NULL', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 2.1.1.139','3-demethylstaurosporine O-methyltransferase','3-demethoxy-3-hydroxystaurosporine O-methyltransferase','NULL','Catalyzes the final step in the biosynthesis of staurosporine,an alkaloidal antibiotic that is a potent inhibitor of proteinkinases, especially protein kinase C.','S-adenosyl-L-methionine + 3-demethylstaurosporine = S-adenosyl-L-homocysteine + staurosporine.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 2.1.1.140','(S)-coclaurine-N-methyltransferase','NULL','NULL','The enzyme is specific for the (S)-isomer of coclaurine.Norcoclaurine can also act as an acceptor.','S-adenosyl-L-methionine + (S)-coclaurine = S-adenosyl-L-homocysteine +(S)-N-methylcoclaurine.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 2.1.1.141','Jasmonate O-methyltransferase','Jasmonic acid carboxyl methyltransferase','NULL','9,10-dihydrojasmonic acid is a poor substrate for the enzyme.The enzyme does not convert 12-oxo-phytodienoic acid (a precursor ofjasmonic acid), salicylic acid, benzoic acid, linolenic acid orcinnamic acid into their corresponding methyl esters.Enzyme activity is inhibited by the presence of divalent cations,e.g., Ca(2+), Cu(2+), Mg(2+) and Zn(2+).','S-adenosyl-L-methionine + jasmonate = S-adenosyl-L-homocysteine + methyljasmonate.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 2.1.1.142','Cycloartenol 24-C-methyltransferase','Sterol C-methyltransferase','NULL','S-adenosyl-L-methionine methylates the Si face of the 24(25)-doublebond with elimination of a hydrogen atom from the pro-Z methyl groupat C-25.','S-adenosyl-L-methionine + cycloartenol = S-adenosyl-L-homocysteine +(24R)-24-methylcycloart-25-en-3-beta-ol.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 2.1.1.143','24-methylenesterol C-methyltransferase','24-methylenelophenol C-24(1)-methyltransferase','NULL','This is the second methylation step of plant sterol biosynthesis (cf.EC 2.1.1.142).','S-adenosyl-L-methionine + 24-methylenelophenol = S-adenosyl-L-homocysteine + (Z)-24-ethylidenelophenol.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 2.1.1.144','Trans-aconitate 2-methyltransferase','NULL','NULL','Also catalyzes the formation of the methyl monoester of cis-aconitate, isocitrate and citrate, but more slowly.While the enzyme from Escherichia coli forms (E)-3-(methoxycarbonyl)-pent-2-enedioate as the product, that from Saccharomyces cerevisiaeforms (E)-2-(methoxycarbonylmethyl)butenedioate and is thereforeclassified as a separate enzyme (cf. EC 2.1.1.145).','S-adenosyl-L-methionine + trans-aconitate = S-adenosyl-L-homocysteine +(E)-3-(methoxycarbonyl)pent-2-enedioate.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 2.1.1.145','Trans-aconitate 3-methyltransferase','NULL','NULL','Also catalyzes the formation of the methyl monoester of cis-aconitate, isocitrate and citrate, but more slowly.While the enzyme from Saccharomyces cerevisiae forms (E)-2-(methoxycarbonylmethyl)butenedioate as the product, that fromEscherichia coli forms (E)-3-(methoxycarbonyl)-pent-2-enedioate andis therefore classified as a separate enzyme (cf. EC 2.1.1.144).','S-adenosyl-L-methionine + trans-aconitate = S-adenosyl-L-homocysteine +(E)-2-(methoxycarbonylmethyl)butenedioate.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 2.1.1.146','(Iso)eugenol O-methyltransferase','NULL','NULL','Acts on eugenol and chavicol as well as isoeugenol.','S-adenosyl-L-methionine + isoeugenol = S-adenosyl-L-homocysteine +isomethyleugenol.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 2.1.1.147','Corydaline synthase','NULL','NULL','Also acts on 7,8-dihydropalmatine.','S-adenosyl-L-methionine + palmatine + 2 NADPH = S-adenosyl-L-homocysteine+ corydaline + 2 NADP(+).', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 2.1.1.148','Thymidylate synthase (FAD)','FDTS','NULL','Contains FAD.All thymidylate synthases catalyze a reductive methylation involvingthe transfer of the methylene group of 5,10-methylenetetrahydrofolateto the C5-position of dUMP and a two electron reduction of themethylene group to a methyl group.Unlike the classical thymidylate synthase, ThyA (EC 2.1.1.45), whichuses folate as both a 1-carbon donor and a source of reducingequivalents, this enzyme uses a flavin coenzyme as a source ofreducing equivalents, which are derived from NADPH.','5,10-methylenetetrahydrofolate + dUMP + NADPH = dTMP + tetrahydrofolate +NADP(+).', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 2.1.1.149','Transferred entry: 2','NULL','NULL','NULL','NULL', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 2.1.1.150','Isoflavone 7-O-methyltransferase','NULL','NULL','The enzyme from alfalfa can methylate daidzein, genistein and6,7,4-trihydroxyisoflavone but not flavones or flavanones.','S-adenosyl-L-methionine + a 7-hydroxyisoflavone = S-adenosyl-L-homocysteine + a 7-methoxyisoflavone.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 2.1.1.151','Cobalt-factor II C(20)-methyltransferase','Cobalt-precorrin-2 C(20)-methyltransferase','NULL','Involved in the anaerobic biosynthesis of vitamin B12.','S-adenosyl-L-methionine + cobalt-factor II = S-adenosyl-L-homocysteine +cobalt-factor III.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 2.1.1.152','Precorrin-6A synthase (deacetylating)','Precorrin-6X synthase (deacetylating)','NULL','In the aerobic cobalamin biosythesis pathway, four enzymes areinvolved in the conversion of precorrin-3A to precorrin-6A.The first of the four steps is carried out by EC 1.14.13.83 (CobG),yielding precorrin-3B as the product.This is followed by three methylation reactions, which introduce amethyl group at C-17 (CobJ; EC 2.1.1.131), C-11 (CobM; 2.1.1.133) andC-1 (CobF; EC 2.1.1.152) of the macrocycle, giving rise to precorrin-4, precorrin-5 and precorrin-6A, respectively.','S-adenosyl-L-methionine + precorrin-5 + H(2)O = S-adenosyl-L-homocysteine+ precorrin-6A + acetate.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 2.1.1.153','Vitexin 2-O-rhamnoside 7-O-methyltransferase','NULL','NULL','The flavonoids vitexin and isovitexin 2-O-arabinoside do not act assubstrates for the enzyme from oats (Avena sativa).','S-adenosyl-L-methionine + vitexin 2-O-beta-L-rhamnoside = S-adenosyl-L-homocysteine + 7-O-methylvitexin 2-O-beta-L-rhamnoside.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 2.1.1.154','Isoliquiritigenin 2-O-methyltransferase','Chalcone OMT','NULL','Not identical to EC 2.1.1.65.While EC 2.1.1.154 can use licodione as a substrate, EC 2.1.1.65cannot use isoliquiritigenin as a substrate.','S-adenosyl-L-methionine + isoliquiritigenin = S-adenosyl-L-homocysteine +2-O-methylisoliquiritigenin.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 2.1.1.155','Kaempferol 4-O-methyltransferase','F 4-OMT','NULL','The enzyme acts on the hydroxy group in the 4-position of someflavones, flavanones and isoflavones.Kaempferol, apigenin and kaempferol triglucoside are substrates,as is genistein, which reacts more slowly.Compounds with an hydroxy group in the 3 and 4 positions, such asquercetin and eriodictyol, do not act as substrates.Similar to EC 2.1.1.75 and EC 2.1.1.83.','S-adenosyl-L-methionine + kaempferol = S-adenosyl-L-homocysteine +kaempferide.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 2.1.1.156','Glycine/sarcosine N-methyltransferase','Glycine sarcosine N-methyltransferase','NULL','Cells of the oxygen-evolving halotolerant cyanobacterium Aphanocthecehalophytica synthesize betaine from glycine by a three-stepmethylation process.This is the first enzyme and it leads to the formation of eithersarcosine or N,N-dimethylglycine, which is further methylated toyield betaine (N,N,N-trimethylglycine) by the action of EC 2.1.1.157.Differs from EC 2.1.1.20, as it can further methylate the product ofthe first reaction.Acetate, dimethylglycine and S-adenosyl-L-homocysteine can inhibitthe reaction.','2 S-adenosyl-L-methionine + glycine = 2 S-adenosyl-L-homocysteine +N,N-dimethylglycine.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 2.1.1.157','Sarcosine/dimethylglycine N-methyltransferase','ApDMT','NULL','Cells of the oxygen-evolving halotolerant cyanobacterium Aphanocthecehalophytica synthesize betaine from glycine by a three-stepmethylation process.The first enzyme, EC 2.1.1.156, leads to the formation of eithersarcosine or N,N-dimethylglycine, which is further methylated toyield betaine (N,N,N-trimethylglycine) by the action of this enzyme.Both of these enzymes can catalyze the formation ofN,N-dimethylglycine from sarcosine.The reactions are strongly inhibited by S-adenosyl-L-homocysteine.','2 S-adenosyl-L-methionine + sarcosine = 2 S-adenosyl-L-homocysteine +betaine.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 2.1.1.158','7-methylxanthosine synthase','Xanthosine methyltransferase','NULL','The enzyme is specific for xanthosine, as XMP and xanthine cannot actas substrates.The enzyme does not have N(1)- or N(3)- methylation activity.This is the first methylation step in the production of caffeine.','S-adenosyl-L-methionine + xanthosine = S-adenosyl-L-homocysteine +7-methylxanthosine.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 2.1.1.159','Theobromine synthase','Monomethylxanthine methyltransferase','NULL','Can also catalyze the conversion of paraxanthine into caffeine,although the paraxanthine pathway is considered to be a minor pathwayfor caffeine biosynthesis.This is the third enzyme in the caffeine biosynthesis pathway.','S-adenosyl-L-methionine + 7-methylxanthine = S-adenosyl-L-homocysteine +3,7-dimethylxanthine.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 2.1.1.160','Caffeine synthase','3N-methyltransferase','NULL','Paraxanthine is the best substrate for this enzyme but theparaxanthine pathway is considered to be a minor pathway for caffeinebiosynthesis.','(1) S-adenosyl-L-methionine + 3,7-dimethylxanthine = S-adenosyl-L-homocysteine + 1,3,7-trimethylxanthine.(2) S-adenosyl-L-methionine + 1,7-dimethylxanthine = S-adenosyl-L-homocysteine + 1,3,7-trimethylxanthine.(3) S-adenosyl-L-methionine + 7-methylxanthine = S-adenosyl-L-homocysteine + 3,7-dimethylxanthine.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 2.1.1.161','Dimethylglycine N-methyltransferase','DMT','NULL','This enzyme, from the marine cyanobacterium Synechococcus sp. strainWH8102, differs from EC 2.1.1.157 in that it cannot use sarcosine asan alternative substrate.Betaine is a compatible solute that enables cyanobacteria to copewith osmotic stress by maintaining a positive cellular turgor.','S-adenosyl-L-methionine + N,N-dimethylglycine = S-adenosyl-L-homocysteine+ betaine.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 2.1.1.162','Glycine/sarcosine/dimethylglycine N-methyltransferase','Glycine sarcosine dimethylglycine N-methyltransferase','NULL','Unlike EC 2.1.1.156, EC 2.1.1.157 and EC 2.1.1.161, this enzyme, fromthe halophilic methanoarchaeon Methanohalophilus portucalensis,can methylate glycine and all of its intermediates to form thecompatible solute betaine.','3 S-adenosyl-L-methionine + glycine = 3 S-adenosyl-L-homocysteine +betaine.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 2.1.1.163','Demethylmenaquinone methyltransferase','2-demethylmenaquinone methyltransferase','NULL','The enzyme catalyzes the last step in menaquinone biosynthesis.It is able to accept substrates with varying polyprenyl side chainlength (the chain length is determined by polyprenyl diphosphatesynthase).The enzyme from Escherichia coli also catalyzes the conversion of2-methoxy-6-octaprenyl-1,4-benzoquinone to 5-methoxy-2-methyl-3-octaprenyl-1,4-benzoquinone during the biosynthesis of ubiquinone.The enzyme probably acts on menaquinol rather than menaquinone.','A demethylmenaquinone + S-adenosyl-L-methionine = a menaquinol +S-adenosyl-L-homocysteine.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 2.1.1.164','Demethylrebeccamycin-D-glucose O-methyltransferase','NULL','NULL','Catalyzes the last step in the biosynthesis of rebeccamycin,an indolocarbazole alkaloid produced by the actinobacteriumLechevalieria aerocolonigenes.The enzyme is able to use a wide variety substrates, toleratingvariation on the imide heterocycle, deoxygenation of the sugarmoiety, and even indolocarbazole glycoside anomers.','4-demethylrebeccamycin + S-adenosyl-L-methionine = rebeccamycin +S-adenosyl-L-homocysteine.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 2.1.1.165','Methyl halide transferase','Methyl chloride transferase','NULL','This enzyme contributes to the methyl halide emissions fromArabidopsis.','S-adenosyl-L-methionine + iodide = S-adenosyl-L-homocysteine + methyliodide.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 2.1.1.166','23S rRNA (uridine(2552)-2-O)-methyltransferase','FTSJ','NULL','The enzyme catalyzes the 2-O-methylation of the universallyconserved U2552 in the A loop of 23S rRNA.','S-adenosyl-L-methionine + uridine(2552) in 23S rRNA = S-adenosyl-L-homocysteine + 2-O-methyluridine(2552) in 23S rRNA.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 2.1.1.167','27S pre-rRNA (guanosine(2922)-2-O)-methyltransferase','NULL','NULL','Spb1p is a site-specific 2-O-ribose RNA methyltransferase thatcatalyzes the formation of 2-O-methylguanosine(2922), a universallyconserved position of the catalytic center of the ribosome that isessential for translation.2-O-Methylguanosine(2922) is formed at a later stage of theprocessing, during the maturation of of the 27S pre-rRNA.In absence of snR52, Spb1p can also catalyze the formation ofuridine(2921).','S-adenosyl-L-methionine + guanosine(2922) in 27S pre-rRNA = S-adenosyl-L-homocysteine + 2-O-methylguanosine(2922) in 27S pre-rRNA.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 2.1.1.168','21S rRNA (uridine(2791)-2-O)-methyltransferase','Mitochondrial rRNA MTase 2','NULL','The enzyme catalyzes the methylation of uridine(2791) ofmitochondrial 21S rRNA.','S-adenosyl-L-methionine + uridine(2791) in 21S rRNA = S-adenosyl-L-homocysteine + 2-O-methyluridine(2791) in 21S rRNA.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 2.1.1.169','Tricetin 3,4,5-O-trimethyltransferase','NULL','NULL','The enzyme from Triticum aestivum catalyzes the sequentialO-methylation of tricetin via 3-O-methyltricetin, 3,5-O-methyltricetin to 3,4,5-O-trimethyltricetin (2).','3 S-adenosyl-L-methionine + tricetin = 3 S-adenosyl-L-homocysteine +3,4,5-O-trimethyltricetin.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 2.1.1.170','16S rRNA (guanine(527)-N(7))-methyltransferase','NULL','NULL','The enzyme specifically methylates guanine(527) at N(7) in 16S rRNA.','S-adenosyl-L-methionine + guanine(527) in 16S rRNA = S-adenosyl-L-homocysteine + N(7)-methylguanine(527) in 16S rRNA.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 2.1.1.171','16S rRNA (guanine(966)-N(2))-methyltransferase','M(2)G966 methyltransferase','NULL','The enzyme efficiently methylates guanine(966) of the assembled 30Ssubunits in vitro.Protein-free 16S rRNA is not a substrate for RsmD.The enzyme specifically methylates guanine(966) at N(2) in 16S rRNA.Formerly EC 2.1.1.52.','S-adenosyl-L-methionine + guanine(966) in 16S rRNA = S-adenosyl-L-homocysteine + N(2)-methylguanine(966) in 16S rRNA.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 2.1.1.172','16S rRNA (guanine(1207)-N(2))-methyltransferase','M(2)G1207 methyltransferase','NULL','The enzyme reacts well with 30S subunits reconstituted from 16S RNAtranscripts and 30S proteins but is almost inactive with thecorresponding free RNA.The enzyme specifically methylates guanine(1207) at N(2) in 16S rRNA.Formerly EC 2.1.1.52.','S-adenosyl-L-methionine + guanine(1207) in 16S rRNA = S-adenosyl-L-homocysteine + N(2)-methylguanine(1207) in 16S rRNA.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 2.1.1.173','23S rRNA (guanine(2445)-N(2))-methyltransferase','NULL','NULL','The enzyme methylates 23S rRNA in vitro, assembled 50S subunits arenot a substrate.The enzyme specifically methylates guanine(2445) at N(2) in 23S rRNA.Formerly EC 2.1.1.52.','S-adenosyl-L-methionine + guanine(2445) in 23S rRNA = S-adenosyl-L-homocysteine + N(2)-methylguanine(2445) in 23S rRNA.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 2.1.1.174','23S rRNA (guanine(1835)-N(2))-methyltransferase','Ribosomal RNA large subunit methyltransferase G','NULL','The enzyme methylates 23S rRNA in vitro, assembled 50S subunits arenot a substrate.The enzyme specifically methylates guanine(1835) at N(2) in 23S rRNA.Formerly EC 2.1.1.52.','S-adenosyl-L-methionine + guanine(1835) in 23S rRNA = S-adenosyl-L-homocysteine + N(2)-methylguanine(1835) in 23S rRNA.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 2.1.1.175','Tricin synthase','NULL','NULL','The enzymes from Oryza sativa (ROMT-15 and ROMT-17) catalyzes thestepwise methylation of tricetin to its 3-mono- and 3,5-dimethylethers.In contrast with the wheat enzyme (EC 2.1.1.167, tricetin 3,4,5-O-trimethyltransferase), tricetin dimethyl ether is not converted toits 3,4,5-trimethylated ether derivative.The enzymes from Hordeum vulgare (HvOMT1) and from Zea mays (ZmOMT1)form the 3,5-dimethyl derivative as the major product.','2 S-adenosyl-L-methionine + tricetin = 2 S-adenosyl-L-homocysteine +3,5-O-dimethyltricetin.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 2.1.1.176','16S rRNA (cytosine(967)-C(5))-methyltransferase','NULL','NULL','The enzyme specifically methylates cytosine(967) at C(5) in 16S rRNA.','S-adenosyl-L-methionine + cytosine(967) in 16S rRNA = S-adenosyl-L-homocysteine + 5-methylcytosine(967) in 16S rRNA.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 2.1.1.177','23S rRNA (pseudouridine(1915)-N(3))-methyltransferase','Pseudouridine methyltransferase','NULL','YbeA does not methylate uridine at position 1915.','S-adenosyl-L-methionine + pseudouridine(1915) in 23S rRNA = S-adenosyl-L-homocysteine + N(3)-methylpseudouridine(1915) in 23S rRNA.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 2.1.1.178','16S rRNA (cytosine(1407)-C(5))-methyltransferase','NULL','NULL','The enzyme specifically methylates cytosine(1407) at C(5) in 16SrRNA.','S-adenosyl-L-methionine + cytosine(1407) in 16S rRNA = S-adenosyl-L-homocysteine + 5-methylcytosine(1407) in 16S rRNA.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 2.1.1.179','16S rRNA (guanine(1405)-N(7))-methyltransferase','Sisomicin-gentamicin methyltransferase','NULL','The enzyme specifically methylates guanine(1405) at N(7) in 16S rRNA.The enzyme from the antibiotic-producing bacterium Micromonosporazionensis methylates guanine(1405) in 16S rRNA to 7-methylguanine,thereby rendering the ribosome resistant to 4,6-disubstituteddeoxystreptamine aminoglycosides, which include gentamicins andkanamycins.','S-adenosyl-L-methionine + guanine(1405) in 16S rRNA = S-adenosyl-L-homocysteine + 7-methylguanine(1405) in 16S rRNA.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 2.1.1.180','16S rRNA (adenine(1408)-N(1))-methyltransferase','NULL','NULL','The enzyme provides a panaminoglycoside-resistant nature throughinterference with the binding of aminoglycosides toward the A site of16S rRNA through N(1)-methylation at position adenine(1408).','S-adenosyl-L-methionine + adenine(1408) in 16S rRNA = S-adenosyl-L-homocysteine + N(1)-methyladenine(1408) in 16S rRNA.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 2.1.1.181','23S rRNA (adenine(1618)-N(6))-methyltransferase','rRNA large subunit methyltransferase F','NULL','The recombinant YbiN protein is able to methylate partiallydeproteinized 50 S ribosomal subunit, but neither the completelyassembled 50 S subunits nor completely deproteinized 23 S rRNA.Formerly EC 2.1.1.48.','S-adenosyl-L-methionine + adenine(1618) in 23S rRNA = S-adenosyl-L-homocysteine + rRNA containing N(6)-methyladenine(1618) in 23S rRNA.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 2.1.1.182','16S rRNA (adenine(1518)-N(6)/adenine(1519)-N(6))-dimethyltransferase','NULL','NULL','KsgA introduces the most highly conserved ribosomal RNA modification,the dimethylation of adenine(1518) and adenine(1519) in 16S rRNA.Strains lacking the methylase are resistant to kasugamycin.Formerly EC 2.1.1.48.','4 S-adenosyl-L-methionine + adenine(1518)/adenine(1519) in 16S rRNA =4 S-adenosyl-L-homocysteine + N(6)-dimethyladenine(1518)/N(6)-dimethyladenine(1519) in 16S rRNA.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 2.1.1.183','18S rRNA (adenine(1779)-N(6)/adenine(1780)-N(6))-dimethyltransferase','NULL','NULL','DIM1 is involved in pre-rRNA processing.Formerly EC 2.1.1.48.','4 S-adenosyl-L-methionine + adenine(1779)/adenine(1780) in 18S rRNA =4 S-adenosyl-L-homocysteine + N(6)-dimethyladenine(1779)/N(6)-dimethyladenine(1780) in 18S rRNA.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 2.1.1.184','23S rRNA (adenine(2085)-N(6))-dimethyltransferase','NULL','NULL','ErmC is a methyltransferase that confers resistance to the macrolide-lincosamide-streptogramin B group of antibiotics by catalyzing themethylation of 23S rRNA at adenine(2085).Formerly EC 2.1.1.48.','2 S-adenosyl-L-methionine + adenine(2085) in 23S rRNA = 2 S-adenosyl-L-homocysteine + N(6)-dimethyladenine(2085) in 23S rRNA.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 2.1.1.185','23S rRNA (guanosine(2251)-2-O)-methyltransferase','NULL','NULL','The enzyme catalyzes the methylation of guanosine(2251),a modification conserved in the peptidyltransferase domain of 23SrRNA.','S-adenosyl-L-methionine + guanosine(2251) in 23S rRNA = S-adenosyl-L-homocysteine + 2-O-methylguanosine(2251) in 23S rRNA.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 2.1.1.186','23S rRNA (cytidine(2498)-2-O)-methyltransferase','NULL','NULL','NULL','S-adenosyl-L-methionine + cytidine(2498) in 23S rRNA = S-adenosyl-L-homocysteine + 2-O-methylcytidine(2498) in 23S rRNA.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 2.1.1.187','23S rRNA (guanine(745)-N(1))-methyltransferase','23S rRNA m(1)G(745) methyltransferase','NULL','The enzyme specifically methylates guanine(745) at N(1) in 23S rRNA.Formerly EC 2.1.1.51.','S-adenosyl-L-methionine + guanine(745) in 23S rRNA = S-adenosyl-L-homocysteine + N(1)-methylguanine(745) in 23S rRNA.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 2.1.1.188','23S rRNA (guanine(748)-N(1))-methyltransferase','23S rRNA m(1)G(748) methyltransferase','NULL','The enzyme specifically methylates guanine(748) at N(1) in 23S rRNA.The methyltransferase RlmA(II) confers resistance to the macrolideantibiotic tylosin in the drug-producing strain Streptomyces fradiae.Formerly EC 2.1.1.51.','S-adenosyl-L-methionine + guanine(748) in 23S rRNA = S-adenosyl-L-homocysteine + N(1)-methylguanine(748) in 23S rRNA.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 2.1.1.189','23S rRNA (uracil(747)-C(5))-methyltransferase','NULL','NULL','The enzyme specifically methylates uracil(747) at C(5) in 23S rRNA.','S-adenosyl-L-methionine + uracil(747) in 23S rRNA = S-adenosyl-L-homocysteine + 5-methyluracil(747) in 23S rRNA.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 2.1.1.190','23S rRNA (uracil(1939)-C(5))-methyltransferase','RNA uridine methyltransferase A','NULL','The enzyme specifically methylates uracil(1939) at C(5) in 23S rRNA.The enzyme contains an [4Fe-4S] cluster coordinated by four conservedcysteine residues.','S-adenosyl-L-methionine + uracil(1939) in 23S rRNA = S-adenosyl-L-homocysteine + 5-methyluracil(1939) in 23S rRNA.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 2.1.1.191','23S rRNA (cytosine(1962)-C(5))-methyltransferase','rRNA large subunit methyltransferase I','NULL','The enzyme specifically methylates cytosine(1962) at C(5) in 23SrRNA.','S-adenosyl-L-methionine + cytosine(1962) in 23S rRNA = S-adenosyl-L-homocysteine + 5-methylcytosine(1962) in 23S rRNA.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 2.1.1.192','23S rRNA (adenine(2503)-C(2))-methyltransferase','NULL','Iron-sulfur.','This enzyme is a member of the AdoMet radical (radical SAM) family.S-adenosyl-L-methionine acts as both a radical generator and as thesource of the appended methyl group.RlmN is an endogenous enzyme used by the cell to refine functions ofthe ribosome in protein synthesis.The enzyme methylates adenosine by a radical mechanism with CH(2)from the S-adenosyl-L-methionine and retention of the hydrogen at C-2of adenosine(2503) of 23S rRNA.It will also methylate 8-methyladenosine(2503) of 23S rRNA.Cf. EC 2.1.1.224.','2 S-adenosyl-L-methionine + adenine(2503) in 23S rRNA = S-adenosyl-L-homocysteine + L-methionine + 5-deoxyadenosine + 2-methyladenine(2503)in 23S rRNA.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 2.1.1.193','16S rRNA (uracil(1498)-N(3))-methyltransferase','M(3)U(1498) specific methyltransferase','NULL','The enzyme specifically methylates uracil(1498) at N(3) in 16S rRNA.','S-adenosyl-L-methionine + uracil(1498) in 16S rRNA = S-adenosyl-L-homocysteine + N(3)-methyluracil(1498) in 16S rRNA.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 2.1.1.194','Deleted entry','NULL','NULL','NULL','NULL', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 2.1.1.195','Cobalt-precorrin-5B (C(1))-methyltransferase','Cobalt-precorrin-6A synthase','NULL','This enzyme catylses the C-1 methylation of cobalt-precorrin-5B inthe anaerobic (early cobalt insertion) pathway of adenosylcobalaminbiosynthesis.','Cobalt-precorrin-5B + S-adenosyl-L-methionine = cobalt-precorrin-6A +S-adenosyl-L-homocysteine.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 2.1.1.196','Cobalt-precorrin-6B (C(15))-methyltransferase (decarboxylating)','Cobalt-precorrin-6Y (C(15))-methyltransferase (decarboxylating)','NULL','This enzyme catalyzes both methylation at C-15 and decarboxylation ofthe C-12 acetate side chain of cobalt-precorrin-6B, a step in theanaerobic (early cobalt insertion) adenosylcobalamin biosynthesispathway.','Cobalt-precorrin-6B + S-adenosyl-L-methionine = cobalt-precorrin-7 +S-adenosyl-L-homocysteine + CO(2).', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 2.1.1.197','Malonyl-[acyl-carrier protein] O-methyltransferase','NULL','NULL','Involved in an early step of biotin biosynthesis in Gram-negativebacteria.This enzyme catalyzes the transfer of a methyl group to the omega-carboxyl group of malonyl-[acyl-carrier protein] forming a methylester.The methyl ester is recognized by the fatty acid synthetic enzymes,which process it via the fatty acid elongation cycle to give pimelyl-[acyl-carrier-protein] methyl ester.While the enzyme can also accept malonyl-CoA, it has a much higheractivity with malonyl-[acyl-carrier protein].','S-adenosyl-L-methionine + malonyl-[acyl-carrier protein] = S-adenosyl-L-homocysteine + malonyl-[acyl-carrier protein] methyl ester.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 2.1.1.198','16S rRNA (cytidine(1402)-2-O)-methyltransferase','NULL','NULL','RsmI catalyzes the 2-O-methylation of cytidine(1402) and RsmH(EC 2.1.1.199) catalyzes the N(4)-methylation of cytidine(1402) in16S rRNA.Both methylations are necessary for efficient translation initiationat the UUG and GUG codons.','S-adenosyl-L-methionine + cytidine(1402) in 16S rRNA = S-adenosyl-L-homocysteine + 2-O-methylcytidine(1402) in 16S rRNA.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 2.1.1.199','16S rRNA (cytosine(1402)-N(4))-methyltransferase','NULL','NULL','RsmH catalyzes the N(4)-methylation of cytosine(1402) and RsmI(EC 2.1.1.198) catalyzes the 2-O-methylation of cytosine(1402) in16S rRNA.Both methylations are necessary for efficient translation initiationat the UUG and GUG codons.','S-adenosyl-L-methionine + cytosine(1402) in 16S rRNA = S-adenosyl-L-homocysteine + N(4)-methylcytosine(1402) in 16S rRNA.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 2.1.1.200','tRNA (cytidine(32)/uridine(32)-2-O)-methyltransferase','TrMet(Xm32)','NULL','In Escherichia coli TrmJ is the only methyltransferase responsiblefor the formation of 2-O-methylcytidine(32) in tRNA.No methylation of cytosine(34) in tRNA(Leu)(CAA).In vitro the enzyme 2-O-methylates cytidine(34) of tRNA(Ser1) anduridine(32) of tRNA(Gln2).','(1) S-adenosyl-L-methionine + cytidine(32) in tRNA = S-adenosyl-L-homocysteine + 2-O-methylcytidine(32) in tRNA.(2) S-adenosyl-L-methionine + uridine(32) in tRNA = S-adenosyl-L-homocysteine + 2-O-methyluridine(32) in tRNA.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 2.1.1.201','2-methoxy-6-polyprenyl-1,4-benzoquinol methylase','NULL','NULL','This enzyme is involved in ubiquinone biosynthesis.Ubiquinones from different organisms have a different number ofprenyl units (for example, ubiquinone-6 in Saccharomyces, ubiquinone-9 in rat and ubiquinone-10 in human), and thus the natural substratefor the enzymes from different organisms has a different number ofprenyl units.However, the enzyme usually shows a low degree of specificityregarding the number of prenyl units.For example, when the COQ5 gene from Saccharomyces cerevisiae isintroduced into Escherichia coli, it complements the respiratorydeficiency of an ubiE mutant.The bifunctional enzyme from E.coli also catalyzes the methylation ofdemethylmenaquinol-8 (this activity is classified as EC 2.1.1.163).','S-adenosyl-L-methionine + 2-methoxy-6-all-trans-polyprenyl-1,4-benzoquinol = S-adenosyl-L-homocysteine + 6-methoxy-3-methyl-2-all-trans-polyprenyl-1,4-benzoquinol.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 2.1.1.202','Multisite-specific tRNA:(cytosine-C(5))-methyltransferase','NULL','NULL','The enzyme from Saccharomyces cerevisiae is responsible for complete5-methylcytosine methylations of yeast tRNA.The incidence of modification depends on the cytosine position intRNA.At positions 34 and 40, 5-methylcytosine is found only in two yeasttRNAs (tRNA(Leu)(CUA) and tRNA(Phe)(GAA), respectively), whereas mostother elongator yeast tRNAs bear either 5-methylcytosine(48) or5-methylcytosine(49), but never both in the same tRNA molecule.The formation of 5-methylcytosine(34) and 5-methylcytosine(40) is astrictly intron-dependent process, whereas the formation of5-methylcytosine(48) and 5-methylcytosine(49) is an intron-independent process.Formerly EC 2.1.1.29.','(1) S-adenosyl-L-methionine + cytosine(34) in tRNA precursor =S-adenosyl-L-homocysteine + 5-methylcytosine(34) in tRNA precursor.(2) S-adenosyl-L-methionine + cytosine(40) in tRNA precursor =S-adenosyl-L-homocysteine + 5-methylcytosine(40) in tRNA precursor.(3) S-adenosyl-L-methionine + cytosine(48) in tRNA precursor =S-adenosyl-L-homocysteine + 5-methylcytosine(48) in tRNA precursor.(4) S-adenosyl-L-methionine + cytosine(49) in tRNA precursor =S-adenosyl-L-homocysteine + 5-methylcytosine(49) in tRNA precursor.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 2.1.1.203','tRNA (cytosine(34)-C(5))-methyltransferase','NULL','NULL','The human enzyme is specific for C(5)-methylation of cytosine(34) intRNA precursors.The intron in the human pre-tRNA(Leu)(CAA) is indispensable for theC(5)-methylation of cytosine in the first position of the anticodon.It is not able to form 5-methylcytosine at positions 48 and 49 ofhuman and yeast tRNA precursors.Formerly EC 2.1.1.29.','S-adenosyl-L-methionine + cytosine(34) in tRNA precursor = S-adenosyl-L-homocysteine + 5-methylcytosine(34) in tRNA precursor.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 2.1.1.204','tRNA (cytosine(38)-C(5))-methyltransferase','NULL','NULL','The eukaryotic enzyme catalyzes methylation of cytosine(38) in theanti-codon loop of tRNA(Asp)(GTC), tRNA(Val)(AAC) and tRNA(Gly)(GCC).Methylation by Dnmt2 protects tRNAs against stress-induced cleavageby ribonuclease.Formerly EC 2.1.1.29.','S-adenosyl-L-methionine + cytosine(38) in tRNA = S-adenosyl-L-homocysteine + 5-methylcytosine(38) in tRNA.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 2.1.1.205','tRNA (cytidine(32)/guanosine(34)-2-O)-methyltransferase','NULL','NULL','The enzyme from Saccharomyces cerevisiae catalyzes the formation of2-O-methylnucleotides at positions 32 and 34 of the yeast tRNA(Phe),tRNA(Trp) and, possibly, tRNA(Leu).','S-adenosyl-L-methionine + cytidine(32)/guanosine(34) in tRNA =S-adenosyl-L-homocysteine + 2-O-methylcytidine(32)/2-O-methylguanosine(34) in tRNA.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 2.1.1.206','tRNA (cytidine(56)-2-O)-methyltransferase','NULL','NULL','The archaeal enzyme specifically catalyzes the S-adenosyl-L-methionine dependent 2-O-ribose methylation of cytidine at position56 in tRNA transcripts.','S-adenosyl-L-methionine + cytidine(56) in tRNA = S-adenosyl-L-homocysteine + 2-O-methylcytidine(56) in tRNA.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 2.1.1.207','tRNA (cytidine(34)-2-O)-methyltransferase','tRNA (cytidine(34)/5-carboxymethylaminomethyluridine(34)-2-O)-methyltransferase','NULL','The enzyme from Escherichia coli catalyzes the 2-O-methylation ofcytidine or 5-carboxymethylaminomethyluridine at the wobble positionat nucleotide 34 in tRNA(Leu)CmAA and tRNA(Leu)cmnm(5)UmAA.The enzyme is selective for the two tRNA(Leu) isoacceptors and onlymethylates these when they present the correct anticodon loopsequence and modification pattern.Specifically, YibK requires a pyrimidine nucleoside at position 34,it has a clear preference for an adenosine at position 35, and itfails to methylate without prior addition of the N(6)-(isopentenyl)-2-methylthioadenosine modification at position 37.','(1) S-adenosyl-L-methionine + cytidine(34) in tRNA = S-adenosyl-L-homocysteine + 2-O-methylcytidine(34) in tRNA.(2) S-adenosyl-L-methionine + 5-carboxymethylaminomethyluridine(34) intRNA(Leu) = S-adenosyl-L-homocysteine + 5-carboxymethylaminomethyl-2-O-methyluridine(34) in tRNA(Leu).', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 2.1.1.208','23S rRNA (uridine(2479)-2-O)-methyltransferase','NULL','NULL','Streptomyces viridochromogenes produces the antibiotic avilamycin Awhich binds to the 50S ribosomal subunit to inhibit proteinsynthesis.To protect itself from the antibiotic, Streptomyces viridochromogenesutilizes two methyltransferases, 23S rRNA (uridine(2479)-2-O)-methyltransferase and EC 2.1.1.209 whose actions confer avilamycinresistance to the RNA.','S-adenosyl-L-methionine + uridine(2479) in 23S rRNA = S-adenosyl-L-homocysteine + 2-O-methyluridine(2479) in 23S rRNA.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 2.1.1.209','23S rRNA (guanine(2535)-N(1))-methyltransferase','NULL','NULL','Streptomyces viridochromogenes produces the antibiotic avilamycin Awhich binds to the 50S ribosomal subunit to inhibit proteinsynthesis.To protect itself from the antibiotic, Streptomyces viridochromogenesutilizes two methyltransferases, 23S rRNA (guanine(2535)-N(1))-methyltransferase and EC 2.1.1.208 whose actions confer avilamycinresistance to the RNA.','S-adenosyl-L-methionine + guanine(2535) in 23S rRNA = S-adenosyl-L-homocysteine + N(1)-methylguanine(2535) in 23S rRNA.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 2.1.1.210','Demethylspheroidene O-methyltransferase','1-HO-carotenoid methylase','NULL','In Rhodopseudomonas capsulata and Rubrivivax gelatinosus the enzymeis involved in biosynthesis of spheroidene.In Rubrivivax gelatinosus the enzyme also catalyzes the methylationof demethylspirilloxanthin to spirilloxanthin and the methylation of3,4-didehydrorhodopin to anhydrorhodovibrin.','S-adenosyl-L-methionine + demethylspheroidene = S-adenosyl-L-homocysteine+ spheroidene.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 2.1.1.211','tRNA(Ser) (uridine(44)-2-O)-methyltransferase','NULL','NULL','The 2-O-methylation of uridine(44) contributes to stability oftRNA(Ser)(CGA).Formerly EC 2.1.1.n2.','S-adenosyl-L-methionine + uridine(44) in tRNA(Ser) = S-adenosyl-L-homocysteine + 2-O-methyluridine(44) in tRNA(Ser).', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 2.1.1.212','2,7,4-trihydroxyisoflavanone 4-O-methyltransferase','NULL','NULL','Specifically methylates 2,4,7-trihydroxyisoflavanone on the4-position.No activity with isoflavones.The enzyme is involved in formononetin biosynthesis in legumes.The protein from pea (Pisum sativum) also methylates (+)-6a-hydroxymaackiain at the 3-position (cf. EC 2.1.1.270).','S-adenosyl-L-methionine + 2,4,7-trihydroxyisoflavanone = S-adenosyl-L-homocysteine + 2,7-dihydroxy-4-methoxyisoflavanone.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 2.1.1.213','tRNA (guanine(10)-N(2))-dimethyltransferase','N(2),N(2)-dimethylguanosine tRNA methyltransferase','NULL','Formerly EC 2.1.1.32.','2 S-adenosyl-L-methionine + guanine(10) in tRNA = 2 S-adenosyl-L-homocysteine + N(2)-dimethylguanine(10) in tRNA.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 2.1.1.214','tRNA (guanine(10)-N(2))-methyltransferase','NULL','NULL','In contrast to the archaeal enzyme EC 2.1.1.213, tRNA (guanine(10)-N(2))-methyltransferase from yeast does not catalyze the methylationfrom N(2)-methylguanine(10) to N(2)-dimethylguanine(10) in tRNA.Formerly EC 2.1.1.32.','S-adenosyl-L-methionine + guanine(10) in tRNA = S-adenosyl-L-homocysteine+ N(2)-methylguanine(10) in tRNA.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 2.1.1.215','tRNA (guanine(26)-N(2)/guanine(27)-N(2))-dimethyltransferase','NULL','NULL','The enzyme from Aquifex aeolicus is similar to the TRM1methyltransferases of archaea and eukarya (see EC 2.1.1.216).However, it catalyzes the double methylation of guanines at bothpositions 26 and 27 of tRNA.Formerly EC 2.1.1.32.','4 S-adenosyl-L-methionine + guanine(26)/guanine(27) in tRNA =4 S-adenosyl-L-homocysteine + N(2)-dimethylguanine(26)/N(2)-dimethylguanine(27) in tRNA.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 2.1.1.216','tRNA (guanine(26)-N(2))-dimethyltransferase','tRNA (m(2)(2)G(26))dimethyltransferase','NULL','The enzyme dissociates from its tRNA substrate between the twoconsecutive methylation reactions.In contrast to EC 2.1.1.215, this enzyme does not catalyze themethylation of guanine(27) in tRNA.Formerly EC 2.1.1.32.','2 S-adenosyl-L-methionine + guanine(26) in tRNA = 2 S-adenosyl-L-homocysteine + N(2)-dimethylguanine(26) in tRNA.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 2.1.1.217','tRNA (adenine(22)-N(1))-methyltransferase','NULL','NULL','The enzyme specifically methylates adenine(22) in tRNA.Formerly EC 2.1.1.36.','S-adenosyl-L-methionine + adenine(22) in tRNA = S-adenosyl-L-homocysteine+ N(1)-methyladenine(22) in tRNA.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 2.1.1.218','tRNA (adenine(9)-N(1))-methyltransferase','tRNA m(1)A(9)-methyltransferase','NULL','The enzyme from Sulfolobus acidocaldarius specifically methylatesadenine(9) in tRNA.The bifunctional enzyme from Thermococcus kodakaraensis alsocatalyzes the methylation of guanine(9) in tRNA (cf. EC 2.1.1.221).Formerly EC 2.1.1.36.','S-adenosyl-L-methionine + adenine(9) in tRNA = S-adenosyl-L-homocysteine+ N(1)-methyladenine(9) in tRNA.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 2.1.1.219','tRNA (adenine(57)-N(1)/adenine(58)-N(1))-methyltransferase','NULL','NULL','The enzyme catalyzes the formation of N(1)-methyladenine at twoadjacent positions (57 and 58) in the T-loop of certain tRNAs (e.g.tRNA(Asp)).Methyladenosine at position 57 is an obligatory intermediate for thesynthesis of methylinosine, which is commonly found at position 57 ofarchaeal tRNAs.Formerly EC 2.1.1.36.','2 S-adenosyl-L-methionine + adenine(57)/adenine(58) in tRNA =2 S-adenosyl-L-homocysteine + N(1)-methyladenine(57)/N(1)-methyladenine(58) in tRNA.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 2.1.1.220','tRNA (adenine(58)-N(1))-methyltransferase','tRNA (m(1)A(58)) methyltransferase','NULL','The enzyme specifically methylates adenine(58) in tRNA.The methylation of A(58) is critical for maintaining the stability ofinitiator tRNA(Met) in yeast.Formerly EC 2.1.1.36.','S-adenosyl-L-methionine + adenine(58) in tRNA = S-adenosyl-L-homocysteine+ N(1)-methyladenine(58) in tRNA.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 2.1.1.221','tRNA (guanine(9)-N(1))-methyltransferase','tRNA m(1)G(9) MTase','NULL','The enzyme from Saccharomyces cerevisiae specifically methylatesguanine(9).The bifunctional enzyme from Thermococcus kodakaraensis alsocatalyzes the methylation of adenine(9) in tRNA (cf. EC 2.1.1.218).Formerly EC 2.1.1.31.','S-adenosyl-L-methionine + guanine(9) in tRNA = S-adenosyl-L-homocysteine+ N(1)-methylguanine(9) in tRNA.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 2.1.1.222','2-polyprenyl-6-hydroxyphenol methylase','2-octaprenyl-6-hydroxyphenol methylase','NULL','UbiG catalyzes both methylation steps in ubiquinone biosynthesis inEscherichia coli.The second methylation is classified as EC 2.1.1.64.In eukaryotes Coq3 catalyzes the two methylation steps in ubiquinonebiosynthesis.However, while the second methylation is common to both enzymes,the first methylation by Coq3 occurs at a different position withinthe pathway, and thus involves a different substrate and isclassified as EC 2.1.1.114.The substrate of the eukaryotic enzyme (3,4-dihydroxy-5-all-trans-polyprenylbenzoate) differs by an additional carboxylate moiety.','S-adenosyl-L-methionine + 3-(all-trans-polyprenyl)benzene-1,2-diol =S-adenosyl-L-homocysteine + 2-methoxy-6-(all-trans-polyprenyl)phenol.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 2.1.1.223','tRNA(1)(Val) (adenine(37)-N(6))-methyltransferase','NULL','NULL','The enzyme specifically methylates adenine(37) in tRNA(1)(Val)(anticodon cmo(5)UAC).','S-adenosyl-L-methionine + adenine(37) in tRNA(1)(Val) = S-adenosyl-L-homocysteine + N(6)-methyladenine(37) in tRNA(1)(Val).', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 2.1.1.224','23S rRNA (adenine(2503)-C(8))-methyltransferase','NULL','Iron-sulfur.','This enzyme is a member of the AdoMet radical (radical SAM) family.S-adenosyl-L-methionine acts as both a radical generator and as thesource of the appended methyl group.Cfr is an plasmid-acquired methyltransferase that protects cells fromthe action of antibiotics.The enzyme methylates adenosine at position 2503 of 23S rRNA by aradical mechanism, transferring a CH(2) group from S-adenosyl-L-methionine while retaining the hydrogen at the C-8 position of theadenine.It will also methylate 2-methyladenine produced by the action ofEC 2.1.1.192.','2 S-adenosyl-L-methionine + adenine(2503) in 23S rRNA = S-adenosyl-L-homocysteine + L-methionine + 5-deoxyadenosine + 8-methyladenine(2503)in 23S rRNA.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 2.1.1.225','tRNA:m(4)X modification enzyme','NULL','NULL','The enzyme from Saccharomyces cerevisiae 2-O-methylates cytidine(4)in tRNA(Pro) and tRNA(Gly)(GCC), and adenosine(4) in tRNA(His).','(1) S-adenosyl-L-methionine + cytidine(4) in tRNA(Pro) = S-adenosyl-L-homocysteine + 2-O-methylcytidine(4) in tRNA(Pro).(2) S-adenosyl-L-methionine + cytidine(4) in tRNA(Gly)(GCC) = S-adenosyl-L-homocysteine + 2-O-methylcytidine(4) in tRNA(Gly)(GCC).(3) S-adenosyl-L-methionine + adenosine(4) in tRNA(His) = S-adenosyl-L-homocysteine + 2-O-methyladenosine(4) in tRNA(His).', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 2.1.1.226','23S rRNA (cytidine(1920)-2-O)-methyltransferase','NULL','NULL','The bifunctional enzyme from Mycobacterium tuberculosis 2-O-methylates cytidine(1920) in helix 69 of 23S rRNA and cytidine(1409)in helix 44 of 16S rRNA (cf. EC 2.1.1.227).These methylations result in increased susceptibility to theantibiotics capreomycin and viomycin.','S-adenosyl-L-methionine + cytidine(1920) in 23S rRNA = S-adenosyl-L-homocysteine + 2-O-methylcytidine(1920) in 23S rRNA.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 2.1.1.227','16S rRNA (cytidine(1409)-2-O)-methyltransferase','NULL','NULL','The bifunctional enzyme from Mycobacterium tuberculosis 2-O-methylates cytidine(1409) in helix 44 of 16S rRNA and cytidine(1920)in helix 69 of 23S rRNA (cf. EC 2.1.1.226).','S-adenosyl-L-methionine + cytidine(1409) in 16S rRNA = S-adenosyl-L-homocysteine + 2-O-methylcytidine(1409) in 16S rRNA.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 2.1.1.228','tRNA (guanine(37)-N(1))-methyltransferase','Transfer RNA (m(1)G(37)) methyltransferase','NULL','This enzyme is important for the maintenance of the correct readingframe during translation.Unlike TrmD from Escherichia coli, which recognizes the G(36)pG(37)motif preferentially, the human enzyme (encoded by TRMT5) alsomethylates inosine at position 37.Formerly EC 2.1.1.31.','S-adenosyl-L-methionine + guanine(37) in tRNA = S-adenosyl-L-homocysteine+ N(1)-methylguanine(37) in tRNA.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 2.1.1.229','tRNA (carboxymethyluridine(34)-5-O)-methyltransferase','tRNA methyltransferase 9','NULL','The enzyme catalyzes the posttranslational modification of uridineresidues at the wobble position 34 of the anticodon loop of tRNA.','S-adenosyl-L-methionine + carboxymethyluridine(34) in tRNA = S-adenosyl-L-homocysteine + 5-(2-methoxy-2-oxoethyl)uridine(34) in tRNA.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 2.1.1.230','23S rRNA (adenosine(1067)-2-O)-methyltransferase','23S rRNA A(1067) 2-methyltransferase','NULL','The methylase that is responsible for autoimmunity in thethiostrepton producer Streptomyces azureus, renders ribosomescompletely resistant to thiostrepton.Formerly EC 2.1.1.66.','S-adenosyl-L-methionine + adenosine(1067) in 23S rRNA = S-adenosyl-L-homocysteine + 2-O-methyladenosine(1067) in 23S rRNA.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 2.1.1.231','Flavonoid 4-O-methyltransferase','4-hydroxyisoflavone methyltransferase','NULL','The enzyme catalyzes the 4-methylation of naringenin.In vitro it catalyzes the 4-methylation of apigenin, quercetin,daidzein and genistein.','S-adenosyl-L-methionine + a 4-hydroxyflavanone = S-adenosyl-L-homocysteine + a 4-methoxyflavanone.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 2.1.1.232','Naringenin 7-O-methyltransferase','NULL','NULL','The enzyme is involved in the biosynthesis of the sakuranetin,an inducible defense mechanism of Oryza sativa against pathogenattack.','S-adenosyl-L-methionine + (2S)-naringenin = S-adenosyl-L-homocysteine +(2S)-sakuranetin.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 2.1.1.233','[Phosphatase 2A protein]-leucine-carboxy methyltransferase','Leucine carboxy methyltransferase-1','NULL','Methylates the C-terminal leucine of phosphatase 2A.A key regulator of protein phosphatase 2A.The methyl ester is hydrolyzed by EC 3.1.1.89.Occurs mainly in the cytoplasm, Golgi region and late endosomes.','S-adenosyl-L-methionine + [phosphatase 2A protein]-leucine = S-adenosyl-L-homocysteine + [phosphatase 2A protein]-leucine methyl ester.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 2.1.1.234','dTDP-3-amino-3,4,6-trideoxy-alpha-D-glucopyranoseN,N-dimethyltransferase','NULL','NULL','The enzyme is involved in the biosynthesis of desosamine,a 3-(dimethylamino)-3,4,6-trideoxyhexose found in certain macrolideantibiotics such as erthyromycin, azithromycin, and clarithromycin.','2 S-adenosyl-L-methionine + dTDP-3-amino-3,4,6-trideoxy-alpha-D-glucopyranose = 2 S-adenosyl-L-homocysteine + dTDP-3-dimethylamino-3,4,6-trideoxy-alpha-D-glucopyranose.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 2.1.1.235','dTDP-3-amino-3,6-dideoxy-alpha-D-glucopyranose N,N-dimethyltransferase','NULL','NULL','The enzyme is involved in the biosynthesis of mycaminose,an essential structural component of the macrolide antibiotictylosin, which is produced by the bacterium Streptomyces fradiae.','2 S-adenosyl-L-methionine + dTDP-3-amino-3,6-dideoxy-alpha-D-glucopyranose = 2 S-adenosyl-L-homocysteine + dTDP-3-dimethylamino-3,6-dideoxy-alpha-D-glucopyranose.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 2.1.1.236','dTDP-3-amino-3,6-dideoxy-alpha-D-galactopyranose N,N-dimethyltransferase','NULL','NULL','The enzyme is involved in the synthesis of dTDP-D-ravidosamine,the amino sugar moiety of the antibiotic ravidomycin V, which isproduced by the bacterium Streptomyces ravidus.','2 S-adenosyl-L-methionine + dTDP-3-amino-3,6-dideoxy-alpha-D-galactopyranose = 2 S-adenosyl-L-homocysteine + dTDP-3-dimethylamino-3,6-dideoxy-alpha-D-galactopyranose.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 2.1.1.237','Mycinamicin III 3-O-methyltransferase','NULL','NULL','The enzyme is involved in the biosynthesis of mycinamicin macrolideantibiotics.','S-adenosyl-L-methionine + mycinamicin III = S-adenosyl-L-homocysteine +mycinamicin IV.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 2.1.1.238','Mycinamicin VI 2-O-methyltransferase','NULL','NULL','The enzyme is involved in the biosynthesis of mycinamicin macrolideantibiotics.Requires Mg(2+) for optimal activity.','S-adenosyl-L-methionine + mycinamicin VI = S-adenosyl-L-homocysteine +mycinamicin III.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 2.1.1.239','L-olivosyl-oleandolide 3-O-methyltransferase','NULL','NULL','The enzyme is involved in the biosynthesis of the macrolideantibiotic oleandomycin in Streptomyces antibioticus.It can also act on other monoglycosylated macrolactones, includingL-rhamnosyl-erythronolide B and L-mycarosyl-erythronolide B.','S-adenosyl-L-methionine + L-olivosyl-oleandolide = S-adenosyl-L-homocysteine + L-oleandrosyl-oleandolide.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 2.1.1.240','Trans-resveratrol di-O-methyltransferase','Pterostilbene synthase','NULL','The enzyme catalyzes the biosynthesis of pterostilbene fromresveratrol.','2 S-adenosyl-L-methionine + trans-resveratrol = 2 S-adenosyl-L-homocysteine + pterostilbene.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 2.1.1.241','2,4,7-trihydroxy-1,4-benzoxazin-3-one-glucoside 7-O-methyltransferase','OMT BX7','NULL','The enzyme is involved in the biosynthesis of the protective andallelophatic benzoxazinoid DIMBOA ((2R)-4-hydroxy-7-methoxy-3-oxo-3,4-dihydro-2H-1,4-benzoxazin) in some plants, most commonly from thefamily of Poacea (grasses).','S-adenosyl-L-methionine + (2R)-4,7-dihydroxy-3-oxo-3,4-dihydro-2H-1,4-benzoxazin-2-yl beta-D-glucopyranoside = S-adenosyl-L-homocysteine +(2R)-4-hydroxy-7-methoxy-3-oxo-3,4-dihydro-2H-1,4-benzoxazin-2-yl beta-D-glucopyranoside.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 2.1.1.242','16S rRNA (guanine(1516)-N(2))-methyltransferase','M(2)G(1516) methyltransferase','NULL','The enzyme specifically methylates guanine(1516) at N(2) in 16S rRNA.','S-adenosyl-L-methionine + guanine(1516) in 16S rRNA = S-adenosyl-L-homocysteine + N(2)-methylguanine(1516) in 16S rRNA.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 2.1.1.243','2-ketoarginine methyltransferase','NULL','NULL','The enzyme is involved in production of the rare amino acid3-methylarginine, which is used by the epiphytic bacteriumPseudomonas syringae pv. syringae as an antibiotic against therelated pathogenic species Pseudomonas syringae pv. glycinea.','S-adenosyl-L-methionine + 5-guanidino-2-oxopentanoate = S-adenosyl-L-homocysteine + 5-guanidino-3-methyl-2-oxopentanoate.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 2.1.1.244','Protein N-terminal methyltransferase','NULL','NULL','This enzyme methylates the N-terminus of target proteins containingthe N-terminal motif (Ala/Pro/Ser)-Pro-Lys after the initiatorL-methionine is cleaved.When the terminal amino acid is L-proline, the enzyme catalyzes twosuccessive methylations of its alpha-amino group.When the first amino acid is either L-alanine or L-serine, the enzymecatalyzes three successive methylations.The Pro-Lys in positions 2-3 cannot be exchanged for other aminoacids.Formerly EC 2.1.1.n5.','(1) 3 S-adenosyl-L-methionine + N-terminal-(A,S)PK-[protein] =3 S-adenosyl-L-homocysteine + N-terminal-N,N,N-trimethyl-N-(A,S)PK-[protein].(2) 2 S-adenosyl-L-methionine + N-terminal-PPK-[protein] = 2 S-adenosyl-L-homocysteine + N-terminal-N,N-dimethyl-N-PPK-[protein].', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 2.1.1.245','5-methyltetrahydrosarcinapterin:corrinoid/iron-sulfur proteinCo-methyltransferase','NULL','NULL','Catalyzes the transfer of a methyl group from the cobamide cofactorof a corrinoid/Fe-S protein to the N5 group oftetrahydrosarcinapterin.Forms, together with EC 1.2.7.4 and EC 2.3.1.169 the acetyl-CoAdecarbonylase/synthase complex that catalyzes the demethylation ofacetyl-CoA in a reaction that also forms CO(2).This reaction is a key step in methanogenesis from acetate.','A [methyl-Co(III) corrinoid Fe-S protein] + tetrahydrosarcinapterin =a [Co(I) corrinoid Fe-S protein] + 5-methyltetrahydrosarcinapterin.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 2.1.1.246','[Methyl-Co(III) methanol-specific corrinoid protein]:coenzyme Mmethyltransferase','NULL','NULL','The enzyme, which is involved in methanogenesis from methanol,catalyzes the transfer of a methyl group from a corrinoid protein(see EC 2.1.1.90), where it is bound to the cobalt cofactor,to coenzyme M, forming the substrate for EC 2.8.4.1, the enzyme thatcatalyzes the final step in methanogenesis.Free methylcob(I)alamin can substitute for the corrinoid protein invitro.','A [methyl-Co(III) methanol-specific corrinoid protein] + coenzyme M =methyl-CoM + a [Co(I) methanol-specific corrinoid protein].', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 2.1.1.247','[Methyl-Co(III) methylamine-specific corrinoid protein]:coenzyme Mmethyltransferase','MT2-A','Zinc.','The enzyme, which is involved in methanogenesis from mono-, di-,and trimethylamine, catalyzes the transfer of a methyl group bound tothe cobalt cofactor of several corrinoid proteins (mono-, di-,and trimethylamine-specific corrinoid proteins, cf. EC 2.1.1.248,EC 2.1.1.249 and EC 2.1.1.250) to coenzyme M, forming the substratefor EC 2.8.4.1, the enzyme that catalyzes the final step inmethanogenesis.','A [methyl-Co(III) methylamine-specific corrinoid protein] + coenzyme M =methyl-CoM + a [Co(I) methylamine-specific corrinoid protein].', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 2.1.1.248','[Methylamine--corrinoid protein] Co-methyltransferase','Monomethylamine methyltransferase','NULL','The enzyme, which catalyzes the transfer of a methyl group frommethylamine to a methylamine-specific corrinoid protein (MtmC),is involved in methanogenesis from methylamine.The enzyme contains the unusual amino acid pyrrolysine.Methylation of the corrinoid protein requires the central cobalt tobe in the Co(I) state.During methylation the cobalt is oxidized to the Co(III) state.The methylated corrinoid protein is substrate for EC 2.1.1.247.','Methylamine + a [Co(I) methylamine-specific corrinoid protein] =a [methyl-Co(III) methylamine-specific corrinoid protein] + ammonia.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 2.1.1.249','[Dimethylamine--corrinoid protein] Co-methyltransferase','Dimethylamine methyltransferase','NULL','The enzyme, which catalyzes the transfer of a methyl group fromdimethylamine to a dimethylamine-specific corrinoid protein (MtbC),is involved in methanogenesis from dimethylamine.The enzyme contains the unusual amino acid pyrrolysine.Methylation of the corrinoid protein requires the central cobalt tobe in the Co(I) state.During methylation the cobalt is oxidized to the Co(III) state.The methylated corrinoid protein is substrate for EC 2.1.1.247.','Dimethylamine + a [Co(I) dimethylamine-specific corrinoid protein] =a [methyl-Co(III) dimethylamine-specific corrinoid protein] +methylamine.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 2.1.1.250','[Trimethylamine--corrinoid protein] Co-methyltransferase','Trimethylamine methyltransferase','NULL','The enzyme, which catalyzes the transfer of a methyl group fromtrimethylamine to a trimethylamine-specific corrinoid protein (MttC),is involved in methanogenesis from trimethylamine.The enzyme contains the unusual amino acid pyrrolysine.Methylation of the corrinoid protein requires the central cobalt tobe in the Co(I) state.During methylation the cobalt is oxidized to the Co(III) state.The methylated corrinoid protein is substrate for EC 2.1.1.247.','Trimethylamine + a [Co(I) trimethylamine-specific corrinoid protein] =a [methyl-Co(III) trimethylamine-specific corrinoid protein] +dimethylamine.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 2.1.1.251','Methylated-thiol--coenzyme M methyltransferase','NULL','NULL','The enzyme, which is involved in methanogenesis from methylatedthiols, such as methane thiol, dimethyl sulfide, and 3-S-methylmercaptopropionate, catalyzes two successive steps - thetransfer of a methyl group from the substrate to the cobalt cofactorof a methylated-thiol-specific corrinoid protein (MtsB), and thesubsequent transfer of the methyl group from the corrinoid protein tocoenzyme M.With most other methanogenesis substrates this process is carried outby two different enzymes (for example, EC 2.1.1.90 and EC 2.1.1.246).The cobalt is oxidized during methylation from the Co(I) state to theCo(III) state, and is reduced back to the Co(I) form duringdemethylation.','Methanethiol + coenzyme M = methyl-CoM + hydrogen sulfide.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 2.1.1.252','Tetramethylammonium--corrinoid protein Co-methyltransferase','Tetramethylammonium methyltransferase','NULL','The enzyme, which catalyzes the transfer of a methyl group fromtetramethylammonium to a tetramethylammonium-specific corrinoidprotein (MtqC), is involved in methanogenesis fromtetramethylammonium.Methylation of the corrinoid protein requires the central cobalt tobe in the Co(I) state.During methylation the cobalt is oxidized to the Co(III) state.The methylated corrinoid protein is substrate for EC 2.1.1.253.','Tetramethylammonium + a [Co(I) tetramethylammonium-specific corrinoidprotein] = a [methyl-Co(III) tetramethylammonium-specific corrinoidprotein] + trimethylamine.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 2.1.1.253','[Methyl-Co(III) tetramethylammonium-specific corrinoid protein]:coenzymeM methyltransferase','NULL','NULL','The enzyme, which is involved in methanogenesis fromtetramethylammonium, catalyzes the transfer of a methyl group from acorrinoid protein (see EC 2.1.1.252), where it is bound to the cobaltcofactor, to coenzyme M, forming the substrate for EC 2.8.4.1,the enzyme that catalyzes the final step in methanogenesis.','A [methyl-Co(III) tetramethylammonium-specific corrinoid protein] +coenzyme M = methyl-CoM + a [Co(I) tetramethylammonium-specific corrinoidprotein].', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 2.1.1.254','Erythromycin 3-O-methyltransferase','NULL','NULL','The enzyme methylates the 3 position of the mycarosyl moiety oferythromycin C, forming the most active form of the antibiotic,erythromycin A.It can also methylate the precursor erythromycin D, formingerythromycin B, which is then converted to erythromycin A byEC 1.14.13.154.','(1) S-adenosyl-L-methionine + erythromycin C = S-adenosyl-L-homocysteine+ erythromycin A.(2) S-adenosyl-L-methionine + erythromycin D = S-adenosyl-L-homocysteine+ erythromycin B.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 2.1.1.255','Geranyl diphosphate 2-C-methyltransferase','2-methyl-GPP synthase','Magnesium.','This enzyme, along with EC 4.2.3.118, produces 2-methylisoborneol,an odiferous compound produced by soil microorganisms with a strongearthy/musty odor.Formerly EC 2.1.1.n6.','S-adenosyl-L-methionine + geranyl diphosphate = S-adenosyl-L-homocysteine+ (E)-2-methylgeranyl diphosphate.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 2.1.1.256','tRNA (guanine(6)-N(2))-methyltransferase','M(2)G6 methyltransferase','NULL','The enzyme specifically methylates guanine(6) at N(2) in tRNA.','S-adenosyl-L-methionine + guanine(6) in tRNA = S-adenosyl-L-homocysteine+ N(2)-methylguanine(6) in tRNA.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 2.1.1.257','tRNA (pseudouridine(54)-N(1))-methyltransferase','M(1)-Psi methyltransferase','NULL','While this archaeal enzyme is specific for the 54 position and doesnot methylate pseudouridine at position 55, the presence ofpseudouridine at position 55 is necessary for the efficientmethylation of pseudouridine at position 54.','S-adenosyl-L-methionine + pseudouridine(54) in tRNA = S-adenosyl-L-homocysteine + N(1)-methylpseudouridine(54) in tRNA.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 2.1.1.258','5-methyltetrahydrofolate:corrinoid/iron-sulfur proteinCo-methyltransferase','NULL','NULL','Catalyzes the transfer of a methyl group from the N(5) group ofmethyltetrahydrofolate to the 5-methoxybenzimidazolylcobamidecofactor of a corrinoid/Fe-S protein.Involved, together with EC 1.2.7.4 and EC 2.3.1.169, in the reductiveacetyl coenzyme A (Wood-Ljungdahl) pathway of autotrophic carbonfixation in various bacteria and archaea.','A [methyl-Co(III) corrinoid Fe-S protein] + tetrahydrofolate = a [Co(I)corrinoid Fe-S protein] + 5-methyltetrahydrofolate.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 2.1.1.259','[Fructose-bisphosphate aldolase]-lysine N-methyltransferase','Ribulose-bisphosphate-carboxylase/oxygenase N-methyltransferase','NULL','The enzyme methylates a conserved lysine in the C-terminal part ofhigher plant fructose-bisphosphate aldolase (EC 4.1.2.13).The enzyme from pea (Pisum sativum) also methylates Lys-14 in thelarge subunits of hexadecameric higher plant ribulose-bisphosphate-carboxylase (EC 4.1.1.39), but that from Arabidopsis thaliana doesnot.','3 S-adenosyl-L-methionine + [fructose-bisphosphate aldolase]-L-lysine =3 S-adenosyl-L-homocysteine + [fructose-bisphosphate aldolase]-N(6),N(6),N(6)-trimethyl-L-lysine.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 2.1.1.260','rRNA small subunit pseudouridine methyltransferase Nep1','NULL','NULL','This enzyme, which occurs in both prokaryotes and eukaryotes,recognizes specific pseudouridine residues (Psi) in small subunits ofribosomal RNA based on the local RNA structure.It recognizes Psi(914) in 16S rRNA from the archaeonMethanocaldococcus jannaschii, Psi(1191) in yeast 18S rRNA,and Psi(1248) in human 18S rRNA.','S-adenosyl-L-methionine + pseudouridine(1191) in yeast 18S rRNA =S-adenosyl-L-homocysteine + N(1)-methylpseudouridine(1191) in yeast 18SrRNA.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 2.1.1.261','4-dimethylallyltryptophan N-methyltransferase','NULL','NULL','The enzyme catalyzes a step in the pathway leading to biosynthesis ofergot alkaloids in certain fungi.','S-adenosyl-L-methionine + 4-dimethylallyl-L-tryptophan = S-adenosyl-L-homocysteine + 4-dimethylallyl-L-abrine.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 2.1.1.262','Squalene methyltransferase','NULL','NULL','Two isoforms differing in their specificity were isolated from thegreen alga Botryococcus braunii BOT22.TMT-1 gave more of the dimethylated form whereas TMT2 gave more ofthe monomethylated form.','2 S-adenosyl-L-methionine + squalene = 2 S-adenosyl-L-homocysteine +3,22-dimethyl-1,2,23,24-tetradehydro-2,3,22,23-tetrahydrosqualene.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 2.1.1.263','Botryococcene C-methyltransferase','NULL','NULL','Isolated from the green alga Botryococcus braunii BOT22.Shows a very weak activity with squalene.','2 S-adenosyl-L-methionine + C(30) botryococcene = 2 S-adenosyl-L-homocysteine + 3,20-dimethyl-1,2,21,22-tetradehydro-2,3,20,21-tetrahydrobotryococcene.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 2.1.1.264','23S rRNA (guanine(2069)-N(7))-methyltransferase','23S rRNA m(7)G(2069) methyltransferase','NULL','The enzyme specifically methylates guanine(2069) at position N7 in23S rRNA.In gamma-proteobacteria the enzyme also catalyzes EC 2.1.1.173, whilein beta-proteobacteria the activities are carried out by separateproteins.The enzyme from the gamma-proteobacterium Escherichia coli has RNAunwinding activity as well.','S-adenosyl-L-methionine + guanine(2069) in 23S rRNA = S-adenosyl-L-homocysteine + N(7)-methylguanine(2069) in 23S rRNA.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 2.1.1.265','Tellurite methyltransferase','NULL','NULL','The enzyme is involved in the detoxification of tellurite.It can also methylate selenite and selenium dioxide.','S-adenosyl-L-methionine + tellurite = S-adenosyl-L-homocysteine +methanetelluronate.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 2.1.1.266','23S rRNA (adenine(2030)-N(6))-methyltransferase','M(6)A(2030) methyltransferase','NULL','The recombinant RlmJ protein is most active in methylatingdeproteinized 23S ribosomal subunit, and does not methylate thecompletely assembled 50S subunits.','S-adenosyl-L-methionine + adenine(2030) in 23S rRNA = S-adenosyl-L-homocysteine + N(6)-methyladenine(2030) in 23S rRNA.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 2.1.1.267','Flavonoid 3,5-methyltransferase','NULL','NULL','Isolated from Vitis vinifera (grape).Most active with delphinidin 3-glucoside but also acts on cyanidin3-glucoside, cyanidin, myricetin, quercetin and quercetin3-glucoside.The enzyme from Catharanthus roseus was most active with myricetin.Formerly EC 2.1.1.149.','(1) S-adenosyl-L-methionine + a 3-hydroxyflavonoid = S-adenosyl-L-homocysteine + a 3-methoxyflavonoid.(2) S-adenosyl-L-methionine + a 5-hydroxy-3-methoxyflavonoid =S-adenosyl-L-homocysteine + a 3,5-dimethoxyflavonoid.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 2.1.1.268','tRNA(Thr) (cytosine(32)-N(3))-methyltransferase','NULL','NULL','The enzyme from Saccharomyces cerevisiae specifically methylatescytosine(32) in tRNA(Thr) and in tRNA(Ser).','(1) S-adenosyl-L-methionine + cytosine(32) in tRNA(Thr) = S-adenosyl-L-homocysteine + N(3)-methylcytosine(32) in tRNA(Thr).(2) S-adenosyl-L-methionine + cytosine(32) in tRNA(Ser) = S-adenosyl-L-homocysteine + N(3)-methylcytosine(32) in tRNA(Ser).', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 2.1.1.269','Dimethylsulfoniopropionate demethylase','Dimethylsulfoniopropionate-dependent demethylase A','NULL','The enzyme from the marine bacteria Pelagibacter ubique and Ruegeriapomeroyi are specific toward S,S-dimethyl-beta-propiothetin.They do not demethylate glycine-betaine.','S,S-dimethyl-beta-propiothetin + tetrahydrofolate =3-(methylthio)propanoate + 5-methyltetrahydrofolate.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 2.1.1.270','(+)-6a-hydroxymaackiain 3-O-methyltransferase','HM3OMT','NULL','The protein from the plant Pisum sativum (garden pea) methylates (+)-6a-hydroxymaackiain at the 3-position.It also methylates 2,7,4-trihydroxyisoflavanone on the 4-position(cf. EC 2.1.1.212).','S-adenosyl-L-methionine + (+)-6a-hydroxymaackiain = S-adenosyl-L-homocysteine + (+)-pisatin.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 2.1.1.271','Cobalt-precorrin-4 methyltransferase','NULL','NULL','Part of the anaerobic route to adenosylcobalamin.','S-adenosyl-L-methionine + cobalt-precorrin-4 = S-adenosyl-L-homocysteine+ cobalt-precorrin-5A.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 2.1.1.272','Cobalt-factor III methyltransferase','NULL','NULL','Isolated from Bacillus megaterium.The enzyme catalyzes both methylation at C-17 and ring contraction.Contains a [4Fe-4S] cluster.It can also convert cobalt-precorrin-3 to cobalt-precorrin-4.The reductant may be thioredoxin.','S-adenosyl-L-methionine + cobalt-factor III + reduced acceptor =S-adenosyl-L-homocysteine + cobalt-precorrin-4 + acceptor.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 2.1.1.273','Benzoate O-methyltransferase','NULL','NULL','While the enzyme from the plant Zea mays is specific for benzoate,the enzymes from Arabidopsis species and Clarkia breweri alsocatalyze the reaction of EC 2.1.1.274.In snapdragon (Antirrhinum majus) two isoforms are found,one specific for benzoate and one that is also active towardsalicylate.The volatile product is an important scent compound in some floweringspecies.','S-adenosyl-L-methionine + benzoate = S-adenosyl-L-homocysteine + methylbenzoate.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 2.1.1.274','Salicylate carboxymethyltransferase','Salicylate O-methyltransferase','NULL','The enzyme, which is found in flowering plants, also has the activityof EC 2.1.1.273.Formerly EC 2.1.1.n9.','S-adenosyl-L-methionine + salicylate = S-adenosyl-L-homocysteine + methylsalicylate.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 2.1.1.275','Gibberellin A(9) O-methyltransferase','NULL','NULL','The enzyme also methylates gibberellins A(20) (95%), A(3) (80%), A(4)(69%) and A(34) (46%) with significant activity.','S-adenosyl-L-methionine + gibberellin A(9) = S-adenosyl-L-homocysteine +methyl gibberellin A(9).', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 2.1.1.276','Gibberellin A(4) carboxyl methyltransferase','Gibberellin A(4) O-methyltransferase','NULL','The enzyme also methylates gibberellins A(34) (80%), A(9) (60%),and A(3) (45%) with significant activity.','S-adenosyl-L-methionine + gibberellin A(4) = S-adenosyl-L-homocysteine +methyl gibberellin A(4).', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 2.1.1.277','Anthranilate O-methyltransferase','NULL','NULL','In the plant maize (Zea mays), the isoforms AAMT1 and AAMT2 arespecific for anthranilate while AAMT3 also has the activity ofEC 2.1.1.273.','S-adenosyl-L-methionine + anthranilate = S-adenosyl-L-homocysteine +O-methyl anthranilate.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 2.1.1.278','Indole-3-acetate O-methyltransferase','IAA carboxylmethyltransferase','Magnesium.','The enzyme is found in plants and is important for regulation of theplant hormone (indol-3-yl)acetate.The product, methyl (indol-3-yl)acetate is inactive as hormone.Formerly EC 2.1.1.n10.','S-adenosyl-L-methionine + (indol-3-yl)acetate = S-adenosyl-L-homocysteine+ methyl (indol-3-yl)acetate.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 2.1.1.279','Trans-anol O-methyltransferase','T-anol O-methyltransferase','NULL','The enzyme from anise (Pimpinella anisum) is highly specific forsubstrates in which the double bond in the propenyl side chain islocated between C(7) and C(8), and, in contrast to EC 2.1.1.146, doesnot have activity with eugenol or chavicol.','(1) S-adenosyl-L-methionine + trans-anol = S-adenosyl-L-homocysteine +trans-anethole.(2) S-adenosyl-L-methionine + isoeugenol = S-adenosyl-L-homocysteine +isomethyleugenol.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 2.1.1.280','Selenocysteine Se-methyltransferase','NULL','NULL','The enzyme uses S-adenosyl-L-methionine as methyl donor less activelythan S-methyl-L-methionine.The enzyme from broccoli (Brassica oleracea var. italica) also hasthe activity of EC 2.1.1.10.Formerly EC 2.1.1.n3.','S-methyl-L-methionine + L-selenocysteine = L-methionine + Se-methyl-L-selenocysteine.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 2.1.1.281','Phenylpyruvate C(3)-methyltransferase','Phenylpyruvate C-beta-methyltransferase','NULL','The enzyme from the bacterium Streptomyces hygroscopicus NRRL3085 isinvolved in synthesis of the nonproteinogenic amino acid (2S,3S)-beta-methyl-phenylalanine, a building block of the antibioticmannopeptimycin.','S-adenosyl-L-methionine + 3-phenylpyruvate = S-adenosyl-L-homocysteine +(3S)-2-oxo-3-phenylbutanoate.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 2.1.1.282','tRNA(Phe) 7-((3-amino-3-carboxypropyl)-4-demethylwyosine(37)-N(4))-methyltransferase','tRNA-yW synthesizing enzyme-3','NULL','The enzyme is involved in the biosynthesis of hypermodified tricyclicbases found at position 37 of certain tRNAs.These modifications are important for translational reading-framemaintenance.The enzyme is found in all eukaryotes and in some archaea, but not inbacteria.The eukaryotic enzyme is involved in the biosynthesis of wybutosine.','S-adenosyl-L-methionine + 7-((3S)-(3-amino-3-carboxypropyl))-4-demethylwyosine(37) in tRNA(Phe) = S-adenosyl-L-homocysteine + 7-((3S)-(3-amino-3-carboxypropyl))wyosine(37) in tRNA(Phe).', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 2.1.1.283','Emodin O-methyltransferase','NULL','NULL','The enzyme is involved in biosynthesis of the seco-anthraquinone (+)-geodin.','S-adenosyl-L-methionine + emodin = S-adenosyl-L-homocysteine + questin.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 2.1.1.284','8-demethylnovobiocic acid C(8)-methyltransferase','NULL','NULL','The enzyme is involved in the biosynthesis of the aminocoumarinantibiotic novobiocin.','S-adenosyl-L-methionine + 8-demethylnovobiocic acid = S-adenosyl-L-homocysteine + novobiocic acid.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 2.1.1.285','Demethyldecarbamoylnovobiocin O-methyltransferase','NULL','NULL','The enzyme is involved in the biosynthesis of the aminocoumarinantibiotic novobiocin.','S-adenosyl-L-methionine + demethyldecarbamoylnovobiocin = S-adenosyl-L-homocysteine + decarbamoylnovobiocin.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 2.1.1.286','25S rRNA (adenine(2142)-N(1))-methyltransferase','25S rRNA m(1)A(2142) methyltransferase','NULL','In the yeast Saccharomyces cerevisiae this methylation is importantfor resistance toward hydrogen peroxide and the antibioticanisomycin.','S-adenosyl-L-methionine + adenine(2142) in 25S rRNA = S-adenosyl-L-homocysteine + N(1)-methyladenine(2142) in 25S rRNA.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 2.1.1.287','25S rRNA (adenine(645)-N(1))-methyltransferase','25S rRNA m(1)A(645) methyltransferase','NULL','The enzyme is found in eukaryotes.The adenine position refers to tRNA the yeast Saccharomycescerevisiae, in which the enzyme is important for ribosome biogenesis.','S-adenosyl-L-methionine + adenine(645) in 25S rRNA = S-adenosyl-L-homocysteine + N(1)-methyladenine(645) in 25S rRNA.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 2.1.1.288','Aklanonic acid methyltransferase','NULL','NULL','The enzyme from the Gram-positive bacterium Streptomyces sp. C5 isinvolved in the biosynthesis of the anthracycline daunorubicin.','S-adenosyl-L-methionine + aklanonate = S-adenosyl-L-homocysteine + methylaklanonate.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 2.1.1.289','Cobalt-precorrin-7 (C(5))-methyltransferase','NULL','NULL','This enzyme catalyzes the methylation at C-5 of cobalt-precorrin-7,a step in the anaerobic (early cobalt insertion) adenosylcobalaminbiosynthesis pathway.','Cobalt-precorrin-7 + S-adenosyl-L-methionine = cobalt-precorrin-8 +S-adenosyl-L-homocysteine.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 2.1.1.290','tRNA(Phe) (7-(3-amino-3-carboxypropyl)wyosine(37)-O)-methyltransferase','tRNA-yW synthesizing enzyme-4','NULL','The enzyme is found only in eukaryotes, where it is involved in thebiosynthesis of wybutosine, a hypermodified tricyclic base found atposition 37 of certain tRNAs.The modification is important for translational reading-framemaintenance.In some species that produce hydroxywybutosine the enzyme uses7-(2-hydroxy-3-amino-3-carboxypropyl)wyosine(37) in tRNA(Phe) assubstrate.The enzyme also has the activity of EC 2.3.1.231.','S-adenosyl-L-methionine + 7-((3S)-3-amino-3-carboxypropyl)wyosine(37) intRNA(Phe) = S-adenosyl-L-homocysteine + 7-((3S)-3-amino-3-(methoxycarbonyl)propyl)wyosine(37) in tRNA(Phe).', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 2.1.1.291','(R,S)-reticuline 7-O-methyltransferase','NULL','NULL','The enzyme from the plant Papaver somniferum (opium poppy) methylates(S)- and (R)-reticuline with equal efficiency and is involved in thebiosynthesis of tetrahydrobenzylisoquinoline alkaloids.','(1) S-adenosyl-L-methionine + (S)-reticuline = S-adenosyl-L-homocysteine+ (S)-laudanine.(2) S-adenosyl-L-methionine + (R)-reticuline = S-adenosyl-L-homocysteine+ (R)-laudanine.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 2.1.1.292','Carminomycin 4-O-methyltransferase','NULL','NULL','The enzymes from the Gram-positive bacteria Streptomyces sp. C5 andStreptomyces peucetius are involved in the biosynthesis of theanthracycline daunorubicin.In vitro the enzyme from Streptomyces sp. C5 also catalyzes the 4-O-methylation of 13-dihydrocarminomycin, rhodomycin D and 10-carboxy-13-deoxycarminomycin.','S-adenosyl-L-methionine + carminomycin = S-adenosyl-L-homocysteine +daunorubicin.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 2.1.1.293','6-hydroxytryprostatin B O-methyltransferase','NULL','NULL','Involved in the biosynthetic pathways of several indole alkaloidssuch as tryprostatins, fumitremorgins and verruculogen.','S-adenosyl-L-methionine + 6-hydroxytryprostatin B = S-adenosyl-L-homocysteine + tryprostatin A.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 2.1.1.294','3-O-phospho-polymannosyl GlcNAc-diphospho-ditrans,octacis-undecaprenol3-phospho-methyltransferase','NULL','NULL','The enzyme is involved in the biosynthesis of the polymannoseO-polysaccharide in the outer leaflet of the membrane of Escherichiacoli serotype O9a.O-polysaccharide structures vary extensively because of differencesin the number and type of sugars in the repeat unit.The dual kinase/methylase WbdD also catalyzes the precedingphosphorylation of alpha-D-Man-(1-','S-adenosyl-L-methionine + 3-O-phospho-alpha-D-Man-(1-', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 2.1.1.295','2-methyl-6-phytyl-1,4-hydroquinone methyltransferase','2-methyl-6-solanyl-1,4-hydroquinone methyltransferase','NULL','Involved in the biosynthesis of plastoquinol, as well as vitaminE (tocopherols and tocotrienols).','(1) S-adenosyl-L-methionine + 2-methyl-6-phytylbenzene-1,4-diol =S-adenosyl-L-homocysteine + 2,3-dimethyl-6-phytylbenzene-1,4-diol.(2) S-adenosyl-L-methionine + 2-methyl-6-all-trans-nonaprenylbenzene-1,4-diol = S-adenosyl-L-homocysteine + plastoquinol.(3) S-adenosyl-L-methionine + 6-geranylgeranyl-2-methylbenzene-1,4-diol =S-adenosyl-L-homocysteine + 6-geranylgeranyl-2,3-dimethylbenzene-1,4-diol.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 2.1.1.296','Methyltransferase cap2','Cap2-MTase','NULL','The enzyme, found in higher eukaryotes including insects andvertebrates, and their viruses, methylates the ribose of thenucleoside at the second transcribed position of mRNAs and snRNAs.This methylation is known as cap2.The human enzyme can also methylate mRNA molecules where the upstreampurine ribonucleotide is not methylated (see EC 2.1.1.57), but withlower efficiency.','S-adenosyl-L-methionine + a 5-(N(7)-methyl 5-triphosphoguanosine)-(2-O-methyl-purine-ribonucleotide)-(ribonucleotide)-(mRNA) = S-adenosyl-L-homocysteine + a 5-(N(7)-methyl 5-triphosphoguanosine)-(2-O-methyl-purine-ribonucleotide)-(2-O-methyl-ribonucleotide)-(mRNA).', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 2.1.1.297','Peptide chain release factor N(5)-glutamine methyltransferase','N(5)-glutamine MTase','NULL','Modifies the glutamine residue in the universally conservedglycylglycylglutamine (GGQ) motif of peptide chain release factor,resulting in almost complete loss of release activity.Formerly EC 2.1.1.n16.','S-adenosyl-L-methionine + [peptide chain release factor 1 or 2]-L-glutamine = S-adenosyl-L-homocysteine + [peptide chain release factor 1or 2]-N(5)-methyl-L-glutamine.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 2.1.1.298','Ribosomal protein L3 N(5)-glutamine methyltransferase','NULL','NULL','Modifies the glutamine residue in the glycylglycylglutamine (GGQ)motif of ribosomal protein L3 (Gln(150) in the protein from thebacterium Escherichia coli).The enzyme does not act on peptide chain release factor 1 or 2.Formerly EC 2.1.1.n15.','S-adenosyl-L-methionine + [ribosomal protein L3]-L-glutamine =S-adenosyl-L-homocysteine + [ribosomal protein L3]-N(5)-methyl-L-glutamine.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 2.1.1.299','Protein N-terminal monomethyltransferase','N-terminal monomethylase','NULL','This enzyme methylates the N-terminus of target proteins containingthe N-terminal motif (Ala/Pro/Ser)-Pro-Lys after the initiatorL-methionine is cleaved.In contrast to EC 2.1.1.244, the protein only adds one methyl groupto the N-terminal.','S-adenosyl-L-methionine + N-terminal-(A,P,S)PK-[protein] = S-adenosyl-L-homocysteine + N-terminal-N-methyl-N-(A,P,S)PK-[protein].', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 2.1.1.300','Pavine N-methyltransferase','PavNMT','NULL','The enzyme, isolated from the plant Thalictrum flavum, alsomethylates (R,S)-stylopine and (S)-scoulerine (11%) with loweractivity (14% and 11%, respectively).Formerly EC 2.1.1.n14.','S-adenosyl-L-methionine + (+-)-pavine = S-adenosyl-L-homocysteine +N-methylpavine.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 2.1.1.n1','Resorcinol O-methyltransferase','O-methyltransferase 12','NULL','The enzyme from Dictyostelium discoideum is involed in modifying theresorcinolic back-bone of the differentiation regulating factor4-methyl-5-pentylbenzene-1,3-diol.It also shows activity toward 5-pentylbenzene-1,3-diol.','S-adenosyl-L-methionine + resorcinol = S-adenosyl-L-homocysteine +3-methoxy phenol.', 'NULL', 'NULL');
