INSERT INTO enzyme VALUES ( 'EC 2.1.1.n3','Transferred entry: 2','NULL','NULL','NULL','NULL', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 2.1.1.n4','Thiocyanate methyltransferase','NULL','NULL','NULL','S-adenosyl-L-methionine + thiocyanate = S-adenosyl-L-homocysteine +methyl thiocyanate.', 'NULL', 'NULL');
