INSERT INTO enzyme VALUES ( 'EC 2.3.3.11','2-hydroxyglutarate synthase','2-hydroxyglutarate glyoxylate-lyase (CoA-propanoylating)','NULL','Formerly EC 4.1.3.9.','Propanoyl-CoA + H(2)O + glyoxylate = 2-hydroxyglutarate + CoA.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 2.3.3.12','3-propylmalate synthase','3-(n-propyl)-malate synthase','NULL','Formerly EC 4.1.3.11.','Pentanoyl-CoA + H(2)O + glyoxylate = 3-propylmalate + CoA.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 2.3.3.13','2-isopropylmalate synthase','3-carboxy-3-hydroxy-4-methylpentanoate 3-methyl-2-oxobutanoate-lyase(CoA-acetylating)','Potassium.','Formerly EC 4.1.3.12.','Acetyl-CoA + 3-methyl-2-oxobutanoate + H(2)O = (2S)-2-isopropylmalate +CoA.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 2.3.3.14','Homocitrate synthase','2-hydroxybutane-1,2,4-tricarboxylate 2-oxoglutarate-lyase (CoA-acetylating)','NULL','Belongs in the alpha-aminoadipate pathway of lysine synthesis, alongwith EC 4.2.1.36.Also acts with oxaloacetate as substrate, but more slowly.Formerly EC 4.1.3.21.','Acetyl-CoA + H(2)O + 2-oxoglutarate = (R)-2-hydroxybutane-1,2,4-tricarboxylate + CoA.', 'NULL', 'NULL');
