INSERT INTO enzyme VALUES ( 'EC 2.4.1.21','Starch synthase','ADP-glucose--starch glucosyltransferase','NULL','The description (official name) various according to the source ofthe enzyme and the nature of its synthetic product, e.g. starchsynthase, bacterial glycogen synthase.A similar enzyme utilizes UDP-glucose (cf. EC 2.4.1.11).','ADP-glucose + (1,4-alpha-D-glucosyl)(n) = ADP + (1,4-alpha-D-glucosyl)(n+1).', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 2.4.1.22','Lactose synthase','N-acetyllactosamine synthase','NULL','The enzyme is a complex of 2 proteins A and B.In the absence of the B protein (alpha-lactalbumin) the enzymecatalyzes the transfer of galactose from UDP-galactose toN-acetylglucosamine (cf. EC 2.4.1.90).','UDP-alpha-D-galactose + D-glucose = UDP + lactose.', 'NULL', 'NULL');
