INSERT INTO enzyme VALUES ( 'EC 2.4.1.209','Cis-p-coumarate glucosyltransferase','NULL','NULL','Cis-caffeic acid also serves as a glucosyl acceptor with the enzymefrom Sphagnum fallax kinggr.The corresponding trans-isomers are not substrates.','UDP-glucose + cis-p-coumarate = 4-O-beta-D-glucosyl-cis-p-coumarate +UDP.', 'NULL', 'NULL');
