INSERT INTO enzyme VALUES ( 'EC 2.4.1.234','Kaempferol 3-O-galactosyltransferase','F3GalTase','NULL','Acts on the endogenous flavonols kaempferol and quercetin, to alesser extent on myricetin and fisetin, and weakly on galangin andisorhamnetin.The reaction can occur equally well in both directions.','UDP-alpha-D-galactose + kaempferol = UDP + kaempferol 3-O-beta-D-galactoside.', 'NULL', 'NULL');
