INSERT INTO enzyme VALUES ( 'EC 2.4.1.315','Diglucosyl diacylglycerol synthase (1,6-linking)','DGlcDAG synthase','NULL','The enzyme is found in several bacterial species.The enzyme from Bacillus subtilis is specific for glucose and canform tetraglucosyl diacylglycerol in vitro.The enzyme from Mycoplasma genitalium can incoporate galactose withsimilar efficiency, but forms mainly 1,2-diacyl-diglucopyranosyl-sn-glycerol in vivo.The enzyme from Staphylococcus aureus can also form glucosyl-glycero-3-phospho-(1-sn-glycerol).','(1) UDP-alpha-D-glucose + 1,2-diacyl-3-O-(alpha-D-glucopyranosyl)-sn-glycerol = 1,2-diacyl-3-O-(alpha-D-glucopyranosyl-(1-', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 2.4.1.316','Tylactone mycaminosyltransferase','NULL','NULL','The enzyme participates in the biosynthetic pathway of the macrolideantibiotic tylosin, which is produced by several species ofStreptomyces bacteria.','Tylactone + dTDP-alpha-D-mycaminose = dTDP + 5-O-beta-D-mycaminosyltylactone.', 'NULL', 'NULL');
