INSERT INTO enzyme VALUES ( 'EC 2.4.1.319','Beta-1,4-mannooligosaccharide phosphorylase','NULL','NULL','The enzyme, isolated from the ruminal bacterium Ruminococcus albus,catalyzes the reversible phosphorolysis of beta-1,4-mannooligosaccharide with a minimum size of three monomers.','((1-', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 2.4.1.320','1,4-beta-mannosyl-N-acetylglucosamine phosphorylase','NULL','NULL','The enzyme isolated from the anaerobic bacterium Bacteroidesthetaiotaomicron is involved in the degradation of host-derivedN-glycans.','4-O-beta-D-mannopyranosyl-N-acetyl-D-glucosamine + phosphate = N-acetyl-D-glucosamine + alpha-D-mannose 1-phosphate.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 2.4.1.321','Cellobionic acid phosphorylase','NULL','NULL','The enzyme occurs in cellulolytic bacteria and fungi.It catalyzes the reversible phosphorolysis of cellobionic acid.In the synthetic direction it produces 4-O-beta-D-glucopyranosyl-D-glucuronate from alpha-D-glucose 1-phosphate and D-glucuronate withlow activity.','4-O-beta-D-glucopyranosyl-D-gluconate + phosphate = alpha-D-glucose1-phosphate + D-gluconate.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 2.4.1.322','Desvancosaminyl-vancomycin vancosaminetransferase','Desvancosamine-vancomycin TDP-vancosaminyltransferase','NULL','The enzyme, isolated from the bacterium Amycolatopsis orientalis,catalyzes the ultimate step in the biosynthesis of the antibioticvancomycin.','dTDP-beta-L-vancosamine + desvancosaminyl-vancomycin = dTDP + vancomycin.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 2.4.1.323','7-deoxyloganetic acid glucosyltransferase','NULL','NULL','Isolated from the plant Catharanthus roseus (Madagascar periwinkle).Involved in loganin and secologanin biosynthesis.Does not react with 7-deoxyloganetin.Cf. EC 2.4.1.324.','UDP-alpha-D-glucose + 7-deoxyloganetate = UDP + 7-deoxyloganate.', 'NULL', 'NULL');
