INSERT INTO enzyme VALUES ( 'EC 2.4.2.1','Purine-nucleoside phosphorylase','Inosine phosphorylase','NULL','Specificity not completely determined.Can also catalyze ribosyltransferase reactions of the type catalyzedby EC 2.4.2.5.','(1) Purine nucleoside + phosphate = purine + alpha-D-ribose 1-phosphate.(2) Purine deoxynucleoside + phosphate = purine + 2-deoxy-alpha-D-ribose1-phosphate.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 2.4.2.2','Pyrimidine-nucleoside phosphorylase','Py-NPase','NULL','Unlike EC 2.4.2.3, and EC 2.4.2.4, this enzyme can accept both theribonucleoside uridine and the 2-deoxyribonucleosides2-deoxyuridine and thymidine.The reaction is reversible, and the enzyme does not distinguishbetween alpha-D-ribose 1-phosphate and 2-deoxy-alpha-D-ribose1-phosphate in the synthetic direction.Formerly EC 2.4.2.23.','(1) Uridine + phosphate = uracil + alpha-D-ribose 1-phosphate.(2) Thymidine + phosphate = thymine + 2-deoxy-alpha-D-ribose 1-phosphate.(3) 2-deoxyuridine + phosphate = uracil + 2-deoxy-alpha-D-ribose1-phosphate.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 2.4.2.3','Uridine phosphorylase','Pyrimidine phosphorylase','NULL','The enzyme participates the the pathways of pyrimidineribonucleosides degradation and salvage.The mammalian enzyme also accepts 2-deoxyuridine.Formerly EC 2.4.2.23.','Uridine + phosphate = uracil + alpha-D-ribose 1-phosphate.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 2.4.2.4','Thymidine phosphorylase','Pyrimidine phosphorylase','NULL','In some tissues also catalyzes deoxyribosyltransferase reactions ofthe type catalyzed by EC 2.4.2.6.Formerly EC 2.4.2.23.','Thymidine + phosphate = thymine + 2-deoxy-alpha-D-ribose 1-phosphate.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 2.4.2.5','Nucleoside ribosyltransferase','NULL','NULL','Base(1) and base(2) represent various purines and pyrimidines.','D-ribosyl-base(1) + base(2) = D-ribosyl-base(2) + base(1).', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 2.4.2.6','Nucleoside deoxyribosyltransferase','NULL','NULL','Base(1) and base(2) represent various purines and pyrimidines.','2-deoxy-D-ribosyl-base(1) + base(2) = 2-deoxy-D-ribosyl-base(2) +base(1).', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 2.4.2.7','Adenine phosphoribosyltransferase','AMP diphosphorylase','NULL','5-amino-4-imidazolecarboxamide can replace adenine.','AMP + diphosphate = adenine + 5-phospho-alpha-D-ribose 1-diphosphate.', 'NULL', 'NULL');
