INSERT INTO enzyme VALUES ( 'EC 2.4.2.26','Protein xylosyltransferase','UDP-D-xylose:core protein beta-D-xylosyltransferase','NULL','Involved in the biosynthesis of the linkage region ofglycosaminoglycan chains as part of proteoglycan biosynthesis(chondroitin, dermatan and heparan sulfates).','Transfers a beta-D-xylosyl residue from UDP-D-xylose to the serinehydroxy group of an acceptor protein substrate.', 'NULL', 'NULL');
