INSERT INTO enzyme VALUES ( 'EC 2.7.1.62','Phosphoramidate--hexose phosphotransferase','NULL','NULL','Activity is observed with several hexoses; of these glucose is thebest substrate and the product from it is alpha-D-glucose1-phosphate.The phosphoramidate donor can be replaced by N-phosphoglycine and byan N-phosphohistidine.May be identical with EC 3.1.3.9.','Phosphoramidate + D-hexose = NH(3) + alpha-D-hexose 1-phosphate.', 'NULL', 'NULL');
