INSERT INTO enzyme VALUES ( 'EC 2.7.1.119','Hygromycin-B 7-O-kinase','APH(7)','NULL','Phosphorylates the antibiotics hygromycin B, 1-N-hygromycin B anddestomycin, but not hygromycin B2, at the 7-hydroxy group in thedestomic acid ring.','ATP + hygromycin B = ADP + 7-O-phosphohygromycin B.', 'NULL', 'NULL');
