INSERT INTO enzyme VALUES ( 'EC 2.7.1.136','Macrolide 2-kinase','NULL','NULL','Erythromycin, spiramycin and some other macrolide antibiotics canalso act as acceptors.','ATP + oleandomycin = ADP + oleandomycin 2-O-phosphate.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 2.7.1.137','Phosphatidylinositol 3-kinase','1-phosphatidylinositol 3-kinase','NULL','NULL','ATP + 1-phosphatidyl-1D-myo-inositol = ADP + 1-phosphatidyl-1D-myo-inositol 3-phosphate.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 2.7.1.138','Ceramide kinase','Acylsphingosine kinase','NULL','NULL','ATP + ceramide = ADP + ceramide 1-phosphate.', 'NULL', 'NULL');
