INSERT INTO enzyme VALUES ( 'EC 2.7.1.140','Inositol-tetrakisphosphate 5-kinase','1D-myo-inositol-tetrakisphosphate 5-kinase','NULL','The enzyme from plants and yeast can also use Ins(1,2,3,4,6)P(5) as asubstrate.','ATP + 1D-myo-inositol 1,3,4,6-tetrakisphosphate = ADP + 1D-myo-inositol1,3,4,5,6-pentakisphosphate.', 'NULL', 'NULL');
