INSERT INTO enzyme VALUES ( 'EC 2.7.2.1','Acetate kinase','Acetate kinase (phosphorylating)','Magnesium.','While purified enzyme from Escherichia coli is specific for acetate,others have found that the enzyme can also use propanoate as asubstrate, but more slowly.Acetate can be converted into the key metabolic intermediate acetyl-CoA by coupling acetate kinase with EC 2.3.1.8.Both this enzyme and EC 2.7.2.15 play important roles in theproduction of propanoate.','ATP + acetate = ADP + acetyl phosphate.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 2.7.2.2','Carbamate kinase','NULL','NULL','NULL','ATP + NH(3) + CO(2) = ADP + carbamoyl phosphate.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 2.7.2.3','Phosphoglycerate kinase','NULL','NULL','NULL','ATP + 3-phospho-D-glycerate = ADP + 3-phospho-D-glyceroyl phosphate.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 2.7.2.4','Aspartate kinase','Aspartokinase','NULL','The enzyme from Escherichia coli is a multifunctional protein, whichalso catalyzes the reaction of EC 1.1.1.3.This is also the case for two of the four isoenzymes in Arabidopsisthaliana.The equilibrium constant strongly favors the reaction from right toleft, i.e. the non-physiological direction of reaction.','ATP + L-aspartate = ADP + 4-phospho-L-aspartate.', 'NULL', 'NULL');
