INSERT INTO enzyme VALUES ( 'EC 2.7.2.7','Butyrate kinase','NULL','NULL','The enzyme from Clostridium sp. also acts, more slowly on pentanoateand propanoate, and on some branched-chain fatty acids (cf.EC 2.7.1.14).','ATP + butanoate = ADP + butanoyl phosphate.', 'NULL', 'NULL');
