INSERT INTO enzyme VALUES ( 'EC 2.7.2.10','Phosphoglycerate kinase (GTP)','NULL','NULL','NULL','GTP + 3-phospho-D-glycerate = GDP + 3-phospho-D-glyceroyl phosphate.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 2.7.2.11','Glutamate 5-kinase','Gamma-glutamyl kinase','NULL','The product rapidly cyclizes to 5-oxoproline and phosphate.','ATP + L-glutamate = ADP + L-glutamate 5-phosphate.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 2.7.2.12','Acetate kinase (diphosphate)','Acetate kinase (pyrophosphate)','NULL','NULL','Diphosphate + acetate = phosphate + acetyl phosphate.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 2.7.2.13','Glutamate 1-kinase','NULL','NULL','NULL','ATP + L-glutamate = ADP + alpha-L-glutamyl phosphate.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 2.7.2.14','Branched-chain-fatty-acid kinase','Isobutyrate kinase','NULL','3-methylbutanoate, 2-methylbutanoate, pentanoate, butanoate,and propanoate can also act as acceptors (cf. EC 2.7.2.7).','ATP + 2-methylpropanoate = ADP + 2-methylpropanoyl phosphate.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 2.7.2.15','Propionate kinase','Propionate/acetate kinase','Magnesium.','Both propanoate and acetate can act as a substrate.Involved in the anaerobic degradation of L-threonine in bacteria.Both this enzyme and EC 2.7.2.1 play important roles in theproduction of propanoate.','ATP + propanoate = ADP + propanoyl phosphate.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 2.7.3.1','Guanidinoacetate kinase','Glycocyamine kinase','NULL','NULL','ATP + guanidinoacetate = ADP + phosphoguanidinoacetate.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 2.7.3.2','Creatine kinase','NULL','NULL','N-ethylglycocyamine can also act as acceptor.','ATP + creatine = ADP + phosphocreatine.', 'NULL', 'NULL');
