INSERT INTO enzyme VALUES ( 'EC 2.7.3.5','Lombricine kinase','NULL','NULL','Also acts on methylated lombricines such as thalassemine; thespecificity varies with the source species.','ATP + lombricine = ADP + N-phospholombricine.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 2.7.3.6','Hypotaurocyamine kinase','NULL','NULL','Also acts, more slowly, on taurocyamine.','ATP + hypotaurocyamine = ADP + N(omega)-phosphohypotaurocyamine.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 2.7.3.7','Opheline kinase','NULL','NULL','Has a little activity on taurocyamine, lombricine andphosphotaurocyamine.','ATP + guanidinoethyl methyl phosphate = ADP +N-phosphoguanidinoethyl methylphosphate.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 2.7.3.8','Ammonia kinase','NULL','NULL','Has a wide specificity.In the reverse direction, N-phosphoglycine and N-phosphohistidine canalso act as phosphate donors, and ADP, dADP, GDP, CDP, dTDP, dCDP,IDP and UDP can act as phosphate acceptors (in decreasing order ofactivity).','ATP + NH(3) = ADP + phosphoramide.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 2.7.3.9','Phosphoenolpyruvate--protein phosphotransferase','Enzyme I of the phosphotransferase system','NULL','Acts only on histidine residues in specific phosphocarrier proteinsof low molecular mass (9.5 kDa) involved in bacterial sugartransport.A similar reaction where the protein is the enzyme EC 2.7.9.2 is partof the mechanism of that enzyme.','Phosphoenolpyruvate + protein L-histidine = pyruvate + protein N(pi)-phospho-L-histidine.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 2.7.3.10','Agmatine kinase','Phosphagen phosphokinase','NULL','L-arginine can act as acceptor, more slowly.','ATP + agmatine = ADP + N(4)-phosphoagmatine.', 'NULL', 'NULL');
