INSERT INTO enzyme VALUES ( 'EC 2.7.4.4','Nucleoside-phosphate kinase','NMP-kinase','NULL','Many nucleotides can act as acceptors.Other nucleoside triphosphates can act instead of ATP.','ATP + nucleoside phosphate = ADP + nucleoside diphosphate.', 'NULL', 'NULL');
