INSERT INTO enzyme VALUES ( 'EC 2.7.4.6','Nucleoside-diphosphate kinase','NDK','NULL','Many nucleoside diphosphates can act as acceptors.Many ribo- and deoxyribonucleoside triphosphates can act as donors.','ATP + nucleoside diphosphate = ADP + nucleoside triphosphate.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 2.7.4.7','Phosphomethylpyrimidine kinase','Hydroxymethylpyrimidine phosphokinase','NULL','NULL','ATP + 4-amino-2-methyl-5-phosphomethylpyrimidine = ADP + 4-amino-2-methyl-5-diphosphomethylpyrimidine.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 2.7.4.8','Guanylate kinase','Deoxyguanylate kinase','NULL','dGMP can also act as acceptor.dATP can act as donor.','ATP + GMP = ADP + GDP.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 2.7.4.9','dTMP kinase','Thymidylate kinase','NULL','NULL','ATP + dTMP = ADP + dTDP.', 'NULL', 'NULL');
