INSERT INTO enzyme VALUES ( 'EC 2.7.4.n2','([Pyruvate, water dikinase] phosphate) phosphotransferase','PSRP','NULL','The enzyme from the bacterium Escherichia coli is bifunctional andalso catalyzes the phosphorylation of pyruvate, water dikinase(EC 2.7.9.2), cf. EC 2.7.11.33.Formerly EC 2.7.4.n2.','[Pyruvate, water dikinase] phosphate + phosphate = [pyruvate, waterdikinase] + diphosphate.', 'NULL', 'NULL');
