INSERT INTO enzyme VALUES ( 'EC 2.7.6.1','Ribose-phosphate diphosphokinase','Phosphoribosyl diphosphate synthetase','NULL','dATP can also act as donor.','ATP + D-ribose 5-phosphate = AMP + 5-phospho-alpha-D-ribose1-diphosphate.', 'NULL', 'NULL');
