INSERT INTO enzyme VALUES ( 'EC 2.7.7.22','Mannose-1-phosphate guanylyltransferase (GDP)','GDP-mannose phosphorylase','NULL','NULL','GDP + alpha-D-mannose 1-phosphate = phosphate + GDP-mannose.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 2.7.7.23','UDP-N-acetylglucosamine diphosphorylase','N-acetylglucosamine-1-phosphate uridyltransferase','NULL','Part of the pathway for acetamido sugar biosynthesis in bacteria andarchaea.The enzyme from several bacteria (e.g., Escherichia coli, Bacillussubtilis and Hemophilus influenzae) has been shown to be bifunctionaland also to possess the activity of EC 2.3.1.157.The enzyme from plants and animals is also active toward N-acetyl-alpha-D-galactosamine 1-phosphate (cf. EC 2.7.7.83), while thebacterial enzyme shows low activity toward that substrate.','UTP + N-acetyl-alpha-D-glucosamine 1-phosphate = diphosphate + UDP-N-acetyl-alpha-D-glucosamine.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 2.7.7.24','Glucose-1-phosphate thymidylyltransferase','dTDP-glucose diphosphorylase','NULL','NULL','dTTP + alpha-D-glucose 1-phosphate = diphosphate + dTDP-alpha-D-glucose.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 2.7.7.25','Transferred entry: 2','NULL','NULL','NULL','NULL', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 2.7.7.26','Transferred entry: 3','NULL','NULL','NULL','NULL', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 2.7.7.27','Glucose-1-phosphate adenylyltransferase','ADP-glucose diphosphorylase','NULL','NULL','ATP + alpha-D-glucose 1-phosphate = diphosphate + ADP-glucose.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 2.7.7.28','Nucleoside-triphosphate-aldose-1-phosphate nucleotidyltransferase','GDP hexose pyrophosphorylase','NULL','In decreasing order of activity, guanosine, inosine and adenosinediphosphate hexoses are substrates in the reverse reaction, witheither glucose or mannose as the sugar.Formerly EC 2.7.7.29.','Nucleoside triphosphate + alpha-D-aldose 1-phosphate = diphosphate + NDP-hexose.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 2.7.7.29','Transferred entry: 2','NULL','NULL','NULL','NULL', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 2.7.7.30','Fucose-1-phosphate guanylyltransferase','GDP-fucose diphosphorylase','NULL','NULL','GTP + beta-L-fucose 1-phosphate = diphosphate + GDP-L-fucose.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 2.7.7.31','DNA nucleotidylexotransferase','Terminal addition enzyme','NULL','Catalyzes template-independent extension of the 3-end of a DNAstrand by one nucleotide at a time.Cannot initiate a chain de novo.Nucleoside may be ribo- or deoxyribo-.','Deoxynucleoside triphosphate + DNA(n) = diphosphate + DNA(n+1).', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 2.7.7.32','Galactose-1-phosphate thymidylyltransferase','dTDP-galactose diphosphorylase','NULL','NULL','dTTP + alpha-D-galactose 1-phosphate = diphosphate + dTDP-galactose.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 2.7.7.33','Glucose-1-phosphate cytidylyltransferase','CDP-glucose diphosphorylase','NULL','NULL','CTP + alpha-D-glucose 1-phosphate = diphosphate + CDP-glucose.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 2.7.7.34','Glucose-1-phosphate guanylyltransferase','GDP-glucose diphosphorylase','NULL','Also acts, more slowly, on D-mannose 1-phosphate.','GTP + alpha-D-glucose 1-phosphate = diphosphate + GDP-glucose.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 2.7.7.35','Ribose-5-phosphate adenylyltransferase','ADP-ribose phosphorylase','NULL','NULL','ADP + D-ribose 5-phosphate = phosphate + ADP-D-ribose.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 2.7.7.36','Aldose-1-phosphate adenylyltransferase','ADP-aldose phosphorylase','NULL','NULL','ADP + alpha-D-aldose 1-phosphate = phosphate + ADP-aldose.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 2.7.7.37','Aldose-1-phosphate nucleotidyltransferase','NDP-aldose phosphorylase','NULL','The enzyme works on a variety of alpha-D-aldose 1-phosphates andbeta-L-aldose 1-phosphates (which have the same anomericconfiguration as the former).','NDP + alpha-D-aldose 1-phosphate = phosphate + NDP-aldose.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 2.7.7.38','3-deoxy-manno-octulosonate cytidylyltransferase','CMP-2-keto-3-deoxyoctulosonic acid synthetase','NULL','NULL','CTP + 3-deoxy-D-manno-octulosonate = diphosphate + CMP-3-deoxy-D-manno-octulosonate.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 2.7.7.39','Glycerol-3-phosphate cytidylyltransferase','CDP-glycerol diphosphorylase','NULL','NULL','CTP + sn-glycerol 3-phosphate = diphosphate + CDP-glycerol.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 2.7.7.40','D-ribitol-5-phosphate cytidylyltransferase','CDP-ribitol diphosphorylase','NULL','NULL','CTP + D-ribitol 5-phosphate = diphosphate + CDP-ribitol.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 2.7.7.41','Phosphatidate cytidylyltransferase','CDP-diacylglycerol synthase','NULL','NULL','CTP + phosphatidate = diphosphate + CDP-diacylglycerol.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 2.7.7.42','[Glutamate--ammonia-ligase] adenylyltransferase','Glutamate-ammonia-ligase adenylyltransferase','NULL','NULL','ATP + [L-glutamate:ammonia ligase (ADP-forming)] = diphosphate +adenylyl-[L-glutamate:ammonia ligase (ADP-forming)].', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 2.7.7.43','N-acylneuraminate cytidylyltransferase','CMP-N-acetylneuraminic acid synthetase','NULL','Acts on N-acetyl- and N-glycolyl- derivatives.','CTP + N-acylneuraminate = diphosphate + CMP-N-acylneuraminate.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 2.7.7.44','Glucuronate-1-phosphate uridylyltransferase','NULL','NULL','Also acts slowly with CTP.','UTP + 1-phospho-alpha-D-glucuronate = diphosphate + UDP-glucuronate.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 2.7.7.45','Guanosine-triphosphate guanylyltransferase','NULL','NULL','Also acts, more slowly, on GDP to form P(1),P(3)-bis(5-guanosyl)triphosphate.','2 GTP = diphosphate + P(1),P(4)-bis(5-guanosyl) tetraphosphate.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 2.7.7.46','Gentamicin 2-nucleotidyltransferase','2-aminoglycoside nucleotidyltransferase','NULL','ATP, dATP, CTP, ITP and GTP can act as donors.Kanamycin, tobramycin and sisomicin can also act as acceptors.The nucleotidyl residue is transferred to the 2-hydroxy group of the3-amino-3-deoxy-D-glucose moiety in the antibiotic.','Nucleoside triphosphate + gentamicin = diphosphate +2-nucleotidylgentamicin.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 2.7.7.47','Streptomycin 3-adenylyltransferase','NULL','NULL','Also acts on spectinomycin.','ATP + streptomycin = diphosphate + 3-adenylylstreptomycin.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 2.7.7.48','RNA-directed RNA polymerase','RNA nucleotidyltransferase (RNA-directed)','NULL','Catalyzes RNA-template-directed extension of the 3-end of an RNAstrand by one nucleotide at a time.Can initiate a chain de novo.See also EC 2.7.7.6.','Nucleoside triphosphate + RNA(n) = diphosphate + RNA(n+1).', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 2.7.7.49','RNA-directed DNA polymerase','DNA nucleotidyltransferase (RNA-directed)','NULL','Catalyzes RNA-template-directed extension of the 3-end of a DNAstrand by one deoxynucleotide at a time.Cannot initiate a chain de novo.Requires a RNA or DNA primer.DNA can also serve as template.See also EC 2.7.7.7.','Deoxynucleoside triphosphate + DNA(n) = diphosphate + DNA(n+1).', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 2.7.7.50','mRNA guanylyltransferase','GTP--RNA guanylyltransferase','NULL','Can modify also synthetic poly(A) and poly(G) to form the structuresm(7)G(5)pppAn and m(7)G(5)pppGn.','GTP + (5)pp-Pur-mRNA = diphosphate + G(5)ppp-Pur-mRNA.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 2.7.7.51','Adenylylsulfate--ammonia adenylyltransferase','NULL','NULL','NULL','Adenylyl sulfate + NH(3) = adenosine 5-phosphoramidate + sulfate.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 2.7.7.52','RNA uridylyltransferase','Terminal uridylyltransferase','NULL','Requires an oligoribonucleotide or polyribonucleotide with a freeterminal 3-OH as a primer.','UTP + RNA(n) = diphosphate + RNA(n+1).', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 2.7.7.53','ATP adenylyltransferase','AP-4-A phosphorylase','NULL','GTP and adenosine tetraphosphate can also act as adenylyl acceptors.','ADP + ATP = phosphate + P(1),P(4)-bis(5-adenosyl) tetraphosphate.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 2.7.7.54','Transferred entry: 6','NULL','NULL','NULL','NULL', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 2.7.7.55','Transferred entry: 6','NULL','NULL','NULL','NULL', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 2.7.7.56','tRNA nucleotidyltransferase','Phosphate-dependent exonuclease','NULL','Brings about the final exonucleolytic trimming of the 3-terminus oftRNA precursors in Escherichia coli by a phosphorolysis, producing amature 3-terminus in tRNA and nucleoside diphosphate.Not identical with EC 2.7.7.8.','tRNA(n+1) + phosphate = tRNA(n) + a nucleoside diphosphate.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 2.7.7.57','N-methylphosphoethanolamine cytidylyltransferase','NULL','NULL','NULL','CTP + N-methylethanolamine phosphate = diphosphate + CDP-N-methylethanolamine.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 2.7.7.58','(2,3-dihydroxybenzoyl)adenylate synthase','2,3-dihydroxybenzoate-AMP ligase','NULL','NULL','ATP + 2,3-dihydroxybenzoate = diphosphate + (2,3-dihydroxybenzoyl)adenylate.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 2.7.7.59','[Protein-PII] uridylyltransferase','PII uridylyl-transferase','NULL','The enzyme uridylylates and de-uridylylates the small trimericprotein PII.','UTP + [protein-PII] = diphosphate + uridylyl-[protein-PII].', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 2.7.7.60','2-C-methyl-D-erythritol 4-phosphate cytidylyltransferase','4-diphosphocytidyl-2-C-methyl-D-erythritol synthase','Manganese or magnesium.','ATP or UTP can replace CTP, but both are less effective.GTP and TTP are not substrates.Forms part of an alternative nonmevalonate pathway for terpenoidbiosynthesis.','CTP + 2-C-methyl-D-erythritol 4-phosphate = diphosphate + 4-(cytidine5-diphospho)-2-C-methyl-D-erythritol.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 2.7.7.61','Citrate lyase holo-[acyl-carrier-protein] synthase','2-(5-phosphoribosyl)-3-dephospho-CoA transferase','NULL','The gamma-subunit of EC 4.1.3.6 serves as an acyl-carrier protein(ACP) and contains the prosthetic group 2-(5-triphosphoribosyl)-3-dephospho-CoA.Synthesis and attachment of the prosthetic group requires theconcerted action of this enzyme and EC 2.4.2.52.In the enzyme from Escherichia coli, the prosthetic group is attachedto serine-14 of the ACP via a phosphodiester bond.','2-(5-triphosphoribosyl)-3-dephospho-CoA + citrate lyase apo-[acyl-carrier-protein] = citrate lyase holo-[acyl-carrier-protein] +diphosphate.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 2.7.7.62','Adenosylcobinamide-phosphate guanylyltransferase','Adenosylcobinamide kinase/adenosylcobinamide-phosphateguanylyltransferase','NULL','In Salmonella typhimurium LT2, under anaerobic conditions, CobU(EC 2.7.7.62 and EC 2.7.1.156), CobT (EC 2.4.2.21),CobC (EC 3.1.3.73) and CobS (EC 2.7.8.26) catalyze reactions in thenucleotide loop assembly pathway, which convert adenosylcobinamide(AdoCbi) into adenosylcobalamin (AdoCbl).CobT and CobC are involved in 5,6-dimethylbenzimidazole activationwhereby 5,6-dimethylbenzimidazole is converted to its riboside,alpha-ribazole.The second branch of the nuclotide loop assembly pathway is thecobinamide (Cbi) activation branch where AdoCbi oradenosylcobinamide-phosphate is converted to the activatedintermediate AdoCbi-GDP by the bifunctional enzyme CobU.The final step in adenosylcobalamin biosynthesis is the condensationof AdoCbi-GDP with alpha-ribazole, which is catalyzed by CobS(EC 2.7.8.26), to yield adenosylcobalamin.CobU is a bifunctional enzyme that has both kinase (EC 2.7.1.156) andguanylyltransferase (EC 2.7.7.62) activities.However, both activities are not required at all times.The kinase activity has been proposed to function only whenS.typhimurium is assimilating cobinamide whereas theguanylyltransferase activity is required for both assimilation ofexogenous cobinamide and for de novo synthesis of adenosylcobalamin.The guanylyltransferase reaction is a two-stage reaction withformation of a CobU-GMP intermediate.','GTP + adenosylcobinamide phosphate = diphosphate + adenosylcobinamide-GDP.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 2.7.7.63','Lipoate--protein ligase','Lipoate protein ligase','Magnesium.','Both 6S- and 6R-lipoates can act as substrates but there is apreference for the naturally occurring R-form.Selenolipoate, i.e. 5-(1,2-diselenolan-3-yl)pentanoic acid,and 6-sulfanyloctanoate can also act as substrates, but more slowly.Responsible for lipoylation in the presence of exogenous lipoic acid.Lipoylation is essential for the function of several key enzymesinvolved in oxidative metabolism, including pyruvate dehydrogenase(E(2) domain), 2-oxoglutarate dehydrogenase (E(2) domain),the branched-chain 2-oxoacid dehydrogenases and the glycine cleavagesystem (H protein).Attaches lipoic acid to the lipoyl domains of these proteins.It is likely that an alternative pathway, involving EC 2.3.1.181 andEC 2.8.1.8 is the normal route for lipoylation.','(1) ATP + lipoate = diphosphate + lipoyl-AMP.(2) Lipoyl-AMP + protein = protein N(6)-(lipoyl)lysine + AMP.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 2.7.7.64','UTP-monosaccharide-1-phosphate uridylyltransferase','UDP-sugar pyrophosphorylase','Manganese or magnesium.','The reaction can occur in either direction and it has been postulatedthat MgUTP and Mg-diphosphate are the actual substrates.Catalyzes the formation of UDP-Glc, UDP-Gal, UDP-GlcA, UDP-L-Ara andUDP-Xyl, showing broad substrate specificity toward monosaccharide1-phosphates.Mannose 1-phosphate, L-fucose 1-phosphate and glucose 6-phosphate arenot substrates and UTP cannot be replaced by other nucleotidetriphosphates.','UTP + a monosaccharide 1-phosphate = diphosphate + UDP-monosaccharide.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 2.7.7.65','Diguanylate cyclase','DGC','Magnesium; Manganese.','Can be activated by BeF3, a phosphoryl mimic, which results indimerization.Dimerization is required but is not sufficient for diguanylate-cyclase activity.Cyclic di-3,5-guanylate is an intracellular signaling molecule thatcontrols motility and adhesion in bacterial cells; it was firstidentified as having a positive allosteric effect on EC 2.4.1.12.','2 GTP = 2 diphosphate + cyclic di-3,5-guanylate.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 2.7.7.66','Malonate decarboxylase holo-[acyl-carrier-protein] synthase','Holo ACP synthase','NULL','The delta subunit of malonate decarboxylase serves as an an acyl-carrier protein (ACP) and contains the prosthetic group2-(5-triphosphoribosyl)-3-dephospho-CoA.Two reactions are involved in the production of the holo-ACP form ofthis enzyme.The first reaction is catalyzed by EC 2.4.2.52.The resulting prosthetic group is then attached to the ACP subunitvia a phosphodiester linkage to a serine residue, thus forming theholo form of the enzyme, in a manner analogous to that ofEC 2.7.7.61.','2-(5-triphosphoribosyl)-3-dephospho-CoA + malonate decarboxylase apo-[acyl-carrier-protein] = malonate decarboxylase holo-[acyl-carrier-protein] + diphosphate.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 2.7.7.67','CDP-archaeol synthase','NULL','Magnesium; Potassium.','Catalyzes one of the steps in the biosynthesis of polar lipids inarchaea, which are characterized by having an sn-glycero-1-phosphatebackbone rather than an sn-glycero-3-phosphate backbone as is foundin bacteria and eukaryotes.The other enzymes involved in the biosynthesis of polar lipids inarchaea are EC 1.1.1.261, EC 2.5.1.41 and EC 2.5.1.42, which togetheralkylate the hydroxy groups of glycerol 1-phosphate to giveunsaturated archaetidic acid, which is acted upon by this enzyme toform CDP-unsaturated archaeol.The final step in the pathway involves the addition of L-serine, withconcomitant removal of CMP, leading to the production of unsaturatedarchaetidylserine.Formerly EC 2.7.7.n2.','CTP + 2,3-bis-O-(geranylgeranyl)-sn-glycero-1-phosphate = diphosphate +CDP-2,3-bis-O-(geranylgeranyl)-sn-glycerol.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 2.7.7.68','2-phospho-L-lactate guanylyltransferase','NULL','NULL','This enzyme is involved in the biosynthesis of coenzyme F420,a redox-active cofactor found in all methanogenic archaea, as well assome eubacteria.','(2S)-2-phospholactate + GTP = (2S)-lactyl-2-diphospho-5-guanosine +diphosphate.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 2.7.7.69','GDP-L-galactose phosphorylase','NULL','NULL','The enzyme catalyzes a reaction of the Smirnoff-Wheeler pathway,the major route to ascorbate biosynthesis in plants.','GDP-L-galactose + phosphate = alpha-L-galactose 1-phosphate + GDP.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 2.7.7.70','D-glycero-beta-D-manno-heptose 1-phosphate adenylyltransferase','D-glycero-D-manno-heptose-1-beta-phosphate adenylyltransferase','NULL','The bifunctional protein hldE includes D-glycero-beta-D-manno-heptose-7-phosphate kinase and D-glycero-beta-D-manno-heptose1-phosphate adenylyltransferase activity (cf. EC 2.7.1.167).The enzyme is involved in biosynthesis of ADP-L-glycero-beta-D-manno-heptose, which is utilized for assembly of the lipopolysaccharideinner core in Gram-negative bacteria.','D-glycero-beta-D-manno-heptose 1-phosphate + ATP = ADP-D-glycero-beta-D-manno-heptose + diphosphate.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 2.7.7.71','D-glycero-alpha-D-manno-heptose 1-phosphate guanylyltransferase','NULL','NULL','The enzyme is involved in biosynthesis of GDP-D-glycero-alpha-D-manno-heptose, which is required for assembly of S-layer glycoproteinin some Gram-positive bacteria.','D-glycero-alpha-D-manno-heptose 1-phosphate + GTP = GDP-D-glycero-alpha-D-manno-heptose + diphosphate.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 2.7.7.72','CCA tRNA nucleotidyltransferase','CCA-adding enzyme','NULL','The acylation of all tRNAs with an amino acid occurs at the terminalribose of a 3 CCA sequence.The CCA sequence is added to the tRNA precursor by stepwisenucleotide addition performed by a single enzyme that is ubiquitousin all living organisms.Although the enzyme has the option of releasing the product aftereach addition, it prefers to stay bound to the product and proceedwith the next addition.Formerly EC 2.7.7.21 and EC 2.7.7.25.','A tRNA precursor + 2 CTP + ATP = a tRNA with a 3 CCA end +3 diphosphate.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 2.7.7.73','Sulfur carrier protein ThiS adenylyltransferase','NULL','Zinc.','The enzyme catalyzes the adenylation of ThiS, a sulfur carrierprotein involved in the biosynthesis of thiamine.The enzyme shows significant structural similarity to ubiquitin-activating enzyme.In Escherichia coli, but not in Bacillus subtilis, the enzyme forms across link from Cys-184 to the ThiS carboxy terminus (the positionthat is also thiolated) via an acyldisulfide.Formerly EC 2.7.7.n3.','ATP + [ThiS] = diphosphate + adenylyl-[ThiS].', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 2.7.7.74','1L-myo-inositol 1-phosphate cytidylyltransferase','IPCT','NULL','In many organisms this activity is catalyzed by a bifunctionalenzyme.The cytidylyltransferase domain of the bifunctionalEC 2.7.7.74/EC 2.7.8.34 (CTP:inositol-1-phosphatecytidylyltransferase/CDP-inositol:inositol-1-phosphate transferase)is absolutely specific for CTP and 1L-myo-inositol 1-phosphate.The enzyme is involved in biosynthesis of bis(1L-myo-inositol)1,3-phosphate, a widespread organic solute in microorganisms adaptedto hot environments.In many organisms this activity is catalyzed by a bifunctionalenzyme.The cytidylyltransferase domain of the bifunctionalEC 2.7.7.74/EC 2.7.8.34 is absolutely specific for CTP and 1L-myo-inositol 1-phosphate.The enzyme is involved in biosynthesis of bis(1L-myo-inositol)1,3-phosphate, a widespread organic solute in microorganisms adaptedto hot environments.','CTP + 1L-myo-inositol 1-phosphate = diphosphate + CDP-1L-myo-inositol.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 2.7.7.75','Molybdopterin adenylyltransferase','NULL','Manganese or magnesium.','Catalyzes the activation of molybdopterin for molybdenum insertion.In eukaryotes, this reaction is catalyzed by the C-terminal domain ofa fusion protein that also includes molybdopterin molybdotransferase(EC 2.10.1.1).The reaction requires a divalent cation such as Mg(2+) or Mn(2+).Catalyzes the activation of molybdopterin for molybdenum insertion.In eukaryotes, this reaction is catalyzed by the C-terminal domain ofa fusion protein that also includes EC 2.10.1.1.Formerly EC 2.7.7.n5.','ATP + molybdopterin = diphosphate + adenylyl-molybdopterin.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 2.7.7.76','Molybdenum cofactor cytidylyltransferase','Mo-MPT cytidyltransferase','Manganese or magnesium.','Catalyzes the cytidylation of the molybdenum cofactor.This modification occurs only in prokaryotes.ATP or GTP cannot replace CTP.','CTP + molybdenum cofactor = diphosphate + cytidylyl molybdenum cofactor.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 2.7.7.77','Molybdenum cofactor guanylyltransferase','MoCo guanylyltransferase','NULL','Catalyzes the guanylation of the molybdenum cofactor.This modification occurs only in prokaryotes.','GTP + molybdenum cofactor = diphosphate + guanylyl molybdenum cofactor.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 2.7.7.78','GDP-D-glucose phosphorylase','NULL','NULL','The enzyme may be involved in prevention of misincorporation ofglucose in place of mannose residues into glycoconjugates i.e. toremove accidentally produced GDP-alpha-D-glucose.Activities with GDP-L-galactose, GDP-D-mannose and UDP-D-glucose areall less than 3% that with GDP-D-glucose.','GDP-alpha-D-glucose + phosphate = alpha-D-glucose 1-phosphate + GDP.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 2.7.7.79','tRNA(His) guanylyltransferase','Histidine tRNA guanylyltransferase','NULL','In eukarya an additional guanosine residue is added post-transcriptionally to the 5-end of tRNA(His) molecules.The addition occurs opposite a universally conserved adenosine(73)and is thus the result of a non-templated 3-5 addition reaction.The additional guanosine residue is an important determinant foraminoacylation by EC 6.1.1.21.The enzyme requires a divalent cation for activity.ATP activation is not required when the substrate contains a5-triphosphate (ppp-tRNA(His)).','p-tRNA(His) + ATP + GTP = pppG-P-tRNA(His) + AMP + diphosphate.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 2.7.7.80','Molybdopterin-synthase adenylyltransferase','NULL','NULL','Adenylates the C-terminus of the small subunit of the molybdopterinsynthase.This activation is required to form the thiocarboxylated C-terminusof the active molybdopterin synthase small subunit.The reaction occurs in prokaryotes and eukaryotes.In the human, the reaction is catalyzed by the N-terminal domain ofthe protein MOCS3, which also includes a molybdopterin-synthasesulfurtransferase (EC 2.8.1.11) C-terminal domain.Formerly EC 2.7.7.n4.','ATP + [molybdopterin-synthase sulfur-carrier protein]-Gly-Gly =diphosphate + [molybdopterin-synthase sulfur-carrier protein]-Gly-Gly-AMP.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 2.7.7.81','Pseudaminic acid cytidylyltransferase','NULL','Magnesium.','NULL','CTP + 5,7-diacetamido-3,5,7,9-tetradeoxy-L-glycero-alpha-L-manno-2-nonulopyranosonic acid = diphosphate + CMP-5,7-diacetamido-3,5,7,9-tetradeoxy-L-glycero-alpha-L-manno-2-nonulopyranosonic acid.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 2.7.7.82','CMP-N,N-diacetyllegionaminic acid synthase','NULL','NULL','Isolated from the bacteria Legionella pneumophila and Campylobacterjejuni.Involved in biosynthesis of legionaminic acid, a sialic acid-likederivative that is incorporated into virulence-associated cellsurface glycoconjugates which may include lipopolysaccharide (LPS),capsular polysaccharide, pili and flagella.','CTP + N,N-diacetyllegionaminate = CMP-N,N-diacetyllegionaminate +diphosphate.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 2.7.7.83','UDP-N-acetylgalactosamine diphosphorylase','NULL','NULL','The enzyme from plants and animals also has activity toward N-acetyl-alpha-D-glucosamine 1-phosphate (cf. EC 2.7.7.23).','UTP + N-acetyl-alpha-D-galactosamine 1-phosphate = diphosphate + UDP-N-acetyl-alpha-D-galactosamine.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 2.7.7.84','2-5 oligoadenylate synthase','NULL','NULL','The enzyme is activated by binding to double-stranded RNA.The resulting product binds to and activates RNase L, whichsubsequently degrades the RNA.Oligoadenylates of chain lengths 2, 4 and 5 are also produced.The dimer does not have any known biological activity.','3 ATP = pppA2p5A2p5A + 2 diphosphate.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 2.7.7.85','Diadenylate cyclase','Cyclic-di-AMP synthase','NULL','Cyclic di-3,5-adenylate is a bioactive molecule produced by somebacteria and archaea, which may function as a secondary signalingmolecule.The intracellular bacterial pathogen Listeria monocytogenes secretesit into the hosts cytosol, where it triggers a cytosolic pathway ofinnate immunity.','2 ATP = 2 diphosphate + cyclic di-3,5-adenylate.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 2.7.7.86','Cyclic GMP-AMP synthase','NULL','NULL','Cyclic 3,5-AMP-GMP is a signaling molecule in mammalian cells thattriggers the production of type I interferons and other cytokines.','ATP + GTP = 2 diphosphate + cyclic 3,5-AMP-GMP.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 2.7.7.87','L-threonylcarbamoyladenylate synthase','NULL','NULL','The enzyme is involved in the synthesis of N(6)-threonylcarbamoyladenosine(37) in tRNAs with the anticodon NNU, i.e.tRNA(Ile), tRNA(Thr), tRNA(Asn), tRNA(Lys), tRNA(Ser) and tRNA(Arg).','L-threonine + ATP + bicarbonate = L-threonylcarbamoyladenylate +diphosphate + H(2)O.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 2.7.7.n1','Adenosine monophosphate-protein transferase','NULL','NULL','NULL','ATP + [protein] = diphosphate + [protein]-AMP.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 2.7.7.n2','Transferred entry: 2','NULL','NULL','NULL','NULL', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 2.7.7.n3','Transferred entry: 2','NULL','NULL','NULL','NULL', 'NULL', 'NULL');
