INSERT INTO enzyme VALUES ( 'EC 3.1.4.1','Phosphodiesterase I','5 nucleotide phosphodiesterase/alkaline phosphodiesterase I','NULL','Hydrolyzes both ribonucleotides and deoxyribonucleotides.Has low activity toward polynucleotides.A 3-phosphate terminus on the substrate inhibits hydrolysis.','Hydrolytically removes 5-nucleotides successively from the 3-hydroxytermini of 3-hydroxy-terminated oligonucleotides.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 3.1.4.2','Glycerophosphocholine phosphodiesterase','NULL','NULL','Also acts on sn-glycero-3-phosphoethanolamine.','sn-glycero-3-phosphocholine + H(2)O = choline + sn-glycerol 3-phosphate.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 3.1.4.3','Phospholipase C','Clostridium oedematiens beta- and gamma-toxins','Zinc.','The bacterial enzyme also acts on sphingomyelin andphosphatidylinositol.The enzyme from seminal plasma does not act on phosphatidylinositol.','A phosphatidylcholine + H(2)O = 1,2-diacyl-sn-glycerol + phosphocholine.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 3.1.4.4','Phospholipase D','Choline phosphatase','NULL','Also acts on other phosphatidyl esters.','A phosphatidylcholine + H(2)O = choline + a phosphatidate.', 'NULL', 'NULL');
