INSERT INTO enzyme VALUES ( 'EC 3.1.26.5','Ribonuclease P','NULL','NULL','Essential for tRNA processing; generates 5-termini of mature tRNAmolecules.','Endonucleolytic cleavage of RNA, removing 5-extranucleotides from tRNAprecursor.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 3.1.26.6','Ribonuclease IV','Endoribonuclease IV','NULL','Forms oligonucleotides with an average chain length of 10.','Endonucleolytic cleavage of poly(A) to fragments terminated by 3-hydroxyand 5-phosphate groups.', 'NULL', 'NULL');
