INSERT INTO enzyme VALUES ( 'EC 3.2.1.124','3-deoxy-2-octulosonidase','NULL','NULL','The enzyme from a bacteriophage catalyzes the depolymerization ofcapsular polysaccharides containing 3-deoxy-2-octulosonide in thecell wall of Escherichia coli.','Endohydrolysis of the beta-ketopyranosidic linkages of 3-deoxy-D-manno-2-octulosonate in capsular polysaccharides.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 3.2.1.125','Raucaffricine beta-glucosidase','NULL','NULL','Highly specific; some other ajmalan glucoside alkaloids arehydrolyzed, more slowly.','Raucaffricine + H(2)O = D-glucose + vomilenine.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 3.2.1.126','Coniferin beta-glucosidase','NULL','NULL','Also hydrolyzes syringin, 4-cinnamyl alcohol beta-glucoside and, moreslowly, some other aryl beta-glycosides.A plant cell-wall enzyme involved in the biosynthesis of lignin.','Coniferin + H(2)O = D-glucose + coniferol.', 'NULL', 'NULL');
