INSERT INTO enzyme VALUES ( 'EC 3.2.1.143','Poly(ADP-ribose) glycohydrolase','NULL','NULL','Specific to (1-2) linkage of ribose-ribose bond of poly(ADP-D-ribose).','Hydrolyzes poly(ADP-D-ribose) at glycosidic (1-2) linkage of ribose-ribose bond to produce free ADP-D-ribose.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 3.2.1.144','3-deoxyoctulosonase','Alpha-Kdo-ase','NULL','Releases Kdo (alpha- and beta-linked 3-deoxy-D-manno-octulosonicacid) from different lipopolysaccharides, including Re-LPS fromEscherichia coli and Salmonella, Rd-LPS from S.minnesota, and de-O-acyl-re-LPS.4-methylumbelliferyl-alpha-Kdo (alpha-Kdo-OMec) is also a substrate.','3-deoxyoctulosonyl-lipopolysaccharide + H(2)O = 3-deoxyoctulosonic acid +lipopolysaccharide.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 3.2.1.145','Galactan 1,3-beta-galactosidase','NULL','NULL','This enzyme removes not only free galactose, but also 6-glycosylatedresidues, e.g., (1-','Hydrolysis of terminal, non-reducing beta-D-galactose residues in (1-', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 3.2.1.146','Beta-galactofuranosidase','Beta-D-galactofuranosidase','NULL','The enzyme from Helminthosporium sacchari detoxifieshelminthosporoside, a bis(digalactosyl)terpene produced by thisfungus, by releasing its four molecules of bound galactose.','Hydrolysis of terminal non-reducing beta-D-galactofuranosides, releasinggalactose.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 3.2.1.147','Thioglucosidase','Myrosinase','NULL','Has a wide specificity for thioglycosides.Formerly EC 3.2.3.1.','A thioglucoside + H(2)O = a sugar + a thiol.', 'NULL', 'NULL');
