INSERT INTO enzyme VALUES ( 'EC 3.4.11.9','Xaa-Pro aminopeptidase','Aminoacylproline aminopeptidase','Manganese or cobalt.','Generally membrane bound enzyme present in both mammalian andbacterial cells.Belongs to peptidase family M24B.','Release of any N-terminal amino acid, including proline, that is linkedto proline, even from a dipeptide or tripeptide.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 3.4.11.10','Bacterial leucyl aminopeptidase','Aeromonas proteolytica aminopeptidase','Zinc.','Similar aminopeptidases were isolated from Escherichia coli andStaphylococcus thermophilus.Belongs to peptidase families M17 and M28.','Release of an N-terminal amino acid, preferentially leucine, but notglutamic or aspartic acids.', 'NULL', 'NULL');
