INSERT INTO enzyme VALUES ( 'EC 3.4.21.19','Glutamyl endopeptidase','Endoproteinase Glu-C','NULL','In appropriate buffer the specificity is restricted to Glu-|-Xaa.Bonds involving bulky side-chains of hydrophobic amino acids arecleaved at a lower rate.Belongs to peptidase family S1B.','Preferential cleavage: Glu-|-Xaa, Asp-|-Xaa.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 3.4.21.20','Cathepsin G','NULL','NULL','From azurophil granules of polymorphonuclear leukocytes.Belongs to peptidase family S1.','Specificity similar to chymotrypsin C.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 3.4.21.21','Coagulation factor VIIa','NULL','NULL','Formed from the precursor factor VII.The cattle enzyme is more readily inhibited by diisopropylfluorophosphate than the human.Belongs to peptidase family S1.','Selective cleavage of Arg-|-Ile bond in factor X to form factor Xa.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 3.4.21.22','Coagulation factor IXa','Christmas factor','NULL','The proenzyme factor IX is activated by EC 3.4.21.27.Belongs to peptidase family S1.','Selective cleavage of Arg-|-Ile bond in factor X to form factor Xa.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 3.4.21.23','Deleted entry','NULL','NULL','NULL','NULL', 'NULL', 'NULL');
