INSERT INTO enzyme VALUES ( 'EC 3.4.21.25','Cucumisin','NULL','NULL','From the sarcocarp of the musk melon (Cucumis melo).Other endopeptidases from plants, which are less well characterizedbut presumably of serine-type, include euphorbain from Euphorbiacerifera, solanain from horse-nettle Solanum elaeagnifolium, hurainfrom Hura crepitans and tabernamontanain from Tabernamontanagrandiflora.Belongs to peptidase family S8.','Hydrolysis of proteins with broad specificity.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 3.4.21.26','Prolyl oligopeptidase','Post-proline cleaving enzyme','NULL','Found in vertebrates, plants and Flavobacterium.Generally cytosolic, commonly activated by thiol compounds.Belongs to peptidase family S9A.Formerly EC 3.4.22.18.','Hydrolysis of Pro-|-Xaa ', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 3.4.21.27','Coagulation factor XIa','Plasma thromboplastin antecedent','NULL','The proenzyme XI is activated by EC 3.4.21.38.Belongs to peptidase family S1.','Selective cleavage of Arg-|-Ala and Arg-|-Val bonds in factor IX to formfactor IXa.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 3.4.21.28','Transferred entry: 3','NULL','NULL','NULL','NULL', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 3.4.21.29','Transferred entry: 3','NULL','NULL','NULL','NULL', 'NULL', 'NULL');
