INSERT INTO enzyme VALUES ( 'EC 3.4.22.5','Transferred entry: 3','NULL','NULL','NULL','NULL', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 3.4.22.6','Chymopapain','Papaya proteinase II','NULL','The major endopeptidase of papaya (Carica papaya) latex.It has multiple chromatographic forms.Belongs to peptidase family C1.Formerly EC 3.4.4.11.','Specificity similar to that of papain.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 3.4.22.7','Asclepain','NULL','NULL','From the latex of milkweed, Asclepias syriaca.It has multiple forms.Belongs to peptidase family C1.','Specificity similar to that of papain.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 3.4.22.8','Clostripain','Clostridiopeptidase B','NULL','From the bacterium Clostridium histolyticum.Activated by calcium; inhibited by EDTA.Belongs to peptidase family C11.Formerly EC 3.4.4.20.','Preferential cleavage: Arg-|-Xaa, including Arg-|-Pro bond, but notLys-|-Xaa.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 3.4.22.9','Transferred entry: 3','NULL','NULL','NULL','NULL', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 3.4.22.10','Streptopain','Streptococcal cysteine proteinase','NULL','From the bacterium, group A Streptococcus.Formed from the proenzyme by limited proteolysis.Belongs to peptidase family C10.Formerly EC 3.4.4.18.','Preferential cleavage with hydrophobic residues at P2, P1 and P1.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 3.4.22.11','Transferred entry: 3','NULL','NULL','NULL','NULL', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 3.4.22.12','Transferred entry: 3','NULL','NULL','NULL','NULL', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 3.4.22.13','Deleted entry','NULL','NULL','NULL','NULL', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 3.4.22.14','Actinidain','Actinidia anionic protease','NULL','From the kiwi fruit or Chinese gooseberry (Actinidia chinensis).Belongs to peptidase family C1.','Specificity close to that of papain.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 3.4.22.15','Cathepsin L','NULL','NULL','A lysosomal enzyme that is readily inhibited by the diazomethaneinhibitor Z-Phe-Phe-CHN(2) or the epoxide inhibitor E-64.Belongs to peptidase family C1.','Specificity close to that of papain. As compared to cathepsin B,cathepsin L exhibits higher activity toward protein substrates, but haslittle activity on Z-Arg-Arg-NHMec, and no peptidyl-dipeptidase activity.', 'NULL', 'NULL');
INSERT INTO enzyme VALUES ( 'EC 3.4.22.16','Cathepsin H','Aleurain','NULL','Present in lysosomes of mammalian cells.Because of the combination of endopeptidase and aminopeptidaseactivity it can be considered as endoaminopeptidase.Belongs to peptidase family C1.','Hydrolysis of proteins, acting as an aminopeptidase (notably, cleavingArg-|-Xaa bonds) as well as an endopeptidase.', 'NULL', 'NULL');
